INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Signifor 0.9mg/ml 30 amp.', 26950, 'سيجنيفور 0.9مجم/مل 30 امبول', '18,699', NULL, 'Hypothalmic hormones.somatostatin analogues', 'Hypothalmic hormones.somatostatin analogues', NULL, 'Pasireotide', 'Novartis > novartis scientific office', 'Amp', 'أمبول', '0.9mg', '30', NULL, NULL, 'indication for the treatment of cushingï-s disease specifically for those patients whom pituitary surgery has not been curative or is not an option. mechanism of action pasireotide activates a broad spectrum of somatostatin receptors exhbiting a much high', NULL, 700, '2022-07-25', NULL, 0, 'For the treatment of Cushing’s disease, specifically for those patients whom pituitary surgery has not been curative or is not an option.', 'Pasireotide activates a broad spectrum of somatostatin receptors, exhbiting a much higher binding affinity for somatostatin receptors 1, 3, and 5 than octreotide in vitro, as well as a comparable binding affinity for somatostatin receptor 2. The binding and activation of the somatostatin receptors causes inhibition of ACTH secretion and results in reduced cortisol secretion in Cushing''s disease patients. Also this agent is more potent than somatostatin in inhibiting the release of human growth hormone (HGH), glucagon, and insulin. ', 'Signifor® is an analogue of somatostatin that promotes  reduced levels of cortisol secretion in Cushing''s disease patients.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Murnez nasal spray 20 ml', 13939, 'مورنيز سبراي بخاخ للانف 20 مل', '99', '75', 'Nasal care', 'Nasal care', NULL, 'Sodium chloride+potassium chloride+aloe vera gel', 'P & c labs', 'Spray', 'بخاخ', '20 ml', '1', NULL, NULL, NULL, NULL, 1619, '2025-10-13', NULL, 0, 'For the treatment of small uncomplicated varicose veins of the lower extremities that show simple dilation with competent valves.', 'Sodium tetradecyl sulfate is a potent toxin for endothelial cells in that brief exposure to even low concentrations are effective in stripping endothelium over a considerable distance and exposing highly thrombogenic endothelium in the process. Diluted sodium tetradecyl sulfate is also able to induce a hypercoagulable state, possibly by selective inhibition of protein C, and can also promote platelet aggregation.', 'Sodium tetradecyl sulfate is an anionic surfactant which occurs as a white, waxy solid. It is used as a sclerosing agent in sclerotherapy. Sclerotherapy is an injection of a sclerosing agent directly through the skin into a lesion and is used primarily for slow-flow vascular anomalies, particularly for venous malformation and lymphatic malformation. Intravenous injection causes intima inflammation and thrombus formation. This usually occludes the injected vein. Subsequent formation of fibrous tissue results in partial or complete vein obliteration that may or may not be permanent.', 'DrugBank');
