INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15591, 'Byetta 10mcg sol. for inj. prefilled pen', 'باييتا 10 ميكروجرام محلول للقحن اقلام معبأة', '675', NULL, 'Anti-diabetic', 'Exenatide', 'Eli lilly > elco for trading and marketing-egypt', 'Inj', 'حقن', '10mcg', '1', NULL, 'about exenatide antidiabetic agent new class agent mechanism of action of exenatide exenatide is an incretin mimic that acts as an agonist at glucagon-like peptide 1 (glp-1) receptor. it increases glucose-dependent insulin secretion by the pancreatic beta', NULL, NULL, 634, '2022-08-17', 0, 'Exenatide is indicated as an adjunct to diet and exercise to improve glycemic control in patients with type 2 diabetes. An extended-release formulation is available which is indicated in patients ≥10 years old,[L45118] while the immediate-acting formulation is approved only for adult patients.[L42690]', 'Exenatide is a human glucacon-like peptide-1(GLP-1) receptor agonist[Label]. By activating this receptor, insulin secretion is increased and glucagon secretion is decreased in a glucose dependant manner[Label]. Exenatide also slows gastric emptying and decreases food intake[Label]. These effects work synergistically to improve glycemic control by reducing the likelihood of hyper and hypoglycemia[Label].', 'When patients take exenatide the body''s natural response to glucose is modulated[Label]. More insulin and less glucagon are released in response to glucose, though in cases of hypoglycemia a normal amount of glucagon is released[Label]. Exenatide also slows gastric emptying, leading to a slower and prolonged release of glucose into the systemic circulation[Label]. Together these effects prevent hyper and hypoglycemia[Label].', 'DrugBank', 0, 0, 0);
