INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Cibacen 10 mg 7 tab', 2546, 'سيباسين 10مجم 7 اقراص', '19', NULL, 'Antihypertensive ace', 'Antihypertensive ace', NULL, 'Benazepril', 'Novartis', 'Tab', 'أقراص', '10 mg', '1', NULL, NULL, NULL, NULL, 989, '2022-09-22', NULL, 0, 'Benazepril is indicated for the treatment of hypertension[A840]. It may be used alone or in combination with thiazide diuretics[FDA Label].', 'Benazeprilat, the active metabolite of Benazepril, competes with angiotensin I for binding at the angiotensin-converting enzyme, blocking the conversion of angiotensin I to angiotensin II[FDA Label]. Inhibition of ACE results in decreased plasma angiotensin II[FDA Label]. As angiotensin II is a vasoconstrictor and a negative-feedback mediator for renin activity, lower concentrations result in a decrease in blood pressure and stimulation of baroreceptor reflex mechanisms, which leads to decreased vasopressor activity and to decreased aldosterone secretion[FDA Label].', 'Benazepril, an angiotensin-converting enzyme (ACE) inhibitor, is a prodrug which, when hydrolyzed by esterases to its active Benazeprilat[A836], is used to treat hypertension and heart failure, to reduce proteinuria and renal disease in patients with nephropathies, and to prevent stroke, myocardial infarction, and cardiac death in high-risk patients[A840,A838,A837]. Benazepril and Benazeprilat inhibit angiotensin-converting enzyme (ACE) in human subjects and animals[A840,A836]. ACE is a peptidyl dipeptidase that catalyzes the conversion of angiotensin I to the vasoconstrictor substance, angiotensin II[FDA Label]. Angiotensin II also stimulates aldosterone secretion by the adrenal cortex[FDA Label].', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Zetadidox 10/10 mg 30 tabs.', 2563, 'زيتاديتوكس 10/10مجم 30 قرص', '66', '37.5', 'Antiemetic', 'Antiemetic', NULL, 'Doxylamine succinate+pyridoxine hcl(vitamin b6)', 'Zeta pharma', 'Tab', 'أقراص', '10 mg', '3', NULL, NULL, NULL, '200406423018', 1864, '2025-01-20', NULL, 0, 'Used alone as a short-term sleep aid, in combination with other drugs as a night-time cold and allergy relief drug. Also used in combination with Vitamin B6 (pyridoxine) to prevent morning sickness in pregnant women.', 'Like other antihistamines, doxylamine acts by competitively inhibiting histamine at H1&nbsp;receptors. It also has substantial sedative and anticholinergic effects.', 'Doxylamine is an antihistamine commonly used as a sleep aid. This drug is also used to relieve symptoms of hay fever (allergic rhinitis), hives (rash or itching), and other allergic reactions. Doxylamine is a member of the ethanolamine class of antihistamines and has anti-allergy power far superior to virtually every other antihistamine on the market, with the exception of diphenhydramine (Benadryl). It is also the most powerful over-the-counter sedative available in the United States, and more sedating than many prescription hypnotics. In a study, it was found to be superior to even the barbiturate, phenobarbital for use as a sedative. Doxylamine is also a potent anticholinergic.', 'DrugBank');
