INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Ambroxol 75mg s.r 10 caps.', 13901, 'امبروكسول ممتد المفعول 75 10 كبسولة', '9', NULL, 'Mucolytic', 'Mucolytic', NULL, 'Ambroxol', 'Glaxo smithkline', 'Cap', 'كبسولة', '75mg', '1', NULL, NULL, '(indications): this medication is a mucolytic agent prescribed for various respiratory diseases such as emphysema with bronchitis pneumoconiosis chronic inflammatory pulmonary conditions tracheobronchitis (respiratory tract inflammation) bronchiectasis br', '6221045001492', 1182, '2022-07-07', NULL, 0, 'Ambroxol is indicated for secretolytic therapy in bronchoplmonary disease with abnormal mucus secretion and transport. It allows the mucus to be more easily cleared and ease a patient''s breathing.', 'Ambroxol is a mucolytic agent. Excessive Nitric oxide (NO) is associated with inflammatory and some other disturbances of airways function. NO enhances the activation of soluble guanylate cyclase and cGMP accumulation. Ambroxol has been shown to inhibit the NO-dependent activation of soluble guanylate cyclase. It is also possible that the inhibition of NO-dependent activation of soluble guanylate cyclase can suppress the excessive mucus secretion, therefore it lowers the phlegm viscosity and improves the mucociliary transport of bronchial secretions.', NULL, 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Cisplatin-ebewe 25mg/50ml vial(n/a)', 2668, 'سيسبلاتين ايبوي 25مجم فيال', '30', NULL, 'Antineoplastic', 'Antineoplastic', NULL, 'Cisplatin', 'Hospira inc.-uk > pfizer scientific office', 'Vial', 'فيال', '25mg', '1', NULL, NULL, 'description cisplatin cisplatinum or cis-diamminedichloroplatinum(ii) (cddp) is a platinum-based chemotherapy drug used to treat various types of cancers including sarcomas some carcinomas (e.g. small cell lung cancer and ovarian cancer) lymphomas and ger', NULL, 814, '2022-09-22', NULL, 0, 'For the treatment of metastatic testicular tumors, metastatic ovarian tumors and advanced bladder cancer.', 'Alkylating agents work by three different mechanisms: 1) attachment of alkyl groups to DNA bases, resulting in the DNA being fragmented by repair enzymes in their attempts to replace the alkylated bases, preventing DNA synthesis and RNA transcription from the affected DNA, 2) DNA damage via the formation of cross-links (bonds between atoms in the DNA) which prevents DNA from being separated for synthesis or transcription, and 3) the induction of mispairing of the nucleotides leading to mutations.', 'Cisplatin is an antineoplastic in the class of alkylating agents and is used to treat various forms of cancer. Alkylating agents are so named because of their ability to add alkyl groups to many electronegative groups under conditions present in cells. They stop tumor growth by cross-linking guanine bases in DNA double-helix strands - directly attacking DNA. This makes the strands unable to uncoil and separate. As this is necessary in DNA replication, the cells can no longer divide. In addition, these drugs add methyl or other alkyl groups onto molecules where they do not belong which in turn inhibits their correct utilization by base pairing and causes a miscoding of DNA. Alkylating agents are cell cycle-nonspecific. Alkylating agents work by three different mechanisms all of which achieve the same end result - disruption of DNA function and cell death.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Mirablad xr 25 mg 10 f.c. tabs.', 22818, 'ميرابلاد اكس ار 25مجم 10 اقراص', '64', NULL, 'Overactive bladder', 'Overactive bladder', NULL, 'Mirabegron', 'Rameda', 'Tab', 'أقراص', '25 mg', '1', 'علاج حالات فرط نشاط المثانة', NULL, 'uses: overactive bladder management of overactive bladder for relief of symptoms associated with voiding (e.g. urge urinary incontinence urgency frequency). how to manage your overactive bladder may be an appropriate alternative in patients with overactiv', NULL, 1754, '2022-05-19', NULL, 0, 'Mirabegron is indicated for the treatment of overactive bladder (OAB) - with symptoms of urge urinary incontinence, urgency, and urinary frequency - either alone or in combination with [solifenacin].[L32853] It is also indicated for the treatment of neurogenic detrusor overactivity (NDO) in pediatric patients 3 years of age and older and weighing 35kg or more.[L32853]', 'Mirabegron is a potent and selective agonist of beta-3 adrenergic receptors. The activation of beta-3 receptors relaxes detrusor smooth muscle during the storage phase of the urinary bladder fill-void cycle, which increases the bladder''s storage capacity thereby alleviating feelings of urgency and frequency.[L32853]', 'Mirabegron exerts its pharmacologic effects by forcing bladder smooth muscle to relax, thereby expanding its capacity and relieving urgency. Mirabegron does not appear to adversely affect the mean maximum flow rate or mean detrusor pressure at maximum flow rate in patients with lower urinary tract symptoms and bladder outlet obstruction (BOO), but should be used with in patients with BOO due to reports of significant urinary retention. Furthermore, mirabegron increases both blood pressure and heart rate in a dose-dependent manner and should therefore be used with caution in patients with severely uncontrolled hypertension or others for whom these increases may prove dangerous.[L32853]', 'DrugBank');
