INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (13692, 'Alexodiab 500 mg 30 tabs', 'اليكسودياب 500 مجم 30 قرص', '12', NULL, 'Anti-diabetic.sensitizers.biguanide', 'Metformin hydrochloride', 'Alexandria', 'Tab', 'أقراص', '500 mg', '3', 'علاج مرضى السكري', 'about metformin biguanide derivative oral anti-diabetic. mechanism of action of metformin it is a biguanide which exerts antidiabetic action. the drug suppresses gluconeogenesis in liver and thus suppresses hepatic glucose output. it enhance insulin media', NULL, NULL, 849, '2022-05-23', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Metformin is a biguanide antihyperglycemic agent and first-line pharmacotherapy used in the management of type II diabetes.[L12207,A176173] 

Metformin is considered an antihyperglycemic drug because it lowers blood glucose concentrations in type II diabetes without causing hypoglycemia. It is commonly described as an "insulin sensitizer", leading to a decrease in insulin resistance and a clinically significant reduction of plasma fasting insulin levels.[A176173] Another well-known benefit of this drug is modest weight loss, making it an effective choice for obese patients type II diabetes.[A36559]

Metformin was first approved in Canada in 1972,[A36552] and received subsequent FDA approval in the US in 1995.[L12207]', '["A10BD23", "A10BD02", "A10BD18", "A10BD11", "A10BD25", "A10BA02", "A10BD22", "A10BD14", "A10BD16", "A10BD17", "A10BD05", "A10BD15", "A10BD07", "A10BD10", "A10BD13", "A10BD26", "A10BD20", "A10BD08", "A10BD27", "A10BD03"]', '[]');
