INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15851, 'Cardiotimide 20mg/2ml 3 amp', 'كارديوتيمايد 20مجم/2مل 3 امبولات', '27', NULL, 'Diuretic.loop', 'Torsemide', 'Biopharm egypt > andalous pharma', 'Amp', 'أمبول', '20mg', '3', NULL, 'about torsemide pyridine-sulfonylurea class loop diuretic antihypertensive in chf diuretic. mechanism of action of torsemide torsemide is a high ceiling loop diuretic which exerts it`s diuretic action by acting at site-2(thick ascending loop of henle). it', NULL, NULL, 791, '2023-01-12', 0, NULL, NULL, NULL, NULL, 0, 0, 0);
