INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15926, 'Casodex 50 mg 28 tabs.', 'كازودكس 50 مجم 28 قرص', '940', '720', 'Antineoplastic', 'Bicalutamide', 'Astra zeneca', 'Tab', 'أقراص', '50 mg', '1', NULL, NULL, '6223003270223', NULL, 1761, '2024-12-04', 0, 'Bicalutamide is indicated in combination with a luteinizing hormone-releasing hormone (LHRH) agonist for the treatment of Stage D2 metastatic carcinoma of the prostate.[L40478]', 'Bicalutamide competes with androgen for the binding of androgen receptors, consequently blocking the action of androgens of adrenal and testicular origin which stimulate the growth of normal and malignant prostatic tissue.', 'Bicalutamide is an antineoplastic hormonal agent primarily used in the treatment of prostate cancer. Bicalutamide is a pure, nonsteroidal anti-androgen with affinity for androgen receptors (but not for progestogen, estrogen, or glucocorticoid receptors). Consequently, Bicalutamide blocks the action of androgens of adrenal and testicular origin which stimulate the growth of normal and malignant prostatic tissue. Prostate cancer is mostly androgen-dependent and can be treated with surgical or chemical castration. To date, antiandrogen monotherapy has not consistently been shown to be equivalent to castration.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15928, 'Castor oil mepaco 60 ml', 'زيت خروع ميباكو 60 مل', '30', NULL, 'Laxative', 'Castor oil', 'Mepaco', 'Oil', 'زيت', '60 ml', '1', 'ملين -- علاج حالات الامساك الشديدة -- يستخدم كشربة لافراغ المعدة والامعاء من الفضلات لاجراء التشخيص او العمليات الجراحية', NULL, '6222018805079', NULL, 1755, '2022-12-03', 0, 'Indicated for over-the-counter use as a laxative for oral use and as a soothing agent for topical use on skin and hair. ', 'Castor oil is a mix of triglycerides consisting of mainly ricinolein, linoleic acid, oleic acid, palmitic acid, stearic acid, dihydroxystearic acid, and traces of other fatty acids [F70]. The main pharmacodynamic effects of castor oil are mediated by ricinoleic acid, a hydroxylated fatty acid released from castor oil by intestinal lipases [A33043]. It was believed that ricinoleic acid acts as an anionic surfactant that reduces net absorption of fluid and electrolytes, and stimulates intestinal peristalsis [F70]. However, a recent study suggests that ricinoleic acid interacts with EP3 prostanoid receptors expressed on intestinal and uterine smooth muscles. Via activating EP3 prostanoid receptors on intestinal and uterine smooth muscle cells, ricinoleic acid promotes laxation and uterus contraction, respectively [A33043]. EP3 receptor act as the major prostanoid receptor in the intestine mediating propulsive effects on gut motility, and activation of EP3 receptors has been demonstrated to evoke contraction of uterine smooth muscle [A33043]. ', 'Castor oil is a potent laxative that was shown to be effective for short-term constipation. In a prospective study, the group receiving oral castor oil was associated with a higher likelihood of initiation of labour compared to the placebo group [A33050]. Castor oil is known to induce diarrhea, and has been used in studies to assess anti-diarrheal effect of some compounds [F70]. ', 'DrugBank', 0, 0, 0);
