INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (16227, 'Chemicetrizine 5 mg 20 f.c. tab.', 'كيميستريزين 5 مجم 20 قرص', '17', NULL, 'Anti-histamine.non-sedative.third-generation', 'Levocetirizine', 'Egpi > chemidica', 'Tab', 'أقراص', '5 mg', '2', 'مضاد للحساسية لعلاج حالات حساسية الانف و الارتيكاريا.', 'about levocetirizine a third-generation non-sedative h1 antagonist antihistamine. mechanism of action of levocetirizine this active enantiomer of cetrizine is a potent nonsedative and selective h1 receptor antagonist. it antagonizes the allergic symptoms', NULL, NULL, 1482, '2022-07-20', 0, 'Levocetirizine is indicated to treat symptoms of perennial allergic rhinitis and uncomplicated skin manifestations of chronic idiopathic urticaria.[L7694] It is also used over the counter for a variety of mild allergy symptoms.[A181748]', 'Levocetirizine selectively inhibits histamine H<sub>1</sub> receptors.[L7694] This action prevents histamine from activating this receptor and causing effects like smooth muscle contraction, increased permeability of vascular endothelium, histidine uptake in basophils, stimulation of cough receptors, and stimulation of flare responses in the nervous system.[A181790]', 'Levocetirizine is a second generation histamine H<sub>1</sub> antagonist used to treat various allergic symptoms.[A181748,A181790,L7694] It has a long duration of action as it is generally taken once daily, and a wide therapeutic window as animal studies show the maximal nonlethal dose is over 100x a normal dose.[L7694] Patients are cautioned to avoid tasks that require complete alertness, avoid alertness, and use caution in patients with factors predisposing urinary retention.[L7694]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (16228, 'Chemitrijulie 120 mg 30 caps.', 'كيميتريجولي 120 مجم 30 كبسولة', '100', NULL, 'Weight loss', 'Orlistat', 'Egpi > chemidica-egypt', 'Cap', 'كبسولة', '120 mg', '3', NULL, 'about orlistat anti-obese drug. saturated derivative of lipstatin. mechanism of action of orlistat its primary function is preventing the absorption of fats from the human diet thereby reducing caloric intake.orlistat works by inhibiting gastric and pancr', NULL, NULL, 965, '2022-07-15', 0, 'Orlistat is indicated for obesity management including weight loss and weight maintenance when used in combination with calorie reduction in overweight and obese adults; this indication applies to both the prescription formulation of 120 mg[L11130] and the over-the-counter formulation of 60 mg.[L31963] Orlistat in the 120 mg prescription formulation is also indicated to reduce the risk of weight regain following weight loss.[L11130]', 'Orlistat is a potent and selective inhibitor of various lipase enzymes responsible for the metabolism of fat. It acts in the gastrointestinal (GI) tract via covalent binding to the serine residues located on the active site of both gastric and pancreatic lipase. When orlistat is taken with food containing fat, it partially inhibits the hydrolysis of triglycerides. This decreases absorption of monoaclglycerides and free fatty acids, contributing to weight maintenance and weight loss.[A37653,L11130]', 'Orlistat helps with weight reduction and maintenance by inhibiting the absorption of dietary fats via the inhibition of lipase enzymes.[A1734,A1735]', 'DrugBank', 0, 0, 0);
