INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (14667, 'Rotadansetron 8mg/4ml 5 i.m./i.v./inf. amp.', 'روتا دانسيترون 8مجم4 مل 5 امبول وريد .عضل', '265', '240', 'Antiemetic', 'Ondansetron', 'Rotabiogen', 'Amp', 'أمبول', '8mg', '5', NULL, NULL, '6224009665518', NULL, 1017, '2025-10-13', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'A competitive serotonin type 3 receptor antagonist. It is effective in the treatment of nausea and vomiting caused by cytotoxic chemotherapy drugs, including cisplatin, and has reported anxiolytic and neuroleptic properties.

Having been developed in the 1980s by GlaxoSmithKline and approved by the US FDA since January 1991, ondansetron has demonstrated a long history of use and efficacy. Commonly formulated as oral tablets, orally disintegrating tablets (ODT), and injections, and available as generic products as well, ondansetron continues to see contemporary innovations in its formulation and use, including the development of orally soluble films that are both discreet in administration and less of a burden in comparison to having patients attempt to swallow pills during emesis.[L5221]

