INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (14784, 'Azalide 200mg/5ml susp. 15ml', 'ازالايد 200 مجم / 5مل معلق 15 مل', '53', '30.75', 'Antibiotic.macrolide', 'Azithromycin', 'T3a pharma', 'Susp', 'معلق', '200mg', '1', NULL, 'description: azithromycin is a semi-synthetic macrolide antibiotic of the azalide class. dose: adult: po- respiratory tract skin and soft tissue infections- 500 mg once daily for 3 days. or 500mg on day 1 followed by 250mg on days 2 to 5. typhoid- 500 mg', '6221014000600', NULL, 2145, '2025-07-10', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Azithromycin is a broad-spectrum macrolide antibiotic with a long half-life and a high degree of tissue penetration [A174172]. It was initially approved by the FDA in 1991 [A174175].

It is primarily used for the treatment of respiratory, enteric and genitourinary infections and may be used instead of other macrolides for some sexually transmitted and enteric infections. It is structurally related to erythromycin [A174169].

Azithromycin [9-deoxo-9a-aza-9a-methyl-9a-homoerythromycin] is a part of the _azalide_ subclass of macrolides, and contains a 15-membered ring, with a methyl-substituted nitrogen instead of a carbonyl group at the 9a position on the aglycone ring, which allows for the prevention of its metabolism. This differentiates azithromycin from other types of macrolides [A174175].

