INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Deponit nt 5 - 7 patches.', 3425, 'ديبونيت 7 قطع لصقة 5 مجم', '26', NULL, 'Anti-ischemic', 'Anti-ischemic', NULL, 'Nitroglycerin', 'Schwarz > mina pharm', 'Patch', 'لصقة', NULL, '1', NULL, NULL, 'indication for the prevention of angina pharmacodynamics nitroglycerin an organic nitrate is available in many forms as a vasodilator. nitroglycerin is used in the treatement of angina pectoris and perioperative hypertension to produce controlled hypotens', '6222003701287', 1036, '2022-12-04', NULL, 0, 'Sublingual nitroglycerin is indicated for the acute relief of an attack or acute prophylaxis of angina pectoris due to coronary artery disease.[L7102,L38369,L42320] Transdermal nitroglycerin is indicated for the prevention of angina pectoris due to coronary artery disease.[L7141]



Intravenous nitroglycerin is indicated for the treatment of peri-operative hypertension; for control of congestive heart failure in the setting of acute myocardial infarction; for treatment of angina pectoris in patients who have not responded to sublingual nitroglycerin and beta (β)-blockers; and for induction of intraoperative hypotension.[L7099] 



Topical nitroglycerin ointment is used to treat moderate to severe pain associated with chronic anal fissure.[L42445]', 'Nitroglycerin is converted by mitochondrial aldehyde dehydrogenase in smooth muscle cells to nitric oxide (NO), a potent vasodilator. NO activates the enzyme guanylate cyclase, which converts guanosine triphosphate (GTP) to cyclic guanosine 3'',5''-monophosphate (cGMP) in vascular smooth muscle and other tissues.[A180166,A180169,A249970] cGMP is an endogenous vasodilator of vascular smooth muscle:[A180172] it causes protein kinase-dependent phosphorylation and activates downstream cascades that promote relaxation and increased blood flow in veins, arteries and cardiac tissue.[A180172,A249970] An _in vitro_ study using mouse aorta suggests that nitric oxide, an active metabolite of nitroglycerin, targets the natriuretic peptide receptors.[A5526] ', 'Nitroglycerin causes the relaxation of vascular smooth muscles, causing arteriolar and venous dilatation.[L38369] It increases blood flow to the myocardium and reduces cardiac preload and afterload, decreasing myocardial wall stress and ameliorating anginal symptoms.[A180172,A249970,L7102] Nitroglycerin also reduces coronary artery spasm, decreasing systemic vascular resistance as well as systolic and diastolic blood pressure.[L38369]



Like other organic nitrates, repeated and prolonged administration of nitroglycerin can lead to the development of tolerance or desensitization of vascular smooth muscle to further nitroglycerin-induced vasorelaxation. This loss of efficacy may be associated with the inhibition of mitochondrial aldehyde dehydrogenase, which is an important enzyme involved in the bioactivation of nitroglycerin.[A180166,A180169,A180172] Nitroglycerin tolerance may be accompanied by pro-oxidant effects, endothelial dysfunction, and increased sensitivity to vasoconstrictors.[A180172]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Treato hair serum 80 ml', 5001, 'تريتوسيروم للشعر 80 مل', '180', '155', 'Hair care', 'Hair care', NULL, 'Silicone oil 98.1%+jojoba oil+olive extract+rosemary oil+snake oil+vitamin e', 'Infinity pharm', 'Serum', 'سيروم', '80 ml', '1', NULL, NULL, NULL, NULL, 808, '2025-06-30', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Salbovent 2mg/5ml syrup 120ml', 26460, 'سالبوفنت 2مجم/5مل شراب 120مل', '24', '16.5', 'Bronchodilator', 'Bronchodilator', NULL, 'Salbutamol', 'Alexandria', 'Syrup', 'شراب', '2mg', '1', NULL, NULL, 'about salbutamol beta2-adrenergic agonist phenethylamine derivative bronchodilator(antiasthma) uterine relaxant. mechanism of action of salbutamol salbutamol is a short acting ?2 receptor agonist. it selectively stimulates ?2 receptors present in airway u', '6223000171158', 2846, '2024-11-12', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Silymarin zinc 30 caps.', 26990, 'سليمارين زنك 30 كبسولة', '21', NULL, 'Liver support supplements', 'Liver support supplements', NULL, 'Silymarin+zinc', 'Arab caps', 'Cap', 'كبسولة', NULL, '3', NULL, NULL, 'about silymarin a polyphenolic flavonoid hepatoprotective(antihepatotoxic) antioxidant. mechanism of action of silymarin silymarin stimulates rna polymerase a and results in an increase in ribosomal protein synthesis and leads to the synthesis of new hepa', '6221042200065', 1763, '2022-12-08', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Eaz-x gel 50 ml', 18008, 'ايز-اكس جل 50 مل', '50', NULL, 'Lubricant', 'Lubricant', NULL, NULL, 'Apple pharma > health line international', 'Gel', 'جل', '50 ml', '1', 'جل يعمل كمزلق طبي', NULL, 'water-based and water soluble personal lubricating gel', NULL, 2712, '2022-04-14', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Epival 10mg/2ml 3 amp.', 18469, 'ابيفال 10 مجم /2مل 3 امبولات', '15', '5', 'Anti-epileptic.benzodiazepines', 'Anti-epileptic.benzodiazepines', NULL, 'Diazepam', 'Eipico', 'Amp', 'أمبول', '10mg', '6', 'علاج حالات الصرع -- تقليل للتشنجات المصاحبة للصرع', NULL, 'about diazepam a benzodiazepine derivative with cns depressant anxiolytic anticonvulsant hypnotic sedative skeletal muscle relaxant and amnestic. mechanism of action of diazepam diazepam mainly acts on limbic system and ascending reticular formation in th', NULL, 1279, '2024-11-06', NULL, 0, 'In general, diazepam is useful in the symptomatic management of mild to moderate degrees of anxiety in conditions dominated by tension, excitation, agitation, fear, or aggressiveness such as may occur in psychoneurosis, anxiety reactions due to stress conditions, and anxiety states with somatic expression.[F3160]



Moreover, in acute alcoholic withdrawal, diazepam may be useful in the symptomatic relief of acute agitation, tremor, and impending acute delirium tremens.[F3160]



Furthermore, diazepam is a useful adjunct for the relief of skeletal muscle spasm due to reflex spasm to local pathologies, such as inflammation of the muscle and joints or secondary to trauma; spasticity caused by upper motor neuron disorders, such as cerebral palsy and paraplegia; athetosis and the rare "stiff man syndrome".[F3160]



Particular label information from the United Kingdom also lists particular age-specific indications, including for adults: (1) The short-term relief (2-4 weeks) only, of anxiety which is severe, disabling, or subjecting the individual to unacceptable distress, occurring alone or in association with insomnia or short-term psychosomatic, organic or psychotic illness, (2) cerebral palsy, (3) muscle spasm, (4) as an adjunct to certain types of epilepsy (eg. myoclonus), (5) symptomatic treatment of acute alcohol withdrawal, (6) as oral premedication for the nervous dental patient, and (7) for premedication before surgery.[L5188]



In the same UK label information, diazepam is indicated in children for: (1) control of tension and irritability in cerebral spasticity in selected cases, (2) as an adjunct to the control of muscle spasm in tetanus, and for (3) oral premedication.[L5188]



A diazepam nasal spray is indicated in patients 6 years and older to treat intermittent, stereotypic episodes of frequent seizure activity that are different than the patient''s usual seizure pattern.[L11247]', 'Diazepam is a benzodiazepine tranquilliser with anticonvulsant, sedative, muscle relaxant and amnesic properties [F3157, F3160, L5188].



Benzodiazepines, such as diazepam, bind to receptors in various regions of the brain and spinal cord. This binding increases the inhibitory effects of gamma-aminobutyric acid (GABA) [F3157, F3160, L5188]. GABAs functions include CNS involvement in sleep induction. Also involved in the control of hypnosis, memory, anxiety, epilepsy and neuronal excitability [F3157, F3160, L5188].', 'Diazepam is a benzodiazepine that exerts anxiolytic, sedative, muscle- relaxant, anticonvulsant and amnestic effects [F3157, F3160, L5188]. Most of these effects are thought to result from facilitation of the action of gamma aminobutyric acid (GABA), an inhibitory neurotransmitter in the central nervous system [F3157, F3160, L5188].', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Olanvexa 10 mg 30 orally disintegrating tabs.', 16782, 'اولانفيكسا 10مجم 30 قرص', '201', '150', 'Psychiatric.antipsychotics', 'Psychiatric.antipsychotics', NULL, 'Olanzapine', 'Multicare', 'Tab', 'أقراص', '10 mg', '3', NULL, NULL, NULL, NULL, 2430, '2025-08-10', NULL, 0, 'Olanzapine was initially used orally and intramuscularly for the chronic treatment of schizophrenia in patients over 13 years old and other psychiatric disorders such as bipolar I disorder including mixed or manic episodes.[A177014] 



Olanzapine is also indicated, in combination with lithium or valproate for the short-term treatment of acute manic or mixed episodes associated with bipolar I disorder in adults.[FDA label]



As well, olanzapine is indicated, in combination with fluoxetine for the treatment of episodes of depression associated with bipolar disorder type 1 and treatment-resistant depression in patients over 10 years old.[A177014]



Olanzapine is also approved for the management of psychomotor agitation associated with schizophrenia and bipolar I mania.[FDA label]



Schizophrenia is a complex biochemical brain disorder that affects the person''s ability to differentiate reality. It is usually observed as the presence of delusions, hallucinations, social withdrawal and disturbed thinking.[L5936]



Bipolar disorder is a mental health condition defined by periods of extreme mood disturbances. It is categorized in different types from which type 1 is known to involve episodes of severe mania and often depression while type 2 presents less severe forms of mania.[L5939]



Olanzapine is also indicated in combination with [samidorphan] for the treatment of bipolar I disorder, either as an adjunct to lithium or valproate or as monotherapy for the acute treatment of manic or mixed episodes or as maintenance therapy, and for the treatment of schizophrenia in adults.[L34359]', 'The activity of olanzapine is achieved by the antagonism of multiple neuronal receptors including the dopamine receptor D1, D2, D3 and D4 in the brain, the serotonin receptors 5HT2A, 5HT2C, 5HT3 and 5HT6, the alpha-1 adrenergic receptor, the histamine receptor H1 and multiple muscarinic receptors.[A176993, A176999]



As abovementioned, olanzapine presents a wide profile of targets, however, its antagonistic effect towards the dopamine D2 receptor in the mesolimbic pathway is key as it blocks dopamine from having a potential action at the post-synaptic receptor. The binding of olanzapine to the dopamine D2 receptors is easily dissociable and hence, it allows for a certain degree of dopamine neurotransmission.[A177014]



On the other hand, olanzapine acts in the serotonin 5HT2A receptors in the frontal cortex in a similar manner than the reported on dopamine D2 receptors. This determined effect allows for a decrease in adverse effects.[A177014]', 'The effect of olanzapine in the D2 receptor is reported to produce the positive effects of this drug such as a decrease in hallucinations, delusions, disorganized speech, disorganized thought, and disorganized behavior. On the other hand, its effect on the serotonin 5HT2A receptor prevents the onset of anhedonia, flat affect, alogia, avolition and poor attention.[A177014] Based on the specific mechanism of action, olanzapine presents a higher affinity for the dopamine D2 receptor when compared to the rest of the dopamine receptor isotypes. This characteristic significantly reduces the presence of side effects.[T554]



Clinical trials for the original use of olanzapine demonstrated significant effectiveness in the treatment of schizophrenia and bipolar disorder in adults and acute manic or mixed episodes associated with bipolar disorder in adolescents.[A177008]



The effect of olanzapine on dopamine and serotonin receptors has been suggested to reduce chemotherapy-induced nausea and vomiting as those receptors are suggested to be involved in this process. For this effect, several clinical trials have been conducted and it has been shown that olanzapine can produce a significant increase in total control of nausea and vomiting.[A176993] In a high-level study of the effect of olanzapine for this condition, a complete response on the delay phase was observed in 84% of the individual and control of emesis of over 80% despite the phase.[A176999]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Biomox 250mg vial', 1553, 'بيوموكس 250مجم فيال', '4', NULL, 'Penicillins.monocomponent', 'Penicillins.monocomponent', NULL, 'Amoxicillin', 'Sedico', 'Vial', 'فيال', '250mg', '1', NULL, NULL, '(indications): is a penicillin-like beta-lactam antibiotic. it is used to treat bacterial infections caused by susceptible microorganisms. it prevents the bacterial growth and is used in the treatment of a number of infections such as pneumonia bronchitis', NULL, 997, '2023-05-19', NULL, 0, 'Amoxicillin alone is indicated to treat susceptible bacterial infections of the ear, nose, throat, genitourinary tract, skin, skin structure, and lower respiratory tract.[L11656,L11659] Amoxicillin is given with calvulanic acid to treat acute bacterial sinusitis, community acquired pneumonia, lower respiratory tract infections, acute bacterial otitis media, skin and skin structure infections, and urinary tract infections.[L11650,L7880,L11653] Amoxicillin is given with omeprazole in the treatment of _Helicobacter pylori_ (_H. pylori_) infection.[L9743,L11647]



Amoxicillin is used in combination with [vonoprazan] and [clarithromycin] as co-packaged triple therapy or in combination with [vonoprazan] as co-packaged dual therapy to treat _H. pylori_ infection in adults.[L41695]', 'Amoxicillin competitively inhibits penicillin-binding protein 1 and other high molecular weight penicillin binding proteins.[A6205,A190678]  Penicillin bind proteins are responsible for glycosyltransferase and transpeptidase reactions that lead to cross-linking of D-alanine and D-aspartic acid in bacterial cell walls.[A190678] Without the action of penicillin binding proteins, bacteria upregulate autolytic enzymes and are unable to build and repair the cell wall, leading to bacteriocidal action.[A190642,A190678]', 'Amoxicillin competitively inhibit penicillin binding proteins, leading to upregulation of autolytic enzymes and inhibition of cell wall synthesis.[A6205,A190678,A190642] Amoxicillin has a long duration of action as it is usually given twice daily.[L7880] Amoxicillin has a wide therapeutic range as mild overdoses are not associated with significant toxicity.[L11650] Patients should be counselled regarding the risk of anaphylaxis, _Clostridium difficile_ infections, and bacterial resistance.[L11656]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Remtozar-d 25/100mg 10 f.c.tab.', 25863, 'ريمتوزار دي 25/100مجم 10 اقراص', '35', NULL, 'Antihypertensive. combined angiotensin blocker with diuretic', 'Antihypertensive. combined angiotensin blocker with diuretic', NULL, 'Hydrochlorothiazide+losartan', 'Mepaco > spimaco misr', 'Tab', 'أقراص', '100mg', '1', NULL, NULL, 'about hydrochlorothiazide thiazide derivatibve diuretic. mechanism of action of hydrochlorothiazide it is thiazide diuretic which exerts its action by acting at site-3(central dilating segment of early distal tubule). it binds to na+cl- symporter and inhi', NULL, 683, '2022-07-12', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Levotavin 500 mg 7 f.c.tabs.', 21772, 'ليفوتافين 500مجم 7 قرص', '92', '64', 'Antibiotic.quinolone', 'Antibiotic.quinolone', NULL, 'Levofloxacin', 'Pharco', 'Tab', 'أقراص', '500 mg', '1', 'مضاد حيوي', NULL, 'about levofloxacin third generation fluoroquinolone (or quinolone) a broad spectrum antiinfective. mechanism of action of levofloxacin levofloxacin is a broad spectrum antibacterial agent. it act by inhibiting the enzyme dna gyrase (topoisomerase 2) and t', '6221151003786', 5411, '2024-08-27', NULL, 0, 'In oral and intravenous formulations, levofloxacin is indicated in adults for the treatment of various infections caused by susceptible bacteria, including infections of the upper respiratory tract, lower respiratory tract, skin, skin structures, urinary tract, and prostate.[L11638,L11692] The oral formulation is also indicated in both adults and children 6 months of age and older for the post-exposure management of inhalational anthrax caused by _Bacillus anthracis_ and for the treatment and/or prophylaxis of plague caused by _Yersinia pestis_.[L11638]



In its ophthalmic formulation, levofloxacin is indicated for the treatment of bacterial conjunctivitis caused by susceptible organisms.[L11641] An inhalational solution available in Canada is indicated for the management of cystic fibrosis patients aged 18 years or older with chronic pulmonary _Pseudomonas aeruginosa_ infections.[L11689]', 'Levofloxacin, like other fluoroquinolone antibiotics, exerts its antimicrobial activity via the inhibition of two key bacterial enzymes: DNA gyrase and topoisomerase IV.[L11638] Both targets are type II topoisomerases, but have unique functions within the bacterial cell. DNA gyrase is an enzyme found only in bacteria that introduces negative supercoils into DNA during replication - this helps to relieve torsional strain caused by the introduction of positive supercoils during replication, and these negative supercoils are essential for chromosome condensation and the promotion of transcription initiation.[A31453] It is comprised of four subunits (two A subunits and two B subunits) of which the A subunits appear to be the target of fluoroquinolone antibiotics.[A190663] Bacterial topoisomerase IV, in addition to contributing to the relaxation of positive supercoils, is essential at the terminal stages of DNA replication and functions to “unlink” newly replicated chromosomes to allow for the completion of cell division.[A31453]



Inhibition of these enzymes by levofloxacin likely occurs via complexation with the topoisomerase enzymes.[A31453] The end result is a blockade of DNA replication, thus inhibiting cell division and resulting in cell death.', 'Levofloxacin is bactericidal and exerts its antimicrobial effects via inhibition of bacterial DNA replication.[L11638] It has a relatively long duration of action in comparison with other antibiotics that allows for once or twice daily dosing. Levofloxacin is associated with QTc-interval prolongation and should be used with caution in patients with other risk factors for prolongation (e.g. hypokalemia, concomitant medications).[L11638]



Levofloxacin has demonstrated _in vitro_ activity against a number of aerobic gram-positive and gram-negative bacteria and may carry some activity against certain species of anaerobic bacteria[L11638] and other pathogens such as _Chlamydia_ and _Legionella_.[A190663] Resistance to levofloxacin may develop, and is generally due to mutations in DNA gyrase or topoisomerase IV, or via alterations to drug efflux.[L11638,A31453] Cross-resistance may occur between levofloxacin and other fluoroquinolones, but is unlikely to develop between levofloxacin and other antibiotic classes (e.g. macrolides) due to significant differences in chemical structure and mechanism of action.[L11638]



As antimicrobial susceptibility patterns are geographically distinct, local antibiograms should be consulted to ensure adequate coverage of relevant pathogens prior to use.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Ciprapro 10mg 30 f.c. tab', 2618, 'سيبرابرو 10مجم 30 قرص', '237', '180', 'Psychiatric.antidepressants', 'Psychiatric.antidepressants', NULL, 'Escitalopram', 'Marcyrl co. > copad pharma', 'Tab', 'أقراص', '10mg', '3', 'علاج للاكتئاب', NULL, NULL, '6224008933137', 5295, '2024-06-25', NULL, 0, 'Escitalopram is indicated for the acute and maintenance treatment of major depressive disorder (MDD) in adults and pediatric patients 12 years old and older and for the acute treatment of generalized anxiety disorder (GAD) in adults and pediatric patients 7 years old and older.[L8513,L8513] It is additionally indicated for symptomatic relief of obsessive-compulsive disorder (OCD) in Canada.[L8516]', 'Escitalopram, like other selective serotonin re-uptake inhibitors, enhances serotonergic activity by binding to the orthosteric (i.e. primary) binding site on the serotonin transporter (SERT), the same site to which endogenous 5-HT binds, and thus prevents the re-uptake of serotonin into the presynaptic neuron.[A185420,A39738,A185726] Escitalopram, along with [paroxetine], is also considered an allosteric serotonin re-uptake inhibitor - it binds to a secondary allosteric site on the SERT molecule to more strongly inhibit 5-HT re-uptake. Its combination of orthosteric and allosteric activity on SERT allows for greater extracellular 5-HT levels, a faster onset of action, and greater efficacy as compared to other SSRIs. [A185825,A185726] The sustained elevation of synaptic 5-HT eventually causes desensitization of 5-HT<sub>1A</sub> auto-receptors, which normally shut down endogenous 5-HT release in the presence of excess 5-HT - this desensitization may be necessary for the full clinical effect of SSRIs and may be responsible for their typically prolonged onset of action.[A185828,A185726]



Escitalopram has shown little-to-no binding affinity at a number of other receptors, such as histamine and muscarinic receptors, and minor activity at these off-targets may explain some of its adverse effects.[L8513,L8516,L8522,A185726]', 'Escitalopram belongs to a class of medications called selective serotonin re-uptake inhibitors (SSRIs). These agents cause an increase in serotonin levels in neuronal synapses by preventing the re-uptake of serotonin (5-HT) into the presynaptic terminals of serotonergic neurons.[A185420,A39738,L8513,L8516] As compared to other SSRIs, it appears to have a relatively quick onset of effect due to its potency.[A185819,A185822]



SSRIs as a class have been associated with abnormal bleeding, particularly in patients receiving concomitant therapy with other medications affecting hemostasis, and with the development of serotonin syndrome. Use escitalopram with caution in patients with a higher-than-baseline risk of bleeding and in patients receiving concomitant therapy with other serotonergic drugs. Escitalopram may also cause a discontinuation syndrome with abrupt removal of the drug, and should be slowly tapered if discontinuation of therapy is warranted.[L8513,L8516,L8522]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Kemocarb 10mg/ml 150mg vial', 21306, 'كيموكارب 10 مجم / مل 150 مجم فيال', '120', NULL, 'Antineoplastic', 'Antineoplastic', NULL, 'Carboplatin', 'Fresenius kabi > egyptian group for import and export', 'Vial', 'فيال', '10mg', '1', NULL, NULL, 'description an organoplatinum compound that possesses antineoplastic activity. indication for the initial treatment of advanced ovarian carcinoma in established combination with other approved chemotherapeutic agents for the palliative treatment of patien', NULL, 684, '2022-07-26', NULL, 0, 'Carboplatin is indicated in combination with an established combination of chemotherapeutic agents for the initial treatment of advanced ovarian carcinoma.[L32253] Carboplatin is also indicated for the palliative treatment of ovarian carcinoma, recurrent after prior chemotherapy.[L32253]', 'Carboplatin predominantly acts by attaching alkyl groups to the nucleotides, leading to the formation of monoadducts, and DNA fragmenting when repair enzymes attempt to correct the error.[A230158,L32253] 2% of carboplatin''s activity comes from DNA cross-linking from a base on one strand to a base on another, preventing DNA strands from separating for synthesis or transcription.[A230158,A1472] Finally, carboplatin can induce a number of different mutations.[A230158]', 'Carboplatin is an organoplatinum antineoplastic alkylating agent used in the treatment of advanced ovarian carcinoma.[L32253] Carboplatin has a long duration of action as it is given every 4 weeks, and a narrow therapeutic index.[L32253] Patients should be counselled regarding bone marrow suppression and anemia.[L32253]', 'DrugBank');
