INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Anschlarin syrup 120 ml', 14204, 'انشلارين شراب 120 مل', '11', NULL, 'Anemia.iron supplement', 'Anemia.iron supplement', NULL, 'Iron', 'Averroes pharma-egypt', 'Syrup', 'شراب', '120 ml', '1', 'مكمل غذائي غني بالحديد لعلاج الأنيميا.', NULL, NULL, '6224008179146', 1554, '2022-12-02', NULL, 0, 'For treatment of patients with documented iron deficiency in whom oral administration is unsatisfactory or impossible. Also used to replenish body iron stores in Non-Dialysis Dependent-Chronic Kidney Disease (NDD-CKD) patients receiving or not receiving erythropoietin and in Hemodialysis Dependent (HDD-CKD) and Peritoneal Dialysis Dependent (PDD-CKD) - Chronic Kidney Disease patients receiving an erythropoietin.', 'After iron dextran is injected, the circulating iron dextran is removed from the plasma by cells of the reticuloendothelial system, which split the complex into its components of iron and dextran. The iron is immediately bound to the available protein moieties to form hemosiderin or ferritin, the physiological forms of iron, or to a lesser extent to transferrin. This iron which is subject to physiological control replenishes hemoglobin and depleted iron stores.', 'Iron dextran is a dark brown, slightly viscous sterile liquid complex of ferric hydroxide and dextran for intravenous or intramuscular use. It is for treatment of patients with documented iron deficiency in whom oral administration is unsatisfactory or impossible. Iron is essential to the formation of hemoglobin and to the function and formation of other heme and nonheme compounds. Untreated depletion of iron stores leads to iron-deficient erythropoiesis and, in turn, to iron deficiency anemia.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Jevacystol cap', 5644, 'جيفاسيستول كبسول', '50', NULL, NULL, NULL, NULL, NULL, 'Company', 'Cap', 'كبسولة', NULL, '1', NULL, NULL, NULL, '6224010912892', 856, '2022-12-05', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Scalpovital 5% scalp lotn. 50 ml', 26586, 'سكالبوفيتال 5% لوشن لفروة الرأس 50 مل', '17', NULL, 'Antihypertensives.hair regrowth.vasodilator agents', 'Antihypertensives.hair regrowth.vasodilator agents', NULL, 'Minoxidil', 'European egyptian pharm. ind.', 'Unknown', 'غير محدد', '5%', '1', NULL, NULL, 'about minoxidil a potassium channel agonist direct-acting vasodilator hair- growth stimulant in androgenic alopecia. mechanism of action of minoxidil minoxidil works by activating pghs 1 (prostaglandin endoperoxide synthase-1) which helps promote hair gro', NULL, 1327, '2022-06-24', NULL, 0, 'For the treatment of severe hypertension and in the topical treatment (regrowth) of androgenic alopecia in males and females and stabilisation of hair loss in patients with androgenic alopecia.', 'Minoxidil is thought to promote the survival of human dermal papillary cells (DPCs) or hair cells by activating both extracellular signal-regulated kinase (ERK) and Akt and by preventing cell death by increasing the ratio of BCl<sup>-</sup>2/Bax. Minoxidil may stimulate the growth of human hairs by prolonging anagen through these proliferative and anti-apoptotic effects on DPCs. Minoxidil, when used as a vasodilator, acts by opening adenosine triphosphate-sensitive potassium channels in vascular smooth muscle cells. This vasodilation may also improve the viability of hair cells or hair follicles.', 'Minoxidil is an orally effective direct acting peripheral vasodilator that reduces elevated systolic and diastolic blood pressure by decreasing peripheral vascular resistance. Minoxidil is also used topically to treat androgenetic alopecia. Microcirculatory blood flow in animals is enhanced or maintained in all systemic vascular beds. In man, forearm and renal vascular resistance decline; forearm blood flow increases while renal blood flow and glomerular filtration rate are preserved. The predominant site of minoxidil action is arterial. Venodilation does not occur with minoxidil; thus, postural hypotension is unusual with its administration. The antihypertensive activity of minoxidil is due to its sulphate metabolite, minoxidil sulfate.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Plentopine 2.5 mg m.r. 10 f.c.tab.', 25007, 'بلينتوبين 2.5مجم 10 اقراص', '12', NULL, 'Antihypertensive.selective calcium channel blocker', 'Antihypertensive.selective calcium channel blocker', NULL, 'Felodipine', 'International drug agency (idi) > novell pharma', 'Tab', 'أقراص', '2.5 mg', '1', NULL, NULL, 'about felodipine a selective calcium channel blocker dihydropyridine derivative anti hypertensive. mechanism of action of felodipine felodipine is a vasoselective long acting dihydropyridine ca channel blocker. it exerts antihypertensive antianginal actio', NULL, 682, '2022-07-15', NULL, 0, 'For the treatment of mild to moderate essential hypertension. ', 'Felodipine decreases arterial smooth muscle contractility and subsequent vasoconstriction by inhibiting the influx of calcium ions through voltage-gated L-type calcium channels. It reversibly competes against nitrendipine and other DHP CCBs for DHP binding sites in vascular smooth muscle and cultured rabbit atrial cells. Calcium ions entering the cell through these channels bind to calmodulin. Calcium-bound calmodulin then binds to and activates myosin light chain kinase (MLCK). Activated MLCK catalyzes the phosphorylation of the regulatory light chain subunit of myosin, a key step in muscle contraction. Signal amplification is achieved by calcium-induced calcium release from the sarcoplasmic reticulum through ryanodine receptors. Inhibition of the initial influx of calcium decreases the contractile activity of arterial smooth muscle cells and results in vasodilation. The vasodilatory effects of felodipine result in an overall decrease in blood pressure. Felodipine may be used to treat mild to moderate essential hypertension.', 'Felodipine belongs to the dihydropyridine (DHP) class of calcium channel blockers (CCBs), the most widely used class of CCBs. There are at least five different types of calcium channels in Homo sapiens: L-, N-, P/Q-, R- and T-type. It was widely accepted that CCBs target L-type calcium channels, the major channel in muscle cells that mediates contraction; however, some studies have shown that felodipine also binds to and inhibits T-type calcium channels. T-type calcium channels are most commonly found on neurons, cells with pacemaker activity and on osteocytes. The pharmacologic significance of T-type calcium channel blockade is unknown. Felodipine also binds to calmodulin and inhibits calmodulin-dependent calcium release from the sarcoplasmic reticulum. The effect of this interaction appears to be minor.  Another study demonstrated that felodipine attenuates the activity of calmodulin-dependent cyclic nucleotide phosphodiesterase (CaMPDE) by binding to the PDE-1B1 and PDE-1A2 enzyme subunits. CaMPDE is one of the key enzymes involved in cyclic nucleotides and calcium second messenger systems. Felodipine also acts as an antagonist to the mineralcorticoid receptor by competing with aldosterone for binding and blocking aldosterone-induced coactivator recruitment of the mineralcorticoid receptor. Felodipine is able to bind to skeletal and cardiac muscle isoforms of troponin C, one of the key regulatory proteins in muscle contraction. Though felodipine exhibits binding to many endogenous molecules, its vasodilatory effects are still thought to be brought about primarily through inhibition of voltage-gated L-type calcium channels. Similar to other DHP CCBs, felodipine binds directly to inactive calcium channels stabilizing their inactive conformation. Since arterial smooth muscle depolarizations are longer in duration than cardiac muscle depolarizations, inactive channels are more prevalent in smooth muscle cells. Alternative splicing of the alpha-1 subunit of the channel gives felodipine additional arterial selectivity. At therapeutic sub-toxic concentrations, felodipine has little effect on cardiac myocytes and conduction cells.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Ivatherm ivapur exfoliating gel 100 ml', 21080, 'ايفاثيرم ايفابور جل مقشر 250 مل', '187', NULL, 'Skin care', 'Skin care', NULL, NULL, 'Ivatherm srl > vamer pharma', 'Gel', 'جل', '100 ml', '1', NULL, NULL, 'ivapur exfoliating gel 100ml remove impurities and dead cells with a very good tolerance. gentle exfoliates the layers of dead cells without causing irritation or skin sensitization prepares the skin for efficient application of the skincare cream prevent', NULL, 721, '2022-08-02', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Barmon cream 60 gm', 14933, 'بارمون كريم 60 جم', '62', NULL, 'Firming topical', 'Firming topical', NULL, 'Methylsilanol hydroxyproline aspartate+darutoside+tamanu oil', 'Egyptian company for cosmetics > eyta pharm', 'Cream', 'كريم', '60 gm', '1', NULL, NULL, 'for stretch marks of pregnancy and weight loss.', NULL, 1015, '2022-07-27', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Amgevita 40mg/0.8ml 2 pref. syringes', 32403, 'امجيفيتا 40 مجم 0.8 مل 2 سرنجة', '11,680', '6916', 'Tnf-inhibitor', 'Tnf-inhibitor', NULL, 'Adalimumab', 'Amgen scientific office', 'Syringe', 'حقنة', '40mg', '2', NULL, NULL, 'adalimumab is a medication used to treat rheumatoid arthritis psoriatic arthritis ankylosing spondylitis crohn s disease ulcerative colitis chronic psoriasis hidradenitis suppurativa and juvenile idiopathic arthritis. in rheumatoid arthritis adalimumab ha', NULL, 624, '2024-12-13', NULL, 0, 'Adalimumab is indicated for the following conditions:[L49101]



- Moderately to severely active Rheumatoid Arthritis (RA) in adults, as monotherapy or in combination with [methotrexate] or other non-biologic disease-modifying anti-rheumatic drugs (DMARDs).[L42290, L42495, L42935, L44953, L45643, L45818, L49101]

- Moderately to severely active polyarticular Juvenile Idiopathic Arthritis (JIA) in patients two years of age and older, as monotherapy or in combination with [methotrexate].[L42290, L42495, L42935, L44953, L45643, L45818, L49101]

- Psoriatic Arthritis (PsA) in adults.[L42290, L42495, L42935, L44953, L45643, L45818, L49101]

- Ankylosing Spondylitis (AS) in adults.[L42290, L42495, L42935, L44953, L45643, L45818, L49101]

- Moderately to severely active Crohn’s Disease (CD) in adults and pediatric patients six years of age and older.[L42290, L42495, L42935, L44953, L45643, L45818, L49101]

- Moderately to severely active Ulcerative Colitis (UC) in adults. Effectiveness has not been established in patients who have lost response to or were intolerant to TNF blockers.[L42290, L42495, L42935, L44953, L45643, L45818, L49101]

- Moderate to severe chronic plaque psoriasis in adult candidates for systemic therapy or phototherapy and when other systemic therapies are medically less appropriate.[L42290, L42495, L42935, L44953, L45643, L45818, L49101]

- Moderate to severe Hidradenitis Suppurativa (HS) in adults.[L44953]

- Non-infectious intermediate, posterior, and panuveitis in adults and pediatric patients two years of age and older.[L35370]



Adalimumab has also been used off-label to treat Pyoderma gangrenosum.[A40001,A40002]', 'Adalimumab binds with specificity to tumor necrosis factor-alpha (TNF-alpha) and inhibits its interaction with the p55 and p75 cell surface TNF receptors. Adalimumab also lyses surface tumor necrosis factor expressing cells in vitro when in the presence of complement.[A39984,A39999] Adalimumab does not bind or inactivate lymphotoxin (Tumor necrosis factor-beta). TNF is a naturally occurring cytokine that plays a role in normal inflammatory and immune responses.[A39999] Increased levels of TNF are found in the joint synovial fluid of rheumatoid arthritis, psoriatic arthritis, and ankylosing spondylitis patients, and play an imperative role in pathologic inflammation and joint destruction that are major complications of these diseases. Increased levels of TNF are also measured in psoriasis plaques. In plaque psoriasis, treatment with adalimumab may decrease the epidermal thickness and inflammatory cell infiltration. The relationship between these pharmacodynamics and the mechanism(s) by which adalimumab achieves its clinical effects is not known. Additionally, adalimumab alters biological responses that are induced/regulated by TNF, including changes in the levels of adhesion molecules responsible for leukocyte migration during inflammation (ELAM-1, VCAM-1, and ICAM-1 with an IC50 of 1-2 X 10-10M).[L35370]', 'After treatment with adalimumab, a decrease in levels of acute phase reactant proteins of inflammation (C­ reactive protein [CRP] and erythrocyte sedimentation rate [ESR]) and serum cytokines (IL-6) was measured compared to baseline in patients diagnosed with rheumatoid arthritis. A decrease in CRP levels was also observed in patients diagnosed with Crohn’s disease. Serum levels of matrix metalloproteinases (MMP-1 and MMP-3) that lead to the tissue remodeling responsible for cartilage destruction were also found to be decreased after administration of adalimumab.[L35370] A reduction in signs and symptoms of disease, the induction of clinical response, inhibition of structural damage, and improvements in physical function in adult and pediatric patients with various inflammatory conditions have been demonstrated.[A39983, A39999, L35370]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('D.t. vaccine (diphteria toxoid &tetanus toxoid) vial', 17041, 'دي . تي مصل', '2', NULL, 'Vaccine', 'Vaccine', NULL, 'Vaccine diphtheria+tetanus', 'Vacsera', 'Vial', 'فيال', NULL, '1', NULL, NULL, NULL, NULL, 862, '2022-07-18', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Acyclovir-misr 5% topical cream 10 gm', 137, 'اسيكلوفير 5% كريم موضعي 10 جرام', '23', '15', 'Antiviral.nucleoside', 'Antiviral.nucleoside', NULL, 'Acyclovir', 'Misr', 'Cream', 'كريم', '5%', '1', 'مضاد للفيروسات -- علاج الهيربس', NULL, '(indications) : this medication is an antiviral agent prescribed for herpes simplex genital herpes herpes zoster and chickenpox. it slows down the growth and spread of the herpes virus. acyclovir is also sometimes used to treat eczema herpeticum herpes in', '6221035008005', 8113, '2024-11-25', NULL, 0, 'An acyclovir topical cream is indicated to treat recurrent herpes labialis in immunocompetent patients 12 years and older.[L7303] Acyclovir oral tablets, capsules, and suspensions are indicated to treat herpes zoster, genital herpes, and chickenpox.[L7315] An acyclovir topical ointment is indicated to treat initial genital herpes and limited non-life-threatening mucocutaneous herpes simplex in immunocompromised patients.[L7318] An acyclovir cream with hydrocortisone is indicated to treat recurrent herpes labialis, and shortening lesion healing time in patients 6 years and older.[L7321] An acyclovir buccal tablet is indicated for the treatment of recurrent herpes labialis.[L7324] An acyclovir ophthalmic ointment is indicated to treat acute herpetic keratitis.[L7327]', 'Acyclovir is becomes acyclovir monophosphate due to the action of viral thymidine kinase.[A180757] Acyclovir monophosphate is converted to the diphosphate form by guanylate kinase.[A903] Acyclovir diphosphate is converted to acyclovir triphosphate by nucleoside diphosphate kinase, pyruvate kinase, creatine kinase, phosphoglycerate kinase, succinyl-CoA synthetase, phosphoenolpyruvate carboxykinase and adenylosuccinate synthetase.[A903,A180781] Acyclovir triphosphate has higher affinity for viral DNA polymerase than cellular DNA polymerase and incorporates into the DNA where the missing 2'' and 3'' carbons causes DNA chain termination.[A180757] In other cases acyclovir triphosphate competes so strongly for viral DNA polymerase that other bases cannot associate with the enzyme, inactivating it.[A180757]', 'Acyclovir is a deoxynucleoside analog that inhibits the action of viral DNA polymerase and DNA replication of different herpesvirus.[L7303,L7315,L7318,L7321,L7324,L7327] Acyclovir has a wide therapeutic window as overdose is rare in otherwise healthy patients.[L7315]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Panthenol novex cream 30 gm', 31068, 'بانثينول نوفكس كريم 30 جم', '33', '24', 'Moisturizing topical', 'Moisturizing topical', NULL, 'Panthenol', 'Novex', 'Cream', 'كريم', '30 gm', '1', NULL, NULL, 'about panthenol alcohol analog of pantothenic acid (vitamin b5) and is thus a provitamin of b5. mechanism of action of panthenol skin:it binds to the hair shaft readily and it coats the hair and seals its surface lubricating the hair shaft and making stra', '769503325866', 2215, '2025-01-27', NULL, 0, 'Panthenol (containing a racemic mixture of dexpanthenol and levopanthenol) is not currently available in any FDA-approved products and therefore does not have an indication.



Please see [DB09357] for FDA-approved uses of the dextrorotatory form of Panthenol. ', 'Panthenol is an alcohol derivative of pantothenic acid, a component of the B complex vitamins and an essential component of a normally functioning epithelium. Dexpanthenol, the active form of panthenol, is enzymatically cleaved to form pantothenic acid (Vitamin B5), which is an essential component of Coenzyme A that acts as a cofactor in many enzymatic reactions that are important for protein metabolism in the epithelium [A32373].



Dermatological effects of the topical use of dexpanthenol include increased fibroblast proliferation and accelerated re-epithelialization in wound healing. Furthermore, it acts as a topical protectant, moisturizer, and has demonstrated anti-inflammatory properties [A32377].', 'Pantothenic acid is a precursor of coenzyme A, which serves as a cofactor for a variety of enzyme-catalyzed reactions involving transfer of acetyl groups. The final step in the synthesis of acetylcholine consists of the choline acetylase transfer of acetyl group from acetylcoenzyme A to choline. Acetylcholine is the neurohumoral transmitter in the parasympathetic system and as such maintains the normal functions of the intestine. Decrease in acetylcholine content would result in decreased peristalsis and in extreme cases adynamic ileus. ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Eumovate 0.05% top. cream 15 gm', 18634, 'ايموفيت 0.05% كريم 15 جم', '13', NULL, 'Topical adenocorticoid', 'Topical adenocorticoid', NULL, 'Clobetasone', 'Glaxo smithkline', 'Cream', 'كريم', '0.05%', '1', NULL, NULL, 'about clobetasone corticosteroid ophthalmic anti inflammatory. mechanism of action of clobetasone clobetasone is a topical corticosteroid. it induces peptide lipocortins which inhibit the enzyme phospholipase a2. phospholipase a2 is essential for the conv', '6221045000693', 1108, '2022-12-05', NULL, 0, 'In dermatology, topical clobestasone butyrate helps to reduce the itchiness and erythema associated with eczema and dermatitis.

In ophthalmology, clobetasone butyrate 0.1% eye drops have been shown to be safe and effective in the treatment of dry eyes in Sjögren''s Syndrome.', 'Topically applied clobeyasone are thought to bind with cytoplasmic receptors in the dermal and intradermal cells and to induce inhibitory proteins, thus leading to decreased activity of prostaglandins, kinins, histamine, liposomal enzymes and other endogenous mediators of inflammation. Topical corticosteroids inhibit the migration of macrophages and leukocytes into areas of inflamed skin by reversing vascular dilation and permeability, resulting in decreased erythema, edema and pruritus.', 'Topical corticosteroid like clobetasone are synthetic derivatives of cortisone which produce anti-inflammatory, antiproliferative, immunosuppressive and vasoconstrictor effects when applied to the skin.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Olykraz 150 mg 30 caps.', 24038, 'اوليكراز 150مجم 30 كبسول', '79', NULL, 'Anti-epileptic.gaba analogs', 'Anti-epileptic.gaba analogs', NULL, 'Pregabalin', 'Mash premiere > nexus international pharmaceuticals', 'Cap', 'كبسولة', '150 mg', '3', 'يعمل على علاج الأوجاع التي تُسببها الأعصاب، مثل: اعتلال الأعصاب السكري ، ونوبة الصرع، ومتلازمة الألم العضلي', NULL, 'about pregabalin a gaba agonist anticonvulsant. mechanism of action of pregabalin pregabalin is a gaba neurotransmitter analog. it produces its action by modulating calcium channels and also reduces the release of neurotransmitters such as glutamate norep', NULL, 871, '2022-07-16', NULL, 0, 'Pregabalin is indicated for the management of neuropathic pain associated with diabetic peripheral neuropathy, postherpetic neuralgia, fibromyalgia, neuropathic pain associated with spinal cord injury, and as adjunctive therapy for the treatment of partial-onset seizures in patients 1 month of age and older.[L7066]', 'Although the mechanism of action has not been fully elucidated, studies involving structurally related drugs suggest that presynaptic binding of pregabalin to voltage-gated calcium channels is key to the antiseizure and antinociceptive effects observed in animal models.[L7066]  



By binding presynaptically to the alpha2-delta subunit of voltage-gated calcium channels in the central nervous system, pregabalin modulates the release of several excitatory neurotransmitters including glutamate, substance-P, norepinephrine, and calcitonin gene related peptide.[A31163] In addition, pregabalin prevents the alpha2-delta subunit from being trafficked from the dorsal root ganglia to the spinal dorsal horn, which may also contribute to the mechanism of action.[A36628] 



Although pregabalin is a structural derivative of the inhibitory neurotransmitter gamma-aminobutyric acid (GABA), it does not bind directly to GABA or benzodiazepine receptors.[A31165]  ', 'Although the structure of pregabalin is similar to gamma-aminobutyric acid (GABA), it does not bind to GABA receptors.[A31163,A187190] Instead, it binds the alpha2-delta subunit of presynaptic voltage-gated calcium channels in the central nervous system.[A31163,A187190] Pregabalin does not modulate dopamine receptors, serotonin receptors, opiate receptors, sodium channels or cyclooxygenase activity.[A187190] ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Levomagictam 500 mg 5 f.c.tab.', 7271, 'ليفوماجيكتام 500 مجم 5 اقراص', '27', NULL, 'Antibiotic.quinolone', 'Antibiotic.quinolone', NULL, 'Levofloxacin', 'Medizen pharmaceutical industries > magic pharma-egypt', 'Tab', 'أقراص', '500 mg', '1', 'مضاد حيوي.', NULL, NULL, '6224008604044', 1274, '2022-05-22', NULL, 0, 'In oral and intravenous formulations, levofloxacin is indicated in adults for the treatment of various infections caused by susceptible bacteria, including infections of the upper respiratory tract, lower respiratory tract, skin, skin structures, urinary tract, and prostate.[L11638,L11692] The oral formulation is also indicated in both adults and children 6 months of age and older for the post-exposure management of inhalational anthrax caused by _Bacillus anthracis_ and for the treatment and/or prophylaxis of plague caused by _Yersinia pestis_.[L11638]



In its ophthalmic formulation, levofloxacin is indicated for the treatment of bacterial conjunctivitis caused by susceptible organisms.[L11641] An inhalational solution available in Canada is indicated for the management of cystic fibrosis patients aged 18 years or older with chronic pulmonary _Pseudomonas aeruginosa_ infections.[L11689]', 'Levofloxacin, like other fluoroquinolone antibiotics, exerts its antimicrobial activity via the inhibition of two key bacterial enzymes: DNA gyrase and topoisomerase IV.[L11638] Both targets are type II topoisomerases, but have unique functions within the bacterial cell. DNA gyrase is an enzyme found only in bacteria that introduces negative supercoils into DNA during replication - this helps to relieve torsional strain caused by the introduction of positive supercoils during replication, and these negative supercoils are essential for chromosome condensation and the promotion of transcription initiation.[A31453] It is comprised of four subunits (two A subunits and two B subunits) of which the A subunits appear to be the target of fluoroquinolone antibiotics.[A190663] Bacterial topoisomerase IV, in addition to contributing to the relaxation of positive supercoils, is essential at the terminal stages of DNA replication and functions to “unlink” newly replicated chromosomes to allow for the completion of cell division.[A31453]



Inhibition of these enzymes by levofloxacin likely occurs via complexation with the topoisomerase enzymes.[A31453] The end result is a blockade of DNA replication, thus inhibiting cell division and resulting in cell death.', 'Levofloxacin is bactericidal and exerts its antimicrobial effects via inhibition of bacterial DNA replication.[L11638] It has a relatively long duration of action in comparison with other antibiotics that allows for once or twice daily dosing. Levofloxacin is associated with QTc-interval prolongation and should be used with caution in patients with other risk factors for prolongation (e.g. hypokalemia, concomitant medications).[L11638]



Levofloxacin has demonstrated _in vitro_ activity against a number of aerobic gram-positive and gram-negative bacteria and may carry some activity against certain species of anaerobic bacteria[L11638] and other pathogens such as _Chlamydia_ and _Legionella_.[A190663] Resistance to levofloxacin may develop, and is generally due to mutations in DNA gyrase or topoisomerase IV, or via alterations to drug efflux.[L11638,A31453] Cross-resistance may occur between levofloxacin and other fluoroquinolones, but is unlikely to develop between levofloxacin and other antibiotic classes (e.g. macrolides) due to significant differences in chemical structure and mechanism of action.[L11638]



As antimicrobial susceptibility patterns are geographically distinct, local antibiograms should be consulted to ensure adequate coverage of relevant pathogens prior to use.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Fluxopride 5mg 30 fast dissolving in mouth tab', 5054, 'فلوكسوبرايد 5مجم 30 قرص سريع الذوبان بالفم', '75', '52.5', 'Selective 5ht4 agonist', 'Selective 5ht4 agonist', NULL, 'Mosapride', 'Marcyrl co.', 'Tab', 'أقراص', '5mg', '3', 'حموضة المعدة -- ارتجاع المرئ -- متلازمة القولون العصبي -- التهاب المعدة', NULL, NULL, '6223003570583', 6283, '2024-10-13', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Psyquel 200mg 10 scored f.c tab.', 25476, 'سايكويل 200مجم 10 اقراص', '60', NULL, 'Psychiatric.antipsychotics', 'Psychiatric.antipsychotics', NULL, 'Quetiapine', 'Chemipharm', 'Tab', 'أقراص', '200mg', '1', NULL, NULL, 'indication for the treatment of schizophrenia and related psychotic disorders. pharmacodynamics quetiapine is a psychotropic agent belonging to the chemical class of benzisoxazole derivatives and is indicated for the treatment of schizophrenia. quetiapine', '6222006500924', 874, '2022-12-08', NULL, 0, 'Quetiapine is used in the symptomatic treatment of schizophrenia.  In addition, it may be used for the management of acute manic or mixed episodes in patients with bipolar I disorder, as a monotherapy or combined with other drugs. It may be used to manage depressive episodes in bipolar disorder. In addition to the above indications, quetiapine is used in combination with antidepressant drugs for the treatment of major depression.[L8546]



Some off-label uses for this drug include the management of post-traumatic stress disorder (PTSD), generalized anxiety disorder, and psychosis associated with Parkinson''s disease.[A185438,A185447,T685]

', 'Although the mechanism of action of quetiapine is not fully understood, several proposed mechanisms exist. In schizophrenia, its actions could occur from the antagonism of dopamine type 2 (D2) and serotonin 2A (5HT2A) receptors. In bipolar depression and major depression, quetiapine''s actions may be attributed to the binding of this drug or its metabolite to the norepinephrine transporter. Additional effects of quetiapine, including somnolence, orthostatic hypotension, and anticholinergic effects, may result from the antagonism of H1 receptors, adrenergic α1 receptors, and muscarinic M1 receptors, respectively.[A2189,A185438,L8546]', 'Quetiapine improves the positive and negative symptoms of schizophrenia and major depression by acting on various neurotransmitter receptors, such as the serotonin and dopamine receptors. In bipolar disorder, it improves both depressive and manic symptoms.[A2189,A185441,A185444]



**A note on suicidality in young patients and administration in the elderly**



Quetiapine can cause suicidal thinking or behavior in children and adolescents and should not be given to children under 10 years of age. It is important to monitor for suicidality if this drug is given to younger patients. In addition, this drug is not indicated for the treatment of psychosis related to dementia due to an increased death rate in elderly patients taking this drug.[L8546]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Metclamide 5/500mg 30 tab.', 22633, 'ميتكلاميد 5/500مجم 30 قرص', '17', NULL, 'Anti-diabetic.combined secretagogues+sensitizers', 'Anti-diabetic.combined secretagogues+sensitizers', NULL, 'Glibenclamide(glyburide)+metformin', 'Epci', 'Tab', 'أقراص', '500mg', '3', NULL, NULL, 'about metformin biguanide derivative oral anti-diabetic. mechanism of action of metformin it is a biguanide which exerts antidiabetic action. the drug suppresses gluconeogenesis in liver and thus suppresses hepatic glucose output. it enhance insulin media', NULL, 868, '2023-01-05', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Gaba 100mg 10 caps.', 19529, 'جابا 100مجم 10 كبسولات', '7', NULL, 'Anti-epileptic.gaba analogs', 'Anti-epileptic.gaba analogs', NULL, 'Gabapentin', 'Multi-apex > multipharma', 'Cap', 'كبسولة', '100mg', '1', 'علاج الصرع -- مسكن لالام الاعصاب', NULL, 'about gabapentin a gaba analogue anticonvulsant mood stabilizer. mechanism of action of gabapentin gabapentin is a gaba derivative and it bounds to lipophilic molecule. the mechanism of action of gabapentin is not exactly known. it crosses blood brain bar', NULL, 1167, '2022-08-28', NULL, 0, 'In the United States, gabapentin is officially indicated for the treatment of postherpetic neuralgia in adults and for the adjunctive treatment of partial-onset seizures, with or without secondary generalization, in patients 3 years of age and older.[L8717] In Europe, gabapentin is indicated for adjunctive therapy in the treatment of partial-onset seizures, with or without secondary generalization, in patients 6 years of age and older and as monotherapy in patients 12 years of age and older. It is also used in adults for the treatment of various types of peripheral neuropathic pain, such as painful diabetic neuropathy.[L8732]', 'The precise mechanism through which gabapentin exerts its therapeutic effects is unclear.[L8717,L8720] The primary mode of action appears to be at the auxillary α2δ-1 subunit of voltage-gated calcium channels (though a low affinity for the α2δ-2 subunit has also been reported).[A186179,A14097,A186277] The major function of these subunits is to facilitate the movement of pore-forming α1 subunits of calcium channels from the endoplasmic reticulum to the cell membrane of pre-synaptic neurons.[A186179] There is evidence that chronic pain states can cause an increase in the expression of α2δ subunits and that these changes correlate with hyperalgesia.[A14097] Gabapentin appears to inhibit the action of α2δ-1 subunits, thus decreasing the density of pre-synaptic voltage-gated calcium channels and subsequent release of excitatory neurotransmitters.[A186179] It is likely that this inhibition is also responsible for the anti-epileptic action of gabapentin.[A186277]



There is some evidence that gabapentin also acts on adenosine receptors[A11009, A186209] and voltage-gated potassium channels,[A186212] though the clinical relevance of its action at these sites is unclear.', 'Gabapentin is an anti-convulsant medication that inhibits the release of excitatory neurotransmitters, allowing for its use against pathologic neurotransmission such as that seen in neuropathic pain and seizure disorders.[L8717,L8732] It has a wide therapeutic index, with doses in excess of 8000 mg/kg failing to cause a fatal reaction in rats.[L8765]



Gabapentin is ineffective in absence seizures and should be used in caution in patients with mixed seizure disorders involving absence seizures. Gabapentin has been associated with drug reaction with eosinophilia and systemic symptoms (DRESS), otherwise known as multi-organ hypersensitivity. This reaction can prove fatal and early symptoms such as fever, lymphadenopathy, and rash should be promptly investigated.[L8717,L8720,L8732]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Vichy normaderm double correction daily care cream 50 ml', 29273, 'فيتشي نورماديرم تصحيح مضاعف للعناية اليومية كريم 50مل', '529', NULL, 'Skin care', 'Skin care', NULL, NULL, 'Vichy laboratories > united company for trading & distribution', 'Cream', 'كريم', '50 ml', '1', NULL, NULL, 'a double-action daily moisturiser for oily blemish-prone skin. this lightweight cream is easily absorbed and targets imperfections whilst revitalizing and hydrating skin for up to 24 hours. in addition to protecting the skin barrier and controlling the ap', NULL, 698, '2022-08-17', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Euthyrox 50mcg 50 tab', 4492, 'يوثيروكس 50 مكجم 50 قرص', '46', '28', 'Thyroid hormone', 'Thyroid hormone', NULL, 'Levothyroxine', 'Merck kgaa f.r.germany > amoun', 'Tab', 'أقراص', '50mcg', '2', NULL, NULL, NULL, '8699808010049', 9864, '2024-09-18', NULL, 0, 'Levothyroxine is indicated as replacement therapy in primary (thyroidal), secondary (pituitary) and tertiary (hypothalamic) congenital or acquired hypothyroidism. It is also indicated as an adjunct to surgery and radioiodine therapy in the management of thyrotropin-dependent well-differentiated thyroid cancer.', 'Levothyroxine is a synthetically prepared levo-isomer of the thyroid hormone thyroxine (T<sub>4</sub>, a tetra-iodinated tyrosine derivative) that acts as a replacement in deficiency syndromes such as hypothyroidism. T<sub>4</sub> is the major hormone secreted from the thyroid gland and is chemically identical to the naturally secreted T<sub>4</sub>: it increases metabolic rate, decreases thyroid-stimulating hormone (TSH) production from the anterior lobe of the pituitary gland, and, in peripheral tissues, is converted to T<sub>3</sub>. Thyroxine is released from its precursor protein thyroglobulin through proteolysis and secreted into the blood where is it then peripherally deiodinated to form triiodothyronine (T<sub>3</sub>) which exerts a broad spectrum of stimulatory effects on cell metabolism. T<sub>4</sub> and T<sub>3</sub> have a relative potency of ~1:4. 



Thyroid hormone increases the metabolic rate of cells of all tissues in the body. In the fetus and newborn, thyroid hormone is important for the growth and development of all tissues including bones and the brain. In adults, thyroid hormone helps to maintain brain function, food metabolism, and body temperature, among other effects. The symptoms of thyroid deficiency relieved by levothyroxine include slow speech, lack of energy, weight gain, hair loss, dry thick skin and unusual sensitivity to cold.



The thyroid hormones have been shown to exert both genomic and non-genomic effects.[A179950] They exert their genomic effects by diffusing into the cell nucleus and binding to thyroid hormone receptors in DNA regions called thyroid hormone response elements (TREs) near genes.[A179605] This complex of T<sub>4</sub>, T<sub>3</sub>, DNA, and other coregulatory proteins causes a conformational change and a resulting shift in transcriptional regulation of nearby genes, synthesis of messenger RNA, and cytoplasmic protein production.[A179605,A179935] For example, in cardiac tissues T<sub>3</sub> has been shown to regulate the genes for α- and β-myosin heavy chains, production of the sarcoplasmic reticulum proteins calcium-activated ATPase (Ca2+-ATPase) and phospholamban, β-adrenergic receptors, guanine-nucleotide regulatory proteins, and adenylyl cyclase types V and VI as well as several plasma-membrane ion transporters, such as Na+/K+–ATPase, Na+/Ca2+ exchanger, and voltage-gated potassium channels, including Kv1.5, Kv4.2, and Kv4.3. [A179938] As a result, many cardiac functions including heart rate, cardiac output, and systemic vascular resistance are closely linked to thyroid status.



The non-genomic actions of the thyroid hormones have been shown to occur through binding to a plasma membrane receptor integrin aVb3 at the Arg-Gly-Asp recognition site.[A179959] From the cell-surface, T<sub>4</sub> binding to integrin results in down-stream effects including activation of mitogen-activated protein kinase (MAPK; ERK1/2) and causes subsequent effects on cellular/nuclear events including angiogenesis and tumor cell proliferation.[A179935, A179956]', 'Oral levothyroxine is a synthetic hormone that exerts the same physiologic effect as endogenous T<sub>4</sub>, thereby maintaining normal T<sub>4</sub> levels when a deficiency is present. 



Levothyroxine has a narrow therapeutic index and is titrated to maintain a euthyroid state with TSH (thyroid stimulating hormone) within a therapeutic range of 0.4–4.0 mIU/L.[A35722] Over- or under-treatment with levothyroxine may have negative effects on growth and development, cardiovascular function, bone metabolism, reproductive function, cognitive function, emotional state, gastrointestinal function and glucose and lipid metabolism. The dose of levothyroxine should be titrated slowly and carefully and patients should be monitored for their response to titration to avoid these effects. TSH levels should be monitored at least yearly to avoid over-treating with levothyroxine which can result in hyperthyroidism (TSH <0.1mIU/L) and symptoms of increased heart rate, diarrhea, tremor, hypercalcemia, and weakness to name a few.[F4636]



As many cardiac functions including heart rate, cardiac output, and systemic vascular resistance are closely linked to thyroid status,[A179938] over-treatment with levothyroxine may result in increases in heart rate, cardiac wall thickness, and cardiac contractility and may precipitate angina or arrhythmias, particularly in patients with cardiovascular disease and in elderly patients. In populations with any cardiac concerns, levothyroxine should be initiated at lower doses than those recommended in younger individuals or in patients without cardiac disease. Patients receiving concomitant levothyroxine and sympathomimetic agents should be monitored for signs and symptoms of coronary insufficiency. If cardiac symptoms develop or worsen, reduce the levothyroxine dose or withhold for one week and restart at a lower dose.[F4636]



Increased bone resorption and decreased bone mineral density may occur as a result of levothyroxine over-replacement, particularly in post-menopausal women. The increased bone resorption may be associated with increased serum levels and urinary excretion of calcium and phosphorous, elevations in bone alkaline phosphatase and suppressed serum parathyroid hormone levels. Administer the minimum dose of levothyroxine that achieves the desired clinical and biochemical response to mitigate this risk.



Addition of levothyroxine therapy in patients with diabetes mellitus may worsen glycemic control and result in increased antidiabetic agent or insulin requirements. Carefully monitor glycemic control after starting, changing or discontinuing levothyroxine.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Sacnel cream 30 gm', 10137, 'ساكنل كريم 30 جرام', '60', '50', 'Anti-acne', 'Anti-acne', NULL, 'Salicylic acid+triclosan+allantoin', 'Weiser', 'Cream', 'كريم', '30 gm', '1', 'كريم لعلاج حب الشباب', NULL, 'for greasy face care', '6223002260157', 2082, '2025-06-14', NULL, 0, 'Key additive in many skin-care products for the treatment of acne, psoriasis, callouses, corns, keratosis pilaris and warts.', 'Salicylic acid directly irreversibly inhibits COX-1 and COX-2 to decrease conversion of arachidonic acid to precursors of prostaglandins and thromboxanes. Salicylate''s use in rheumatic diseases is due to it''s analgesic and anti-inflammatory activity. Salicylic acid is a key ingredient in many skin-care products for the treatment of acne, psoriasis, calluses, corns, keratosis pilaris, and warts. Salicylic acid allows cells of the epidermis to more readily slough off. Because of its effect on skin cells, salicylic acid is used in several shampoos used to treat dandruff. Salicylic acid is also used as an active ingredient in gels which remove verrucas (plantar warts). Salicylic acid competitively inhibits oxidation of uridine-5-diphosphoglucose (UDPG) with nicotinamide adenosine dinucleotide (NAD) and noncompetitively with UDPG. It also competitively inhibits the transferring of the glucuronyl group of uridine-5-phosphoglucuronic acid (UDPGA) to a phenolic acceptor. Inhibition of mucopoly saccharide synthesis is likely responsible for the slowing of wound healing with salicylates.', 'Salicylic acid treats acne by causing skin cells to slough off more readily, preventing pores from clogging up. This effect on skin cells also makes salicylic acid an active ingredient in several shampoos meant to treat dandruff. Use of straight salicylic solution may cause hyperpigmentation on unpretreated skin for those with darker skin types (Fitzpatrick phototypes IV, V, VI), as well as with the lack of use of a broad spectrum sunblock. Subsalicylate in combination with bismuth form the popular stomach relief aid known commonly as Pepto-Bismol. When combined the two key ingredients help control diarrhea, nausea, heartburn, and even gas. It is also very mildly anti-biotic.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Tress follicle booster shampoo 150 ml', 31513, 'تريس فوليكل بوستر شامبو 150 مل', '175', NULL, 'Hair care', 'Hair care', NULL, NULL, 'Macro group pharmaceuticals', 'Amp', 'أمبول', '150 ml', '1', NULL, NULL, NULL, '6224007384169', 665, '2024-12-02', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Freshen cream 30 gm', 19406, 'فريش شين كريم 30جم', '25', NULL, 'Soothing topical', 'Soothing topical', NULL, 'Lanolin+wheatgerm oil+glycerin+vit e+honey+beeswax+dimethicon+propolis+sesame oil+glyceryl monostear', 'Egyptian company for cosmetics > pharma 3', 'Cream', 'كريم', '30 gm', '1', NULL, NULL, 'skin soothing cream', NULL, 872, '2022-07-08', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Zyrovazet 10/10mg 30 tablets', 30061, 'زيروفازيت 10/10مجم 30 قرص', '216', '162', 'Antihyperlipidemic.statins', 'Antihyperlipidemic.statins', NULL, 'Rosuvastatin+ezetimibe', 'Marcyrl > xeedia pharma', 'Tablet', 'أقراص', '10mg', '3', 'خافض للدهون والكوليسترول', NULL, NULL, '6225000331839', 24387, '2024-09-25', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Cefumax 1500mg i.m./i.v.vial', 16071, 'سيفيوماكس 1500 مجم فيال', '40', NULL, 'Antibiotic.cephalosporin.second-generation', 'Antibiotic.cephalosporin.second-generation', NULL, 'Cefuroxime', 'Pharco b', 'Vial', 'فيال', '1500mg', '1', NULL, NULL, 'about cefuroxime second generation cephalosporin antibiotic. mechanism of action of cefuroxime cefuroxime is a 2nd generation cephalosporin which exerts its bactericidal action against both gram positive & gram negative organisms by inhibiting bacterial c', NULL, 938, '2022-07-18', NULL, 0, 'For the treatment of many different types of bacterial infections such as bronchitis, sinusitis, tonsillitis, ear infections, skin infections, gonorrhea, and urinary tract infections.', 'Cefuroxime, like the penicillins, is a beta-lactam antibiotic. By binding to specific penicillin-binding proteins (PBPs) located inside the bacterial cell wall, it inhibits the third and last stage of bacterial cell wall synthesis. Cell lysis is then mediated by bacterial cell wall autolytic enzymes such as autolysins; it is possible that cefuroxime interferes with an autolysin inhibitor.', 'Cefuroxime is a &beta;-lactam type antibiotic. More specifically, it is a second-generation cephalosporin. Cephalosporins work the same way as penicillins: they interfere with the peptidoglycan synthesis of the bacterial wall by inhibiting the final transpeptidation needed for the cross-links. This effect is bactericidal. Cefuroxime is effective against the following organisms: Aerobic Gram-positive Microorganisms: <i>Staphylococcus aureus</i>, <i>Streptococcus pneumoniae</i>, <i>Streptococcus pyogenes</i>. Aerobic Gram-negative Microorganisms: <i>Escherichia coli</i>, <i>Haemophilus influenzae</i> (including beta-lactamase-producing strains), <i>Haemophilus parainfluenzae</i>, <i>Klebsiella pneumoniae</i>, <i>Moraxella catarrhalis</i> (including beta-lactamase-producing strains), <i>Neisseria gonorrhoeae</i> (including beta-lactamase-producing strains). Spirochetes: <i>Borrelia burgdorferi</i>. Cefuroxime axetil is the prodrug ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Markolive cream 75 gm', 2719, 'ماركوليف كريم 75 جم', '125', NULL, 'Soothing cream', 'Soothing cream', NULL, 'Micronized zinc oxide+panthenol+olive oil+vitamins(d+e+b5+a)+cucumis sativus+cod liver oil+green tea+chamomile+aloe vera+jojoba oil+shea butter+calendula', 'Egyptian co. for cosmetics > meroza pharma', 'Cream', 'كريم', '75 gm', '1', NULL, NULL, NULL, NULL, 815, '2023-07-12', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Bilastomed 12.5mg/5ml syrup 120 ml', 18750, 'بيلاستوميد 12.5مجم5 مل شراب 120 مل', '50', '36', 'Anti-histamine.second-generation', 'Anti-histamine.second-generation', NULL, 'BILASTINE', 'Biomed', 'Syrup', 'شراب', '12.5mg', '1', NULL, NULL, NULL, NULL, 729, '2024-04-24', NULL, 0, 'For symptomatic relief of nasal and non-nasal symptoms of seasonal rhinitis in patients 12 years of age and older and for symptomatic relief in chronic spontaneous urticaria in patients 18 years of age and older [FDA Label].', 'Bilastine is a selective histamine H1 receptor antagonist (Ki = 64nM) [FDA Label]. During allergic response mast cells undergo degranulation which releases histamine and other subastances. By binding to and preventing activation of the H1 receptor, bilastine reduces the development of allergic symptoms due to the release of histamine from mast cells.', 'Bilastine is an antiallergenic and acts to reduce allergic symptoms such as nasal congestion and urticaria [FDA Label].', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Voltaren 25mg 30 enteric coated tab.', 12554, 'فولتارين 25 مجم 30 قرص', '14', NULL, 'Nsaid.acetic acid derivatives.analgesic.antipyretic', 'Nsaid.acetic acid derivatives.analgesic.antipyretic', NULL, 'Diclofenac sodium', 'Novartis', 'Tab', 'أقراص', '25mg', '3', 'مسكن آلام ومضاد التهاب وخافض حرارة', NULL, 'description a non-steroidal anti-inflammatory agent (nsaid) with antipyretic and analgesic actions. it is primarily available as the sodium salt. indication : for the acute and chronic treatment of signs and symptoms of osteoarthritis and rheumatoid arthr', '6223002640362', 1523, '2022-12-14', NULL, 0, 'Diclofenac is indicated for use in the treatment of pain and inflammation from varying sources including inflammatory conditions such as osteoarthritis, rheumatoid arthritis, and ankylosing spondylitis, as well as injury-related inflammation due to surgery and physical trauma. It is often used in combination with [misoprostol] as a gastro-protective agent in patients with high risk of developing NSAID-induced ulcers.', 'Diclofenac inhibits cyclooxygenase-1 and -2, the enzymes responsible for production of prostaglandin (PG) G<sub>2</sub> which is the precursor to other PGs.[label,T116] These molecules have broad activity in pain and inflammation and the inhibition of their production is the common mechanism linking each effect of diclofenac.



PGE<sub>2</sub> is the primary PG involved in modulation of nociception.[A179023] It mediates peripheral sensitization through a variety of effects.[T116,A179023] PGE<sub>2</sub> activates the G<sub>q</sub>-coupled EP<sub>1</sub> receptor leading to increased activity of the inositol trisphosphate/phospholipase C pathway. Activation of this pathway releases intracellular stores of calcium which directly reduces action potential threshold and activates protein kinase C (PKC) which contributes to several indirect mechanisms. PGE<sub>2</sub> also activates the EP<sub>4</sub> receptor, coupled to G<sub>s</sub>, which activates the adenylyl cyclase/protein kinase A (AC/PKA) signaling pathway. PKA and PKC both contribute to the potentiation of transient receptor potential cation channel subfamily V member 1 (TRPV1) potentiation, which increases sensitivity to heat stimuli. They also activate tetrodotoxin-resistant sodium channels and inhibit inward potassium currents. PKA further contributes to the activation of the P2X3 purine receptor and sensitization of T-type calcium channels. The activation and sensitization of depolarizing ion channels and inhibition of inward potassium currents serve to reduce the intensity of stimulus necessary to generate action potentials in nociceptive sensory afferents. PGE<sub>2</sub> act via EP<sub>3</sub> to increase sensitivity to bradykinin and via EP<sub>2</sub> to further increase heat sensitivity. Central sensitization occurs in the dorsal horn of the spinal cord and is mediated by the EP<sub>2</sub> receptor which couples to G<sub>s</sub>. Pre-synaptically, this receptor increases the release of pro-nociceptive neurotransmitters glutamate, CGRP, and substance P. Post-synaptically it increases the activity of AMPA and NMDA receptors and produces inhibition of inhibitory glycinergic neurons. Together these lead to a reduced threshold of activating, allowing low intensity stimuli to generate pain signals. PGI<sub>2</sub> is known to play a role via its G<sub>s</sub>-coupled IP receptor although the magnitude of its contribution varies. It has been proposed to be of greater importance in painful inflammatory conditions such as arthritis. By limiting sensitization, both peripheral and central, via these pathways NSAIDs can effectively reduce inflammatory pain.



PGI<sub>2</sub> and PGE<sub>2</sub> contribute to acute inflammation via their IP and EP<sub>2</sub> receptors.[T116,A179044] Similarly to β adrenergic receptors these are G<sub>s</sub>-coupled and mediate vasodilation through the AC/PKA pathway. PGE<sub>2</sub> also contributes by increasing leukocyte adhesion to the endothelium and attracts the cells to the site of injury.[T116] PGD<sub>2</sub> plays a role in the activation of endothelial cell release of cytokines through its DP<sub>1</sub> receptor.[A179044] PGI<sub>2</sub> and PGE<sub>2</sub> modulate T-helper cell activation and differentiation through IP, EP<sub>2</sub>, and EP<sub>4</sub> receptors which is believed to be an important activity in the pathology of arthritic conditions. By limiting the production of these PGs at the site of injury, NSAIDs can reduce inflammation.



PGE<sub>2</sub> can cross the blood-brain barrier and act on excitatory G<sub>q</sub> EP<sub>3</sub> receptors on thermoregulatory neurons in the hypothalamus.[T116] This activation triggers an increase in heat-generation and a reduction in heat-loss to produce a fever. NSAIDs prevent the generation of PGE<sub>2</sub> thereby reducing the activity of these neurons.

', 'Diclofenac reduces inflammation and by extension reduces nociceptive pain and combats fever.[label,T116] It also increases the risk of developing a gastrointestinal ulcer by inhibiting the production of protective mucus in the stomach.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Phenobarbital 100 mg 20 tab.', 24868, 'فينوباربيتال 100مجم 20 اقراص', '9', NULL, 'Anti-epileptic.barbiturates', 'Anti-epileptic.barbiturates', NULL, 'Phenobarbitone', 'Eipico', 'Tab', 'أقراص', '100 mg', '2', NULL, NULL, 'about phenobarbitone barbiturate derivative sedative-hypnotic anticonvulsant. mechanism of action of phenobarbitone sedative and antiepileptic actions: phenobarbitone mainly acts on picrotoxin site of gaba -bzd receptor cl- (chloride ion) channel complex.', NULL, 1200, '2022-07-08', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Dago hair shampoo 250ml', 2146, 'داجو شامبو للشعر 250 مل', '250', '200', 'Hair care', 'Hair care', NULL, 'Zinc pyrithion+caffeine+aloe vera+tea tree oil+panthenol', 'Hi-care > bluebell pharmaceuticals', 'Amp', 'أمبول', '250ml', '1', NULL, NULL, NULL, '6224008235804', 920, '2025-02-14', NULL, 0, 'Zinc can be used for the treatment and prevention of zinc deficiency/its consequences, including stunted growth and acute diarrhea in children, and slowed wound healing. It is also utilized for boosting the immune system, treating the common cold and recurrent ear infections, as well as preventing lower respiratory tract infections [L2172].  



', '**Zinc has three primary biological roles**: _catalytic_, _structural_, and _regulatory_. The catalytic and structural role of zinc is well established, and there are various noteworthy reviews on these functions. For example, zinc is a structural constituent in numerous proteins, inclusive of growth factors, cytokines, receptors, enzymes, and transcription factors for different cellular signaling pathways. It is implicated in numerous cellular processes as a cofactor for approximately 3000 human proteins including enzymes, nuclear factors, and hormones [L2096].



Zinc promotes resistance to epithelial apoptosis through cell protection (cytoprotection) against reactive oxygen species and bacterial toxins, likely through the antioxidant activity of the cysteine-rich metallothioneins [A32419].



In HL-60 cells (promyelocytic leukemia cell line), zinc enhances the up-regulation of A20 mRNA, which, via TRAF pathway, decreases NF-kappaB activation, leading to decreased gene expression and generation of tumor necrosis factor-alpha (TNF-alpha), IL-1beta, and IL-8 [A32418].



There are several mechanisms of action of zinc on acute diarrhea. Various mechanisms are specific to the gastrointestinal system: zinc restores mucosal barrier integrity and enterocyte brush-border enzyme activity, it promotes the production of antibodies and circulating lymphocytes against intestinal pathogens, and has a direct effect on ion channels, acting as a potassium channel blocker of adenosine 3-5-cyclic monophosphate-mediated chlorine secretion. Cochrane researchers examined the evidence available up to 30 September 2016 [L2106].



Zinc deficiency in humans decreases the activity of serum _thymulin_ (a hormone of the thymus), which is necessary for the maturation of T-helper cells. T-helper 1 (Th(1)) cytokines are decreased but T-helper 2 (Th(2)) cytokines are not affected by zinc deficiency in humans [A32417].



The change of _Th(1)_ to _Th(2)_ function leads to cell-mediated immune dysfunction. Because IL-2 production (Th(1) cytokine) is decreased, this causes decreased activity of natural-killer-cell (NK cell) and T cytolytic cells, normally involved in killing viruses, bacteria, and malignant cells [A32424]. 



In humans, zinc deficiency may lead to the generation of new CD4+ T cells, produced in the thymus. In cell culture studies (HUT-78, a Th(0) human malignant lymphoblastoid cell line), as a result of zinc deficiency, nuclear factor-kappaB (NF-kappaB) activation, phosphorylation of IkappaB, and binding of NF-kappaB to DNA are decreased and this results in decreased Th(1) cytokine production [A32417].



In another study, zinc supplementation in human subjects suppressed the gene expression and production of pro-inflammatory cytokines and decreased oxidative stress markers [A32424]. In HL-60 cells (a human pro-myelocytic leukemia cell line), zinc deficiency increased the levels of TNF-alpha, IL-1beta, and IL-8 cytokines and mRNA. In such cells, zinc was found to induce A20, a zinc finger protein that inhibited NF-kappaB activation by the tumor necrosis factor receptor-associated factor pathway. This process decreased gene expression of pro-inflammatory cytokines and oxidative stress markers [A32417].



The exact mechanism of zinc in acne treatment is poorly understood. However, zinc is considered to act directly on microbial inflammatory equilibrium and facilitate antibiotic absorption when used in combination with other agents. Topical zinc alone as well as in combination with other agents may be efficacious because of its anti-inflammatory activity and ability to reduce P. acnes bacteria by the inhibition of P. acnes lipases and free fatty acid levels [L2102].', 'Zinc is involved in various aspects of cellular metabolism. It has been estimated that approximately 10% of human proteins may bind zinc, in addition to hundreds of proteins that transport and traffic zinc. It is required for the catalytic activity of more than 200 enzymes, and it plays a role in immune function wound healing, protein synthesis, DNA synthesis, and cell division. Zinc is an essential element for a proper sense of taste and smell and supports normal growth and development during pregnancy, childhood, and adolescence. It is thought to have antioxidant properties, which may be protective against accelerated aging and helps to speed up the healing process after an injury; however, studies differ as to its effectiveness. Zinc ions are effective antimicrobial agents even if administered in low concentrations [L2211].



Studies on oral zinc for specific conditions shows the following evidence in various conditions [L2095]:



**Colds:** Evidence suggests that if zinc lozenges or syrup are taken within 24 hours after cold symptoms start, the supplement may shorten the length of colds. The use intranasal zinc has been associated with the loss of the sense of smell, in some cases long-term or permanently [L2095].



**Wound healing:** Patients with skin ulcers and decreased levels of zinc may benefit from oral zinc supplements [L2095]. 



**Diahrrea**: Oral zinc supplements can reduce the symptoms of diarrhea in children with low levels of zinc, especially in cases of malnutrition [L2095].

', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Seroquel 200mg 10 tab', 26856, 'سيروكويل 200مجم 10 قرص', '143', '125', 'Psychiatric.antipsychotics', 'Psychiatric.antipsychotics', NULL, 'Quetiapine', 'Astra zeneca', 'Tab', 'أقراص', '200mg', '1', NULL, NULL, NULL, NULL, 972, '2024-11-25', NULL, 0, 'Quetiapine is used in the symptomatic treatment of schizophrenia.  In addition, it may be used for the management of acute manic or mixed episodes in patients with bipolar I disorder, as a monotherapy or combined with other drugs. It may be used to manage depressive episodes in bipolar disorder. In addition to the above indications, quetiapine is used in combination with antidepressant drugs for the treatment of major depression.[L8546]



Some off-label uses for this drug include the management of post-traumatic stress disorder (PTSD), generalized anxiety disorder, and psychosis associated with Parkinson''s disease.[A185438,A185447,T685]

', 'Although the mechanism of action of quetiapine is not fully understood, several proposed mechanisms exist. In schizophrenia, its actions could occur from the antagonism of dopamine type 2 (D2) and serotonin 2A (5HT2A) receptors. In bipolar depression and major depression, quetiapine''s actions may be attributed to the binding of this drug or its metabolite to the norepinephrine transporter. Additional effects of quetiapine, including somnolence, orthostatic hypotension, and anticholinergic effects, may result from the antagonism of H1 receptors, adrenergic α1 receptors, and muscarinic M1 receptors, respectively.[A2189,A185438,L8546]', 'Quetiapine improves the positive and negative symptoms of schizophrenia and major depression by acting on various neurotransmitter receptors, such as the serotonin and dopamine receptors. In bipolar disorder, it improves both depressive and manic symptoms.[A2189,A185441,A185444]



**A note on suicidality in young patients and administration in the elderly**



Quetiapine can cause suicidal thinking or behavior in children and adolescents and should not be given to children under 10 years of age. It is important to monitor for suicidality if this drug is given to younger patients. In addition, this drug is not indicated for the treatment of psychosis related to dementia due to an increased death rate in elderly patients taking this drug.[L8546]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Panthenol 2% topical cream 50 gm', 24519, 'بانثينول 2% كريم 50 جرام', '80', '72', 'Moisturizing topicals', 'Moisturizing topicals', NULL, 'Panthenol', 'El nile.', 'Cream', 'كريم', '2%', '1', 'كريم مرطب للجلد', NULL, 'about panthenol alcohol analog of pantothenic acid (vitamin b5) and is thus a provitamin of b5. mechanism of action of panthenol skin:it binds to the hair shaft readily and it coats the hair and seals its surface lubricating the hair shaft and making stra', '6221077073818', 6508, '2025-07-29', NULL, 0, 'Panthenol (containing a racemic mixture of dexpanthenol and levopanthenol) is not currently available in any FDA-approved products and therefore does not have an indication.



Please see [DB09357] for FDA-approved uses of the dextrorotatory form of Panthenol. ', 'Panthenol is an alcohol derivative of pantothenic acid, a component of the B complex vitamins and an essential component of a normally functioning epithelium. Dexpanthenol, the active form of panthenol, is enzymatically cleaved to form pantothenic acid (Vitamin B5), which is an essential component of Coenzyme A that acts as a cofactor in many enzymatic reactions that are important for protein metabolism in the epithelium [A32373].



Dermatological effects of the topical use of dexpanthenol include increased fibroblast proliferation and accelerated re-epithelialization in wound healing. Furthermore, it acts as a topical protectant, moisturizer, and has demonstrated anti-inflammatory properties [A32377].', 'Pantothenic acid is a precursor of coenzyme A, which serves as a cofactor for a variety of enzyme-catalyzed reactions involving transfer of acetyl groups. The final step in the synthesis of acetylcholine consists of the choline acetylase transfer of acetyl group from acetylcoenzyme A to choline. Acetylcholine is the neurohumoral transmitter in the parasympathetic system and as such maintains the normal functions of the intestine. Decrease in acetylcholine content would result in decreased peristalsis and in extreme cases adynamic ileus. ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Lezberg trio 20/5/12.5mg 30 f.c.tabs.', 12573, 'ليزبيرج تريو 20/5/12.5مجم 30 قرص', '114', '87', 'Anti-hypertensive.combined angiotensin blocker & ca++ channel blocker & diuretic', 'Anti-hypertensive.combined angiotensin blocker & ca++ channel blocker & diuretic', NULL, 'Amlodipine+olmesartan+hydrochlorothiazide', 'Global advanced pharmaceuticals (gap)', 'Tab', 'أقراص', '12.5mg', '3', 'مدر للبول ويستخدم لعلاج ارتفاع ضغط الدم. قد يقلل خفض ضغط الدم من خطر الإصابة بسكتة دماغية أو نوبة قلبية.', NULL, NULL, '6223005941299', 1802, '2024-10-28', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Mash calamine lotion 100 ml', 26467, 'ماش كالامين لوشن 100 مل', '45', NULL, 'Skin care.soothing', 'Skin care.soothing', NULL, NULL, 'Mash premiere', 'Lotion', 'لوشن', '100 ml', '1', NULL, NULL, NULL, NULL, 788, '2023-04-27', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Amotenolol 50mg 10 f.c.tab.', 14005, 'اموتينولول 50 مجم 10 أقراص', '3', NULL, 'Antihypertensive.beta blocker.selective b1', 'Antihypertensive.beta blocker.selective b1', NULL, 'Atenolol', 'Amoun', 'Tab', 'أقراص', '50mg', '1', NULL, NULL, 'description : a cardioselective beta-adrenergic blocker possessing properties and potency similar to propranolol but without a negative inotropic effect. dose: adult: po- angina pectoris; htn- 50-100 mg once daily. migraine prophylaxis 50-100 mg/day. indi', NULL, 759, '2022-07-25', NULL, 0, '**Indicated** for:[label]



1) Management of hypertension alone and in combination with other antihypertensives.



2) Management of angina pectoris associated with coronary atherosclerosis.



3) Management of acute myocardial infarction in hemodynamically stable patients with a heart rate greater than 50 beats per minutes and a systolic blood pressure above 100 mmHg.



**Off-label** uses include:



1) Secondary prevention of myocardial infarction.[A178156]



2) Management of heart failure.[A178153]



3) Management of atrial fibrillation.[A178141]



4) Management of supraventricular tachycardia.[A178162]



5) Management of ventricular arrythmias such as congenital long-QT and arrhythmogenic right ventricular cardiomyopathy.[A178168]



6) Management of symptomatic thyrotoxicosis in combination with [methimazole].[A178147]



7) Prophylaxis of migraine headaches.[A178171]



8) Management of alcohol withdrawal.[A178174,A178177]', 'Atenolol is a cardioselective beta-blocker, called such because it selectively binds to the β1-adrenergic receptor as an antagonist up to a reported 26 fold more than β2 receptors.[A178372] Selective activity at the β1 receptor produces cardioselectivity due to the higher population of this receptor in cardiac tissue. Some binding to β2 and possibly β3 receptors can still occur at therapeutic dosages but the effects mediated by antagonizing these are significantly reduced from those of non-selective agents. β1 and β2 receptors are G<sub>s</sub> coupled therefore antagonism of their activation reduces activity of adenylyl cyclase and its downstream signalling via cyclic adenosime monophosphate and protein kinase A (PKA). 



In cardiomyocytes PKA is thought to mediate activation of L-type calcium channels and ryanodine receptors through their phosphorylation.[A178396] L-type calcium channels can then provide an initial rise in intracellular calcium and trigger the ryanodine receptors to release calcium stored in the sarcoplasmic reticulum (SR) and increased contractility. PKA also plays a role in the cessation of contraction by phosphorylating phospholamban which in turn increases the affinity of SR Ca<sup>2+</sup> ATPase to increase reuptake of calcium into the SR. It also phophorylates troponin I to reduce affinity of the protein for calcium. Both of these events lead to a reduction in contraction which, when coupled with the initial increase in contraction, allows for faster cycling and consequently higher heart rate with increased contractility. L-type calcium channels are also a major contributor to cardiac depolarization and their activation can increase frequency of action potentials and possibly the incidence of ectopic potentials.[A178405] 



Similar inihibitory events occur in the bronchial smooth muscle to mediate relaxation including phosphorylation of myosin light-chain kinase, reducing its affinity for calcium.[A178408] PKA also inhibits the excitatory G<sub>q</sub> coupled pathway by phosphorylating the inositol trisphosphate receptor and phospholipase C resulting in inhibition of intracellular calcium release. Antagonism of this activity by beta-blocker agents like atenolol can thus cause increased bronchoconstriction.

', 'Atenolol is a cardio-selective beta-blocker and as such exerts most of its effects on the heart.[label] It acts as an antagonist to sympathetic innervation and prevents increases in heart rate, electrical conductivity, and contractility in the heart due to increased release of norepinephrine from the peripheral nervous system.[label,T116,A178258] Together the decreases in contractility and rate produce a reduction in cardiac output resulting in a compensatory increase in peripheral vascular resistance in the short-term. This response later declines to baseline with long-term use of atenolol. More importantly, this reduction in the work demanded of the myocardium also reduces oxygen demand which provides therapeutic benefit by reducing the mismatch of oxygen supply and demand in settings where coronary blood flow is limited, such as in coronary atherosclerosis. Reducing oxygen demand, particularly due to exercise, can reduce the frequency of angina pectoris symptoms and potentially improve survival of the remaining myocardium after myocardial infarction. The decrease in rate of sinoatrial node potentials, electrical conduction, slowing of potentials traveling through the atrioventricular node, and reduced frequency of ectopic potentials due to blockade of adrenergic beta receptors has led to benefit in arrhythmic conditions such as atrial fibrillation by controlling the rate of action potential generation and allowing for more effective coordinated contractions. Since a degree of sympathetic activity is necessary to maintain cardiac function, the reduced contractility induced by atenolol may precipitate or worsen heart failure, especially during volume overload.[label]



The effects of atenolol on blood pressure have been established, although it is less effective than alternative beta-blockers, but the mechanism has not yet been characterized.[label,T116] As a β1 selective drug, it does not act via the vasodilation produced by non-selective agents.[T116] Despite this there is a sustained reduction in peripheral vascular resistance, and consequently blood pressure, alongside a decrease in cardiac output. It is thought that atenolol''s antihypertensive activity may be related to action on the central nervous system (CNS) or it''s inhibition of the renin-aldosterone-angiotensin system rather than direct effects on the vasculature.[label]



Atenolol produces CNS effects similar to other beta-blockers, but does so to a lesser extent due to reduces ability to cross the blood-brain barrier.[T116] It has the potential to produce fatigue, depression, and sleep disturbances such as nightmares or insomnia.[label,T116] The exact mechanisms behind these have not been characterized but their occurrence must be considered as they represent clinically relevant adverse effects.



Atenolol exerts some effects on the respiratory system although to a much lesser extent than non-selective beta-blockers.[label] Interaction with β2 receptors in the airways can produce bronchoconstriction by blocking the relaxation of bronchial smooth muscle mediated by the sympathetic nervous system.[T116] The same action can interfere with β-agonist therapies used in asthma and chronic obstructive pulmonary disease.[label,A178258,T116]



Unlike some other beta-blocker drugs, atenolol does not have intrinsic sympathomimetic or membrane stabilizing activity nor does it produce changes in glycemic control.[label]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Sly-impress massage gel 30 gm', 1181, 'سلي امبريس مساج جل 30 جم', '82', NULL, 'Massage gel', 'Massage gel', NULL, 'Menthol crystals 5%+camphor crystals 3%+vit e (tocopherol)+vit b5 (pantothenic acid)+panthenol+eucalyptus+ginger+clove bud oils+thymus vulgaris oil+mentha piperita oil', 'Elsaid saliy pharma', 'Gel', 'جل', '30 gm', '1', NULL, NULL, NULL, NULL, 956, '2022-09-06', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Vital care shampoo imperial argan 500ml', 29473, 'فيتال كير شامبو 500مل', '110', NULL, 'Hair care', 'Hair care', NULL, NULL, 'So.di.co. srl > bright star -egypt', 'Amp', 'أمبول', '500ml', '1', NULL, NULL, NULL, NULL, 716, '2022-07-30', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Exelon 18 mg 10 cm 9.5mg/24h 30 transdermal patches', 18695, 'اكسيلون 9مجم 5 سم 4.5مجم/24 ساعة 30 لصقة', '1,069', '822', 'Anticholinesterase', 'Anticholinesterase', NULL, 'Rivastigmine', 'Lts lohmann therapie systeme ag-germany > novartis', 'Patch', 'لصقة', '18 mg', '1', NULL, NULL, 'about rivastigmine a central reversible non competitive anticholinesterase antidementia. mechanism of action of rivastigmine alzheimer`s disease may result from a deficiency in neurotransmitters used by nerves in the brain to communicate with one another.', NULL, 1022, '2024-12-28', NULL, 0, 'For the treatment of mild to moderate dementia associated with Parkinson''s disease or of the Alzheimer''s type.', 'Rivastigmine is a carbamate derivative that is structurally related to physostigmine, but not to donepezil and tacrine. The precise mechanism of rivastigmine has not been fully determined, but it is suggested that rivastigmine binds reversibly with and inactivates chlolinesterase (eg. acetylcholinesterase, butyrylcholinesterase), preventing the hydrolysis of acetycholine, and thus leading to an increased concentration of acetylcholine at cholinergic synapses. The anticholinesterase activity of rivastigmine is relatively specific for brain acetylcholinesterase and butyrylcholinesterase compared with those in peripheral tissues.', 'Rivastigmine is a parasympathomimetic and a reversible cholinesterase inhibitor. An early pathophysiological feature of Alzheimer''s disease that is associated with memory loss and cognitive deficits is a deficiency of acetylcholine as a result of selective loss of cholinergic neurons in the cerebral cortex, nucleus basalis, and hippocampus. Tacrine is postulated to exert its therapeutic effect by enhancing cholinergic function. While the precise mechanism of rivastigmine''s action is unknown, it is postulated to exert its therapeutic effect by enhancing cholinergic function. This is accomplished by increasing the concentration of acetylcholine through reversible inhibition of its hydrolysis by cholinesterase. If this proposed mechanism is correct, rivastigmine''s effect may lessen as the disease progresses and fewer cholinergic neurons remain functionally intact.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Beesline whitening body lotion bridal vanilla 200 ml', 15027, 'بيزلين لوشن لتفتيح الجسم برائحة الفانيلا 200 مل', '255', NULL, 'Skin care', 'Skin care', NULL, NULL, 'Beesline > pharma cruise medical', 'Lotion', 'لوشن', '200 ml', '1', NULL, NULL, 'active ingredients sweet almond oil coconut oil sodium lactate vegetable glycerin lemon peel olive leaf parsley honey lactic acid & vitamin c. aromatic fast-absorbing lotion that moisturises & lightens dry skin for a smooth blemish-free & radiant finish.', NULL, 777, '2022-07-15', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Wega 10 sachets', 12824, 'ويجا 10 اكياس', '90', '90', 'Multi vitamin', 'Multi vitamin', NULL, 'Lactoferrin 100mg+magnesium+calcium', 'Lasanta', 'Sachet', 'أكياس', NULL, '1', 'مكمل غذائى لدعم صحة العظام ويعزز مرونة المفاصل ويحمي الغضاريف ويعزز الجهاز المناعى', NULL, NULL, NULL, 1169, '2024-03-24', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Adcocycline 3% oint. 15 gm', 151, 'ادكوسيكلين 3% مرهم 15 جم', '7', NULL, 'Antibiotic.tetracycline', 'Antibiotic.tetracycline', NULL, 'Tetracycline', 'Arab drug company.', 'Oint', 'مرهم', '3%', '1', 'مرهم مضاد حيوي', NULL, 'about tetracycline a broad-spectrum polyketide antibiotic tetracycline derivative. mechanism of action of tetracycline tetracycline is a broad spectrum antibiotic which has more specific action against gram positive organisms than gram negative organisms.', '6221060001385', 2036, '2022-05-27', NULL, 0, 'Used to treat bacterial infections such as Rocky Mountain spotted fever, typhus fever, tick fevers, Q fever, rickettsialpox and Brill-Zinsser disease. May be used to treat infections caused by Chlamydiae spp., B. burgdorferi (Lyme disease), and upper respiratory infections caused by typical (S. pneumoniae, H. influenzae, and M. catarrhalis) and atypical organisms (C. pneumoniae, M. pneumoniae, L. pneumophila). May also be used to treat acne. Tetracycline may be an alternative drug for people who are allergic to penicillin.', 'Tetracycline passively diffuses through porin channels in the bacterial membrane and reversibly binds to the 30S ribosomal subunit, preventing binding of tRNA to the mRNA-ribosome complex, and thus interfering with protein synthesis.', 'Tetracycline is a short-acting antibiotic that  inhibits bacterial growth by inhibiting translation. It binds to the 30S ribosomal subunit and prevents the amino-acyl tRNA from binding to the A site of the ribosome. It also binds to some extent to the 50S ribosomal subunit. This binding is reversible in nature. Additionally tetracycline may alter the cytoplasmic membrane of bacteria causing leakage of intracellular contents, such as nucleotides, from the cell. ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Abstral 200mcg 10 sublingual tabs.', 13292, 'ابيسترال 200مكجم 10 قرص تحت اللسان', '1,184', NULL, 'Analgesics.opioid', 'Analgesics.opioid', NULL, 'Fentanyl', 'Kyowa kirin limited > egyptian pharmaceutical trading company', 'Tab', 'أقراص', '200mcg', '1', NULL, NULL, 'fentanyl about fentanyl citrate synthetic primary opioid agonist anilidopiperidine derivative narcotic analgesic general anesthetic. mechanism of action of fentanyl citrate this drug primarily acts on the cns. fentanyl is a pure opioid agonist and primari', NULL, 1017, '2022-07-14', NULL, 0, 'Fentanyl intravenous or intramuscular injections are indicated for short term analgesia during induction, maintenance, and recovery from general or regional anesthesia.[Label] These injections are also used with a neuroleptic for premedication, induction, and as an adjunct to maintenance of anesthesia.[Label] Finally, fentanyl intravenous or intramuscular injections are used with oxygen for anesthesia in high risk patients.[Label]



Fentanyl sublingual tablets, transmucosal lozenges, buccal tablets, sublingual sprays, transdermal systems, and nasal sprays are indicated for the management of breakthrough pain in opioid tolerant cancer patients who require around the clock pain management.[L6598,L6601,L6604,L6607,L922,L6610]', 'Fentanyl binds to opioid receptors, especially the mu opioid receptor, which are coupled to G-proteins.[A179533] Activation of opioid receptors causes GTP to be exchanged for GDP on the G-proteins which in turn down regulates adenylate cyclase, reducing concentrations of cAMP.[A179533] Reduced cAMP decreases cAMP dependant influx of calcium ions into the cell.[A179533] The exchange of GTP for GDP results in hyperpolarization of the cell and inhibition of nerve activity.[A179533]', 'Fentanyl produces strong analgesia through its activation of opioid receptors.[Label,A179533] It has a duration of action of several hours and a wider therapeutic index as patients develop tolerance to opioids.[Label] Fentanyl is associated with a risk of addiction and abuse and should not be mixed with alcohol or benzodiazepines.[Label,L6598,L6601,L6604,L6607,L922,L6610,L6613]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Clindacine 1% topical gel 20 gm', 16516, 'كلينداسين 1% جل موضعي 20 جم', '7', NULL, 'Antibiotic.lincomycins', 'Antibiotic.lincomycins', NULL, 'Clindamycin', 'Rameda', 'Gel', 'جل', '1%', '1', NULL, NULL, NULL, NULL, 1441, '2022-08-08', NULL, 0, 'In oral and parenteral formulations, clindamycin is indicated for the treatment of serious infections caused by susceptible anaerobic bacteria, as well as susceptible staphylococci, streptococci, and pneumococci.[L11599,L11602] Used topically, it is indicated for the treatment of acne vulgaris[L11593,L11611,L11605] and is available in combination with [benzoyl peroxide][L11584] or [tretinoin][L11590] for this purpose, or as a triple combination therapy with benzoyl peroxide and [adapalene].[L48666] Clindamycin is also indicated as a vaginal cream[L11596], suppository[L11608], or gel[L39416] for the treatment of bacterial vaginosis in non-pregnant females.



Clindamycin is used for antimicrobial prophylaxis against _Viridans_ group streptococcal infections in susceptible patients undergoing oral, dental, or upper respiratory surgery, and may be used for prophylaxis against bacterial endocarditis in penicillin-allergic patients at high risk of these infections.[L11629]', 'Clindamycin inhibits bacterial protein synthesis by binding to 23S RNA of the 50S subunit of the bacterial ribosome.[L11599] It impedes both the assembly of the ribosome and the translation process.[L11629] The molecular mechanism through which this occurs is thought to be due to clindamycin''s three-dimensional structure, which closely resembles the 3''-ends of L-Pro-Met-tRNA and deacylated-tRNA during the peptide elongation cycle - in acting as a structural analog of these tRNA molecules, clindamycin impairs peptide chain initiation and may stimulate dissociation of peptidyl-tRNA from bacterial ribosomes.[A190621]



The mechanism through which topical clindamycin treats acne vulgaris is unclear, but may be related to its activity against _Propionibacterium acnes_, a bacteria that has been associated with acne.[L11593] ', 'Clindamycin exerts its bacteriostatic effect via inhibition of microbial protein synthesis.[A190621] Clindamycin has a relatively short T<sub>max</sub> and half-life necessitating administration every six hours to ensure adequate antibiotic concentrations.[L11629] 



_Clostridium difficile_ associated diarrhea (CDAD) has been observed in patients using clindamycin, ranging in severity from mild diarrhea to fatal colitis and occasionally occurring over two months following cessation of antibiotic therapy.[L11602] Overgrowth of _C. difficile_ resulting from antibiotic use, along with its production of A and B toxins, contributes to morbidity and mortality in these patients. Because of the associated risks, clindamycin should be reserved for serious infections for which the use of less toxic antimicrobial agents are inappropriate.[L11602]



Clindamycin is active against a number of gram-positive aerobic bacteria, as well as both gram-positive and gram-negative anaerobes.[L11599] Resistance to clindamycin may develop, and is generally the result of base modification within the 23S ribosomal RNA. Cross-resistance between clindamycin and lincomycin is complete, and may also occur between clindamycin and macrolide antibiotics (e.g. [erythromycin]) due to similarities in their binding sites.[L11599]



As antimicrobial susceptibility patterns are geographically distinct, local antibiograms should be consulted to ensure adequate coverage of relevant pathogens prior to use.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Midodrine 2.5mg 20 tab', 8161, 'ميدودرين 2.5مجم 20 قرص', '50', '38', 'Orthostatic hypotension', 'Orthostatic hypotension', NULL, 'Midodrine', 'El nile.', 'Tab', 'أقراص', '2.5mg', '2', 'لحالات ضغط الدم المنخفض', NULL, NULL, '6221077014019', 9252, '2024-11-09', NULL, 0, 'For the treatment of symptomatic orthostatic hypotension (OH).', 'Midodrine undergoes metabolism to form its pharmacologically active metabolite, desglymidodrine. Desglymidodrine acts as an agonist at the alpha<sub>1</sub>-adrenergic receptors expressed in the arteriolar and venous vasculature. Activation of alpha<sub>1</sub>-adrenergic receptor signaling pathways lead to an increase in the vascular tone and elevation of blood pressure. Desglymidodrine is reported to have negligible effect on the cardiac beta-adrenergic receptors.', 'Midodrine is a prodrug, i.e., the therapeutic effect of orally administered midodrine is due to the major metabolite desglymidodrine formed by deglycination of midodrine. Administration of midodrine results in a rise in standing, sitting, and supine systolic and diastolic blood pressure in patients with orthostatic hypotension of various etiologies. Standing systolic blood pressure is elevated by approximately 15 to 30 mmHg at 1 hour after a 10-mg dose of midodrine, with some effect persisting for 2 to 3 hours. Midodrine has no clinically significant effect on standing or supine pulse rates in patients with autonomic failure.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Nizaxid 15 mg/ml syrup 90 ml', 23650, 'نيزاكسيد 15مجم/مل شراب 90مل', '47', '29', 'Peptic ulcer.anti-histamine.h2 antagonist', 'Peptic ulcer.anti-histamine.h2 antagonist', NULL, 'Nizatidine', 'Sabaa > activa therapeutics', 'Syrup', 'شراب', '15 mg', '1', NULL, NULL, 'indication for the treatment of acid-reflux disorders (gerd) peptic ulcer disease active benign gastric ulcer and active duodenal ulcer. pharmacodynamics nizatidine is a competitive reversible inhibitor of histamine at the histamine h2-receptors particula', '6224000808280', 1082, '2023-12-22', NULL, 0, 'For the treatment of acid-reflux disorders (GERD), peptic ulcer disease, active benign gastric ulcer, and active duodenal ulcer.', 'Nizatidine competes with histamine for binding at the H<sub>2</sub>-receptors on the gastric basolateral membrane of parietal cells. Competitive inhibition results in reduction of basal and nocturnal gastric acid secretions. The drug also decreases the gastric acid response to stimuli such as food, caffeine, insulin, betazole, or pentagastrin.', 'Nizatidine is a competitive, reversible inhibitor of histamine at the histamine H2-receptors, particularly those in the gastric parietal cells. By inhibiting the action of histamine on stomach cells, nizatidine reduces stomach acid production. Nizatidine had no demonstrable antiandrogenic action. Full-dose therapy for the problems treated by nizatidine lasts no longer than 8 weeks. It has been demonstrated that treatment with a reduced dose of nizatidine is effective as maintenance therapy following healing of active duodenal ulcers.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Sanso c plus 28 tabs', 26510, 'سانسو سي بلس 28 قرص', '190', '180', 'Multivitamins', 'Multivitamins', NULL, 'Zinc+vitamin c+vitamin d3+vitamin e+vitamin a', 'Aug pharma', 'Tab', 'أقراص', NULL, '1', 'فيتامينات و املاح ومعادن -- لتقوية الجهاز المناعي -- زيادة قدرة الجسم على مقاومة الامراض', NULL, NULL, NULL, 4124, '2025-02-05', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('U-richi panthenol cream 50 gm', 33512, 'يو ريتشي بانثينول كريم 50 جم', '85', '45', 'Skin care moisturizing cream', 'Skin care moisturizing cream', NULL, 'Dexpanthenol+vitamin c+nicotinamide+liquorice extract+cocoa butter+grape seed oil+collagen+elastin+honey+zink oxide+raspberry extract+glutathione.', 'U-richi', 'Cream', 'كريم', '50 gm', '1', NULL, NULL, NULL, '6224011774093', 3679, '2024-08-29', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Thromoxiban 20 mg 30 tabs.', 4980, 'ثروموكسيبان 20 مجم 30 قرص', '390', NULL, 'Antiplatlet.direct factor xa inhibitor', 'Antiplatlet.direct factor xa inhibitor', NULL, 'Rivaroxaban', 'Medizen pharmaceutical industries', 'Tab', 'أقراص', '20 mg', '3', NULL, NULL, NULL, NULL, 918, '2025-06-22', NULL, 0, 'Rivaroxaban is indicated for the prevention of venous thromboembolic events (VTE) in patients who have undergone total hips replacements and total knee replacement surgery; prevention of stroke and systemic embolism in patients with nonvalvular atrial fibrillation; treatment of deep vein thrombosis (DVT) and pulmonary embolism (PE); to reduce risk of recurrent DVT and/or PE. Rivaroxaban is also indicated, in combination with aspirin, for reducing the risk of major cardiovascular events in patients with chronic coronary artery disease or peripheral artery disease. Its use is also not recommended in those with severe renal impairment (<30mL/min).[L12819]



Rivaroxaban is also indicated for the treatment and prevention of VTE in pediatric patients (from birth to 18 years of age) and for thromboprophylaxis in pediatric patients ≥2 years old with congenital heart disease following the Fontan procedure.[L12819]

', 'Rivaroxaban competitively inhibits free and clot bound factor Xa. Factor Xa is needed to activate prothrombin (factor II) to thrombin (factor IIa). Thrombin is a serine protease that is required to activate fibrinogen to fibrin, which is the loose meshwork that completes the clotting process. Since one molecule of factor Xa can generate more than 1000 molecules of thrombin, selective inhibitors of factor Xa are profoundly useful in terminating the amplification of thrombin generation. The action of rivaroxaban is irreversible.

 

', 'Rivaroxaban is an anticoagulant which binds directly to factor Xa. Thereafter, it effectively blocks the amplification of the coagulation cascade, preventing the formation of thrombus. Rivaroxaban is a unqiue anticoagulant for two reasons. First of all, it is does not involve antithrombin III (ATIII) to exert its anticoagulant effects. Secondly, it is an oral agent whereas the widely used unfractionated heparin and low molecular weight heparins are for parenteral use only. Although the activated partial thromboplastin time (aPTT) and HepTest (a test developed to assay low molecular weight heparins) are prolonged in a dose-dependant manner, neither test is recommended for the assessment of the pharmacodynamic effects of rivaroxaban. Anti-Xa activity and inhibition of anti-Xa activity monitoring is also not recommended despite being influenced by rivaroxaban.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('New well vitamin b-12 spray 30 ml', 7093, 'نيو ويل فيتامين ب12 سبراي 30 مل', '219', NULL, 'Supports nervous system health - supports the formation of red blood cells', 'Supports nervous system health - supports the formation of red blood cells', NULL, 'Vitamin b-12', 'Eureka pharma llc', 'Spray', 'بخاخ', '30 ml', '1', NULL, NULL, NULL, NULL, 758, '2025-08-23', NULL, 0, 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Hepta panthenol rich body lotion 250 ml', 6594, 'هيبتا بانثينول ريتش بودي لوشن 250 مل', '199', NULL, 'Skin care.moisturizing', 'Skin care.moisturizing', NULL, 'Panthenol+prebiotics', 'Hepta', 'Lotion', 'لوشن', '250 ml', '1', NULL, NULL, NULL, NULL, 722, '2025-08-08', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Dentohex mouth spray 60 ml', 30394, 'دينتوهكس بخاخ للفم 60 مل', '50', '25', 'Mouth spray', 'Mouth spray', NULL, 'Chlorhexidine+sodium fluoride+thymol+menthol+eucalyptus+green tea extract+grape seed extract+sorbitol+saccharin+glycerin', 'Italian cosmetics > ivet', 'Spray', 'بخاخ', '60 ml', '1', NULL, NULL, 'anti septic mouth spray mouth refreshing mouth cleaner mouth cleanser', NULL, 1671, '2024-03-30', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Stamaril yellow fever vaccine', 27445, 'ستاماريل لقاح للحمى الصفراء', '50', NULL, 'Vaccine', 'Vaccine', NULL, 'Vaccine yellow fever', 'Sanofi pasteur-france > etimex-egypt', 'Unknown', 'غير محدد', NULL, '1', NULL, NULL, NULL, NULL, 994, '2022-07-07', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Omecarbex 40/1100mg 20 caps.', 24043, 'اوميكاربيكس 40/1100مجم 20 كبسولة', '98', '45', 'Peptic ulcer.proton pump inhibitor', 'Peptic ulcer.proton pump inhibitor', NULL, 'Omeprazole+sodium bicarbonate', 'Sigma > andalous pharma', 'Cap', 'كبسولة', '1100mg', '2', 'لعلاج إرتجاع المري -- لعلاج قرحة المعدة والإثنى عشر', NULL, 'about omeprazole proton pump inhibitor substituted benzimidazole anti-secretory antiulcer. mechanism of action of omeprazole omeprazole is a proton pump inhibitor and it is a prodrug. after administration it diffuses in to the parietal cell of the stomach', '6224000396466', 1787, '2025-01-14', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Vaxigrip tetra 15mcg/0.5ml prefilled syringe', 29051, 'فاكسيجريب تيترا 15مكجم/0.5مل سرنجة معبأة', '370', '258', 'Vaccine', 'Vaccine', NULL, 'Quadrivalent inactivated influenza avirus haemagglutinin vaccine', 'Sanofi pasteur-france > etimex-egypt', 'Syringe', 'حقنة', '15mcg', '1', NULL, NULL, 'description the active substances are: influenza virus (inactivated split) of the following strains: a/h1n1 a/h3n b/ yamagata lineage and b/ victoria lineage each contains 15 micrograms haemagglutinin per 0.5 ml dose. vaxigrip tetra is a vaccine. this vac', NULL, 3041, '2024-08-10', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Debeskon oral susp. 120 ml', 3336, 'ديبيسكون معلق 120 مل', '44', '29.5', 'Antacid', 'Antacid', NULL, 'Sodium bicarbonate+sodium alginate', 'Debeiky', 'Susp', 'معلق', '120 ml', '1', 'مضاد للعلاج الحموضة وحرقان المعدة', NULL, 'for hyperacidity and reflux esophagitis', '6225000005440', 5077, '2025-01-02', NULL, 0, 'For the treatment of small uncomplicated varicose veins of the lower extremities that show simple dilation with competent valves.', 'Sodium tetradecyl sulfate is a potent toxin for endothelial cells in that brief exposure to even low concentrations are effective in stripping endothelium over a considerable distance and exposing highly thrombogenic endothelium in the process. Diluted sodium tetradecyl sulfate is also able to induce a hypercoagulable state, possibly by selective inhibition of protein C, and can also promote platelet aggregation.', 'Sodium tetradecyl sulfate is an anionic surfactant which occurs as a white, waxy solid. It is used as a sclerosing agent in sclerotherapy. Sclerotherapy is an injection of a sclerosing agent directly through the skin into a lesion and is used primarily for slow-flow vascular anomalies, particularly for venous malformation and lymphatic malformation. Intravenous injection causes intima inflammation and thrombus formation. This usually occludes the injected vein. Subsequent formation of fibrous tissue results in partial or complete vein obliteration that may or may not be permanent.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Ducray nutricerat emulsion 100 ml', 17942, 'دوكري نيوتريسيرات معلق 100 مل', '250', '245', 'Hair care', 'Hair care', NULL, NULL, 'Pierre - fabre france > biotech egypt', 'Unknown', 'غير محدد', '100 ml', '1', NULL, NULL, '*daily emulsion that repairs and protects very dry and damaged hair ends. *to repair and protect the dry and damaged ends of your hair apply a small amount of nutricerat intense-nutrition emulsion every day. the ends of your hair will be repaired nourishe', '3282779178686', 699, '2023-09-03', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Photoderm kid spray spf 50+ 200ml', 24897, 'فوتوديرم سبراي للاطفال للوقاية من الشمس 200مل', '650', NULL, 'Sun block', 'Sun block', NULL, 'Sun protection formula', 'Bioderma dermatological laboratories > biotech egypt', 'Spray', 'بخاخ', '200ml', '1', NULL, NULL, 'specific photoprotection for childrenï-s delicate sensitive skin. ï- photoderm kidï-s anti-uva-uvb filter system safely protects the delicate skin of children. the exclusive patented cellular bioprotectionï- complex* reinforced by peptide c provides optim', NULL, 710, '2022-07-15', NULL, 0, NULL, NULL, NULL, NULL);
