INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Cobalamin depot 1000mcg/ml 5 amp. i.m.', 16595, 'كوبالامين ديبو 1000مكجم/2مل 5 امبولات عضل', '55', '35', 'Vitamin b', 'Vitamin b', NULL, 'Hydroxocobalamin', 'Pharopharma', 'Amp', 'أمبول', '1000mcg', '5', 'فيتامين ب12', NULL, 'description injectable form of vitamin b 12 that has been used therapeutically to treat vitamin b 12 deficiency. mechanism of action vitamin b12 exists in four major forms referred to collectively as cobalamins; deoxyadenosylcobalamin methylcobalamin hydr', '6221094504166', 13157, '2024-08-14', NULL, 0, 'For treatment of pernicious anemia and the prevention and treatment of vitamin B12 deficiency arising from alcoholism, malabsorption, tapeworm infestation, celiac, hyperthyroidism, hepatic-biliary tract disease, persistent diarrhea, ileal resection, pancreatic cancer, renal disease, prolonged stress, vegan diets, macrobiotic diets or other restrictive diets. Also for the treatment of known or suspected cyanide poisoning.', 'Vitamin B12 exists in four major forms referred to collectively as cobalamins; deoxyadenosylcobalamin, methylcobalamin, hydroxocobalamin, and cyanocobalamin. Two of these, methylcobalamin and 5-deoxyadenosyl cobalamin, are primarily used by the body. Methionine synthase needs methylcobalamin as a cofactor. This enzyme is involved in the conversion of the amino acid homocysteine into methionine. Methionine in turn is required for DNA methylation. 5-Deoxyadenosyl cobalamin is a cofactor needed by the enzyme that converts L-methylmalonyl-CoA to succinyl-CoA. This conversion is an important step in the extraction of energy from proteins and fats. Furthermore, succinyl CoA is necessary for the production of hemoglobin, the substances that carries oxygen in red blood cells.', 'Hydroxocobalamin is a synthetic, injectable form of Vitamin B12. Hydroxocobalamin is actually a precursor of two cofactors or vitamins (Vitamin B12 and Methylcobalamin) which are involved in various biological systems in man. Vitamin B12 is required for the conversion of methylmalonate to succinate. Deficiency of this enzyme could therefore interfere with the production of lipoprotein in myelin sheath tissue and so give rise to neurological lesions. The second cofactor, Methylcobalamin, is necessary for the conversion of homocysteine to methionine which is essential for the metabolism of folic acid. Deficiency of tetrahydrafolate leads to reduced synthesis of thymidylate resulting in reduced synthesis of DNA which is essential for cell maturation. Vitamin B12 is also concerned in the maintenance of sulphydryl groups in reduced form, deficiency leading to decreased amounts of reduced SH content of erythrocytes and liver cells. Overall, vitamin B12 acts as a coenzyme for various metabolic functions, including fat and carbohydrate metabolism and protein synthesis. It is necessary for growth, cell replication, hematopoiesis, and nucleoprotein as well as myelin synthesis. This is largely due to its effects on metabolism of methionine folic acid, and malonic acid.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Felopress 2.5/2.5 mg 21 f.c. tab.', 18911, 'فيلوبريس 2.5 / 2.5 مجم 21 قرص', '33', NULL, 'Antihypertensive', 'Antihypertensive', NULL, 'Felodipine+ramipril', 'Liptis', 'Tab', 'أقراص', '2.5 mg', '3', NULL, NULL, 'about felodipine a selective calcium channel blocker dihydropyridine derivative anti hypertensive. mechanism of action of felodipine felodipine is a vasoselective long acting dihydropyridine ca channel blocker. it exerts antihypertensive antianginal actio', NULL, 1151, '2022-07-11', NULL, 0, NULL, NULL, NULL, NULL);
