INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Moxipen 500mg 12 caps.', 8382, 'موكسيبن 500 مجم 12 كبسولة', '15', NULL, 'Penicillins.monocomponent', 'Penicillins.monocomponent', NULL, 'Amoxicillin', 'Misr', 'Cap', 'كبسولة', '500mg', '2', 'مضاد حيوي', NULL, NULL, '6221035012163', 4538, '2023-03-11', NULL, 0, 'Amoxicillin alone is indicated to treat susceptible bacterial infections of the ear, nose, throat, genitourinary tract, skin, skin structure, and lower respiratory tract.[L11656,L11659] Amoxicillin is given with calvulanic acid to treat acute bacterial sinusitis, community acquired pneumonia, lower respiratory tract infections, acute bacterial otitis media, skin and skin structure infections, and urinary tract infections.[L11650,L7880,L11653] Amoxicillin is given with omeprazole in the treatment of _Helicobacter pylori_ (_H. pylori_) infection.[L9743,L11647]



Amoxicillin is used in combination with [vonoprazan] and [clarithromycin] as co-packaged triple therapy or in combination with [vonoprazan] as co-packaged dual therapy to treat _H. pylori_ infection in adults.[L41695]', 'Amoxicillin competitively inhibits penicillin-binding protein 1 and other high molecular weight penicillin binding proteins.[A6205,A190678]  Penicillin bind proteins are responsible for glycosyltransferase and transpeptidase reactions that lead to cross-linking of D-alanine and D-aspartic acid in bacterial cell walls.[A190678] Without the action of penicillin binding proteins, bacteria upregulate autolytic enzymes and are unable to build and repair the cell wall, leading to bacteriocidal action.[A190642,A190678]', 'Amoxicillin competitively inhibit penicillin binding proteins, leading to upregulation of autolytic enzymes and inhibition of cell wall synthesis.[A6205,A190678,A190642] Amoxicillin has a long duration of action as it is usually given twice daily.[L7880] Amoxicillin has a wide therapeutic range as mild overdoses are not associated with significant toxicity.[L11650] Patients should be counselled regarding the risk of anaphylaxis, _Clostridium difficile_ infections, and bacterial resistance.[L11656]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Valponex 200mg 40 enteric coated tabs.', 28933, 'فالبونكس 200مجم 40 اقراص', '39', NULL, 'Anti-epileptic.fatty acid', 'Anti-epileptic.fatty acid', NULL, 'Sodium valproate', 'Western pharmaceuticals industries', 'Tab', 'أقراص', '200mg', '4', NULL, NULL, NULL, NULL, 723, '2022-07-27', NULL, 0, 'For the treatment of small uncomplicated varicose veins of the lower extremities that show simple dilation with competent valves.', 'Sodium tetradecyl sulfate is a potent toxin for endothelial cells in that brief exposure to even low concentrations are effective in stripping endothelium over a considerable distance and exposing highly thrombogenic endothelium in the process. Diluted sodium tetradecyl sulfate is also able to induce a hypercoagulable state, possibly by selective inhibition of protein C, and can also promote platelet aggregation.', 'Sodium tetradecyl sulfate is an anionic surfactant which occurs as a white, waxy solid. It is used as a sclerosing agent in sclerotherapy. Sclerotherapy is an injection of a sclerosing agent directly through the skin into a lesion and is used primarily for slow-flow vascular anomalies, particularly for venous malformation and lymphatic malformation. Intravenous injection causes intima inflammation and thrombus formation. This usually occludes the injected vein. Subsequent formation of fibrous tissue results in partial or complete vein obliteration that may or may not be permanent.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Mucolase 5% syrup 120 ml', 23069, 'ميكوليز 5% شراب 120 مل', '6', NULL, 'Mucolytic', 'Mucolytic', NULL, 'Carboxymethylcysteine', 'Arab drug company.', 'Syrup', 'شراب', '5%', '1', 'مذيب للبلغم يعمل على تقليل لزوجة المخاط في المجاري التنفسية مما يسهل خروجها عن طريق السعال', NULL, NULL, '6223002147335', 1428, '2022-06-14', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Doxorubicin ebewe 10 mg vial', 17879, 'دوكسوريبسين 2مجم/مل 10مجم فيال', '103', '49.5', 'Antineoplastic', 'Antineoplastic', NULL, 'Doxorubicin', 'Sandoz > b.p pharma', 'Vial', 'فيال', '10 mg', '1', NULL, NULL, NULL, NULL, 889, '2025-02-03', NULL, 0, 'Doxorubicin is indicated for the treatment of neoplastic conditions like acute lymphoblastic leukemia, acute myeloblastic leukemia, Hodgkin and non-Hodgkin lymphoma, metastatic breast cancer, metastatic Wilms’ tumor, metastatic neuroblastoma, metastatic soft tissue and bone sarcomas, metastatic ovarian carcinoma, metastatic transitional cell bladder carcinoma, metastatic thyroid carcinoma, metastatic gastric carcinoma, and metastatic bronchogenic carcinoma.[L45231] Doxorubicin is also indicated for use as a component of adjuvant therapy in women with evidence of axillary lymph node involvement following resection of primary breast cancer.[L45231] For the liposomal formulation, doxorubicin is indicated for the treatment of ovarian cancer that has progressed or recurred after platinum-based chemotherapy, AIDS-Related Kaposi''s Sarcoma after the failure of prior systemic chemotherapy or intolerance to such therapy, and multiple myeloma in combination with bortezomib in patients who have not previously received bortezomib and have received at least one prior therapy.[L45226]', 'Generally, doxorubicin is thought to exert its antineoplastic activity through 2 primary mechanisms: intercalation into DNA and disrupt topoisomerase-mediated repairs and free radicals-mediated cellular damages.[A257614]



Doxorubicin can intercalate into DNA through the anthraquinone ring, which stabilizes the complex by forming hydrogen bonds with DNA bases.[A257659] Intercalation of doxorubicin can introduce torsional stress into the polynucleotide structure, thus destabilizing nucleosome structures and leading to nucleosome eviction and replacement.[A257664,A18538] Additionally, the doxorubicin-DNA complex can interfere with topoisomerase II enzyme activity by preventing relegation of topoisomerase-mediated DNA breaks, thus inhibiting replication and transcription and inducing apoptosis.[A257669,A257674] 



Moreover, doxorubicin can be metabolized by microsomal NADPH-cytochrome P-450 reductase into a semiquinone radical, which can be reoxidized in the presence of oxygen to form oxygen radicals.[A1579,A257584] Reactive oxygen species have been known to cause cellular damage through various mechanisms, including lipid peroxidation and membrane damage, DNA damage, oxidative stress, and apoptosis.[A257634] Although free radicals generated from this pathway can be deactivated by catalase and superoxide dismutase, tumor and myocardial cells tend to lack these enzymes, thus explaining doxorubicin''s effectiveness against cancer cells and tendency to cause cardiotoxicity.[A1579,A257584,A1578]

', 'Doxorubicin is a cytotoxic, cell-cycle non-specific anthracycline antibiotic.[A1574,A257674] It is generally thought to exert its antitumor effect by destabilizing DNA structures through intercalation, thus introducing DNA strand breakages and damages.[A257664,A18538,A257674] Not only does it alter the transcriptomes of the cells, failure in repairing DNA structures can also initiate the apoptotic pathways.[A257674,A257679] Additionally, doxorubicin intercalation can also interfere with vital enzyme activity, such as topoisomerase II, DNA polymerase, and RNA polymerase, leading to cell cycle arrests.[A257674] Finally, doxorubicin can also generate cytotoxic reactive oxygen species to exert cellular damages.[A257584]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Venoruton 300mg 20 caps.', 12182, 'فينوروتون 300مجم 20 كبسولة', '17', NULL, 'Vascular-protecting agent', 'Vascular-protecting agent', NULL, 'Rutoside', 'Novartis > glaxo smithkline', 'Cap', 'كبسولة', '300mg', '2', 'تقوية الاوردة الدموية -- تحسين الدورة الدموية -- تقليل الالام -- حالات الصدمات والكدمات', NULL, 'description a flavonol glycoside found in many plants including buckwheat; tobacco; forsythia; hydrangea; viola etc. it has been used therapeutically to decrease capillary fragility.', '6223002640393', 2044, '2022-12-14', NULL, 0, NULL, NULL, NULL, NULL);
