INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Recartex topical cream 50 gm', 25761, 'ريكارتكس كريم موضعي 50 جرام', '60', '45', 'Massage', 'Massage', NULL, 'Boswellia serrate ext.+chamomile ext.+aloe vera ext.+emu oil+panthenol', 'Hi-care > rony pharma', 'Cream', 'كريم', '50 gm', '1', 'كريم مساج', NULL, 'soothing & emollient cream', '6224008147602', 2521, '2025-08-19', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Myrin 100 mg 30 tabs', 23204, 'ميرين 100مجم 30 قرص', '4,975', NULL, 'Immunosuppressants', 'Immunosuppressants', NULL, 'Thalidomide', 'Lipomed ag > egyptian pharmaceutical trading company', 'Tab', 'أقراص', '100 mg', '3', 'يعمل علي تقليل المناعة في حالات زراعة الأعضاء ليجعل الجسم يتقبل العضو الجديد', NULL, NULL, NULL, 1142, '2022-04-10', NULL, 0, 'Thalidomide is primarily used for the acute treatment and maintenance therapy to prevent and suppress the cutaneous manifestations of moderate to severe erythema nodosum leprosum (ENL).[L41235]  ', 'The mechanism of action of thalidomide is not fully understood. Previous research indicate that thalidomide binds to cerebron, a component of the E3 ubiquitin ligase complex, to selectively degrade the transcription factor IKZF3 and IKZF1. These 2 transcription factors are vital for the proliferation and survival of malignant myeloma cells. [A246125] 



Regarding TNF-alpha, thalidomide seems to block this mediator via a variety of mechanism. Thalidomide can inhibit the expression myeloid differentiating factor 88 (MyD88), an adaptor protein that is involved in the TNF-alpha production signalling pathway, at the protein and RNA level. Additionally, thalidomide prevents the activation of Nuclear Factor Kappa B (NF-kB), another upstream effector of the TNF-alpha production pathway. Finally, some evidences suggest that thalidomide can block alpha-1 acid glycoprotein (AGP), a known inducer of the NF-kB/MyD88 pathway, thus inhibiting the expression of TNF-alpha [A246195]. The down-regulation of NF-kB and MyD88 can also affect the cross talk between the NF-kB/MyD88 and VEGF pathway, resulting in thalidomide''s anti-angiogenic effect. [A246200]', 'Thalidomide, originally developed as a sedative, is an immunomodulatory and anti-inflammatory agent with a spectrum of activity that is not fully characterized. However, thalidomide is believed to exert its effect through inhibiting and modulating the level of various inflammatory mediators, particularly tumor necrosis factor-alpha (TNF-a) and IL-6. [A714] Additionally, thalidomide is also shown to inhibit basic fibroblast growth factor (bFGF) and vascular endothelial growth factor (VEGF), suggesting a potential anti-angiogenic application of thalidomide in cancer patients. [A246185]



Thalidomide is racemic &mdash; it contains both left and right handed isomers in equal amounts: the (+)R enantiomer is effective against morning sickness, and the (&minus;)S enantiomer is teratogenic. The enantiomers are interconverted to each other in vivo; hence, administering only one enantiomer will not prevent the teratogenic effect in humans [A246190].', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Oxitruhans 60 capsules', 23606, 'اوكسيتروهانز 60 كبسولة', '299', NULL, 'Multivitamin for skin hair and nails', 'Multivitamin for skin hair and nails', NULL, 'Biotin+vitamin d3+zinc+selenium+vitamin e', 'Medcare pharmaceuticals', 'Capsule', 'كبسولة', NULL, '6', NULL, NULL, NULL, NULL, 2058, '2022-07-15', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Pulmocare syrup 125ml', 25485, 'بالموكار شراب 125 مل', '6', NULL, 'Anti-cough.antitussive and expectorant', 'Anti-cough.antitussive and expectorant', NULL, 'Chlorpheniramine+dextromethorphan+guaifenesin+pseudoephedrine', 'Eva pharma', 'Syrup', 'شراب', '125ml', '1', NULL, NULL, 'description a histamine h1 antagonist used in allergic reactions hay fever rhinitis urticaria and asthma. indication for the treatment of rhinitis urticaria allergy common cold asthma and hay fever. pharmacodynamics in allergic reactions an allergen inter', NULL, 1115, '2022-07-08', NULL, 0, NULL, NULL, NULL, NULL);
