INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Scalogen hair serum 120 ml', 10215, 'سكالوجين سيرم للشعر 120 مل', '220', '185', 'Hair care', 'Hair care', NULL, 'Saw palmetto extract+ginko biloba extract+ginseng extract+royal jelly extract+aloe vera extract+rose', 'Hi-care > roventis pharma', 'Serum', 'سيروم', '120 ml', '1', NULL, NULL, NULL, '6224007611012', 1576, '2025-01-02', NULL, 0, NULL, NULL, NULL, NULL);
