INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Treato hair shampoo 250 ml', 28417, 'تريتو للشعر 250مل', '190', '175', 'Hair care', 'Hair care', NULL, NULL, 'Egyptian company for cosmetics > infinity pharm', 'Amp', 'أمبول', '250 ml', '1', NULL, NULL, 'properties ï- formulated to purify & protect color treated tresses leaving it shiny reducing static and frizz. ï- helps to re-balance hair shaft by rebuilding broken bonds & replenishing nutrients. ï- repairs damaged hair & leaves the hair elastic and sof', '6222019600758', 685, '2025-06-30', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Cata hair serum 150 ml', 15930, 'كاتا سيروم للشعر 150 مل', '79', NULL, 'Hair care', 'Hair care', NULL, 'Jojoba oil+snake oil+lavender oil+rosemary oil+olive oil+emu oil+eruca oil', 'El-helou perfumes & cosmetics > capital pharma', 'Serum', 'سيروم', '150 ml', '1', NULL, NULL, NULL, NULL, 891, '2022-07-27', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Pancuronium 4mg/2ml 100 i.v. amp.', 24495, 'بانكورونيوم 4مجم/2مل 100 امبول حقن وريد', '700', NULL, 'Skeletal muscle relaxant', 'Skeletal muscle relaxant', NULL, 'Pancuronium', 'Sunny medical', 'Amp', 'أمبول', '4mg', '100', NULL, NULL, 'about pancuronium nicotinic antagonist long acting nondepolarizing neuromuscular blocker skeletal muscle relaxant. mechanism of action of pancuronium this bis-quaternary ammonium steroid is a non-depolarizing neuromuscular blocking agent that competes wit', NULL, 700, '2023-04-07', NULL, 0, 'Used as a muscle relaxant during anesthesia and surgical procedures.', 'Nondepolarizing neuromuscular blocking agents inhibit neuromuscular transmission by competing with acetylcholine for the cholinergic receptors of the motor end plate, thereby reducing the response of the end plate to acetylcholine. This type of neuromuscular block is usually antagonized by anticholinesterase agents.', 'Pancuronium is a non-depolarising muscle relaxant similar to curare. It acts as a competitive acetylcholine antagonist on neuromuscular junctions, displacing acetylcholine (hence competitive) from its post-synaptic nicotinic acetylcholine receptors. It is, unlike suxamethonium, a non-depolarising agent, which means, that it causes no spontaneous depolarisations upon association with the nicotinic receptor in neuromuscular junction, thus producing no muscle fasciculations upon administration. Pancuronium has no hormonal activity. It exerts slight vagolytic activity (i.e. diminishing activity of the vagus nerve) and no ganglioplegic (i.e., blocking ganglions) activity.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Calendron 4 f.c. tabs.', 15694, 'كاليندرون 4 اقراص', '32', NULL, 'Osteoporosis.bisphosphonates.plus vitamin d', 'Osteoporosis.bisphosphonates.plus vitamin d', NULL, 'Alendronate+vitamin d3', 'Debeiky > andalous pharma', 'Tab', 'أقراص', NULL, '1', NULL, NULL, 'it is indicated for the treatment of osteoporosis in postmenopausal women. in postmenopausal women it increases bone mass and reduces the incidence of fractures including those of the hip and spine (vertebral compression fractures). alendronate sodium is', NULL, 830, '2022-07-16', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Novex calamine lotion 100 ml', 25699, 'نوفكس كالامين لوشن 100 مل', '49', '45', 'Skin care.soothing', 'Skin care.soothing', NULL, 'Calamine+glycerin+camphor', 'Novex', 'Lotion', 'لوشن', '100 ml', '1', NULL, NULL, NULL, NULL, 767, '2025-08-03', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Koselugo 10 mg 60 caps.', 6304, 'كوسيلوجو 10مجم 60 كبسولة', '125,360', NULL, 'Mitogen-activated protein kinase inhibitor', 'Mitogen-activated protein kinase inhibitor', NULL, 'Selumetinib', 'Astrazeneca', 'Cap', 'كبسولة', '10 mg', '1', NULL, NULL, NULL, NULL, 897, '2022-08-16', NULL, 0, 'Selumetinib is indicated for the treatment of neurofibromatosis type 1 (NF1) in patients two years and older who have symptomatic, inoperable plexiform neurofibromas (PN).[A193611,L12852,L12969,L49986]', 'The Ras-Raf-MEK-ERK signaling cascade is known to be activated in several types of cancer, and regulates the transcription of proteins involved in apoptosis.[A193614,A193632] In addition, studies have shown that mutations of the Raf component of the pathway can contribute to chemotherapy drug resistance.[A193614,A193617] 



Ras as well as several kinases and phosphatases are responsible for regulating the Raf-MEK-ERK pathway.[A193614] Often in cancers, Ras (a G-protein coupled receptor) is deregulated, allowing downstream signalling to proceed unchecked.[A193614] Through several complex steps, Raf phosphorylates and activates MEK, which then phosphorylates and activates ERK.[A193602,A193614] ERK is then able to exert its effects on several downstream targets.[A193602,A193614] 



As such, therapies inhibiting upstream components of this pathway have become attractive targets for cancer treatment.[A193602] Selumetinib exerts its effects by selectively inhibiting MEK1 and MEK2 which can effectively blunt the pleiotropic effects of the Ras-Raf-MEK-ERK cascade.[A193533,A193620] By inhibiting this oncogenic pathway, selumetinib reduces cell proliferation, and promotes pro-apoptotic signal transduction.[A193602,A193602,A193632]', 'Selumetinib is a non-ATP-competitive mitogen-activated protein kinase kinase 1 and 2 (MEK1 and MEK2) inhibitor.[A193611] By selectively targeting MEK1 and MEK2, selumetinib is able to inhibit oncogenic downstream effects of the Raf-MEK-ERK signaling pathway, which is often overactive in certain types of cancer.[A193611] Indeed, a study investigating the effects of selumetinib in children with NF-1 found that treatment with the anti-neoplastic resulted in reduced tumor size.[A193533] Decreases in tumor-associated pain and improvements in overall function were also subjectively reported.[A193533]



Selumetinib has minimal off-target activity, contributing to its impressive safety profile.[A193638]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Alertam 120mg 10 f.c.tab.', 291, 'اليرتام 120مجم 10 اقراص', '33', '21', 'Antihistamine.antiallergy', 'Antihistamine.antiallergy', NULL, 'Fexofenadine', 'Mup', 'Tab', 'أقراص', '120mg', '1', 'علاج قوي لأعراض الحساسية.', NULL, 'about fexofenadine second generation h1 antagonist antihistamine. mechanism of action of fexofenadine fexofenadine blocks one type of peripheral receptor for histamine (the h1 receptor) and thus prevents activation of h1 receptor-containing cells by hista', '6221508511414', 6728, '2025-09-21', NULL, 0, 'In the United States, fexofenadine is indicated for the symptomatic treatment of allergic rhinitis in patients ≥2 years old and chronic idiopathic urticaria in patients ≥6 months old.[L4269] In Canada, fexofenadine carries the same indications but is approved only for patients ≥12 years old.[L10779] Fexofenadine is also available in combination with [pseudoephedrine] for the symptomatic treatment of season allergic rhinitis in patients ≥12 years old.[L10800]', 'The H<sub>1</sub> histamine receptor is responsible for mediating hypersensitivity and allergic reactions. Exposure to an allergen results in degranulation of mast cells and basophils, which then release histamine and other inflammatory mediators. Histamine binds to, and activates, H<sub>1</sub> receptors, which results in the further release of pro-inflammatory cytokines, such as interleukins, from basophils and mast cells. These downstream effects of histamine binding are responsible for a wide variety of allergic symptoms, such as pruritus, rhinorrhea, and watery eyes.[A1495]



Fexofenadine is considered an “inverse agonist” of the H<sub>1</sub> receptor because it binds to and stabilizes the inactive form of the receptor, preventing its activation and subsequent downstream effects.[A1495] It has a potent and selective affinity for H<sub>1</sub> receptors, and there is no evidence that it carries antidopaminergic, antiserotonergic, anticholinergic, sedative, or adrenergic blocking activity.[L10779] Fexofenadine does not cross the blood-brain barrier and thus is unlikely to cause significant CNS effects.[L10779]', 'Fexofenadine relieves allergy symptoms by antagonizing the actions of histamine, an endogenous compound predominantly responsible for allergic symptomatology.[L4269] The relatively long duration of action of fexofenadine (approximately 24 hours)[A1495] allows for once or twice daily dosing, and its rapid absorption allows for an onset of action within 1-3 hours. Fexofenadine should not be taken with fruit juice, as this may impair its absorption.[L4269]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Ivymegano - n 120 ml syrup', 2525, 'ايفيميجانو - ن 120 مل شراب', '75', NULL, 'Symptomatic treatment of chronic inflammatory bronchial conditions. for irritating and productive cough. mucolytic in case of bronchitis asthma and emphysema. expectorant in case of productive coughs associated with cold. supports normal functions of the immune system.', 'Symptomatic treatment of chronic inflammatory bronchial conditions. for irritating and productive cough. mucolytic in case of bronchitis asthma and emphysema. expectorant in case of productive coughs associated with cold. supports normal functions of the immune system.', NULL, 'Ivy leaf dry extract 35 mg+n-acetyl l-cysteine 50 mg+zinc gluconate 13.065 mg', 'Valuemed pharma egypt', 'Syrup', 'شراب', '120 ml', '1', NULL, NULL, NULL, NULL, 1344, '2025-01-21', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Herbazoole syrup 120 ml', 6058, 'هيربازول شراب 120 مل', '19', NULL, 'Cough products', 'Cough products', NULL, 'Guava leaves+tilia flower+fennel oil+honey', 'El-nour pharm', 'Syrup', 'شراب', '120 ml', '1', 'مذيب للبلغم ومهدئ للسعال ويخفف من الكحة الجافة ويستخدم فى حالات العطس والرشح ويعمل على تخفيف اعراض البرد', NULL, NULL, '6224007331101', 1096, '2022-12-05', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Tryptizol 10mg 60 tab', 11749, 'تريبتيزول 10مجم 60 قرص', '30', NULL, 'Psychiatric.antidepressants', 'Psychiatric.antidepressants', NULL, 'Amitriptyline', 'Kahira', 'Tab', 'أقراص', '10mg', '3', 'مضاد للاكتئاب', NULL, NULL, '6221068934609', 7718, '2022-12-13', NULL, 0, 'This drug in indicated for the following conditions [FDA label]:



Major depressive disorder in adults [L46357]



Management of neuropathic pain in adults



Prophylactic treatment of chronic tension-type headache (CTTH) in adults



Prophylactic treatment of migraine in adults



Treatment of nocturnal enuresis in children aged 6 years and above when organic pathology, including spina bifida and related disorders, have been excluded and no response has been achieved to all other non-drug and drug treatments, including antispasmodics and vasopressin-related products. This product should only be prescribed by a healthcare professional with expertise in the management of persistent enuresis [FDA label]



Off-label uses: irritable bowel syndrome, sleep disorders, diabetic neuropathy, agitation, fibromyalgia, and insomnia ', 'The mechanism of action of this drug is not fully elucidated. It is suggested that amitriptyline inhibits the membrane pump mechanism responsible for the re-uptake of transmitter amines, such as norepinephrine and serotonin, thereby increasing their concentration at the synaptic clefts of the brain [FDA label], [A174673].   These amines are important in regulating mood.  The monoamine hypothesis in depression, one of the oldest hypotheses, postulates that deficiencies of serotonin (5-HT) and/or norepinephrine (NE) neurotransmission in the brain lead to depressive effects [A174670].   This drug counteracts these mechanisms, and this may be the mechanism of amitriptyline in improving depressive symptoms.



Whether its analgesic effects are related to its mood-altering activities or attributable to a different, less obvious pharmacological action (or a combination of both) is unknown [A174661].', '**Effects in pain and depression**



Amitriptyline is a tricyclic antidepressant and an analgesic. It has anticholinergic and sedative properties [FDA label]. 

Clinical studies have shown that oral amitriptyline achieves, at a minimum, good to moderate response in up to 2/3 of patients diagnosed with post-herpetic neuralgia and 3/4 of patients diagnosed with diabetic neuropathic pain, and neurogenic pain syndromes that are frequently unresponsive to narcotic analgesics. Amitriptyline has also shown efficacy in diverse groups of patients with chronic non-malignant pain. There have also been some studies showing efficacy in managing fibromyalgia (an off-label use of this drug) [A174658], [A174667].



**Cardiovascular and Anticholinergic Effects**



Amitriptyline has strong anticholinergic properties and may cause ECG changes and quinidine-like effects on the heart [F3454]. Amitriptyline may inhibit ion channels, which are necessary for cardiac repolarization (hERG channels), in the upper micromolar range of therapeutic plasma concentrations. Therefore, amitriptyline may increase the risk for cardiac arrhythmia [FDA label]. Orthostatic hypotension and tachycardia can be a problem in elderly patients receiving this drug at normal doses for depression. There is evidence in the literature that these effects may occur, rarely, at the lower dosages utilized in the treatment of pain. As with any other tricyclic antidepressant agent, increased glucose levels can occur with amitriptyline [A174661]. 



**Effects on seizure threshold**



This drug also decreases the convulsive threshold and causes alterations in EEG and sleep patterns [F3454].  ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Docavarico 20 mg 5 i.v. amps.', 17747, 'دوكافاريكو 20 مجم 5 امبولات وريد', '360', '205', 'Sclerosant', 'Sclerosant', NULL, 'Polidocanol', 'Amoun', 'Amp', 'أمبول', '20 mg', '5', 'علاج دوالي المرئ -- علاج دوالي الساقين', NULL, NULL, NULL, 1982, '2024-07-25', NULL, 0, 'Polidocanol is a sclerosing agent indicated to treat uncomplicated spider veins and uncomplicated reticular veins in the lower extremity. ', 'When administered, polidocanol locally damages blood vessel endothelium. Following the endothelial damage, platelets aggregate at the site and attach to the venous wall eventually resulting in a dense network of platelets, cellular debris, and fibrin that occludes the vessel. Eventually the vessel is replaced by connective fibrous tissue.', 'Polidocanol has a concentration and volume dependent damaging effect on the blood vessel endothelium. ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Egy growth powder 200 gm sachet', 18112, 'ايجي جروث باودر 200 جرام كيس', '105', '95', 'Dietary supplement', 'Dietary supplement', NULL, 'High protein drink powder', 'Lemaco > bonvital', 'Powder', 'بودرة', '200 gm', '1', NULL, NULL, 'use: 2 spoons into a cup of milk or fruit drink once daily for children and 2-3 times for adults', '6224001079115', 6774, '2025-11-04', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Chloramphenicol 0.5% eye drops.', 16245, 'كلورامفينيكول 0.5% قطرة عين', '4', NULL, 'Antibiotic', 'Antibiotic', NULL, 'Chloramphenicol', 'Memphis', 'Amp', 'أمبول', '0.5%', '1', NULL, NULL, 'description an antibiotic first isolated from cultures of streptomyces venequelae in 1947 but now produced synthetically. it has a relatively simple structure and was the first broad-spectrum antibiotic to be discovered. it acts by interfering with bacter', '6221050100029', 1586, '2022-12-03', NULL, 0, 'Used in treatment of cholera, as it destroys the vibrios and decreases the diarrhea. It is effective against tetracycline-resistant vibrios. It is also used in eye drops or ointment to treat bacterial conjunctivitis.', 'Chloramphenicol is lipid-soluble, allowing it to diffuse through the bacterial cell membrane. It then reversibly binds to the L16 protein of the 50S subunit of bacterial ribosomes, where transfer of amino acids to growing peptide chains is prevented (perhaps by suppression of peptidyl transferase activity), thus inhibiting peptide bond formation and subsequent protein synthesis.', 'Chloramphenicol is a broad-spectrum antibiotic that was derived from the bacterium Streptomyces venezuelae and is now produced synthetically. Chloramphenicol is effective against a wide variety of microorganisms, but due to serious side-effects (e.g., damage to the bone marrow, including aplastic anemia) in humans, it is usually reserved for the treatment of serious and life-threatening infections (e.g., typhoid fever). Chloramphenicol is bacteriostatic but may be bactericidal in high concentrations or when used against highly susceptible organisms. Chloramphenicol stops bacterial growth by binding to the bacterial ribosome (blocking peptidyl transferase) and inhibiting protein synthesis.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Natrixam m.r. 1.5/10mg 28 f.c. tabs.', 23296, 'ناتريكسام ام ار 1.5/10مجم 28 قرص', '102', '88', 'Antihypertensive.combined diuretic with calcium channel blocker', 'Antihypertensive.combined diuretic with calcium channel blocker', NULL, 'Indapamide+amlodipine', 'Servier', 'Tab', 'أقراص', '10mg', '4', 'لعلاج مرضى ارتفاع ضغط الدم', NULL, 'amlodipine/indapamide is indicated as substitution therapy for treatment of essential hypertension in patients already controlled with indapamide and amlodipine given concurrently at the same dose level. dosage: one tablet per day as single dose preferabl', '6223003960711', 2706, '2025-03-05', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Blephamide ophth. susp. 5 ml', 15374, 'بليفاميد نقط 5 مل', '7', NULL, 'Glucocorticoid', 'Glucocorticoid', NULL, 'Prednisolone+sulfacetamide', 'Eipico > allergan', 'Susp', 'معلق', '5 ml', '1', NULL, NULL, NULL, '6221032330055', 918, '2022-07-18', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Pexal-400 depotabs s.r. 20 tab.', 24780, 'بيكسال 400 20 اقراص', '26', NULL, 'Anti-ischemic', 'Anti-ischemic', NULL, 'Pentoxifylline', 'Mup > acino', 'Tab', 'أقراص', NULL, '2', NULL, NULL, 'about pentoxifylline a xanthine derivative phosphodiesterase inhibitor blood viscosity reducing agent. mechanism of action of pentoxifylline the drug exerts its pharmacological action by improving rheological (flow) properties of blood. it improves blood', NULL, 1328, '2022-07-15', NULL, 0, 'Pentoxifylline is indicated for the treatment of intermittent claudication in patients with chronic occlusive arterial disease. Pentoxifylline may improve limb function and reduce symptoms but cannot replace other therapies such as surgical bypass or removal of vascular obstructions.[L30300]', 'Patients with peripheral arterial disease (PAD) may suffer from intermittent claudication, exertional leg pain that resolves upon rest, which is underscored by a complex etiology including vascular dysfunction (reduced limb perfusion, angiogenesis, and microcirculatory flow), systemic inflammation, and skeletal muscle dysfunction.[A226410] Pentoxifylline (PTX), (3,7-dimethyl-1-(5-oxohexyl)-3,7-dihydro-1H-purine-2,6-dione) or 1-(5-oxohexyl)-3,7-­dimethylxanthine, is a methyl-xanthine derivative that acts to lower blood viscosity by increasing erythrocyte flexibility, reducing plasma fibrinogen, inhibiting neutrophil activation, and suppressing erythrocyte/platelet aggregation; it also has antioxidant and anti-inflammatory effects.[A226415, L30300] Although the precise mechanism of action has yet to be elucidated, numerous studies have suggested several effects of PTX.



The classical interpretation of PTX''s broad effects is due to its ability to act, _in vitro_, as a non-specific cyclic-3'',5''-phosphodiesterase (PDE) inhibitor at millimolar concentrations; specifically, it has been proposed that inhibition of PDE type III and IV isozymes leads to elevated cyclic adenosine monophosphate (cAMP) levels, which mediate diverse downstream effects.[A226420, A226425, A226430] This view has been challenged, specifically by observing those plasma concentrations of PTX in routine clinical use are typically only around 1μM, far lower than those used to inhibit PDEs _in vitro_.[A226430] Instead, several studies have suggested that PTX can modulate adenosine receptor function, specifically the Gα-coupled A2A receptor (A2AR). Whether PTX acts directly as an A2AR agonist is unclear, although it can clearly increase the response of A2AR to adenosine.[A16840, A16821, A226350, A226435] A2AR activation activates adenylyl cyclase, which increases intracellular cAMP levels; this observation may explain PTX''s ability to increase intracellular cAMP in a PDE-independent fashion.[A226440]



Elevated cAMP levels have numerous downstream effects. cAMP-mediated activation of protein kinase A (PKA) suppresses nuclear translocation of NF-κB, which suppresses transcription of pro-inflammatory cytokines such as tumour necrosis factor (TNF-α), interleukin-1 (IL-1), and IL-6 as well as TNF-induced molecules such as adhesion molecules (ICAM1 and VCAM1) and the C-reactive protein (CRP).[A226415, A226445, A226450, A226455] PTX has also been shown to prevent the downstream phosphorylation of p38 MAPK and ERK, which are responsible for assembling the NADPH oxidase involved in the neutrophil oxidative burst. This effect is due to a PKA-independent decrease in Akt phosphorylation and a PKA-dependent decrease in phosphorylation of p38 MAPK and ERK.[A226450, A226465, A226470] This transcriptional regulation at least partially explains the anti-inflammatory and anti-oxidative properties of PTX.



Also, activated PKA can activate the cAMP response element-binding protein (CREB), which itself blocks SMAD-driven gene transcription, effectively disrupting transforming growth factor (TGF-β1) signalling.[A226440, A226475] This results in lower levels of fibrinogenic molecules such as collagens, fibronectin, connective tissue growth factor, and alpha-smooth muscle actin.[A226440, A226450, A226475, A226460] Hence, disruption of TGF-β1 signalling may explain the anti-fibrotic effects of PTX, including at least some of the decrease in blood viscosity.



The picture is complicated by the observation that PTX metabolites M1, M4, and M5 have been shown to inhibit C5 Des Arg- and formyl-methionylleucylphenylalanine-induced superoxide production in neutrophils and M1 and M5 significantly contribute to PTX''s observed hemorheological effects.[A226415, A226430, A226480] Overall, PTX administration is associated with decreased pro-inflammatory molecules, an increase in anti-inflammatory molecules such as IL-10, and decreased production of fibrinogenic and cellular adhesion molecules.', 'Pentoxifylline, a synthetic dimethylxanthine derivative structurally related to [theophylline] and [caffeine], exhibits hemorheological, anti-oxidative, and anti-inflammatory properties and is traditionally indicated in the treatment of peripheral arterial disease (PAD). In PAD patients with concurrent cerebrovascular and coronary artery diseases, pentoxifylline treatment has occasionally been associated with angina, arrhythmia, and hypotension. Concurrent use with [warfarin] should be associated with more frequent monitoring of prothrombin times. Also, patients with risk factors complicated by hemorrhages, such as retinal bleeding, peptic ulceration, and recent surgery, should be monitored periodically for bleeding signs.[L30300]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Gatipram 3mg/ml ophth. soln. 5 ml', 19621, 'جاتيبرام 3مجم/مل قطرة عين 5 مل', '14', NULL, 'Antibiotic.quinolone', 'Antibiotic.quinolone', NULL, 'Gatifloxacin', 'Kahira > modern office for import', 'Sol', 'محلول', '3mg', '1', NULL, NULL, NULL, NULL, 845, '2023-05-04', NULL, 0, 'For the treatment of bronchitis, sinusitis, community-acquired pneumonia, and skin infections (abscesses, wounds) caused by S. pneumoniae, H. influenzae, S. aureus, M. pneumoniae, C. pneumoniae, L. pneumophila, S. pyogenes', 'The bactericidal action of Gatifloxacin results from inhibition of the enzymes topoisomerase II (DNA gyrase) and topoisomerase IV, which are required for bacterial DNA replication, transcription, repair, and recombination.', 'Gatifloxacin is a synthetic broad-spectrum 8-methoxyfluoroquinolone antibacterial agent for oral or intravenous administration. is bactericidal and its mode of action depends on blocking of bacterial DNA replication by binding itself to an enzyme called DNA gyrase, which allows the untwisting required to replicate one DNA double helix into two. Notably the drug has 100 times higher affinity for bacterial DNA gyrase than for mammalian. Gatifloxacin is a broad-spectrum antibiotic that is active against both Gram-positive and Gram-negative bacteria. It should be used only to treat or prevent infections that are proven or strongly suspected to be caused by bacteria.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Sleep-aid 5mg 10 tab.', 27083, 'سليب ايد 5مجم 10 اقراص', '9', NULL, 'Psychiatric.anxiolytics-hypnotics', 'Psychiatric.anxiolytics-hypnotics', NULL, 'Zaleplon', 'October pharma', 'Tab', 'أقراص', '5mg', '1', NULL, NULL, 'about zaleplon pyrazolopyrimidine derivative( a nonbenzodiazepine sedative-hypnotic. mechanism of action of zaleplon zaleplon is a non benzodiazepine sedative-hypnotic drug. it binds to the benzodiazepine binding site on gaba-a receptors containing the al', '6221088010826', 812, '2022-12-08', NULL, 0, 'For the treatment of short-term treatment of insomnia in adults.', 'Zaleplon exerts its action through subunit modulation of the GABA<sub>B</sub>Z receptor chloride channel macromolecular complex. Zaleplon also binds selectively to the brain omega-1 receptor located on the alpha subunit of the GABA-A/chloride ion channel receptor complex and potentiates t-butyl-bicyclophosphorothionate (TBPS) binding.', 'Zaleplon is a nonbenzodiazepine hypnotic from the pyrazolopyrimidine class and is indicated for the short-term treatment of insomnia. While Zaleplon is a hypnotic agent with a chemical structure unrelated to benzodiazepines, barbiturates, or other drugs with known hypnotic properties, it interacts with the gamma-aminobutyric acid-benzodiazepine (GABA<sub>B</sub>Z) receptor complex. Subunit modulation of the GABA<sub>B</sub>Z receptor chloride channel macromolecular complex is hypothesized to be responsible for some of the pharmacological properties of benzodiazepines, which include sedative, anxiolytic, muscle relaxant, and anticonvulsive effects in animal models. Zaleplon also binds selectively to the CNS GABA<sub>A</sub>-receptor chloride ionophore complex at benzodiazepine(BZ) omega-1 (BZ1, ο1) receptors. ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Potassium m 3.3gm/100ml syrup 120ml', 25093, 'بوتاسيوم ام شراب 120 مل', '29', '20', 'Potassium supplement', 'Potassium supplement', NULL, 'Potassium chloride', 'El nile.', 'Syrup', 'شراب', '3.3gm', '1', 'مصدر للبوتاسيوم -- الحفاظ علي صحة القلب -- الحفاظ علي صحة العضلات -- الحفاظ علي صحة الكليتين', NULL, 'about potassium chloride electrolyte replenisher potassium supplement. mechanism of action of potassium chloride potassium chloride is used when the action of potassium cation is desired.it is used in the treatment of hypokalaemia or hypochloremic alkalos', '6221077053216', 4047, '2024-12-02', NULL, 0, 'For use as an electrolyte replenisher and in the treatment of hypokalemia.', 'Supplemental potassium in the form of high potassium food or potassium chloride may be able to restore normal potassium levels.', 'The potassium ion is in the principle intracellular cation of most body tissues. Potassium ions participate in a number of essential physiological processes including the maintenance of intracellular tonicity, the transmission of nerve impulses, the contraction of cardiac, skeletal and smooth muscle, and the maintenance of normal renal function. The intracellular concentration of potassium is approximately 150 to 160 mEq per liter. The normal adult plasma concentration is 3.5 to 5 mEq per liter. An active ion transport system maintains this gradient across the plasma membrane. Potassium is a normal dietary constituent and under steady-state conditions the amount of potassium absorbed from the gastrointestinal tract is equal to the amount excreted in the urine. The usual dietary intake of potassium is 50 to 100 mEq per day. Potassium depletion will occur whenever the rate of potassium loss through renal excretion and/or loss from the gastrointestinal tract exceeds the rate of potassium intake. Such depletion usually develops as a consequence of therapy with diuretics, primarily or secondary hyperaldosteronism, diabetic ketoacidosis, or inadequate replacement of potassium in patients on prolonged parenteral nutrition. Depletion can develop rapidly with severe diarrhea, especially if associated with vomiting. Potassium depletion due to these causes is usually accompanied by concomitant loss of chloride and is manifested by hypokalemia and metabolic alkalosis. Potassium depletion may produce weakness, fatigue, disturbances of cardiac rhythm (primarily ectopic beats), prominent U-waves in the electrocardiogram, and, in advanced cases, flaccid paralysis and/or impaired ability to concentrate urine. If potassium depletion associated with metabolic alkalosis cannot be managed by correcting the fundamental cause of the deficiency, e.g., where the patient requires long-term diuretic therapy, supplemental potassium in the form of high potassium food or potassium chloride may be able to restore normal potassium levels. In rare circumstances (e.g., patients with renal tubular acidosis) potassium depletion may be associated with metabolic acidosis and hyperchloremia. In such patients, potassium replacement should be accomplished with potassium salts other than the chloride, such as potassium bicarbonate, potassium citrate, potassium acetate, or potassium gluconate.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Myvi bones 60 pieces', 7412, 'ميفي بونز 60 قطعة', '460', NULL, NULL, NULL, NULL, NULL, NULL, 'Piece', 'قطعة', NULL, '1', NULL, NULL, NULL, NULL, 840, '2022-08-16', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Ravemantine 10mg 30 f.c. tabs.', 25735, 'رافيمانتين 10مجم 30 اقراص', '180', '120', 'Dopamine d2 agonist', 'Dopamine d2 agonist', NULL, 'Memantine', 'Eva pharma', 'Tab', 'أقراص', '10mg', '3', NULL, NULL, 'about memantine n-methyl-d-aspartate receptor antagonist dopamine d2 agonist antidementia agent in alzheimer`s disease. mechanism of action of memantine memantine in an uncompetitive (low to moderate affinity to) nmda (n-methyl-d-aspartate) receptor antag', '6223002455980', 897, '2024-08-24', NULL, 0, 'Memantine is used to manage moderate to severe Alzheimer''s dementia [FDA label]. 



A more recent systemic review and meta-analysis [A177106] indicates that memantine is beneficial as a first line drug for the treatment of Alzheimer''s dementia.  Cholinesterase inhibitors may be added to memantine for further beneficial effects on behavioral symptoms and other symptoms of dementia [A177106].', 'Continuous activation of the N-methyl-D-aspartate (NMDA) receptors in the central nervous system caused by _glutamate_ is thought to cause some of the Alzheimer''s disease symptoms. This overactivation is thought to contribute to neurotoxicity due to the excitatory properties of glutamate [T556]. The pharmacological effect of memantine likely occurs via the drug''s behavior as an uncompetitive (open-channel) NMDA receptor antagonist, preventing glutamate action on this receptor.  Memantine has a preference for the NMDA receptor-operated cation channels. Despite these antagonist effects, memantine has not been proven to prevent or retard the neurodegeneration seen in patients diagnosed with Alzheimer’s disease [FDA label].', '**General effects**



This drug inhibits calcium influx into cells that is normally caused by chronic NMDA receptor activation by glutamate [A1640]. This leads to the improvement of Alzheimer''s dementia symptoms, demonstrated by increased cognition and other beneficial central nervous system effects [A1640]. 



**Effects on neuroplasticity**



Like other NMDA receptor antagonists, memantine at high doses can reduce neuronal synaptic plasticity that is involved in learning and memory processes. At lower concentrations, which are normally used in the clinical setting, memantine can enhance neuronal synaptic plasticity in the brain, improve memory, and act as a neuroprotectant against the destruction of neurons caused by excitatory neurotransmitters [A1639]. 



**Effect on various receptors**



Memantine has demonstrated minimal activity for GABA, benzodiazepine, dopamine, adrenergic, histamine, and glycine receptors, as well as voltage-dependent Ca2+, Na+ or K+ channels. This drug has shown antagonist activity at the 5HT3 receptors. Laboratory studies suggest that memantine does not affect the reversible inhibition of the acetylcholinesterase normally caused by donepezil, galantamine, or tacrine [FDA label].', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Effaclar h cleansing cream 200 ml', 18094, 'ايفاكلار اتش كريم منظف 200 مل', '269', NULL, 'Skin care', 'Skin care', NULL, NULL, 'La roche-posay > united company for trading & distribution', 'Cream', 'كريم', '200 ml', '1', NULL, NULL, '*properties oily skin weakened by drying skin care products. tested under dermatological control in combination with over-drying treatments. non-comedogenic. suitable for dry and irritated skin. use lather a small amount of product with water in the palm', NULL, 677, '2022-08-05', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Calcitonium 100 i.u./ml 5 amps.', 30249, 'كالسيتونيوم 100وحدة/مل 5 مل', '155', '105', 'Osteoporosis.antiresorptives', 'Osteoporosis.antiresorptives', NULL, 'Calcitonin', 'Acdima international trading', 'Amp', 'أمبول', NULL, '5', NULL, NULL, 'prescribed for: injectable calcitonin is used to treat osteoporosis and paget s disease. injectable calcitonin also is used to treat seriously elevated blood calcium levels. the nasal spray is used only for osteoporosis in women who are at least 5 years p', '6224000588601', 3022, '2025-05-14', NULL, 0, 'Investigated for use/treatment in myocardial infarction, heart disease, and asthma.', 'While current drugs for treating HF and MI have a single mechanism of action, CGRP appears to work on several levels: for example, it (1) increases local blood flow enhancing hemodynamic proficiency; (2) protects heart muscle cells from the damage due to lack of oxygen (ischemia); and, (3) modulates the immune system by boosting anti-inflammatory cytokine expression while suppressing pro-inflammatory cytokines, which reduces the inflammatory response to provide a cardioprotective effect. All these actions are meant to minimize heart muscle damage and subsequent scar tissue formation, while promoting the healing process.', NULL, 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Extra vitol plus syrup 120 ml', 18724, 'اكسترا فيتول بلس 120 مل', '16', NULL, 'Multivitamins', 'Multivitamins', NULL, 'Multivitamins+minerals', 'Modern pharma > rashdeen pharma group', 'Syrup', 'شراب', '120 ml', '1', NULL, NULL, NULL, NULL, 931, '2022-07-09', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Tavegyl 1 mg 20 tab.', 27937, 'تافيجيل 1مجم 20 قرص', '9', NULL, 'Antihistamine.antiallergy', 'Antihistamine.antiallergy', NULL, 'Clemastine', 'Novartis > glaxo smithkline', 'Tab', 'أقراص', '1 mg', '2', NULL, NULL, 'description an ethanolamine-derivative first generation histamine h1 antagonist used in hay fever rhinitis allergic skin conditions and pruritus. indication for the relief of symptoms associated with allergic rhinitis such as sneezing rhinorrhea pruritus', '6223002640591', 1375, '2023-05-01', NULL, 0, 'For the relief of symptoms associated with allergic rhinitis such as sneezing, rhinorrhea, pruritus and acrimation. Also for the management of mild, uncomplicated allergic skin manifestations of urticaria and angioedema. Used as self-medication for temporary relief of symptoms associated with the common cold.', 'Clemastine is a selective histamine H1 antagonist and binds to the histamine H1 receptor. This blocks the action of endogenous histamine, which subsequently leads to temporary relief of the negative symptoms brought on by histamine.', 'Clemastine is an antihistamine that also induces anticholinergic and sedative effects. Antihistamines competitively antagonize various physiological effects of histamine including increased capillary permeability and dilatation, the formation of edema, the "flare" and "itch" response, and gastrointestinal and respiratory smooth muscle constriction. Within the vascular tree, H1- receptor antagonists inhibit both the vasoconstrictor and vasodilator effects of histamine. Depending on the dose, H1- receptor antagonists can produce CNS stimulation or depression. Most antihistamines exhibit central and/or peripheral anticholinergic activity. Antihistamines act by competitively blocking H1- receptor sites. Antihistamines do not pharmacologically antagonize or chemically inactivate histamine, nor do they prevent the release of histamine.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Physiomer normal jet nasal spray 135ml', 24920, 'فيزيومير نورمال جيت بخاخ للانف 135 مل', '350', '300', 'Nasal decongestant', 'Nasal decongestant', NULL, 'Natural undiluted and isotonic seawater', 'Laboratoire de la mer > star international company', 'Spray', 'بخاخ', '135ml', '1', NULL, NULL, '*for adults and children age 6 + *in case of stuffy nose it clears the upper respiratory tract and helps restore nasal breathing comfort and wellbeing. *in case of fluid or abundant secretions due to a cold or any other nasal infection: cleanses moistens', '3564300031258', 1901, '2025-10-07', NULL, 0, NULL, 'The predominant targets of NK cells are ‘stressed’ cells with downregulated expression of class I MHC (MHC-I), a common mechanism used by malignant or virus-infected cells to evade recognition by the body’s immune system. In response to encountering these cells, NK cells release two types of proteins, perforins and granzyme B, which attack the target cell’s membrane, damage its organelles, and trigger apoptosis. NK Cell immunotherapy is an emerging anti-cancer strategy, as these cells are able to exert effects without activating the graft-versus-host disease cascade. In addition, their broad mechanism allows for generally safe administration of NK cells that are not immunologically matched to a patient. ', NULL, 'DrugBank');
