INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Tova guard sanitizer gel 250ml', 28376, 'توفا جارد جل 250مل', '25', NULL, 'Disinfectant', 'Disinfectant', NULL, 'Ethyl alcohol 70% + aloe vera + chlorhexidine', 'Marketova healthcare', 'Gel', 'جل', '250ml', '1', NULL, NULL, NULL, NULL, 689, '2022-08-02', NULL, 0, 'Investigated for use/treatment in burns and burn infections, cardiac surgery, inflammatory disorders (unspecified), ischemic reperfusion injury, and sepsis and septicemia.', 'CTI-01 inhibits the systemic release of cytokines, such as TNF-alpha and HMGB1, which promote the body''s inflammatory response. The over-expression of these cytokines has been linked to diseases that occur in critical care settings, such as severe organ damage following cardiopulmonary bypass (CPB) and post-operative ileus following abdominal surgery.', NULL, 'DrugBank');
