INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Pacinos eye contour gel 30 gm', 24436, 'باسينوس آي كونتور جل 30 جرام', '185', '165', 'Eye contour', 'Eye contour', NULL, 'Sodium hyaluronate+collagen+caviar ext.+vitamin (c+e)+glutathione+liquorice ext.+coffee arabica seed ext.+', 'Egyptian co. for cosmetics > biohealth company for medicines', 'Gel', 'جل', '30 gm', '1', 'كريم لمحيط العين -- تقليل الهالات حول العين -- تقليل التجاعيد -- شدالجلد حول العين', NULL, 'composition: sodium hyaluronate+collagen+caviar ext.+vitamin (c+e)+glutathione+liquorice ext.+coffee arabica seed ext.+hydrolyzed rice protein+hydrolyzed yeast protein+retinol+carica papaya fruit ext.+cyclohexasiloxane. indication: anti aging eye contour', NULL, 4650, '2025-09-21', NULL, 0, 'For the treatment of small uncomplicated varicose veins of the lower extremities that show simple dilation with competent valves.', 'Sodium tetradecyl sulfate is a potent toxin for endothelial cells in that brief exposure to even low concentrations are effective in stripping endothelium over a considerable distance and exposing highly thrombogenic endothelium in the process. Diluted sodium tetradecyl sulfate is also able to induce a hypercoagulable state, possibly by selective inhibition of protein C, and can also promote platelet aggregation.', 'Sodium tetradecyl sulfate is an anionic surfactant which occurs as a white, waxy solid. It is used as a sclerosing agent in sclerotherapy. Sclerotherapy is an injection of a sclerosing agent directly through the skin into a lesion and is used primarily for slow-flow vascular anomalies, particularly for venous malformation and lymphatic malformation. Intravenous injection causes intima inflammation and thrombus formation. This usually occludes the injected vein. Subsequent formation of fibrous tissue results in partial or complete vein obliteration that may or may not be permanent.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Densitin 30 caps', 17247, 'دينسيتين 30 كبسولة', '144', '96', 'Calcium supplement plus vitamin d', 'Calcium supplement plus vitamin d', NULL, 'Calcium+magnesium+vitamin c+boron+vitamin d', 'Arab caps > multicare', 'Cap', 'كبسولة', NULL, '3', NULL, NULL, NULL, '6224007326343', 3423, '2025-10-20', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Neocarbon 30 caps', 13339, 'نيوكاربون 30 كبسولة', '185', '165', 'Antiflatulent', 'Antiflatulent', NULL, 'Activated charcoal+anise+peppermint', 'Marnys > walfarm pharmaceuticals', 'Cap', 'كبسولة', NULL, '2', 'مضاد للانتفاخات -- تقليل للغازات -- تقليل تقلصات المعدة', NULL, NULL, '8410885090423', 6613, '2025-08-03', NULL, 0, 'Used as a antidote to treat poisonings following excessive oral ingestion of certain medications or poisons. ', 'Active charcoal acts by binding to the pharmaceutical drugs or poisons such as organophosphates and decreasing the systemic absorption of toxic agents. Molecules with large volume of distribution, thus likely having higher lipid solubility, tends to bind have better absorptive binding to activated charcoal. Following the administration of activated charcoal, cathartics are indicated to evacuate the charcoal-poison bonded complex from the gastrointestinal tract. 

Activated charcoal may also have an effect on systemic drug levels by lowering the serum levels of already absorbed drugs or toxins. Many absorbed drugs that undergo significant hepatic metabolism and conjugation are eliminated via bile into the small intestines. When they reach the small intestines, drug conjugates can undergo hydrolysis and return to the enterohepatic circulation. Activated charcoal interferes with this process and binds to the conjugated drug before hydrolysis or the free deconjugated drug before reabsorption. ', 'Activated charcoal is used as a gastric decontamination agent in emergency clinical settings in case of poison or medication overdose. Studies show that early administration of one dose of activated charcoal can adsorb poison in the stomach and reduce absorption while it also works long after ingestion, by interruption of enterohepatic and enterovascular cycling of poison. ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Telfast 120mg 20 f.c. tab', 11247, 'تلفاست 120مجم 20 قرص', '116', '99', 'Antihistamine.antiallergy', 'Antihistamine.antiallergy', NULL, 'Fexofenadine', 'Sanofi', 'Tab', 'أقراص', '120mg', '2', 'علاج قوي لأعراض الحساسية.', NULL, NULL, '6223003991364', 13859, '2025-05-07', NULL, 0, 'In the United States, fexofenadine is indicated for the symptomatic treatment of allergic rhinitis in patients ≥2 years old and chronic idiopathic urticaria in patients ≥6 months old.[L4269] In Canada, fexofenadine carries the same indications but is approved only for patients ≥12 years old.[L10779] Fexofenadine is also available in combination with [pseudoephedrine] for the symptomatic treatment of season allergic rhinitis in patients ≥12 years old.[L10800]', 'The H<sub>1</sub> histamine receptor is responsible for mediating hypersensitivity and allergic reactions. Exposure to an allergen results in degranulation of mast cells and basophils, which then release histamine and other inflammatory mediators. Histamine binds to, and activates, H<sub>1</sub> receptors, which results in the further release of pro-inflammatory cytokines, such as interleukins, from basophils and mast cells. These downstream effects of histamine binding are responsible for a wide variety of allergic symptoms, such as pruritus, rhinorrhea, and watery eyes.[A1495]



Fexofenadine is considered an “inverse agonist” of the H<sub>1</sub> receptor because it binds to and stabilizes the inactive form of the receptor, preventing its activation and subsequent downstream effects.[A1495] It has a potent and selective affinity for H<sub>1</sub> receptors, and there is no evidence that it carries antidopaminergic, antiserotonergic, anticholinergic, sedative, or adrenergic blocking activity.[L10779] Fexofenadine does not cross the blood-brain barrier and thus is unlikely to cause significant CNS effects.[L10779]', 'Fexofenadine relieves allergy symptoms by antagonizing the actions of histamine, an endogenous compound predominantly responsible for allergic symptomatology.[L4269] The relatively long duration of action of fexofenadine (approximately 24 hours)[A1495] allows for once or twice daily dosing, and its rapid absorption allows for an onset of action within 1-3 hours. Fexofenadine should not be taken with fruit juice, as this may impair its absorption.[L4269]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Bebovi syrup 120ml', 32513, 'بيبوفى سبراى 120مل', '42', NULL, 'Multivitamin', 'Multivitamin', NULL, 'Iron+zinc+vit c+copper', 'Organix > pedia pharm', 'Syrup', 'شراب', '120ml', '1', NULL, NULL, NULL, NULL, 593, NULL, NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Gincostazen 30 cap', 5495, 'جينكوستازين 30 كبسولة', '150', '120', 'Antirheumatic.osteoarthritis.anabolic agents', 'Antirheumatic.osteoarthritis.anabolic agents', NULL, 'Glucosamine+ginko biloba leaf extract', 'Medizen pharmaceutical industries', 'Cap', 'كبسولة', NULL, '3', 'علاج الروماتيزم -- تقليل الالام المفاصل و العظام -- الحفاظ علي السائل الغضروفي -- تقليل التهاب المفاصل والعظام', NULL, 'about glucosamine amino sugar monosaccharide derivative anti-inflammatory antiarthritic. mechanism of action of glucosamine it stimulates cartilage cells to produce glucosaminoglycans and proteoglycans which are the building blocks of cartilage. this stop', '6222012400072', 6568, '2025-10-20', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Renu multiplus solution 60 ml', 25883, 'رينيو مالتي بلس محلول 60مل', '180', '140', 'Contact lenses solution', 'Contact lenses solution', NULL, 'Boric acid+edetate disodium+sodium borate+sodium chloride+dymed+hydranate+poloxamine', 'Bausch & lomb > kandil for trading', 'Solution', 'محلول', '60 ml', '1', NULL, NULL, 'multi-purpose solution for soft contact lenses including silicon hydrogels', NULL, 773, '2024-08-22', NULL, 0, 'No FDA- or EMA-approved therapeutic indications on its own.', 'Information regarding the mechanism of action of boric acid in mediating its antibacterial or antifungal actions is limited. Boric acid inhibits biofilm formation and hyphal transformation of _Candida albicans_, which are critical virulence factors [A32457]. In addition, arrest of fungal growth was observed with the treatment of boric acid [A32457].', 'Boric acid exhibits minimal bacteriostatic and antifungal activities [L2140]. Boric acid is likely to mediate antifungal actions at high concentrations over prolonged exposures [A32457]. ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Concreta hair lotion 120 ml', 30343, 'كونكريتا لوسيون للشعر 120 مل', '220', '195', 'Hair lotion', 'Hair lotion', NULL, 'Caffein+saw palmetto+biotin+panthenol+jojoba oil+castor oil+olive oil+keratin', 'El_nile factory for cosmetics > harmony care', 'Lotion', 'لوشن', '120 ml', '1', 'لوسيون للشعر غنى بالفيتامينات والكيراتين لترطيب وتغذية الشعر ومنع التساقط والتقصف والهيشان', NULL, 'hair lotion for hair growth', NULL, 1931, '2025-08-13', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Fenac 100 mg 10 tab.', 4784, 'فيناك 100مجم 10 اقراص', '8', NULL, 'Nsaid.acetic acid derivatives', 'Nsaid.acetic acid derivatives', NULL, 'Aceclofenac', 'European egyptian pharm. ind.', 'Tab', 'أقراص', '100 mg', '1', NULL, NULL, NULL, '6221076010074', 1362, '2022-12-05', NULL, 0, 'Aceclofenac is indicated for the relief of pain and inflammation in osteoarthritis, rheumatoid arthritis and ankylosing spondylitis. ', 'Through COX-2 inhibition, aceclofenac downregulates the production of various inflammatory mediators including prostaglandin E2 (PGE2), IL-1β, and TNF from the arachidonic acid (AA) pathway. Inhibition of IL-6 is thought to be mediated by diclofenac converted from aceclofenac [A19671]. Suppressed action of inflammatory cytokines decreases the production of reactive oxygen species. Aceclofenac is shown to decreased production of nitrous oxide in human articular chondrocytes [A19667]. In addition, aceclofenac interferes with neutrophil adhesion to endothelium by decreasing the expression of L-selectin (CD62L), which is a cell adhesion molecule expressed on lymphocytes [A19673]. Aceclofenac is proposed to stimulate the synthesis of glycosaminoglycan in human osteoarthritic cartilage which may be mediated through its inhibitory action on IL-1 production and activity [A19666]. The chrondroprotective effects are generated by 4''-hydroxyaceclofenac which suppresses IL-1 mediated production of promatrix metalloproteinase-1 and metalloproteinase-3 and interferes with the release of proteoglycan from chrondrocytes [A19666, A19667, A19672]. ', 'Aceclofenac is a NSAID that inhibits both isoforms of COX enzyme, a key enzyme involved in the inflammatory cascade. COX-1 enzyme is a constitutive enzyme involved in prostacyclin production and protective functions of gastric mucosa whereas COX-2 is an inducible enzyme involved in the production of inflammatory mediators in response to inflammatory stimuli. Aceclofenac displays more selectivity towards COX-2 (IC50 of 0.77uM) than COX-1 (IC50 of >100uM), which promotes its gastric tolerance compared to other NSAIDs. The primary metabolite, 4''-hydroxyaceclofenac, also minimally inhibits COX-2 with IC50 value of 36uM [A19667]. Although the mode of action of aceclofenac is thought to mainly arise from the inhibition of synthesis of prostaglandins (PGE2), aceclofenac also inhibits the production of inflammatory cytokines, interleukins (IL-1β, IL-6), and tumor necrosis factors (TNF) [A19666, A19667]. It is also reported that aceclofenac also affects the cell adhesion molecules from neutrophils [A19673]. Aceclofenac also targets the synthesis of glycosaminoglycan and mediates chrondroprotective effects [A19666]. ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Starville whitening hair reducer roll-on 60 ml', 7152, 'ستارفيل وايتننج هير ريديوسر رول اون 60 مل', '160', '140', 'Skin care.whitening', 'Skin care.whitening', NULL, NULL, 'Parkville', 'Unknown', 'غير محدد', '60 ml', '1', NULL, NULL, NULL, '6223012631800', 758, '2025-11-02', NULL, 0, NULL, NULL, NULL, NULL);
