INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Selfora hair strong lotion 120 ml', 26739, 'سيلفورا هير لوشن 120 مل', '45', NULL, 'Hair care', 'Hair care', NULL, 'Rosemary+chamomile+panthenol', 'El-helou perfumes & cosmetics > m.n.b', 'Lotion', 'لوشن', '120 ml', '1', NULL, NULL, NULL, NULL, 572, '2022-08-04', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Carbosin 150mg vial', 15826, 'كاربوسين 150 مجم فيال', '200', NULL, 'Antineoplastic', 'Antineoplastic', NULL, 'Carboplatin', 'Pharmachemie-netherlands > copad pharma', 'Vial', 'فيال', '150mg', '1', NULL, NULL, NULL, NULL, 678, '2022-08-07', NULL, 0, 'Carboplatin is indicated in combination with an established combination of chemotherapeutic agents for the initial treatment of advanced ovarian carcinoma.[L32253] Carboplatin is also indicated for the palliative treatment of ovarian carcinoma, recurrent after prior chemotherapy.[L32253]', 'Carboplatin predominantly acts by attaching alkyl groups to the nucleotides, leading to the formation of monoadducts, and DNA fragmenting when repair enzymes attempt to correct the error.[A230158,L32253] 2% of carboplatin''s activity comes from DNA cross-linking from a base on one strand to a base on another, preventing DNA strands from separating for synthesis or transcription.[A230158,A1472] Finally, carboplatin can induce a number of different mutations.[A230158]', 'Carboplatin is an organoplatinum antineoplastic alkylating agent used in the treatment of advanced ovarian carcinoma.[L32253] Carboplatin has a long duration of action as it is given every 4 weeks, and a narrow therapeutic index.[L32253] Patients should be counselled regarding bone marrow suppression and anemia.[L32253]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Isorifa 150/300 mg 10 caps.', 21032, 'ايزوريفا 150/300 مجم 10 كبسولات', '11', NULL, 'Antitubercular', 'Antitubercular', NULL, 'Isoniazid+rifampicin', 'Jedco int. co. for pharmaceuticals', 'Cap', 'كبسولة', '300 mg', '1', NULL, NULL, 'about isoniazid hydrazine derivative antitubercular agent. mechanism of action of isoniazid isoniazid is a bactericidal drug. i t inhibits the mycolic acid synthesis which is essential for the cell wall formation. it also inhibits dna rna and protein synt', NULL, 1306, '2022-07-21', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Lupron depot 22.5mg/2ml vial for i.m inj.', 22125, 'لوبرون ديبوت 22.5 مجم/2مل امبول حقن', '2,490', NULL, 'Antineoplastic', 'Antineoplastic', NULL, 'Leuprolide acetate', 'Takeda > abbott laboratories', 'Inj', 'حقن', '22.5mg', '1', NULL, NULL, 'about leuprolide gnrh analog lh-rh agonist antineoplastic. mechanism of action of leuprolide this gnrh analogue is a potent inhibitor of gonadotropin secretion. by continuous administration of this drug cause down regulation of lhrh receptors and an uncou', NULL, 702, '2022-08-04', NULL, 0, 'Leuprolide is indicated for the treatment of advanced prostate cancer [L13781, L34415] and as palliative treatment of advanced prostate cancer.[L13790] 



It is also used for the treatment of pediatric patients with central precocious puberty (CPP).[L13784, L13787] 



In combination with oral [norethisterone] (also known as norethindrone), leuprolide is also indicated for the initial treatment of the symptoms of endometriosis.[L10310] Finally, in combination with iron supplementation, leuprolide is indicated for the preoperative hematological improvement of anemic patients with uterine leiomyomata (uterine fibroids).[L13814]', 'Gonadotropin-releasing hormone (GnRH) is a naturally occurring decapeptide that modulates the hypothalamic-pituitary-gonadal (HPG) axis. GnRH binds to corresponding receptors (GnRHRs) on the anterior pituitary gonadotropes, which in turn release luteinizing hormone (LH) and follicle-stimulating hormone (FSH); these, in turn, affect the downstream synthesis and release of the sex hormones testosterone, dihydrotestosterone, estrone, and estradiol.[A203126, A203132]



Despite the variety of conditions indicated for treatment with leuprolide, the mechanism of action underlying efficacy is the same in all cases. As a GnRHR agonist, leuprolide binds to and initially activates downstream LH and FSH release; this initial spike in gonadotropin levels is responsible for some of the adverse effects associated with treatment. After 2-4 weeks of treatment, continuous stimulation of GnRHR results in feedback inhibition and significant downregulation of LH, FSH, and their corresponding downstream effects, producing a therapeutic benefit. These effects are reversible upon treatment discontinuation.[A203126, A203129, A203132, A203222, L10310, L13781, L13784, L13787, L13790, L13814]', 'Leuprolide is a gonadotropin-releasing hormone (GnRH) analogue that functions as a GnRH receptor superagonist.[A203132, A203222] After an initial spike in GnRH-mediated steroidal production, including testosterone and estradiol, prolonged use results in a significant drop in circulating steroid levels, in line with those produced through other forms of androgen-deprivation therapy (ADT).[A203126, A203129, A203132] The corresponding hormonal/steroidal changes produce specific adverse effects in different patient populations.



In women undergoing treatment for endometriosis or uterine leiomyomata, careful consideration regarding pregnancy status is advised. The initial increase in estradiol levels may worsen symptoms such as pain and bleeding. Long-term use of leuprolide is associated with loss of bone mineral density. Patients co-administered with [norethisterone] may experience sudden vision loss, proptosis, diplopia, migraine, thrombophlebitis, and pulmonary embolism and may also be at higher risk of cardiovascular disease. Patients with a history of depression may experience severe recurrence of depressive symptoms.[L10310, L13814]



In men undergoing palliative treatment for advanced/metastatic prostate cancer, short-term spikes in testosterone levels may cause tumour flare and associated symptoms such as bone pain, hematuria, neuropathy, bladder and/or ureteral obstruction, and spinal cord compression. In addition, patients are at increased risk of developing hyperglycemia, diabetes, and cardiovascular disease, which may manifest through myocardial infarction, stroke, cardiac death, or prolonged QT/QTc interval. In addition, Leuprolide may cause convulsions and embryo-fetal toxicity.[L13781, L13790]



In pediatric patients undergoing treatment for central precocious puberty (CPP), the initial steroidal spike may be associated with increased clinical signs of puberty within 2-4 weeks of treatment initiation. In addition, leuprolide may cause convulsions and psychiatric symptoms, including irritability, impatience, aggression, anger, and crying.[L13784, L13787]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Imutrexate 15 mg 10 tabs.', 32983, 'ايموتريكسات 15 مجم 10 قرص', '105', '80', 'Antineoplastic', 'Antineoplastic', NULL, 'Methotrexate', 'Eva pharma', 'Tab', 'أقراص', '15 mg', '1', 'مضاد حمض الفوليك ويستخدم فى علاج الاورام السرطانية والتهاب المفاصل الروماتويدي الشديد.', NULL, NULL, '6224011817257', 3127, '2024-09-25', NULL, 0, 'Methotrexate oral solution is indicated for pediatric acute lymphoblastic leukemia and pediatric polyarticular juvenile idiopathic arthritis.[L7144] Methotrexate injections for subcutaneous use are indicated for severe active rheumatoid arthritis, polyarticular juvenile idiopathic arthritis and severe, recalcitrant, disabling psoriasis.[L7147,L7150,L10457] It has also been approved by the EMA for the treatment of adult patients requiring systemic therapy for moderate-to-severe plaque psoriasis.[L48796]



Other formulations are indicated to treat gestational choriocarcinoma, chorioadenoma destruens, hydatiform mole, breast cancer, epidermoid cancer of the head and neck, advanced mycosis fungoides, lung cancer, and advanced non-Hodgkin''s lymphoma.[L7180] It is also used in the maintenance of acute lymphocytic leukemia.[L7180] Methotrexate is also given before treatment with leucovorin to prolong relapse-free survival following surgical removal of a tumour in non-metastatic osteosarcoma.[L7180]', 'Methotrexate enters tissues and is converted to a methotrexate polyglutamate by folylpolyglutamate.[A180322]



Methotrexate''s mechanism of action is due to its inhibition of enzymes responsible for nucleotide synthesis including  dihydrofolate reductase, thymidylate synthase, aminoimidazole caboxamide ribonucleotide transformylase (AICART), and amido phosphoribosyltransferase.[A180322] Inhibtion of nucleotide synthesis prevents cell division.



In rheumatoid arthritis, methotrexate polyglutamates inhibit AICART more than methotrexate.[A180322] This inhibition leads to accumulation of AICART ribonucleotide, which inhibits adenosine deaminase, leading to an accumulation of adenosine triphosphate and adenosine in the extracellular space, stimulating adenosine receptors, leading to anti-inflammatory action.[A180322]', 'Methotrexate inhibits enzymes responsible for nucleotide synthesis which prevents cell division and leads to anti-inflammatory actions.[A180322] It has a long duration of action and is generally given to patients once weekly.[A180322,L7144,L7147,L7150] Methotrexate has a narrow therapeutic index.[A180325]



Do not take methotrexate daily.[L7147,L7150]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Lacricarmin 2mg/0.4ml eye drops 30 x 0.4 ml sdu', 33075, 'لاكريكارمين 2 مجم /0.4مل قطرة للعين 30 * 0.4 مل وحدات مفردة', '72', '30', 'Eye lubricant', 'Eye lubricant', NULL, 'Sodium carboxymethylcellulose', 'Eva pharma', 'Drops', 'نقط', '2mg', '1', NULL, NULL, NULL, NULL, 703, '2025-08-06', NULL, 0, 'For the treatment of small uncomplicated varicose veins of the lower extremities that show simple dilation with competent valves.', 'Sodium tetradecyl sulfate is a potent toxin for endothelial cells in that brief exposure to even low concentrations are effective in stripping endothelium over a considerable distance and exposing highly thrombogenic endothelium in the process. Diluted sodium tetradecyl sulfate is also able to induce a hypercoagulable state, possibly by selective inhibition of protein C, and can also promote platelet aggregation.', 'Sodium tetradecyl sulfate is an anionic surfactant which occurs as a white, waxy solid. It is used as a sclerosing agent in sclerotherapy. Sclerotherapy is an injection of a sclerosing agent directly through the skin into a lesion and is used primarily for slow-flow vascular anomalies, particularly for venous malformation and lymphatic malformation. Intravenous injection causes intima inflammation and thrombus formation. This usually occludes the injected vein. Subsequent formation of fibrous tissue results in partial or complete vein obliteration that may or may not be permanent.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Sprinkazen 50mg sprinkle 10 caps.', 27429, 'سبرينكازين 50مجم 10 كبسولات', '13', NULL, 'Anti-epileptic.fructose derivative', 'Anti-epileptic.fructose derivative', NULL, 'Topiramate', 'Medizen pharmaceutical industries', 'Cap', 'كبسولة', '50mg', '1', NULL, NULL, 'about topiramate a sulfamate-substituted monosaccharide carbonic anhydrase inhibitor anticonvulsant antimigraine. mechanism of action of topiramate topiramate acts on the motor cortex where it stabilizes neuronal membrane and inhibits the spread of seizur', NULL, 793, '2022-07-18', NULL, 0, 'Topiramate is indicated  for the following conditions: 1)Monotherapy for partial onset or primary generalized tonic-clonic seizures for patients 2 years of age and above 2)Adjunctive therapy for partial onset seizures or primary generalized tonic-clonic seizures for both adult and pediatric patients above 2 years old 3)Adjunctive therapy for seizures associated with Lennox-Gastaut syndrome in patients above 2 years of age 4)Prophylaxis of migraine in children 12 years of age and older and adults.[L10544]



Topiramate is also used off-label as an adjunct therapy for weight management[L10550] and for mood disorders.[A188312]', 'A seizure is an abnormal and unregulated electrical discharge occurring in the brain. This leads to transient interruption in brain function, manifested by reduced alertness, abnormal sensations, and focal involuntary movements or convulsions. Several types of seizures exist, with common types including tonic-clonic seizures and partial onset seizures.[L5548]



The exact mechanisms by which topiramate exerts pharmacological actions on seizures and migraines are currently not fully characterized.[A188336,L10544]  Several properties of this drug, however, are likely to contribute to its therapeutic effects. Topiramate has been observed to exert actions on voltage-dependent sodium channels, GABA receptors, and glutamate receptors.[A175249,L10544] 



Topiramate stimulates GABA-A receptor activity at brain non-benzodiazepine receptor sites and reduces glutamate activity at both AMPA and kainate receptors. Normally, GABA-A receptors are inhibitory and glutaminergic receptors are stimulatory for neuronal activity.[A175243,T752] By increasing GABA activity and inhibiting glutamate activity, topiramate blocks neuronal excitability, preventing seizures and migraines.[A188333,A188309,L10544] Additionally, it blocks the voltage-dependent sodium channels, further blocking seizure activity.[A175243] Topiramate has been shown to inhibit various carbonic anhydrase isozymes, but the clinical significance of this is unknown at this time.[A10342,A10348,A20066,L10544]



', 'Topiramate prevents the occurrence of seizures and prevents migraine symptoms by reducing neural pathway excitability.[A188330,L10544] It is important to note that this drug may cause metabolic acidosis, mood changes, suicidal thoughts and attempts, as well as kidney stones. When topiramate is combined with [valproic acid], it is known to cause hypothermia.[L10544]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Diclowad 50mg 12 sachets.', 17623, 'ديكلواد 50مجم 12 اكياس', '22', NULL, 'Nsaid.acetic acid derivatives', 'Nsaid.acetic acid derivatives', NULL, 'Diclofenac potassium', 'Egpi > al rowad pharmaceutical industrial co.', 'Sachet', 'أكياس', '50mg', '1', NULL, NULL, 'about diclofenac potassium nsaid anti-inflammatory antipyretic and analgesic. mechanism of action of diclofenac potassium diclofenac possess analgesic anti-inflammatory and antipyretic action. it inhibits the enzyme cyclo-oxygenase and there by inhibits t', NULL, 891, '2022-07-23', NULL, 0, 'Diclofenac is indicated for use in the treatment of pain and inflammation from varying sources including inflammatory conditions such as osteoarthritis, rheumatoid arthritis, and ankylosing spondylitis, as well as injury-related inflammation due to surgery and physical trauma. It is often used in combination with [misoprostol] as a gastro-protective agent in patients with high risk of developing NSAID-induced ulcers.', 'Diclofenac inhibits cyclooxygenase-1 and -2, the enzymes responsible for production of prostaglandin (PG) G<sub>2</sub> which is the precursor to other PGs.[label,T116] These molecules have broad activity in pain and inflammation and the inhibition of their production is the common mechanism linking each effect of diclofenac.



PGE<sub>2</sub> is the primary PG involved in modulation of nociception.[A179023] It mediates peripheral sensitization through a variety of effects.[T116,A179023] PGE<sub>2</sub> activates the G<sub>q</sub>-coupled EP<sub>1</sub> receptor leading to increased activity of the inositol trisphosphate/phospholipase C pathway. Activation of this pathway releases intracellular stores of calcium which directly reduces action potential threshold and activates protein kinase C (PKC) which contributes to several indirect mechanisms. PGE<sub>2</sub> also activates the EP<sub>4</sub> receptor, coupled to G<sub>s</sub>, which activates the adenylyl cyclase/protein kinase A (AC/PKA) signaling pathway. PKA and PKC both contribute to the potentiation of transient receptor potential cation channel subfamily V member 1 (TRPV1) potentiation, which increases sensitivity to heat stimuli. They also activate tetrodotoxin-resistant sodium channels and inhibit inward potassium currents. PKA further contributes to the activation of the P2X3 purine receptor and sensitization of T-type calcium channels. The activation and sensitization of depolarizing ion channels and inhibition of inward potassium currents serve to reduce the intensity of stimulus necessary to generate action potentials in nociceptive sensory afferents. PGE<sub>2</sub> act via EP<sub>3</sub> to increase sensitivity to bradykinin and via EP<sub>2</sub> to further increase heat sensitivity. Central sensitization occurs in the dorsal horn of the spinal cord and is mediated by the EP<sub>2</sub> receptor which couples to G<sub>s</sub>. Pre-synaptically, this receptor increases the release of pro-nociceptive neurotransmitters glutamate, CGRP, and substance P. Post-synaptically it increases the activity of AMPA and NMDA receptors and produces inhibition of inhibitory glycinergic neurons. Together these lead to a reduced threshold of activating, allowing low intensity stimuli to generate pain signals. PGI<sub>2</sub> is known to play a role via its G<sub>s</sub>-coupled IP receptor although the magnitude of its contribution varies. It has been proposed to be of greater importance in painful inflammatory conditions such as arthritis. By limiting sensitization, both peripheral and central, via these pathways NSAIDs can effectively reduce inflammatory pain.



PGI<sub>2</sub> and PGE<sub>2</sub> contribute to acute inflammation via their IP and EP<sub>2</sub> receptors.[T116,A179044] Similarly to β adrenergic receptors these are G<sub>s</sub>-coupled and mediate vasodilation through the AC/PKA pathway. PGE<sub>2</sub> also contributes by increasing leukocyte adhesion to the endothelium and attracts the cells to the site of injury.[T116] PGD<sub>2</sub> plays a role in the activation of endothelial cell release of cytokines through its DP<sub>1</sub> receptor.[A179044] PGI<sub>2</sub> and PGE<sub>2</sub> modulate T-helper cell activation and differentiation through IP, EP<sub>2</sub>, and EP<sub>4</sub> receptors which is believed to be an important activity in the pathology of arthritic conditions. By limiting the production of these PGs at the site of injury, NSAIDs can reduce inflammation.



PGE<sub>2</sub> can cross the blood-brain barrier and act on excitatory G<sub>q</sub> EP<sub>3</sub> receptors on thermoregulatory neurons in the hypothalamus.[T116] This activation triggers an increase in heat-generation and a reduction in heat-loss to produce a fever. NSAIDs prevent the generation of PGE<sub>2</sub> thereby reducing the activity of these neurons.

', 'Diclofenac reduces inflammation and by extension reduces nociceptive pain and combats fever.[label,T116] It also increases the risk of developing a gastrointestinal ulcer by inhibiting the production of protective mucus in the stomach.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Temodal 100mg 5 caps.', 27998, 'تيمودال 10مجم 5 كبسول', '3,199', NULL, 'Antineoplastic', 'Antineoplastic', NULL, 'Temozolomide', 'Schering plough > ramco', 'Cap', 'كبسولة', '100mg', '1', NULL, NULL, NULL, NULL, 1511, '2022-07-22', NULL, 0, 'Temozolomide is indicated in adult patients for the treatment of newly diagnosed glioblastoma concomitantly with radiotherapy and for use as maintenance treatment thereafter. It is also indicated for the treatment of refractory anaplastic astrocytoma in adult patients or adjuvant therapy for adults with newly diagnosed anaplastic astrocytoma.[L48265]', 'Glioblastoma (glioblastoma multiforme) is the most common and aggressive adult primary brain tumour, accounting for 45.6% of all primary malignant brain tumours. Primarily defined histopathologically by necrosis and microvascular proliferation (WHO grade IV classification), glioblastomas are commonly treated through radiotherapy and concomitant alkylation-based chemotherapy with temozolomide.[A229848] Temozolomide (TMZ) is a small (194 Da) lipophilic alkylating agent of the imidazotetrazine class that is stable at acidic pH, allowing for both oral and intravenous dosing, and can cross the blood-brain barrier to affect CNS tumours.[A229853, A229858, L32033, A229893] After absorption, TMZ undergoes spontaneous nonenzymatic breakdown at physiological pH to form 5-(3-methyltriazen-1-yl) imidazole-4-carboxamide (MTIC), which then reacts with water to produce 5-aminoimidazole-4-carboxamide (AIC) and a highly reactive methyl diazonium cation.[A229853] Brain tumours such as glioblastoma typically possess a more alkaline pH than healthy tissue, favouring TMZ activation within tumour tissue.[A229853]



The methyl diazonium cation is highly reactive and methylates DNA at the N7 position of guanine (N7-MeG, 70%), the N3 position of adenine (N3-MeA, 9%), and the O6 position of guanine (O6-MeG, 6%). Although more prevalent, N7-MeG and N3-MeA are rapidly repaired by the base excision repair pathway and are not primary mediators of temozolomide toxicity, although N3-MeA lesions are lethal if not repaired. By comparison, repair of O6-MeG requires action by the suicide enzyme methylguanine-DNA methyltransferase (MGMT), which removes the methyl group to restore guanine. If not repaired by MGMT, O6-MeG mispairs with thymine, activating the DNA mismatch repair (MMR) pathway that removes the thymine (not the O6-MeG), resulting in futile cycles of repair and eventual DNA strand breaks leading to apoptosis.[A229853] As MMR activity is crucial for temozolomide cytotoxicity, cells that have reduced or absent MGMT function and an intact MMR pathway are the most sensitive to temozolomide treatment.[A229853, A229858] Glioblastomas that upregulate MGMT downregulate MMR or alter both are resistant to TMZ, leading to treatment failure.[A229853]



More recently, increased interest has also been shown in the immunomodulatory effects of TMZ, related to its myelosuppressive effects. Counterintuitively, lymphodepletion may enhance the antitumour effects of cellular immunotherapy and improve the dynamics of memory cells by altering tumour-specific versus tumour-tolerant populations. The depletion of tumour-localized immunosuppressive T<sub>reg</sub> cells may contribute to an improved response to immunotherapy. Hence, TMZ treatment may also form the backbone of immunotherapy strategies against glioblastoma in the future.[A229873]', 'Temozolomide is a prodrug of the imidazotetrazine class that requires nonenzymatic hydrolysis at physiological pH _in vivo_ to perform alkylation of adenine/guanine residues, leading to DNA damage through futile repair cycles and eventual cell death. Temozolomide treatment is associated with myelosuppression, which is likely to be more severe in females and geriatric patients. Patients must have an ANC of ≥1.5 x 10<sup>9</sup>/L and a platelet count of ≥100 x 10<sup>9</sup>/L before starting therapy and must be monitored weekly during the concomitant radiotherapy phase, on days one and 22 of maintenance cycles, and weekly at any point where the ANC/platelet count falls below the specified values until recovery. Cases of myelodysplastic syndrome and secondary malignancies, including myeloid leukemia, have been observed following temozolomide administration. Pneumocystis pneumonia may occur in patients undergoing treatment, and prophylaxis should be provided for patients in the concomitant phase of therapy with monitoring at all stages. Severe hepatotoxicity has also been reported, and liver testing should be performed at baseline, midway through the first cycle, before each subsequent cycle, and approximately two to four weeks after the last dose. Animal studies suggest that temozolomide has significant embryo-fetal toxicity; male and female patients should practice contraception up to three and six months following the last dose of temozolomide, respectively.[L32033]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Superkids kids shampoo 300 ml', 33724, 'سوبركيدز شامبو 300 مل', '135', '110', 'Hair care', 'Hair care', NULL, NULL, 'Parkville', 'Amp', 'أمبول', '300 ml', '1', NULL, NULL, NULL, NULL, 436, '2025-03-10', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Appetovar syrup 120 ml', 7986, 'ابيتوفار شراب 120 مل', '80', NULL, 'Appetizer', 'Appetizer', NULL, 'Vitamin b1 (7.86 mg)+vitamin b2 (4.124 mg)+niacinamide (10 mg)+vitamin b6 (4.11 mg)+vitamin b12 ( 15 mcg )+lysine', 'Tetracare limited', 'Syrup', 'شراب', '120 ml', '1', NULL, NULL, NULL, NULL, 815, '2025-11-15', NULL, 0, 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Caro plus 20 tabs', 8603, 'كارو بلس 20 قرص', '125', '75', 'For bone health', 'For bone health', NULL, 'Calcium+vitamin d', 'Linko', 'Tab', 'أقراص', NULL, '1', NULL, NULL, NULL, '6224008585978', 2577, '2025-06-21', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Metricason 50/25 mcg 120 metered actuations', 22670, 'متريكازون 50/25مكجم 120 جرعة بخاخ', '46', NULL, 'Bronchodilator.glucocorticoid steroid and long-acting b2 agonist', 'Bronchodilator.glucocorticoid steroid and long-acting b2 agonist', NULL, 'Fluticasone+salmeterol', 'Mdi pharma', 'Unknown', 'غير محدد', '25 mcg', '1', 'موسع للشعب الهوائية -- لعلاج ضيق النفس -- مضاد للحساسية والالتهاب', NULL, 'about fluticasone synthetic topical glucocorticoid anti inflammatory and antipruritic in corticosteroid-responsive dermatoses; atopic dermatitis. mechanism of action of fluticasone it reduces prostaglandin synthesis by inhibiting cyclo oxygenase enzymes b', NULL, 2355, '2022-12-03', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Intefen 5m.i.u. vial', 20884, 'انتيفين 5 مليون وحدة دولية فيال', '53', NULL, 'Antiviral .interferon alfa', 'Antiviral .interferon alfa', NULL, 'Interferon alfa-2a', 'Shenyang sunshine pharmaceutical co.-china > egyptian pharmex', 'Vial', 'فيال', NULL, '1', NULL, NULL, 'indication for the treatment of chronic hepatitis c hairy cell leukemia aids-related kaposi s sarcoma and chronic myelogenous leukemia. also for the treatment of oral warts arising from hiv infection. mechanism of action interferon alpha binds to type i i', NULL, 918, '2022-07-30', NULL, 0, 'For the treatment of venereal or genital warts caused by the Human Papiloma Virus.', 'Interferon alpha binds to type I interferon receptors (IFNAR1 and IFNAR2c) which, upon dimerization, activate two Jak (Janus kinase) tyrosine kinases (Jak1 and Tyk2). These transphosphorylate themselves and phosphorylate the receptors. The phosphorylated INFAR receptors then bind to Stat1 and Stat2 (signal transducers and activators of transcription)which dimerize and activate multiple (~100) immunomodulatory and antiviral proteins. Interferon alpha binds less stably to type I interferon receptors than interferon beta.', 'Upregulates the expression of MHC I proteins, allowing for increased presentation of peptides derived from viral antigens. This enhances the activation of CD8+ T cells that are the precursors for cytotoxic T lymphocytes (CTLs) and makes the macrophage a better target for CTL-mediated killing. Interferon alpha also induce the synthesis of several key antiviral mediators, including 2''-5'' oligoadenylate synthetase (2''-5'' A synthetase) and protein kinase R.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Marnys royal provite 5000 - 10 oral bottles 11 ml', 6256, 'مارنيز رويال بروفيت 5000 - 10 زجاجات بالفم 11 مل', '550', '400', 'Multivitamins', 'Multivitamins', NULL, 'ROYAL JELLY + BEE POLLEN + BEE PROPOLIS + VITAMIN E', 'WALFARM', 'Bottle', 'زجاجة', '11 ml', '10', NULL, NULL, 'dose: drink 1 vial once daily before breakfast. shake well before use.', NULL, 1058, '2025-07-03', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Letrotume 2.5 mg 28 tabs.', 21721, 'ليتروتيوم 2.5مجم 28 قرص', '550', '336', 'Non-steroidal aromatase inhibitor', 'Non-steroidal aromatase inhibitor', NULL, 'Letrozole', 'Hikma specialized pharmaceuticals', 'Tab', 'أقراص', '2.5 mg', '2', 'لتنشيط التبويض في علاج حالات العقم وتأخر الانجاب', NULL, NULL, '6223003847715', 4356, '2025-07-25', NULL, 0, 'Letrozole is indicated to treat postmenopausal women with hormone receptor (HR) positive early breast cancer, postmenopausal women with early breast cancer who have periviously been treated with tamoxifen, and postmenopausal women with HR+ or unknown advanced breast cancer.[L11623] Letrozole, given with ribociclib, is indicated to treat pre, peri, and postmenopausal women with HR+ and human epidermal growth factor 2 (HER2) negative advanced or metastatic breast cancer.[L11626]', 'Letrozole is a non-steroidal type II aromatase inhibitor.[A1559] It blocks the active site, and therefore the electron transfer chain of CYP19A1.[A1559] This competitive inhibition prevents the conversion of androgens to estrogen.[A1559] This action leads to a reduction in uterine weight and elevated leuteinizing hormone.[L11623] In postmenopausal women, the action of aromatase is responsible for the majority of estrogen production.[L11623] With reduced availability of estrogen, estrogen-dependant tumors regress.[L11623] Third generation aromatase inhibitors do not significantly affect cortisol, aldosterone, and thyroxine levels.[A190546]', 'Letrozole is an aromatase inhibitor used in the treatment of breast cancer. Aromatase inhibitors work by inhibiting the action of the enzyme aromatase, which converts androgens into estrogens by a process called aromatization. As breast tissue is stimulated by estrogens, decreasing their production is a way of suppressing recurrence of the breast tumor tissue.



Letrozole is a third generation type II aromatase inhibitor used to treat estrogen dependant breast cancers.[L11623] It has a long duration of action as it has a half life of over 42 hours in breast cancer patients.[A190546,A190555,L11623] Patients should be counselled regarding the risk of interstitial lung disease, pneumonitis, QT prolongation, elevated transaminase levels, neutropenia, and embryo-fetal toxicity.[L11623]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Lidocaine hydrochloride 2% & epinephrine 0.01mg/ml 5 amp. 10 ml', 21813, 'ليدوكايين هيدروكلوريد 2% مع ابينفرين 0.01مجم/مل 5 امبولات 10 مل', '44', NULL, 'Analgesic.local anesthetic', 'Analgesic.local anesthetic', NULL, 'Lidocaine+epinephrine', 'Pharco b', 'Amp', 'أمبول', '2%', '5', NULL, NULL, NULL, NULL, 722, '2022-06-21', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Celeborg 200mg 10 caps.', 16085, 'سيليبورج 200مجم 10 كبسولات', '50', '30', 'Nsaid.selective cox-2 inhibitors', 'Nsaid.selective cox-2 inhibitors', NULL, 'Celecoxib', 'Borg', 'Cap', 'كبسولة', '200mg', '1', 'مضاد للالتهاب يستخدم في حالات --الروماتويد--التهاب المفاصل--النقرص', NULL, NULL, '6223002570898', 2401, '2023-02-17', NULL, 0, 'Celecoxib is indicated for symptomatic treatment of adult osteoarthritis (OA) and adult rheumatoid arthritis (RA).[L7646] Celecoxib is not a substitute for aspirin for cardiovascular event prophylaxis.[L7646] 



It may be also be used to treat acute pain from various sources, juvenile rheumatoid arthritis in children over 2, ankylosing spondylitis, and primary dysmenorrhea.[L7646]



Celecoxib, in combination with [tramadol], is indicated for the management of acute pain in adults severe enough to require an opioid analgesic and in whom alternative treatments are inadequate.[L38949]', 'Unlike most NSAIDs, which inhibit both types of cyclooxygenases (COX-1 and COX-2), celecoxib is a selective noncompetitive inhibitor of cyclooxygenase-2 (COX-2) enzyme. COX-2 is expressed heavily in inflamed tissues where it is induced by inflammatory mediators.[L7646] The inhibition of this enzyme reduces the synthesis of metabolites that include prostaglandin E2 (PGE2), prostacyclin (PGI2), thromboxane (TXA2), prostaglandin D2 (PGD2), and prostaglandin F2 (PGF2). Resultant inhibition of these mediators leads to the alleviation of pain and inflammation.[A34124,L7646] 



By inhibiting prostaglandin synthesis, non-steroidal anti-inflammatory drugs (NSAIDs) cause mucosal damage, ulceration and ulcer complication throughout the gastrointestinal tract.[L7646] Celecoxib poses less of an ulceration risk than other NSAIDS, owing to its decreased effect on gastric mucosal prostaglandin synthesis when compared to placebo.[A34152] 



Celecoxib exerts anticancer effects by binding to the cadherin-11 (CDH11)protein,  which is thought to be involved in the progression of tumors, and inhibiting the 3-phosphoinositide-dependent kinase-1 (PDK-1) signaling mechanism.[T647,A11861] In addition, celecoxib has been found to inhibit carbonic anhydrase enzymes 2 and 3, further enhancing its anticancer effects.[A28426,A20066]



As mentioned in the pharmacodynamics section of this drug entry, celecoxib may cause an increased risk of thrombotic events. The risk of thrombosis resulting from COX-2 inhibition is caused by the vasoconstricting actions of thromboxane A2, leading to enhanced platelet aggregation, which is uncontrolled when the actions of prostacyclin, a platelet aggregation inhibitor, are suppressed through the inhibition of COX-2.[L7622]', '

Celecoxib inhibits cyclooxygenase 2 (COX-2) enzyme, reducing pain and inflammation. It is important to note that though the risk of bleeding with celecoxib is lower than with certain other NSAIDS, it exists nonetheless and caution must be observed when it is administered to those with a high risk of gastrointestinal bleeding.[L7607]



**A note on the risk of cardiovascular events**



Significant concerns regarding the safety of COX-2 selective NSAIDs emerged in the early 2000s. [Rofecoxib], another member of the COX-2 inhibitor drug class, also known as Vioxx, was withdrawn from the market due to prothrombotic cardiovascular risks.[L7622] Following an FDA Advisory Committee meeting in 2005, in which data from large clinical outcome trials were evaluated, the FDA concluded that the risk for cardiovascular thrombotic events for both COX-2 selective NSAIDs and nonselective NSAIDs was evident.[L3293] It was determined that the benefits of celecoxib treatment, however, outweighed the risks.[L7622] Postmarketing cardiovascular outcomes trial (PRECISION) revealed that the lowest possible dose of celecoxib was similar in cardiovascular safety to moderate strength doses of both naproxen and ibuprofen. Patients who had previous cardiovascular events including acute MI, coronary revascularization, or coronary stent insertion were not evaluated in the trial. It is not advisable to administer NSAIDS to these groups of patients.[L3293]

', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('E-rosh anti-dandruff shampoo 200 ml', 30485, 'اي روش شامبو للقشرة 200 مل', '140', '120', 'Hair care', 'Hair care', NULL, NULL, '> future pharm', 'Amp', 'أمبول', '200 ml', '1', NULL, NULL, NULL, NULL, 5568, '2025-08-13', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Dostilact 0.5 mg 2 tabs.', 31875, 'دوستيلاكت 0.5مجم 2 قرص', '105', '45', 'Dopamine agonist & prolactin inhibitor', 'Dopamine agonist & prolactin inhibitor', NULL, 'Cabergoline', 'Atco pharma > omega pharma opci', 'Tab', 'أقراص', '0.5 mg', '1', 'علاج بعض مشاكل الدورة الشهرية.ومقوى للرجال والنساء.أورام الغدة النخامية.الحد من إفراز حليب الأم من الغدد الثديية.', NULL, 'description cabergoline an ergot derivative is a long-acting dopamine agonist and prolactin inhibitor. it is used to treat hyperprolactinemic disorders and parkinsonian syndrome. cabergoline possesses potent agonist activity on dopamine d2 receptors. indi', '6224009194056', 2064, '2025-02-20', NULL, 0, 'For the treatment of hyperprolactinemic disorders, either idiopathic or due to prolactinoma (prolactin-secreting adenomas). May also be used to manage symptoms of Parkinsonian Syndrome as monotherapy during initial symptomatic management or as an adjunct to levodopa therapy during advanced stages of disease. ', 'The dopamine D<sub>2</sub> receptor is a 7-transmembrane G-protein coupled receptor associated with G<sub>i</sub> proteins. In lactotrophs, stimulation of dopamine D<sub>2</sub> causes inhibition of adenylyl cyclase, which decreases intracellular cAMP concentrations and blocks IP3-dependent release of Ca<sup>2+</sup> from intracellular stores. Decreases in intracellular calcium levels may also be brought about via inhibition of calcium influx through voltage-gated calcium channels, rather than via inhibition of adenylyl cyclase. Additionally, receptor activation blocks phosphorylation of p42/p44 MAPK and decreases MAPK/ERK kinase phosphorylation. Inhibition of MAPK appears to be mediated by c-Raf and B-Raf-dependent inhibition of MAPK/ERK kinase. Dopamine-stimulated growth hormone release from the pituitary gland is mediated by a decrease in intracellular calcium influx through voltage-gated calcium channels rather than via adenylyl cyclase inhibition. Stimulation of dopamine D<sub>2</sub> receptors in the nigrostriatal pathway leads to improvements in coordinated muscle activity in those with movement disorders. Cabergoline is a long-acting dopamine receptor agonist with a high affinity for D2 receptors. Receptor-binding studies indicate that cabergoline has low affinity for dopamine D1, &alpha;<sub>1</sub>,- and &alpha;<sub>2</sub>- adrenergic, and 5-HT<sub>1</sub>- and 5-HT<sub>2</sub>-serotonin receptors.', 'Cabergoline stimulates centrally-located dopaminergic receptors resulting in a number of pharmacologic effects. Five dopamine receptor types from two dopaminergic subfamilies have been identified. The dopaminergic D1 receptor subfamily consists of D<sub>1</sub> and D<sub>5</sub> subreceptors, which are associated with dyskinesias. The dopaminergic D2 receptor subfamily consists of D<sub>2</sub>, D<sub>3</sub> and D<sub>4</sub> subreceptors, which are associated with improvement of symptoms of movement disorders. Thus, agonist activity specific for D2 subfamily receptors, primarily D<sub>2</sub> and D<sub>3</sub> receptor subtypes, are the primary targets of dopaminergic antiparkinsonian agents. It is thought that postsynaptic D2 stimulation is primarily responsible for the antiparkinsonian effect of dopamine agonists, while presynaptic D2 stimulation confers neuroprotective effects. This semisynthetic ergot derivative exhibits potent agonist activity on dopamine D<sub>2</sub>- and D<sub>3</sub>-receptors. It also exhibits: agonist activity (in order of decreasing binding affinities) on 5-hydroxytryptamine (5-HT)<sub>2B</sub>, 5-HT<sub>2A</sub>, 5-HT<sub>1D</sub>, dopamine D<sub>4</sub>, 5-HT<sub>1A</sub>, dopamine D<sub>1</sub>, 5-HT<sub>1B</sub> and 5-HT<sub>2C</sub> receptors and antagonist activity on &alpha;<sub>2B</sub>, &alpha;<sub>2A</sub>, and &alpha;<sub>2C</sub> receptors. Parkinsonian Syndrome manifests when approximately 80% of dopaminergic activity in the nigrostriatal pathway of the brain is lost. As this striatum is involved in modulating the intensity of coordinated muscle activity (e.g. movement, balance, walking), loss of activity may result in dystonia (acute muscle contraction), Parkinsonism (including symptoms of bradykinesia, tremor, rigidity, and flattened affect), akathesia (inner restlessness), tardive dyskinesia (involuntary muscle movements usually associated with long-term loss of dopaminergic activity), and neuroleptic malignant syndrome, which manifests when complete blockage of nigrostriatal dopamine occurs. High dopaminergic activity in the mesolimbic pathway of the brain causes hallucinations and delusions; these side effects of dopamine agonists are manifestations seen in patients with schizophrenia who have overractivity in this area of the brain. The hallucinogenic side effects of dopamine agonists may also be due to 5-HT<sub>2A</sub> agonism. The tuberoinfundibular pathway of the brain originates in the hypothalamus and terminates in the pituitary gland. In this pathway, dopamine inhibits lactotrophs in anterior pituitary from secreting prolactin. Increased dopaminergic activity in the tuberoinfundibular pathway inhibits prolactin secretion. ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Egy zinc cream 75 gm', 3591, 'ايجي زنك كريم 75 جرام', '100', NULL, 'Skin smothing and moisturising cream', 'Skin smothing and moisturising cream', NULL, 'Zinc oxide + cod liver oil + panthenol + honey + glycerin', 'Egy minds', 'Cream', 'كريم', '75 gm', '1', NULL, NULL, NULL, NULL, 922, '2022-09-24', NULL, 0, 'Zinc can be used for the treatment and prevention of zinc deficiency/its consequences, including stunted growth and acute diarrhea in children, and slowed wound healing. It is also utilized for boosting the immune system, treating the common cold and recurrent ear infections, as well as preventing lower respiratory tract infections [L2172].  



', '**Zinc has three primary biological roles**: _catalytic_, _structural_, and _regulatory_. The catalytic and structural role of zinc is well established, and there are various noteworthy reviews on these functions. For example, zinc is a structural constituent in numerous proteins, inclusive of growth factors, cytokines, receptors, enzymes, and transcription factors for different cellular signaling pathways. It is implicated in numerous cellular processes as a cofactor for approximately 3000 human proteins including enzymes, nuclear factors, and hormones [L2096].



Zinc promotes resistance to epithelial apoptosis through cell protection (cytoprotection) against reactive oxygen species and bacterial toxins, likely through the antioxidant activity of the cysteine-rich metallothioneins [A32419].



In HL-60 cells (promyelocytic leukemia cell line), zinc enhances the up-regulation of A20 mRNA, which, via TRAF pathway, decreases NF-kappaB activation, leading to decreased gene expression and generation of tumor necrosis factor-alpha (TNF-alpha), IL-1beta, and IL-8 [A32418].



There are several mechanisms of action of zinc on acute diarrhea. Various mechanisms are specific to the gastrointestinal system: zinc restores mucosal barrier integrity and enterocyte brush-border enzyme activity, it promotes the production of antibodies and circulating lymphocytes against intestinal pathogens, and has a direct effect on ion channels, acting as a potassium channel blocker of adenosine 3-5-cyclic monophosphate-mediated chlorine secretion. Cochrane researchers examined the evidence available up to 30 September 2016 [L2106].



Zinc deficiency in humans decreases the activity of serum _thymulin_ (a hormone of the thymus), which is necessary for the maturation of T-helper cells. T-helper 1 (Th(1)) cytokines are decreased but T-helper 2 (Th(2)) cytokines are not affected by zinc deficiency in humans [A32417].



The change of _Th(1)_ to _Th(2)_ function leads to cell-mediated immune dysfunction. Because IL-2 production (Th(1) cytokine) is decreased, this causes decreased activity of natural-killer-cell (NK cell) and T cytolytic cells, normally involved in killing viruses, bacteria, and malignant cells [A32424]. 



In humans, zinc deficiency may lead to the generation of new CD4+ T cells, produced in the thymus. In cell culture studies (HUT-78, a Th(0) human malignant lymphoblastoid cell line), as a result of zinc deficiency, nuclear factor-kappaB (NF-kappaB) activation, phosphorylation of IkappaB, and binding of NF-kappaB to DNA are decreased and this results in decreased Th(1) cytokine production [A32417].



In another study, zinc supplementation in human subjects suppressed the gene expression and production of pro-inflammatory cytokines and decreased oxidative stress markers [A32424]. In HL-60 cells (a human pro-myelocytic leukemia cell line), zinc deficiency increased the levels of TNF-alpha, IL-1beta, and IL-8 cytokines and mRNA. In such cells, zinc was found to induce A20, a zinc finger protein that inhibited NF-kappaB activation by the tumor necrosis factor receptor-associated factor pathway. This process decreased gene expression of pro-inflammatory cytokines and oxidative stress markers [A32417].



The exact mechanism of zinc in acne treatment is poorly understood. However, zinc is considered to act directly on microbial inflammatory equilibrium and facilitate antibiotic absorption when used in combination with other agents. Topical zinc alone as well as in combination with other agents may be efficacious because of its anti-inflammatory activity and ability to reduce P. acnes bacteria by the inhibition of P. acnes lipases and free fatty acid levels [L2102].', 'Zinc is involved in various aspects of cellular metabolism. It has been estimated that approximately 10% of human proteins may bind zinc, in addition to hundreds of proteins that transport and traffic zinc. It is required for the catalytic activity of more than 200 enzymes, and it plays a role in immune function wound healing, protein synthesis, DNA synthesis, and cell division. Zinc is an essential element for a proper sense of taste and smell and supports normal growth and development during pregnancy, childhood, and adolescence. It is thought to have antioxidant properties, which may be protective against accelerated aging and helps to speed up the healing process after an injury; however, studies differ as to its effectiveness. Zinc ions are effective antimicrobial agents even if administered in low concentrations [L2211].



Studies on oral zinc for specific conditions shows the following evidence in various conditions [L2095]:



**Colds:** Evidence suggests that if zinc lozenges or syrup are taken within 24 hours after cold symptoms start, the supplement may shorten the length of colds. The use intranasal zinc has been associated with the loss of the sense of smell, in some cases long-term or permanently [L2095].



**Wound healing:** Patients with skin ulcers and decreased levels of zinc may benefit from oral zinc supplements [L2095]. 



**Diahrrea**: Oral zinc supplements can reduce the symptoms of diarrhea in children with low levels of zinc, especially in cases of malnutrition [L2095].

', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Propamethone top. cream. 20 gm', 25358, 'بروباميثون كريم 20 جم', '30', '20.5', NULL, NULL, NULL, 'Betamethasone+clotrimazole+gentamicin', 'Egpi', 'Cream', 'كريم', '20 gm', '1', 'الاكزيما', NULL, 'this cream is indicated for the treatment of corticosteroid-responsive dermatoses when complicated by infections caused by bacteria (sensitive to gentamicin) and fungi (sensitive to clotrimazole) or when the possibility of such infections is suspected. th', '6223003930776', 8062, '2024-11-07', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Lacosadelta 100mg 30 tabs', 4068, 'لاكوزاديلتا 100مجم 30 قرص', '180', NULL, NULL, NULL, NULL, 'Lacosamide', 'Delta pharma', 'Tab', 'أقراص', '100mg', '3', NULL, NULL, NULL, NULL, 1082, '2025-06-10', NULL, 0, 'In the US and Europe, lacosamide is indicated for the treatment of partial-onset seizures in children and adults.[L49191, L49196, L49201] In Canada, it is reserved for use in adults.[L49206]



It is also used as an adjunctive therapy in the treatment of primary generalized tonic-clonic seizures in patients four years of age and older.[L49191, L49201]



The extended-release capsules of lacosamide are indicated for the treatment of partial-onset seizures in adults and in pediatric patients weighing at least 50 kg.[L49196]', 'Caused by neuronal hyperexcitability, seizures in epilepsy involve sustained firing of sodium-dependent action potentials. The slow inactivation process, intrinsic to voltage-gated sodium channel functioning, has been implicated in the paroxysmal depolarizing shifts associated with epileptic activity.[A262616] The exact mechanism of action of lacosamide is not fully known; however, in vitro electrophysiological studies have shown that lacosamide selectively enhances the slow inactivation of voltage-gated sodium channels, shifting the slow inactivation curve to more hyperpolarized potentials and augmenting the maximal fraction of channels in the slow inactivated state.[A262686] This results in the stabilization of hyperexcitable neuronal membranes and inhibition of repetitive neuronal firing.[A262616, L49191] Lacosamide does not affect the fast component of voltage-gated sodium currents, unlike traditional sodium channel blockers.[A262616]', 'Lacosamide is an antiepileptic drug with high oral potency, stereoselectivity,[A262686] and anticonvulsant effects.[A262616] By blocking sensory neuronal voltage-gated sodium channels that mediate neuropathic pain responses, lacosamide was shown to possess analgesic activity.[A4071, A262616] Lacosamide is a chiral functionalized amino acid. The S-stereoisomer does not exhibit antiepileptic activity.[A262616]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Vortiocyrl 15 mg 14 f.c. tabs.', 29597, 'فورتيوسيرل 15مجم 14 اقراص', '134', NULL, 'Psychiatric.antidepressants', 'Psychiatric.antidepressants', NULL, 'Vortioxetine', 'Marcyrl co.', 'Tab', 'أقراص', '15 mg', '2', NULL, NULL, 'vortioxetine is an atypical antidepressant (a serotonin modulator and stimulator). vortioxetine is used as a treatment for major depressive disorder.', NULL, 749, '2022-08-03', NULL, 0, 'Vortioxetine is indicated for the treatment of major depressive disorder (MDD).', 'Vortioxetine is classified as a serotonin modulator and simulator (SMS) as it has a multimodal mechanism of action towards the serotonin neurotransmitter system whereby it simultaneously modulates one or more serotonin receptors and inhibits the reuptake of serotonin. More specifically, vortioxetine acts via the following biological mechanisms: as a serotonin reuptake inhibitor (SRI) through inhibition of the serotonin transporter, while also acting as a partial agonist of the 5-HT1B receptor, an agonist of 5-HT1A, and antagonist of the 5-HT3, 5-HT1D, and 5-HT7 receptors.', 'Vortioxetine binds with high affinity to the human serotonin transporter (Ki=1.6 nM), but not to the norepinephrine (Ki=113 nM) or dopamine (Ki>1000 nM) transporters. Vortioxetine potently and selectively inhibits reuptake of serotonin by inhibition of the serotonin transporter (IC50=5.4 nM). Specifically, vortioxetine binds to 5­HT3 (Ki=3.7 nM), 5­HT1A (Ki=15 nM), 5­HT7 (Ki=19 nM), 5­HT1D (Ki=54 nM), and 5­HT1B (Ki=33 nM), receptors and is a 5­HT3, 5­HT1D, and 5­HT7 receptor antagonist, 5­HT1B receptor partial agonist, and 5­HT1A receptor agonist.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Rametalina xr 25/1000 mg 30 ext. rel. f.c. tabs.', 4555, 'راميتالينا إكس آر 25/1000 مجم 30 قرص', '504', NULL, 'Anti-diabetic.secretagogues.sglt2 inhibitors', 'Anti-diabetic.secretagogues.sglt2 inhibitors', NULL, 'Empagliflozin+metformin', 'Rameda', 'Tab', 'أقراص', '1000 mg', '3', NULL, NULL, NULL, NULL, 1071, '2025-06-17', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Napi-care oint.', 23250, 'نابي كير مرهم', '2', NULL, 'Diaper rash', 'Diaper rash', NULL, 'Zinc oxide', 'Arab drug company.', 'Oint', 'مرهم', NULL, '1', NULL, NULL, 'about zinc oxide zinc compound a sunscreen antipruritic. mechanism of action of zinc oxide it works by providing a skin barrier and helps in healing and prevent skin irritation. it acts as a sunscreen which absorbs incident solar radiation in the uva rang', NULL, 942, '2022-07-26', NULL, 0, 'For adjunctive treatment of diaper dermatitis. Also, it can be used to treat minor skin irritations (eg, cuts, burns, and scrapes, poison ivy).

Zinc oxide can be used in ointments, creams, and lotions to protect against sunburn and other damage to the skin caused by ultraviolet light.', 'It acts by providing a physical barrier to prevent skin irritation and help heal damaged skin.', 'Zinc oxide has astringent, soothing and protective properties and is used in topical preparations for eczema, slight excoriations, wounds and haemorrhoids. It also reflects ultraviolet radiation and can be used as a physical sunscreen.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Amlobenzamir 5/20mg 10 caps.', 13960, 'املوبنزامير 5/20 مجم 10 كبسولات', '14', NULL, 'Antihypertensive.combined ace with calcium channel blocker', 'Antihypertensive.combined ace with calcium channel blocker', NULL, 'Amlodipine+benazepril', 'Medizen pharmaceutical industries > mira international', 'Cap', 'كبسولة', '20mg', '1', NULL, NULL, 'mechanism of action of amlodipine amlodipine is a second generation dihydropyridine ca channel blocker. it exerts it`s antihypertensive antianginal actions through blocking the influx of ca ions through voltage gated l-type ca channels to the peripheral v', NULL, 1043, '2022-07-27', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Scranb 20 capsule', 2663, 'سكرانب 20 كبسولة', '100', NULL, 'Cranberry 350 mg', 'Cranberry 350 mg', NULL, 'Cranberry 350 mg', 'Sky care pharma', 'Capsule', 'كبسولة', NULL, '2', NULL, NULL, NULL, NULL, 963, '2025-01-27', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Jointonex 30 tabs', 31851, 'جوينتونيكس 30 قرص', '300', '225', 'Supports health of bones & cartilages', 'Supports health of bones & cartilages', NULL, 'Collagen type 2+glucosamine+chondroitin+gingerol+folic acid+vit d+vit c+selenium+zinc+manganese+copper+vit b12', 'Copad egypt > integra pharm', 'Tab', 'أقراص', NULL, '3', 'مكمل غذائى لدعم صحة العظام والغضاريف', NULL, 'nutreceutical product for osteoarthritis management', '6224009369416', 8409, '2024-04-24', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Cefotax 500mg vial (eipico)', 16041, 'سيفوتاكس 500مجم فيال ايبيكو', '29', '21', 'Antibiotic.cephalosporin.third-generation', 'Antibiotic.cephalosporin.third-generation', NULL, 'Cefotaxime', 'Eipico', 'Vial', 'فيال', '500mg', '1', NULL, NULL, NULL, '6221032310101', 2275, '2024-08-26', NULL, 0, 'Used to treat gonorrhoea, meningitis, and severe infections including infections of the kidney (pyelonephritis) and urinary system. Also used before an operation to prevent infection after surgery.', 'The bactericidal activity of cefotaxime results from the inhibition of cell wall synthesis via affinity for penicillin-binding proteins (PBPs). Cefotaxime shows high affinity for penicillin-binding proteins in the cell wall including PBP Ib and PBP III.', 'Cefotaxime is a third generation intravenous cephalosporin antibiotic. It has broad spectrum activity against Gram positive and Gram negative bacteria. It does not have activity against <i>Pseudomonas aeruginosa</i>. Cefotaxime works by inhibiting bacterial cell wall biosynthesis. A positive feature of cefotaxime is that it display a resistance to penicillinases and is useful to treat infections that are resistant to penicillin derivatives.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Thromoxiban 10 mg 10 tabs.', 32334, 'ثروموكسيبان 10 مجم 10 قرص', '130', NULL, 'Antiplatlet.direct factor xa inhibitor', 'Antiplatlet.direct factor xa inhibitor', NULL, 'Rivaroxaban', 'Medizen pharmaceutical industries', 'Tab', 'أقراص', '10 mg', '1', NULL, NULL, NULL, NULL, 557, '2023-04-23', NULL, 0, 'Rivaroxaban is indicated for the prevention of venous thromboembolic events (VTE) in patients who have undergone total hips replacements and total knee replacement surgery; prevention of stroke and systemic embolism in patients with nonvalvular atrial fibrillation; treatment of deep vein thrombosis (DVT) and pulmonary embolism (PE); to reduce risk of recurrent DVT and/or PE. Rivaroxaban is also indicated, in combination with aspirin, for reducing the risk of major cardiovascular events in patients with chronic coronary artery disease or peripheral artery disease. Its use is also not recommended in those with severe renal impairment (<30mL/min).[L12819]



Rivaroxaban is also indicated for the treatment and prevention of VTE in pediatric patients (from birth to 18 years of age) and for thromboprophylaxis in pediatric patients ≥2 years old with congenital heart disease following the Fontan procedure.[L12819]

', 'Rivaroxaban competitively inhibits free and clot bound factor Xa. Factor Xa is needed to activate prothrombin (factor II) to thrombin (factor IIa). Thrombin is a serine protease that is required to activate fibrinogen to fibrin, which is the loose meshwork that completes the clotting process. Since one molecule of factor Xa can generate more than 1000 molecules of thrombin, selective inhibitors of factor Xa are profoundly useful in terminating the amplification of thrombin generation. The action of rivaroxaban is irreversible.

 

', 'Rivaroxaban is an anticoagulant which binds directly to factor Xa. Thereafter, it effectively blocks the amplification of the coagulation cascade, preventing the formation of thrombus. Rivaroxaban is a unqiue anticoagulant for two reasons. First of all, it is does not involve antithrombin III (ATIII) to exert its anticoagulant effects. Secondly, it is an oral agent whereas the widely used unfractionated heparin and low molecular weight heparins are for parenteral use only. Although the activated partial thromboplastin time (aPTT) and HepTest (a test developed to assay low molecular weight heparins) are prolonged in a dose-dependant manner, neither test is recommended for the assessment of the pharmacodynamic effects of rivaroxaban. Anti-Xa activity and inhibition of anti-Xa activity monitoring is also not recommended despite being influenced by rivaroxaban.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Sodium chloride 0.9% (otsuka ateco) i.v. inf. 500 ml (rubber cap)', 27172, 'صوديوم كلوريد 0.9% اوتوسوكا اتيكو محلول ملح وريدي 500 مل بغطاء مطاطي', '14', NULL, 'Normal saline', 'Normal saline', NULL, 'Sodium chloride', 'Otsuka', 'Cap', 'كبسولة', '0.9%', '1', NULL, NULL, NULL, '6224000983307', 790, '2022-07-22', NULL, 0, 'This intravenous solution is indicated for use in adults and pediatric patients as a source of electrolytes and water for hydration. Also, designed for use as a diluent and delivery system for intermittent intravenous administration of compatible drug additives. ', 'Sodium and chloride — major electrolytes of the fluid compartment outside of cells (i.e., extracellular) — work together to control extracellular volume and blood pressure. Disturbances in sodium concentrations in the extracellular fluid are associated with disorders of water balance.', 'Sodium, the major cation of the extracellular fluid, functions primarily in the control of water distribution, fluid balance, and osmotic pressure of body fluids. Sodium is also associated with chloride and bicarbonate in the regulation of the acid-base equilibrium of body fluid.

Chloride, the major extracellular anion, closely follows the metabolism of sodium, and changes in the acid-base balance of the body are reflected by changes in the chloride concentration. ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Omniscan 0.5mmol/ml (10ml) vial for i.v. inj.', 24095, 'اومنيسكان 0.5ممول/مل امبول 10مل', '180', NULL, 'Contrast agent', 'Contrast agent', NULL, 'Gadodiamide', 'Ge healthcare-ireland > new technology', 'Inj', 'حقن', '10ml', '1', NULL, NULL, 'description gadodiamide is a gadolinium based contrast agent used in mr imaging procedures to assist in the visualization of blood vessels. mechanism of action based on the behavior of protons when placed in a strong magnetic field which is interpreted an', NULL, 910, '2022-07-17', NULL, 0, 'Gadodiamide is indicated for the visualization of lesions with abnormal vascularity in the brain (intracranial lesions), spine, and associated tissues and the body (including  the thoracic (noncardiac), abdominal, pelvic cavities, and retroperitoneal space) by the FDA and Health Canada.[L36873,L49881] Additionally, gadoliamide is approved by Health Canada to detect and localize tenosis in renal arteries and aorto-iliac arteries in magnetic resonance angiography (MRA).[L36873]

', 'Gadodiamide  paramagnetic molecule that develops a magnetic moment when placed in a magnetic field. The magnetic moment alters the relaxation rates of water protons in its vicinity in the body. Its use in magnetic resonance imaging (MRI) allows to selectively increase contrast in tissues where gadodiamide accumulates.[L49881]', 'In magnetic resonance imaging, visualization of normal and pathologic tissue depends in part on variations in the radiofrequency signal intensity. These variations occur due to: changes in proton density; alteration of the spin-lattice or longitudinal relaxation time (T1); and variation of the spin-spin or transverse relaxation time (T2). Gadodiamide is a paramagnetic agent with unpaired electron spins which generate a local magnetic field. As water protons move through this local magnetic field, the changes in the magnetic field experienced by the protons reorient them with the main magnetic field more quickly than in the absence of a paramagnetic agent.[L49881]



By increasing the relaxation rate, gadodiamide decreases both the T1 and T2 relaxation times in tissues where it is distributed. At clinical doses, the effect is primarily on the T1 relaxation time and produces an increase in signal intensity. Disruption of the blood-brain barrier or abnormal vascularity allows the accumulation of gadodiamide in lesions such as neoplasms, abscesses, and subacute infarcts. The pharmacokinetic parameters of gadodiamide in various lesions are not known.[L49881]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Triderm cream 15 gm', 28501, 'تريديرم كريم 15جم', '8', NULL, NULL, NULL, NULL, 'Betamethasone+clotrimazole+gentamicin', 'Mup > schering plough', 'Cream', 'كريم', '15 gm', '1', NULL, NULL, 'this cream is indicated for the treatment of corticosteroid-responsive dermatoses when complicated by infections caused by bacteria (sensitive to gentamicin) and fungi (sensitive to clotrimazole) or when the possibility of such infections is suspected. th', NULL, 1106, '2022-07-12', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Tareg 160mg 28 f.c. tab.', 11177, 'تارج 160مجم 28 قرص', '320', '244', 'Antihypertensive.angiotensin blocker', 'Antihypertensive.angiotensin blocker', NULL, 'Valsartan', 'Novartis', 'Tab', 'أقراص', '160mg', '4', NULL, NULL, 'about valsartan angiotensin ii receptor antagonist antihypertensive. mechanism of action of valsartansit is a competitive antagonist of angiotensin-2 at at-1 receptor. it decreases peripheral resistance and lowers bp in hypertensive individuals. it blocks', '622300264024', 3621, '2024-08-05', NULL, 0, 'Valsartan is indicated for the treatment of hypertension to reduce the risk of fatal and nonfatal cardiovascular events, primarily strokes and myocardial infarctions. It is also indicated for the treatment of heart failure (NYHA class II-IV) and for left ventricular dysfunction or failure after myocardial infarction when the use of an angiotensin-converting enzyme inhibitor (ACEI) is not appropriate.[F4703, L11305]



It is also used in combination with [sacubitril].[L36445]', 'Valsartan belongs to the angiotensin II receptor blocker (ARB) family of drugs, which selectively bind to angiotensin receptor 1 (AT1) and prevent angiotensin II from binding and exerting its hypertensive effects. These include vasoconstriction, stimulation and synthesis of aldosterone and ADH, cardiac stimulation, and renal reabsorption of sodium among others. Overall, valsartan''s physiologic effects lead to reduced blood pressure, lower aldosterone levels, reduced cardiac activity, and increased excretion of sodium. 



Valsartan also affects the renin-angiotensin aldosterone system (RAAS), which plays an important role in hemostasis and regulation of kidney, vascular, and cardiac functions. Pharmacological blockade of RAAS via  AT1 receptor blockade inhibits negative regulatory feedback within RAAS which is a contributing factor to the pathogenesis and progression of cardiovascular disease, heart failure, and renal disease. In particular, heart failure is associated with chronic activation of RAAS, leading to inappropriate fluid retention, vasoconstriction, and ultimately a further decline in left ventricular function. ARBs have been shown to have a protective effect on the heart by improving cardiac function, reducing afterload, increasing cardiac output and prevent ventricular hypertrophy.[A174154]



The angiotensin-converting enzyme inhibitor (ACEI) class of medications (which includes drugs such as [ramipril], [lisinopril], and [perindopril]) inhibits the conversion of angiotensin I to angiotensin II by inhibiting the ACE enzyme but does not prevent the formation of all angiotensin II. ARB activity is unique in that it blocks all angiotensin II activity, regardless of where or how it was synthesized. 



Valsartan is commonly used for the management of hypertension, heart failure, and type 2 diabetes-associated nephropathy, particularly in patients who are unable to tolerate ACE inhibitors. ARBs such as valsartan have been shown in a number of large-scale clinical outcomes trials to improve cardiovascular outcomes including reducing risk of myocardial infarction, stroke, the progression of heart failure, and hospitalization.[A174124,A178153,A173869,A185324,A185327,A185333,A185342,A185345] Valsartan also slows the progression of diabetic nephropathy due to its renoprotective effects.[A174157,A174160,A174163] Improvements in chronic kidney disease with valsartan include both clinically and statistically significant decreases in urinary albumin and protein excretion in patients diagnosed with type 2 diabetes and in nondiabetic patients diagnosed with chronic kidney disease.[A174124,A173869]



Valsartan also binds to the AT2 receptor, however AT2 is not known to be associated with cardiovascular homeostasis like AT1. Valsartan has about 20,000-fold higher affinity for the AT1 receptor than for the AT2 receptor. The increased plasma levels of angiotensin II following AT1 receptor blockade with valsartan may stimulate the unblocked AT2 receptor.[L11305]', 'Valsartan inhibits the pressor effects of angiotensin II with oral doses of 80 mg inhibiting the pressor effect by about 80% at peak with approximately 30% inhibition persisting for 24 hours. Removal of the negative feedback of angiotensin II causes a 2- to 3-fold rise in plasma renin and consequent rise in angiotensin II plasma concentration in hypertensive patients. Minimal decreases in plasma aldosterone were observed after administration of valsartan.



In multiple-dose studies in hypertensive patients, valsartan had no notable effects on total cholesterol, fasting triglycerides, fasting serum glucose, or uric acid.[L11305]



**Hypotension** 



Excessive hypotension was rarely seen (0.1%) in patients with uncomplicated hypertension treated with valsartan alone. In patients with an activated renin-angiotensin system, such as volume- and/or salt-depleted patients receiving high doses of diuretics, symptomatic hypotension may occur. This condition should be corrected prior to administration of valsartan, or the treatment should start under close medical supervision. 



Caution should be observed when initiating therapy in patients with heart failure. Patients with heart failure given valsartan commonly have some reduction in blood pressure, but discontinuation of therapy because of continuing symptomatic hypotension usually is not necessary when dosing instructions are followed. In controlled trials in heart failure patients, the incidence of hypotension in valsartan-treated patients was 5.5% compared to 1.8% in placebo-treated patients. 



If excessive hypotension occurs, the patient should be placed in the supine position and, if necessary, given an intravenous infusion of normal saline. A transient hypotensive response is not a contraindication to further treatment, which usually can be continued without difficulty once the blood pressure has stabilized.[L11305]





**Impaired Renal Function**



Changes in renal function including acute renal failure can be caused by drugs that inhibit the renin-angiotensin system and by diuretics. Patients whose renal function may depend in part on the activity of the renin-angiotensin system (e.g., patients with renal artery stenosis, chronic kidney disease, severe congestive heart failure, or volume depletion) may be at particular risk of developing acute renal failure on valsartan. Monitor renal function periodically in these patients. Consider withholding or discontinuing therapy in patients who develop a clinically significant decrease in renal function on valsartan.[L11305]





**Hyperkalemia**



Some patients with heart failure have developed increases in potassium. These effects are usually minor and transient, and they are more likely to occur in patients with pre-existing renal impairment. Dosage reduction and/or discontinuation of valsartan may be required.[L11305]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Flixotide diskus 100mcg/dose 60 doses', 19140, 'فليكسوتايد ديسكس 100 ميكروجرام / جرعة 60 جرعة', '107', '69', 'Anti-inflammatory.glucocorticoid.bronchodilator', 'Anti-inflammatory.glucocorticoid.bronchodilator', NULL, 'Fluticasone propionate', 'Glaxo smithkline > modern pharma', 'Unknown', 'غير محدد', '100mcg', '1', NULL, NULL, 'description fluticasone propionate a medium-potency synthetic corticosteroid is used topically to relieve inflammatory and pruritic symptoms of dermatoses and psoriasis intranasally to manage symptoms of allergic and non-allergic rhinitis and orally for t', '6221045008101', 827, '2025-02-24', NULL, 0, 'Fluticasone propionate is indicated as an inhaler for the treatment and management of asthma by prophylaxis[FDA Label]as well as inflammatory and pruritic dermatoses[F4355]. Fluticasone propionate nasal spray is indicated for managing allergic and nonallergic rhinitis[L8309,F4358].', 'Fluticasone propionate works through an unknown mechanism to  affect the action of various cell types and mediators of inflammation[F4358]. Fluticasone propionate activates glucocorticoid receptors and inhibits lung eosinophilia in rats[F4358,F4355].', 'Systemically, fluticasone propionate activates glucocorticoid receptors, and inhibits lung eosinophilia in rats[F4358][FDA Label]. Fluticasone propionate as a topical formulation is also associated with vasoconstriction in the skin[F4355,A177118].', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Bisolock 10mg 30 f.c.tab', 1793, 'بيسولوك 10مجم 30 قرص', '78', '58.5', 'Antihypertensive.beta blocker.selective b1', 'Antihypertensive.beta blocker.selective b1', NULL, 'Bisoprolol fumarate', 'Pharco', 'Tab', 'أقراص', '10mg', '3', NULL, NULL, NULL, '6221151498827', 998, '2024-12-22', NULL, 0, 'Bisoprolol is indicated for the treatment of mild to moderate hypertension.[L7219] It may be used off-label to treat heart failure, atrial fibrillation, and angina pectoris.[A180460,A180463]', 'Though the mechanism of action of bisoprolol has not been fully elucidated in hypertension, it is thought that therapeutic effects are achieved through the antagonism of β-1adrenoceptors to result in lower cardiac output. Bisoprolol is a competitive, cardioselective β1-adrenergic antagonist. When β1-receptors (located mainly in the heart)[T640] are activated by adrenergic neurotransmitters such as epinephrine, both the blood pressure and heart rate increase, leading to greater cardiovascular work, increasing the demand for oxygen.  Bisoprolol reduces cardiac workload by decreasing contractility and the need for oxygen through competitive inhibition of β1-adrenergic receptors.[A180502,T640]



Bisoprolol is also thought to reduce the output of renin in the kidneys, which normally increases blood pressure. Additionally, some central nervous system effects of bisoprolol may include diminishing sympathetic nervous system output from the brain, decreasing blood pressure and heart rate.[L7219]', 'Bisoprolol decreases heart rate (chronotropy), decreases contractility (inotropy), and reduces blood pressure.[T640,L7219] The results of various clinical studies indicate that bisoprolol reduces cardiovascular mortality and all-cause mortality in patients with heart failure and decreased cardiac ejection fraction (EF).[A180466,A180505] ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Biperiden 2mg 30 tab.', 1586, 'بيبريدين 2مجم 30 قرص', '51', '27', 'Antiparkinson agents. muscarinic antagonist', 'Antiparkinson agents. muscarinic antagonist', NULL, 'Biperidene', 'Pharco', 'Tab', 'أقراص', '2mg', '3', 'لعلاج مرض باركنسون', NULL, NULL, '6221151645016', 4885, '2024-11-26', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Bronchicum 20 lozenges', 30232, 'برونشيكم 20 قرص استحلاب', '90', '44', 'Cough products', 'Cough products', NULL, 'Thyme fluid extract', 'Sanofi aventis > sanofi', 'Lozenges', 'استحلاب', NULL, '2', 'برونشيكم اقراص للاستحلاب مكمل غذائي لتخفيف أعراض الكحة في كل مكان.', NULL, 'supportive supplement for cough symptoms', '661288064605', 8938, '2024-07-15', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Gypravastin 20 mg 7 f.c. tabs.', 32934, 'جيبرافاستين 20 مجم 7قرص', '32', '26.5', 'Antihyperlipidemic.statins', 'Antihyperlipidemic.statins', NULL, 'Rosuvastatin', 'Gypto pharma', 'Tab', 'أقراص', '20 mg', '1', 'تستخدم لخفض الكوليسترول و الوقاية من أمراض القلب ، بما في ذلك النوبات القلبية والسكتات الدماغية', NULL, NULL, '6224008874089', 2309, '2025-02-04', NULL, 0, 'The FDA monograph states that rosuvastatin is indicated as an adjunct to diet in the treatment of triglyceridemia, Primary Dysbetalipoproteinemia (Type III Hyperlipoproteinemia), and Homozygous Familial Hypercholesterolemia.[F4649]



The Health Canada monograph for rosuvastatin further specifies that rosuvastatin is indicated for the reduction of elevated total cholesterol (Total-C), LDL-C, ApoB, the Total-C/HDL-C ratio and triglycerides (TG) and for increasing HDL-C in hyperlipidemic and dyslipidemic conditions when response to diet and exercise alone has been inadequate. It is also indicated for the prevention of major cardiovascular events (including risk of myocardial infarction, nonfatal stroke, and coronary artery revascularization) in adult patients without documented history of cardiovascular or cerebrovascular events, but with at least two conventional risk factors for cardiovascular disease.[F4652]



Prescribing of statin medications is considered standard practice following any cardiovascular events and for people with a moderate to high risk of development of CVD. Statin-indicated conditions include diabetes mellitus, clinical atherosclerosis (including myocardial infarction, acute coronary syndromes, stable angina, documented coronary artery disease, stroke, trans ischemic attack (TIA), documented carotid disease, peripheral artery disease, and claudication), abdominal aortic aneurysm, chronic kidney disease, and severely elevated LDL-C levels.[A181087, A181406]', 'Rosuvastatin is a statin medication and a competitive inhibitor of the enzyme HMG-CoA (3-hydroxy-3-methylglutaryl coenzyme A) reductase, which catalyzes the conversion of HMG-CoA to mevalonate, an early rate-limiting step in cholesterol biosynthesis.[A181421] Rosuvastatin acts primarily in the liver, where decreased hepatic cholesterol concentrations stimulate the upregulation of hepatic low density lipoprotein (LDL) receptors which increases hepatic uptake of LDL. Rosuvastatin also inhibits hepatic synthesis of very low density lipoprotein (VLDL).[F4649] The overall effect is a decrease in plasma LDL and VLDL. 



In vitro and in vivo animal studies also demonstrate that rosuvastatin exerts vasculoprotective effects independent of its lipid-lowering properties, also known as the pleiotropic effects of statins.[A181424] This includes improvement in endothelial function, enhanced stability of atherosclerotic plaques, reduced oxidative stress and inflammation, and inhibition of the thrombogenic response.



Statins have also been found to bind allosterically to β2 integrin function-associated antigen-1 (LFA-1), which plays an important role in leukocyte trafficking and in T cell activation.[A181559]



Rosuvastatin exerts an anti-inflammatory effect on rat mesenteric microvascular endothelium by attenuating leukocyte rolling, adherence and transmigration.[A1807] The drug also modulates nitric oxide synthase (NOS) expression and reduces ischemic-reperfusion injuries in rat hearts.[A1787] Rosuvastatin increases the bioavailability of nitric oxide[A1807,A1799,A1787] by upregulating NOS[A1791] and by increasing the stability of NOS through post-transcriptional polyadenylation.[A1796] It is unclear as to how rosuvastatin brings about these effects though they may be due to decreased concentrations of mevalonic acid.', 'Rosuvastatin is a synthetic, enantiomerically pure antilipemic agent. It is used to lower total cholesterol, low density lipoprotein-cholesterol (LDL-C), apolipoprotein B (apoB), non-high density lipoprotein-cholesterol (non-HDL-C), and trigleride (TG) plasma concentrations while increasing HDL-C concentrations. High LDL-C, low HDL-C and high TG concentrations in the plasma are associated with increased risk of atherosclerosis and cardiovascular disease. The total cholesterol to HDL-C ratio is a strong predictor of coronary artery disease and high ratios are associated with higher risk of disease. Increased levels of HDL-C are associated with lower cardiovascular risk. By decreasing LDL-C and TG and increasing HDL-C, rosuvastatin reduces the risk of cardiovascular morbidity and mortality.[A181087, A181406]



Elevated cholesterol levels, and in particular, elevated low-density lipoprotein (LDL) levels, are an important risk factor for the development of CVD.[A181087] Use of statins to target and reduce LDL levels has been shown in a number of landmark studies to significantly reduce the risk of development of CVD and all-cause mortality.[A181090,A181093,A181096,A181427,A181475] Statins are considered a cost-effective treatment option for CVD due to their evidence of reducing all-cause mortality including fatal and non-fatal CVD as well as the need for surgical revascularization or angioplasty following a heart attack.[A181087, A181406] Evidence has shown that even for low-risk individuals (with <10% risk of a major vascular event occurring within 5 years) statins cause a 20%-22% relative reduction in major cardiovascular events (heart attack, stroke, coronary revascularization, and coronary death) for every 1 mmol/L reduction in LDL without any significant side effects or risks.[A181397, A181403]



**Skeletal Muscle Effects**



Cases of myopathy and rhabdomyolysis with acute renal failure secondary to myoglobinuria have been reported with HMG-CoA reductase inhibitors, including rosuvastatin. These risks can occur at any dose level, but are increased at the highest dose (40 mg). Rosuvastatin should be prescribed with caution in patients with predisposing factors for myopathy (e.g., age ≥ 65 years, inadequately treated hypothyroidism, renal impairment).



The risk of myopathy during treatment with rosuvastatin may be increased with concurrent administration of some other lipid-lowering therapies (such as [fenofibrate] or [niacin]), [gemfibrozil], [cyclosporine], [atazanavir]/[ritonavir], [lopinavir]/ritonavir, or [simeprevir].  Cases of myopathy, including rhabdomyolysis, have been reported with HMG-CoA reductase inhibitors, including rosuvastatin, coadministered with [colchicine], and caution should therefore be exercised when prescribing these two medications together.[F4649] 



Real-world data from observational studies has suggested that 10-15% of people taking statins may experience muscle aches at some point during treatment.[A182258]



**Liver Enzyme Abnormalities**



Increases in serum transaminases have been reported with HMG-CoA reductase inhibitors, including rosuvastatin. In most cases, the elevations were transient and resolved or improved on continued therapy or after a brief interruption in therapy. There were two cases of jaundice, for which a relationship to rosuvastatin therapy could not be determined, which resolved after discontinuation of therapy. There were no cases of liver failure or irreversible liver disease in these trials.[F4649]



**Endocrine Effects**



Increases in HbA1c and fasting serum glucose levels have been reported with HMG-CoA reductase inhibitors, including rosuvastatin calcium tablets. Based on clinical trial data with rosuvastatin, in some instances these increases may exceed the threshold for the diagnosis of diabetes mellitus.[F4649]



An in vitro study found that [atorvastatin], [pravastatin], [rosuvastatin], and [pitavastatin] exhibited a dose-dependent cytotoxic effect on human pancreas islet β cells, with reductions in cell viability of 32, 41, 34 and 29%, respectively, versus control]. Moreover, insulin secretion rates were decreased by 34, 30, 27 and 19%, respectively, relative to control.[A182012]



HMG-CoA reductase inhibitors interfere with cholesterol synthesis and lower cholesterol levels and, as such, might theoretically blunt adrenal or gonadal steroid hormone production. Rosuvastatin demonstrated no effect upon nonstimulated cortisol levels and no effect on thyroid metabolism as assessed by TSH plasma concentration. In rosuvastatin treated patients, there was no impairment of adrenocortical reserve and no reduction in plasma cortisol concentrations. Clinical studies with other HMG-CoA reductase inhibitors have suggested that these agents do not reduce plasma testosterone concentration. The effects of HMG-CoA reductase inhibitors on male fertility have not been studied. The effects, if any, on the pituitarygonadal axis in premenopausal women are unknown.[F4652]



**Cardiovascular**



Ubiquinone levels were not measured in rosuvastatin clinical trials, however significant decreases in circulating ubiquinone levels in patients treated with other statins have been observed. The clinical significance of a potential long-term statin-induced deficiency of ubiquinone has not been established. It has been reported that a decrease in myocardial ubiquinone levels could lead to impaired cardiac function in patients with borderline congestive heart failure.[F4652]



**Lipoprotein A**



In some patients, the beneficial effect of lowered total cholesterol and LDL-C levels may be partly blunted by a concomitant increase in the Lipoprotein(a) [Lp(a)] concentrations. Present knowledge suggests the importance of high Lp(a) levels as an emerging risk factor for coronary heart disease. It is thus desirable to maintain and reinforce lifestyle changes in high-risk patients placed on rosuvastatin therapy.[F4652] Further studies have demonstrated statins affect Lp(a) levels differently in patients with dyslipidemia depending on their apo(a) phenotype; statins increase Lp(a) levels exclusively in patients with the low molecular weight apo(a) phenotype.[A181418]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Serviflox 750 mg 10 f.c. tab.', 10379, 'سرفيفلوكس 750 مجم 10 اقراص', '39', NULL, 'Antibiotic.quinolone', 'Antibiotic.quinolone', NULL, 'Ciprofloxacin', 'Novartis', 'Tab', 'أقراص', '750 mg', '1', 'مضاد حيوي لعلاج التهابات الجهاز البولي والتنفسي والعظام', NULL, 'about ciprofloxacin it is a broad spectrum fluoroquinolone with activity against pseudomonas aeruginosa. eye drops: it is a fluroquinolone antibacterial having broad spectrum of activity. indications for ciprofloxacin 1.urinary tract infection 2.respirato', '6223002640676', 1953, '2023-06-02', NULL, 0, 'Ciprofloxacin is only indicated in infections caused by susceptible bacteria.[L6469,L6472,L6475,L6478,L6481,L6484,L6487,L6490,L6493]



Ciprofloxacin immediate release tablets, oral suspensions, and intravenous injections are indicated for the treatment of skin and skin structure infections, bone and joint infections, complicated intra-abdominal infections, nosocomial pneumonia, febrile neutropenia, adults who have inhaled anthrax, plague, chronic bacterial prostatitis, lower respiratory tract infections including acute exacerbations of chronic bronchitis, urinary tract infections, complicated urinary tract infections in pediatrics, complicated pyelonephritis in pediatrics, and acute sinusitis.[L6481,L6478]



A ciprofloxacin otic solution and otic suspension with hydrocortisone are indicated for acute otitis externa.[L6469,L6484] Ciprofloxacin suspension with dexamethasone is indicated for acute otitis media in pediatric patients with tympanostomy tubes or acute otitis externa.[L6490] A ciprofloxacin intratympanic injection is indicated for pediatric patients with bilateral otitis media with effusion who are having tympanostomy tubes placed or pediatric patients 6 months or older with acute otitis externa.[L6493]



A ciprofloxacin eye drop is indicated for bacterial corneal ulcers and conjunctivitis.[L6472] A ciprofloxacin eye ointment is indicated for bacterial conjunctivitis.[L6475]



A ciprofloxacin extended release tablet is indicated for uncomplicated urinary tract infections, complicated urinary tract infections, and acute uncomplicated pyelonephritis.[L6487]', 'Ciprofloxacin acts on bacterial topoisomerase II (DNA gyrase) and topoisomerase IV.[A178885] Ciprofloxacin''s targeting of the alpha subunits of DNA gyrase prevents it from supercoiling the bacterial DNA which prevents DNA replication.[A178876,A178882]', 'Ciprofloxacin is a second generation fluoroquinolone that is active against many Gram negative and Gram positive bacteria.[A178870,L6469,L6472,L6475,L6478,L6481,L6484,L6487,L6490,L6493] It produces its action through inhibition of bacterial DNA gyrase and topoisomerase IV.[A178885] Ciprofloxacin binds to bacterial DNA gyrase with 100 times the affinity of mammalian DNA gyrase.[A178894] There is no cross resistance between fluoroquinolones and other classes of antibiotics, so it may be of clinical value when other antibiotics are no longer effective.[A178894] Ciprofloxain and its derivatives are also being investigated for its action against malaria, cancers, and AIDS.[A178885]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Rameceftrax 500 mg vial for i.v. inj.', 25643, 'رامسيفتراكس 500مجم فيال وريد', '46', '21.75', 'Antibiotic.cephalosporin.third-generation', 'Antibiotic.cephalosporin.third-generation', NULL, 'Ceftriaxone', 'Rameda', 'Inj', 'حقن', '500 mg', '1', NULL, NULL, 'about ceftriaxone third generation cephalosporin antibiotic(parenteral). mechanism of action of ceftriaxone ceftriaxone is a 3rd generation cephalosporin which exerts its bactericidal action against both gram positive & gram negative organisms by inhibiti', '6223003972844', 788, '2024-08-01', NULL, 0, 'Ceftriaxone is used for the treatment of the infections (respiratory, skin, soft tissue, UTI, ENT) caused by susceptible organisms.[L15082] Organisms that are generally susceptible to ceftriaxone include S. pneumoniae, S. pyogenes (group A beta-hemolytic streptococci), coagulase-negative staphylococci, Some Enterobacter spp, H. influenzae, N. gonorrhoeae, P. mirabilis, E. coli, Klebsiella spp, M. catarrhalis, B. burgdorferi, and some oral anaerobes.[A215582] ', 'Ceftriaxone works by inhibiting the mucopeptide synthesis in the bacterial cell wall.[T324,L15082] The beta-lactam moiety of ceftriaxone binds to carboxypeptidases, endopeptidases, and transpeptidases in the bacterial cytoplasmic membrane. These enzymes are involved in cell-wall synthesis and cell division. Binding of ceftriaxone to these enzymes causes the enzyme to lose activity; therefore, the bacteria produce defective cell walls, causing cell death.', 'Ceftriaxone is a cephalosporin/cephamycin beta-lactam antibiotic used in the treatment of bacterial infections caused by susceptible, usually gram-positive, organisms.[L15082] Ceftriaxone has <i>in vitro</i> activity against gram-positive aerobic, gram-negative aerobic, and anaerobic bacteria.[L15087] The bactericidal activity of ceftriaxone results from the inhibition of cell wall synthesis and is mediated through ceftriaxone binding to penicillin-binding proteins (PBPs).[T324] Ceftriaxone is stable against hydrolysis by a variety of beta-lactamases, including penicillinases, and cephalosporinases and extended-spectrum beta-lactamases.[L15082] However, resistance to ceftriaxone usually occurs through beta-lactamase hydrolysis, altered PBPs, or reduced bacterial cell permeability.[T324] Ceftriaxone should not be mixed with or giving in the same IV line as diluents/products containing calcium as they may cause ceftriaxone to precipitate.[L15082] Ceftriaxone use may also cause biliary sludge or gallbladder pseudolithiasis.[L15082,A215602] ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Lanzyplex 15 mg 10 caps.', 21610, 'لانزيبلكس 15مجم 10 كبسولات', '48', '32', 'Peptic ulcer.proton pump inhibitor', 'Peptic ulcer.proton pump inhibitor', NULL, 'Lansoprazole', 'Biosystem pharmaceutical group', 'Cap', 'كبسولة', '15 mg', '1', 'للحموضة وقرحة المعدة', NULL, 'description lansoprazole is a proton pump inhibitor which prevents the stomach from producing acid. it is manufactured by tap pharmaceutical products mechanism of action lansoprazole belongs to a class of antisecretory compounds the substituted benzimidaz', NULL, 2002, '2025-05-12', NULL, 0, 'Lansoprazole is used to reduce gastric acid secretion and is approved for short term treatment of active gastric ulcers, active duodenal ulcers, erosive reflux oesophagitis, symptomatic gastroesophageal reflux disease, and non-steroidal anti-inflammatory drug (NSAID) induced gastric and duodenal ulcers. [A4892][A177065][FDA Label]  It may be used in the maintenance and healing of several gastric conditions including duodenal ulcers, NSAID related gastric ulcers, and erosive esophagitis.[FDA Label] Lansoprazole prevents recurrence of gastric ulcers in patients who have a documented history of gastric ulcers who also use NSAIDs chronically. [FDA Label]  Predictably, it is also useful in the management of hypersecretory conditions including Zollinger-Ellison syndrome. [FDA Label]  Lansoprazole is effective at eradicating H. pylori when used in conjunction with amoxicillin and clarithromycin (triple therapy) or with amoxicillin alone (dual therapy). [FDA Label]', 'As a PPI, lansoprazole is a prodrug and requires protonation via an acidic environment to become activated. [A174244]  Once protonated, lansoprazole is able to react with cysteine residues, specifically Cys813 and Cys321, on parietal H+,K+-ATPase resulting in stable disulfides.[A174244][A177077]  PPI''s in general are able to provide prolonged inhibition of acid secretion due to their ability to bind covalently to their targets.[A174244]   ', 'Lansoprazole decreases gastric acid secretion by targeting H+,K+-ATPase, which is the enzyme that catalyzes the final step in the acid secretion pathway in parietal cells. [A177080]  Conveniently, lansoprazole administered any time of day is able to inhibit both daytime and nocturnal acid secretion.[A177080]  The result is that lansoprazole is effective at healing duodenal ulcers, reduces ulcer-related pain, and offers relief from symptoms of heartburn [A177080]  Lansoprazole also reduces pepsin secretion, making it a useful treatment option for hypersecretory conditions such as Zollinger-Ellison syndrome.[A177089][A177080]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Irospect syrup 120 ml', 23966, 'ايروسبيكت شراب 120 مل', '75', '55', 'Iron supplement', 'Iron supplement', NULL, 'Iron+zinc vitamin d3+copper+vitamin a+b-complex+vitamin b1+vitamin b2+vitamin b3+vitamin b5+vitamin b6+vitamin b7+vitamin b9+vitamin b12+vitamin e+magnesium+manganese+chromium+selenium+vitamin c.', 'Pharma zad > safe life', 'Syrup', 'شراب', '120 ml', '1', NULL, NULL, NULL, NULL, 1805, '2025-01-03', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Gamafer caps 20 caps', 31968, 'جامافير كبسولات 20 كبسولة', '98', '78', 'Iron supplement', 'Iron supplement', NULL, 'Liposomal iron+ferrous bisglycinate+vitamins', 'Organix > gama pharma', 'Cap', 'كبسولة', NULL, '2', 'مكمل غذائى لعلاج حالات فقر الدم', NULL, 'the first iron supplement in the egyptian market has integrated iron that integrate liposomal iron with amino acid chelated iron ( ferrous bisglycinate ) plus lactoferrin and complete hematinic formula in one capsule.', NULL, 3985, '2025-09-04', NULL, 0, 'Investigated for use/treatment in neuropathy (diabetic) and peripheral vascular disease.', 'The encapsulated formulation (Liprostin) has been shown to improve the therapeutic index of PGE-1, positively impacting many areas of treatment, such as heart attacks, occlusive disease, ischemic ulcers, critical limb salvage, claudicants, and arthritis.', NULL, 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Vismed multi 0.3% eye gel 10 ml', 5092, 'فيزميد مالتي 0.3% جل للعين 10 مل', '335', NULL, 'Eye surgery.tissue lubricant', 'Eye surgery.tissue lubricant', NULL, 'Sodium hyaluronate', 'Rheuma', 'Gel', 'جل', '0.3%', '1', NULL, NULL, NULL, NULL, 708, '2025-06-23', NULL, 0, 'For the treatment of small uncomplicated varicose veins of the lower extremities that show simple dilation with competent valves.', 'Sodium tetradecyl sulfate is a potent toxin for endothelial cells in that brief exposure to even low concentrations are effective in stripping endothelium over a considerable distance and exposing highly thrombogenic endothelium in the process. Diluted sodium tetradecyl sulfate is also able to induce a hypercoagulable state, possibly by selective inhibition of protein C, and can also promote platelet aggregation.', 'Sodium tetradecyl sulfate is an anionic surfactant which occurs as a white, waxy solid. It is used as a sclerosing agent in sclerotherapy. Sclerotherapy is an injection of a sclerosing agent directly through the skin into a lesion and is used primarily for slow-flow vascular anomalies, particularly for venous malformation and lymphatic malformation. Intravenous injection causes intima inflammation and thrombus formation. This usually occludes the injected vein. Subsequent formation of fibrous tissue results in partial or complete vein obliteration that may or may not be permanent.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Myolgin 250/300mg 20 caps.', 23198, 'ميولجين 250/300مجم 20 كبسولة', '16', NULL, 'Muscle relaxant', 'Muscle relaxant', NULL, 'Chlorzoxazone+paracetamol(acetaminophen)', 'Glaxo smithkline', 'Cap', 'كبسولة', '300mg', '2', NULL, NULL, NULL, '6221045009696', 4489, '2022-07-07', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Rapido 180mg 10 caps.', 25713, 'رابيدو 180مجم 10 كبسول', '12', NULL, 'Antihistamine.antiallergy', 'Antihistamine.antiallergy', NULL, 'Fexofenadine', 'Sedico', 'Cap', 'كبسولة', '180mg', '1', NULL, NULL, 'about fexofenadine second generation h1 antagonist antihistamine. mechanism of action of fexofenadine fexofenadine blocks one type of peripheral receptor for histamine (the h1 receptor) and thus prevents activation of h1 receptor-containing cells by hista', '6221042181012', 945, '2022-12-08', NULL, 0, 'In the United States, fexofenadine is indicated for the symptomatic treatment of allergic rhinitis in patients ≥2 years old and chronic idiopathic urticaria in patients ≥6 months old.[L4269] In Canada, fexofenadine carries the same indications but is approved only for patients ≥12 years old.[L10779] Fexofenadine is also available in combination with [pseudoephedrine] for the symptomatic treatment of season allergic rhinitis in patients ≥12 years old.[L10800]', 'The H<sub>1</sub> histamine receptor is responsible for mediating hypersensitivity and allergic reactions. Exposure to an allergen results in degranulation of mast cells and basophils, which then release histamine and other inflammatory mediators. Histamine binds to, and activates, H<sub>1</sub> receptors, which results in the further release of pro-inflammatory cytokines, such as interleukins, from basophils and mast cells. These downstream effects of histamine binding are responsible for a wide variety of allergic symptoms, such as pruritus, rhinorrhea, and watery eyes.[A1495]



Fexofenadine is considered an “inverse agonist” of the H<sub>1</sub> receptor because it binds to and stabilizes the inactive form of the receptor, preventing its activation and subsequent downstream effects.[A1495] It has a potent and selective affinity for H<sub>1</sub> receptors, and there is no evidence that it carries antidopaminergic, antiserotonergic, anticholinergic, sedative, or adrenergic blocking activity.[L10779] Fexofenadine does not cross the blood-brain barrier and thus is unlikely to cause significant CNS effects.[L10779]', 'Fexofenadine relieves allergy symptoms by antagonizing the actions of histamine, an endogenous compound predominantly responsible for allergic symptomatology.[L4269] The relatively long duration of action of fexofenadine (approximately 24 hours)[A1495] allows for once or twice daily dosing, and its rapid absorption allows for an onset of action within 1-3 hours. Fexofenadine should not be taken with fruit juice, as this may impair its absorption.[L4269]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Zyrovazet 10/20mg 30 f.c. tablets', 30062, 'زيروفازيت 10/20مجم 30 قرص', '294', '222', 'Antihyperlipidemic.statins', 'Antihyperlipidemic.statins', NULL, 'Rosuvastatin+ezetimibe', 'Al esraa pharmaceutical optima > xeedia pharma', 'Tablet', 'أقراص', '20mg', '3', 'خافض للكوليسترول ودهون الدم', NULL, NULL, '6224010509030', 40309, '2024-08-10', NULL, 0, NULL, NULL, NULL, NULL);
