INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Top clean topical spray 100 ml', 28284, 'توب كلين سبراي 100مل', '40', '35', 'Antiseptic', 'Antiseptic', NULL, 'Ethyl alcohol+chloroxylenol+glycerol+propylene glycol+benzyl alcohol', 'Hi-care > kmt pharma', 'Spray', 'بخاخ', '100 ml', '1', 'مطهر يستخدم لتطهير الجلد وتنظيف الأدوات الجراحية.', NULL, NULL, NULL, 1056, '2024-02-10', NULL, 0, 'Investigated for use/treatment in burns and burn infections, cardiac surgery, inflammatory disorders (unspecified), ischemic reperfusion injury, and sepsis and septicemia.', 'CTI-01 inhibits the systemic release of cytokines, such as TNF-alpha and HMGB1, which promote the body''s inflammatory response. The over-expression of these cytokines has been linked to diseases that occur in critical care settings, such as severe organ damage following cardiopulmonary bypass (CPB) and post-operative ileus following abdominal surgery.', NULL, 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Gescamoli extra 30 fct', 22785, 'جسكامولي اكسترا 30 قرص', '33', NULL, 'Analgesic - antipyretic', 'Analgesic - antipyretic', NULL, 'PARACETAMOL 500 MG + CAFFEINE 50 MG', 'AMCOMED', 'Unknown', 'غير محدد', NULL, '3', 'مسكن للالام وخافض للحرارة ومضاد للالتهابات', NULL, NULL, NULL, 965, '2022-08-14', NULL, 0, NULL, NULL, NULL, NULL);
