INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Neostigmine 1mg/ml vial', 23396, 'نيوستجمين 1مجم/مل فيال', '6', NULL, 'Para-sympathomimetic', 'Para-sympathomimetic', NULL, 'Neostigmine', 'Memphis', 'Vial', 'فيال', '1mg', '1', NULL, NULL, 'about neostigmine para-sympathomimetic a reversible anticholinesterase in myasthenia gravis treatment an adjunct in general anaesthesia curare antagonist. mechanism of action of neostigmine it inhibits the destruction of acetylcholine by cholinesterase (b', NULL, 1454, '2022-07-13', NULL, 0, 'Neostigmine is used for the symptomatic treatment of myasthenia gravis by improving muscle tone.', 'Neostigmine is a parasympathomimetic, specifically, a reversible cholinesterase inhibitor. The drug inhibits acetylcholinesterase which is responsible for the degredation of acetylcholine. So, with acetylcholinesterase inhibited, more acetylcholine is present By interfering with the breakdown of acetylcholine, neostigmine indirectly stimulates both nicotinic and muscarinic receptors which are involved in muscle contraction.. It does not cross the blood-brain barrier.', 'Neostigmine is a cholinesterase inhibitor used in the treatment of myasthenia gravis and to reverse the effects of muscle relaxants such as gallamine and tubocurarine. Neostigmine, unlike physostigmine, does not cross the blood-brain barrier. By inhibiting acetylcholinesterase, more acetylcholine is available in the synapse, therefore, more of it can bind to the fewer receptors present in myasthenia gravis and can better trigger muscular contraction.



', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Omega 3 strem advanced 125 ml syrup', 26369, 'اوميجا 3 ستريم ادفانسد شراب 125 مل', '125', '65', 'Support brain function', 'Support brain function', NULL, NULL, 'UNITRADE PHARMA', 'Syrup', 'شراب', '125 ml', '1', 'مكمل غذائى لتنشيط الذاكرة وزيادة التركيز', NULL, NULL, '796554975320', 2305, '2025-08-16', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Oravit b12 sr 10 cap', 9351, 'اورافيت ب12 م المفعول 10 كبسولات', '18', NULL, 'Vitamin b', 'Vitamin b', NULL, 'Vitamin b12', 'Sovalue pharma', 'Cap', 'كبسولة', NULL, '1', NULL, NULL, NULL, '6221051020692', 1410, '2022-12-07', NULL, 0, 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Virokan 100mg/5ml syrup 60ml', 29406, 'فيروكان 100مجم/5مل شراب 60 مل', '14', NULL, 'Antiviral.nucleoside', 'Antiviral.nucleoside', NULL, 'Ribavirin', 'Hikma pharma', 'Syrup', 'شراب', '100mg', '1', NULL, NULL, 'about ribavirin a nucleoside antimetabolite triazole derivative antiviral. mechanism of action of ribavirin ribavirin is a nucleoside analogue and it is phosphorylated by adenosine kinase enzyme in the host cell to form ribavirin monophosphate.this will i', NULL, 847, '2022-06-18', NULL, 0, 'Indicated for the treatment of chronic Hepatitis C virus (HCV) infection in combination with other antiviral agents with the intent to cure or achieve a sustained virologic response (SVR). Typically added to improve SVR and reduce relapse rates [A19644].



The addition of ribavirin in Technivie therapy indicated for treating HCV genotype 1a and 4 infections is recommended in patients with or without cirrhosis. 



Resistance: viral genetic determinants resulting in variable response to ribavirin therapy has not been yet determined. ', 'Ribavirin is reported to have several mechanism of actions that lead to inhibition of viral RNA and protein synthesis. 

After activation by adenosine kinase to ribavirin mono-, di-, and triphosphate metabolites. Ribavirin triphosphate (RTP) is the predominant metabolite which directly inhibits viral mRNA polymerase by binding to the nucleotide binding site of the enzyme. This prevents the binding of the correct nucleotides, leading to a reduction in viral replication or to the production of defective virions [A19645]. RTP also demonstrates an inhibitory action on viral mRNA guanylyltransferase and mRNA 2′-O-methyltransferase of dengue virus. Inhibition of these enzymes disrupts the posttranslational capping of the 5′ end of viral mRNA through ribavirin being incorporated at the 5′ end in place of guanosine and preventing the cap methylation step. 



Inhibition of host inosine monophosphate dehydrogenase (IMPDH) and subsequent depletion of GTP pool is proposed to be another mechanism of action of ribavirin. IMPDH catalyzes the rate-limiting step where inosine 5′-monophosphate is converted to xanthine monophosphate during guanosine monophosphate (GMP) synthesis. GMP is later converted to guanosine triphoshpate (GTP). Ribavirin monophosphate mimics inosine 5′-monophosphate and acts as a competitive inhibitor of IMPDH. Inhibited de novo synthesis of guanine nucleotides and decreased intracellular GTP pools leads to a decline in viral protein synthesis and limit replication of viral genomes [A19645].



Ribavirin acts as a mutagen in the target virus to cause an ''error catastrophe'' due to increased viral mutations. RTP pairs with cytidine triphosphate or uridine triphosphate with equal efficiency and to block HCV RNA elongation. It causes premature termination of nascent HCV RNA and increases mutagenesis by producing defective virions [A19645].



Ribavirin also exerts an immunomodulatory action of the host to the virus by shifting a Th2 response in favor of a Th1 phenotype. Th2 response and production of type 2 cytokines such as IL-4, IL-5, and IL-10 stimulates the humoral response which enhances immunity toward the virus [A19645]. Ribavirin enhanced induction of interferon-related genes, including the interferon-α receptor, and down-regulation of genes involved in interferon inhibition, apoptosis, and hepatic stellate cell activation in vitro [A19644]. ', 'Ribavirin mediates direct antiviral activity against a number of DNA and RNA viruses by increasing the mutation frequency in the genomes of several RNA viruses. It is a member of the nucleoside antimetabolite drugs that interfere with duplication of the viral genetic material. The drug inhibits the activity of the enzyme RNA dependent RNA polymerase, due to its resemblence to building blocks of the RNA molecules. ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Stopadol forte 1000 mg 10 eff. sachets', 33696, 'ستوبادول فورت 1000 مجم 10 كيس', '60', '40', 'Mild analgesic', 'Mild analgesic', NULL, 'Paracetamol(acetaminophen)', 'Eva pharma', 'Sachet', 'أكياس', '1000 mg', '1', NULL, NULL, 'mechanism: it is thought paracetamol reduces fever by affecting an area of the brain that regulates our body temperature (the hypothalamic heat-regulating center). paracetamol is about as effective as aspirin or ibuprofen at relieving mild to moderate pai', '6223007350280', 2552, '2024-08-22', NULL, 0, NULL, NULL, NULL, NULL);
