INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Doxy plus stada 100mg 12 m.r.caps.', 17888, 'دوكسى بلس ستادا 100 مجم 12', '22', NULL, 'Antibiotic with mucolytic', 'Antibiotic with mucolytic', NULL, 'Ambroxol+doxycycline', 'Global napi pharmaceuticals > stada', 'Cap', 'كبسولة', '100mg', '1', NULL, NULL, 'mechanism of action of doxycycline doxycycline is a broad spectrum antibiotic which acts against both gram positive and gram negative organisms. it exerts its bacteriostatic action by accumulating inside the bacteria through specific transporter protein a', '6223002145317', 1170, '2022-07-13', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Abimol 300mg 5 rectal supp.', 27, 'ابيمول 300مجم لبوس 5 اقماع شرجية', '15', '6.5', 'Antipyretic.analgesic', 'Antipyretic.analgesic', NULL, 'Paracetamol(acetaminophen)', 'Glaxo smithkline', 'Supp', 'لبوس', '300mg', '1', 'خافض حرارة ومسكن', NULL, 'about paracetamol acetanilide derivative non narcotic analgesic antipyretic. mechanism of action of paracetamol paracetamol has analgesic and antipyretic action. it is more active on cyclo-oxygenase enzyme in brain. peripherally it is a poor inhibitor of', '6221045000808', 9887, '2023-09-20', NULL, 0, NULL, NULL, NULL, NULL);
