INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Intragel one 2% 2.5ml prefilled syringe', 20902, 'انتراجل ون 2% سرنجة معبأة 2.5 مل', '2,600', '1650', 'Viscosuppletive joint device', 'Viscosuppletive joint device', NULL, 'Sodium hyaluronate', 'Ibsa farmaceutici > habib scientific office', 'Syringe', 'حقنة', '2%', '1', 'للمفاصل', NULL, 'about sodium hyaluronate a visco-elastic polymer(glycosaminoglycan) a tissue lubricant(prevent vaginal dryness) in osteoarthritis. mechanism of action of sodium hyaluronate sodium hyaluronate is a sterile nonpyrogenic highly purified noninflammatory high', NULL, 7135, '2024-09-30', NULL, 0, 'For the treatment of small uncomplicated varicose veins of the lower extremities that show simple dilation with competent valves.', 'Sodium tetradecyl sulfate is a potent toxin for endothelial cells in that brief exposure to even low concentrations are effective in stripping endothelium over a considerable distance and exposing highly thrombogenic endothelium in the process. Diluted sodium tetradecyl sulfate is also able to induce a hypercoagulable state, possibly by selective inhibition of protein C, and can also promote platelet aggregation.', 'Sodium tetradecyl sulfate is an anionic surfactant which occurs as a white, waxy solid. It is used as a sclerosing agent in sclerotherapy. Sclerotherapy is an injection of a sclerosing agent directly through the skin into a lesion and is used primarily for slow-flow vascular anomalies, particularly for venous malformation and lymphatic malformation. Intravenous injection causes intima inflammation and thrombus formation. This usually occludes the injected vein. Subsequent formation of fibrous tissue results in partial or complete vein obliteration that may or may not be permanent.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Renu multiplus solution 120 ml', 25880, 'رينيو مالتي بلس محلول عدسات 120 مل', '310', '295', 'Contact lenses solution', 'Contact lenses solution', NULL, 'Boric acid+edetate disodium+sodium borate+sodium chloride+dymed+hydranate+poloxamine', 'Bausch & lomb > kandil for trading', 'Solution', 'محلول', '120 ml', '1', NULL, NULL, 'multi-purpose solution for soft contact lenses including silicon hydrogels', '7391899858357', 788, '2024-02-29', NULL, 0, 'No FDA- or EMA-approved therapeutic indications on its own.', 'Information regarding the mechanism of action of boric acid in mediating its antibacterial or antifungal actions is limited. Boric acid inhibits biofilm formation and hyphal transformation of _Candida albicans_, which are critical virulence factors [A32457]. In addition, arrest of fungal growth was observed with the treatment of boric acid [A32457].', 'Boric acid exhibits minimal bacteriostatic and antifungal activities [L2140]. Boric acid is likely to mediate antifungal actions at high concentrations over prolonged exposures [A32457]. ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Vichy liftactiv supreme night cream 50 ml', 29268, 'فيتشي ليفتاكتيف سوبريم كريم للاستعمال الليلي 50مل', '509', NULL, 'Skin care', 'Skin care', NULL, NULL, 'Vichy laboratories > united company for trading & distribution', 'Cream', 'كريم', '50 ml', '1', NULL, NULL, 'night supreme is our breakthrough night-time formula featuring rhamnose. helps combat the signs of skin aging by visibly reducing the appearance of wrinkles & helping to boost skin firmness. ultimately this enables the skin youthfulness to be restored. fo', NULL, 585, '2022-08-17', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Baby steps goat milk infant formula stage 1 - 300 gm', 6456, 'بيبي ستيبس لبن ماعز للمرحلة 1 - 300 جم', '485', '384', 'Milk products.second stage(age 0-6 monthes)', 'Milk products.second stage(age 0-6 monthes)', NULL, 'Milk formula stage 1', 'Nig nutritionals limited', 'Unknown', 'غير محدد', '300 gm', '1', 'لبن اطفال للمرحلة 1', NULL, 'baby steps formula based on normal secretion whole goat s milk from 0 to 6 months adequate nutrition ï¿½ high tolerability ï¿½ advanced technologies *provides your baby with adequate nutrition 318 nutrients *the only source of animal fat *after reconstitu', NULL, 1656, '2023-08-28', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Gaptin 400 mg 30 caps.', 5324, 'جابتين 400مجم 30 كبسولة', '156', '115.5', 'Anti-epileptic.gaba analogs', 'Anti-epileptic.gaba analogs', NULL, 'Gabapentin', 'Delta pharma', 'Cap', 'كبسولة', '400 mg', '3', 'مهديء عصبي', NULL, 'about gabapentin a gaba analogue anticonvulsant mood stabilizer. mechanism of action of gabapentin gabapentin is a gaba derivative and it bounds to lipophilic molecule. the mechanism of action of gabapentin is not exactly known. it crosses blood brain bar', '6221163007994', 8380, '2024-06-02', NULL, 0, 'In the United States, gabapentin is officially indicated for the treatment of postherpetic neuralgia in adults and for the adjunctive treatment of partial-onset seizures, with or without secondary generalization, in patients 3 years of age and older.[L8717] In Europe, gabapentin is indicated for adjunctive therapy in the treatment of partial-onset seizures, with or without secondary generalization, in patients 6 years of age and older and as monotherapy in patients 12 years of age and older. It is also used in adults for the treatment of various types of peripheral neuropathic pain, such as painful diabetic neuropathy.[L8732]', 'The precise mechanism through which gabapentin exerts its therapeutic effects is unclear.[L8717,L8720] The primary mode of action appears to be at the auxillary α2δ-1 subunit of voltage-gated calcium channels (though a low affinity for the α2δ-2 subunit has also been reported).[A186179,A14097,A186277] The major function of these subunits is to facilitate the movement of pore-forming α1 subunits of calcium channels from the endoplasmic reticulum to the cell membrane of pre-synaptic neurons.[A186179] There is evidence that chronic pain states can cause an increase in the expression of α2δ subunits and that these changes correlate with hyperalgesia.[A14097] Gabapentin appears to inhibit the action of α2δ-1 subunits, thus decreasing the density of pre-synaptic voltage-gated calcium channels and subsequent release of excitatory neurotransmitters.[A186179] It is likely that this inhibition is also responsible for the anti-epileptic action of gabapentin.[A186277]



There is some evidence that gabapentin also acts on adenosine receptors[A11009, A186209] and voltage-gated potassium channels,[A186212] though the clinical relevance of its action at these sites is unclear.', 'Gabapentin is an anti-convulsant medication that inhibits the release of excitatory neurotransmitters, allowing for its use against pathologic neurotransmission such as that seen in neuropathic pain and seizure disorders.[L8717,L8732] It has a wide therapeutic index, with doses in excess of 8000 mg/kg failing to cause a fatal reaction in rats.[L8765]



Gabapentin is ineffective in absence seizures and should be used in caution in patients with mixed seizure disorders involving absence seizures. Gabapentin has been associated with drug reaction with eosinophilia and systemic symptoms (DRESS), otherwise known as multi-organ hypersensitivity. This reaction can prove fatal and early symptoms such as fever, lymphadenopathy, and rash should be promptly investigated.[L8717,L8720,L8732]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Detorex 10sachets', 32733, 'ديتوريكس 10 اكياس', '100', '90', 'Source of fibers', 'Source of fibers', NULL, 'Psyllium husk + chromium picolonate + vit c', 'Medlink group pharma', 'Sachet', 'أكياس', NULL, '1', NULL, NULL, NULL, NULL, 729, '2023-10-21', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Sodium chloride 0.9 % (el nile) i.v. inf. 100 ml (rubber cap)', 27161, 'صوديوم كلورايد 0.9% النيل محلول ملح وريدي 100 مل بغطاء مطاطي', '5', NULL, 'Normal saline', 'Normal saline', NULL, 'Sodium chloride', 'El nile', 'Cap', 'كبسولة', '0.9 %', '1', NULL, NULL, NULL, NULL, 1273, '2022-07-08', NULL, 0, 'This intravenous solution is indicated for use in adults and pediatric patients as a source of electrolytes and water for hydration. Also, designed for use as a diluent and delivery system for intermittent intravenous administration of compatible drug additives. ', 'Sodium and chloride — major electrolytes of the fluid compartment outside of cells (i.e., extracellular) — work together to control extracellular volume and blood pressure. Disturbances in sodium concentrations in the extracellular fluid are associated with disorders of water balance.', 'Sodium, the major cation of the extracellular fluid, functions primarily in the control of water distribution, fluid balance, and osmotic pressure of body fluids. Sodium is also associated with chloride and bicarbonate in the regulation of the acid-base equilibrium of body fluid.

Chloride, the major extracellular anion, closely follows the metabolism of sodium, and changes in the acid-base balance of the body are reflected by changes in the chloride concentration. ', 'DrugBank');
