INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Atrakta delicate hair shampoo 400 ml', 14584, 'اتراكتا ديليكيت شامبو للشعر 400 مل', '450', '400', 'Hair care', 'Hair care', NULL, NULL, 'Ce.is.co srl > macro group pharmaceuticals', 'Amp', 'أمبول', '400 ml', '1', NULL, NULL, NULL, NULL, 684, '2024-05-16', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Minro 30 tablets', 5992, 'مينرو 30 قرص', '195', NULL, 'Iron supplement', 'Iron supplement', NULL, 'Ferrous fumarate+lactoferrin+folic acid+vitamin b1+vitamin b6+vitamin b12+vitamin c', 'Ultra pharma plus', 'Tablet', 'أقراص', NULL, '1', NULL, NULL, NULL, NULL, 708, '2025-07-16', NULL, 0, 'Ferrous sulfate is used for the prevention and treatment of iron deficiency anemia in adults and children.[A190804,L2240,L11800]

', 'Iron is required to maintain optimal health, particularly for helping to form red blood cells (RBC) that carry oxygen around the body. A deficiency in iron indicates that the body cannot produce enough normal red blood cells.[A32514,L11800] Iron deficiency anemia occurs when body stores of iron decrease to very low levels, and the stored iron is insufficient to support normal red blood cell (RBC) production. Insufficient dietary iron, impaired iron absorption, bleeding, pregnancy, or loss of iron through the urine can lead to iron deficiency.[A32514,L11794]  Symptoms of iron deficiency anemia include fatigue, breathlessness, palpitations, dizziness, and headache.



Taking iron in supplement form, such as ferrous sulfate, allows for more rapid increases in iron levels when dietary supply and stores are not sufficient.[L2175] Iron is transported by the divalent metal transporter 1 (DMT1) across the endolysosomal membrane to enter the macrophage. It can then can be incorporated into ferritin and be stored in the macrophage or carried of the macrophage by ferroportin. This exported iron is  oxidized by the enzyme to ceruloplasmin to Fe3+, followed by sequestration by transferrin for transport in the serum to various sites, including the bone marrow for hemoglobin synthesis or into the liver.[A32524] Iron combines with porphyrin and globin chains to form hemoglobin, which is critical for oxygen delivery from the lungs to other tissues.[L2263]', 'Ferrous sulfate replenishes iron, an essential component in hemoglobin, myoglobin, and various enzymes. It replaces the iron that is usually found in hemoglobin and myoglobin. Iron participates in oxygen transport and storage, electron transport and energy metabolism, antioxidant and beneficial pro-oxidant functions, oxygen sensing, tissue proliferation and growth, as well as DNA replication and repair.[A32524,A32514]



', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Fortalgin 10mg 10 tab', 5106, 'فورتالجين 10مجم 10 اقراص', '9', NULL, NULL, NULL, NULL, 'Hyoscine+dicyclomine+paracetamol', 'Gsk', 'Tab', 'أقراص', '10mg', '1', NULL, NULL, NULL, NULL, 814, '2023-02-03', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Psyllose 10 sachets', 25475, 'سيلوز 10 اكياس', '45', NULL, 'Supplement', 'Supplement', NULL, 'Psyllium husk+ pineapple extract +apple extract +grapefruit extract', 'Zakuro > mercy pharma', 'Sachet', 'أكياس', NULL, '1', 'مكمل غذائي __ مصدر للطاقة __ يساعد علي التخسيس __ يحافظ علي شكل الجسم', NULL, 'blend of natural herbs specially (psyllium husk) that is enriched in fibres that regulate git function. very effective in (dyspepsia and ibs) cases', NULL, 1914, '2022-04-18', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Normesar 20mg 30 f.c. tabs.', 23726, 'نورميسار 20مجم 30 اقراص', '63', NULL, 'Antihypertensive.angiotensin blocker', 'Antihypertensive.angiotensin blocker', NULL, 'Olmesartan', 'Jedco int. co. for pharmaceuticals > four a pharma', 'Tab', 'أقراص', '20mg', '3', NULL, NULL, 'about olmesartan an angiotensin ii receptor antagonist imidazole derivative antihypertensive. mechanism of action of olmesartan olmesartan is a competitive antagonist of angiotensin-ii at at-1 receptor. it decreases peripheral resistance and lowers bp in', '6225000086685', 910, '2022-12-07', NULL, 0, 'Olmesartan is indicated for the treatment of hypertension either alone or in combination with other antihypertensive agents.[F4709,F4712,A173869] 



Olmesartan is also used off-label for the management Type 2 Diabetes-associated nephropathy, heart failure, and post-myocardial infarction, particularly in patients who are unable to tolerate ACE inhibitors.[A178153,A185912,A185915] ARBs such as olmesartan have been shown in a number of large-scale clinical outcomes trials to improve cardiovascular outcomes including reducing risk of myocardial infarction, stroke, the progression of heart failure, and hospitalization.[A174124,A178153,A173869,A185324,A185327,A185333,A185342,A185345] Like other ARBs, olmesartan blockade of RAAS slows the progression of diabetic nephropathy due to its renoprotective effects.[A185906,A185909,A185912] ', 'Olmesartan belongs to the angiotensin II receptor blocker (ARB) family of drugs, which also includes [telmisartan], [candesartan], [losartan], [valsartan], and [irbesartan]. ARBs selectively bind to angiotensin receptor 1 (AT1) and prevent the protein angiotensin II from binding and exerting its hypertensive effects. As the principal pressor agent of the renin-angiotensin system, Angiotensin II causes vasoconstriction, stimulation of synthesis and release of aldosterone, cardiac stimulation and renal reabsorption of sodium. Olmesartan blocks the vasoconstrictor effects of angiotensin II by selectively blocking the binding of angiotensin II to the AT1 receptor in vascular smooth muscle. Its action is, therefore, independent of the pathways for angiotensin II synthesis.[F4709] Overall, olmesartan''s physiologic effects lead to reduced blood pressure, lower aldosterone levels, reduced cardiac activity, and increased excretion of sodium. 



Olmesartan also effects on the renin-angiotensin aldosterone system (RAAS) plays an important role in hemostasis and regulation of kidney, vascular, and cardiac functions. Pharmacological blockade of RAAS via  AT1 receptor blockade inhibits negative regulatory feedback within RAAS, which is a contributing factor to the pathogenesis and progression of cardiovascular disease, heart failure, and renal disease. In particular, heart failure is associated with chronic activation of RAAS, leading to inappropriate fluid retention, vasoconstriction, and ultimately a further decline in left ventricular function. ARBs have been shown to have a protective effect on the heart by improving cardiac function, reducing afterload, increasing cardiac output and preventing ventricular hypertrophy and remodelling.[A174154] ', 'Overall, olmesartan''s physiologic effects lead to reduced blood pressure, lower aldosterone levels, reduced cardiac activity, and increased excretion of sodium. 





**Hypotension in Volume- or Salt-Depleted Patients**



In patients with an activated renin-angiotensin aldosterone system, such as volume-and/or salt-depleted patients (e.g., those being treated with high doses of diuretics), symptomatic hypotension may be anticipated after initiation of treatment with olmesartan. Initiate treatment under close medical supervision. If hypotension does occur, place the patient in the supine position and, if necessary, give an intravenous infusion of normal saline. A transient hypotensive response is not a contraindication to further treatment, which usually can be continued without difficulty once the blood pressure has stabilized.[F4709,F4712]



Valvular Stenosis: there is concern on theoretical grounds that patients with aortic stenosis might be at a particular risk of decreased coronary perfusion, because they do not develop as much afterload reduction.[F4712]





**Impaired Renal Function**



As a consequence of inhibiting the renin-angiotensin-aldosterone system, changes in renal function may be anticipated in susceptible individuals treated with olmesartan. In patients whose renal function may depend upon the activity of the renin-angiotensin- aldosterone system (e.g., patients with severe congestive heart failure), treatment with angiotensin converting enzyme (ACE) inhibitors and angiotensin receptor antagonists has been associated with oliguria and/or progressive azotemia and rarely with acute renal failure and/or death. Similar results may be anticipated in patients treated with olmesartan.



In studies of ACE inhibitors in patients with unilateral or bilateral renal artery stenosis, increases in serum creatinine or blood urea nitrogen (BUN) have been reported. There has been no long-term use of olmesartan medoxomil in patients with unilateral or bilateral renal artery stenosis, but similar results may be expected.[F4709,F4712]





**Sprue-like Enteropathy**



Severe, chronic diarrhea with substantial weight loss has been reported in patients taking olmesartan months to years after drug initiation. Intestinal biopsies of patients often demonstrated villous atrophy. If a patient develops these symptoms during treatment with olmesartan, exclude other etiologies. Consider discontinuation of olmesartan medoxomil in cases where no other etiology is identified.[F4709,F4712]





**Electrolyte Imbalances**



Olmesartan medoxomil contains olmesartan, a drug that inhibits the renin-angiotensin system (RAS). Drugs that inhibit the RAS can cause hyperkalemia. Monitor serum electrolytes periodically.[F4709,F4712]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Bronotrol 2.5mg/5ml syrup 120 ml', 1758, 'برونوترول شراب موسع للشعب', '17', '7', 'Bronchodilator.short-acting b2 agonist', 'Bronchodilator.short-acting b2 agonist', NULL, 'Fenoterol hydrobromide', 'Sigma', 'Syrup', 'شراب', '2.5mg', '1', NULL, NULL, 'description an adrenergic beta-2 agonist that is used as a bronchodilator and tocolytic indication fenoterol is used for the treatment of asthma. mechanism of action beta(2)-receptor stimulation in the lung causes relaxation of bronchial smooth muscle bro', '6221051040621', 989, '2025-07-13', NULL, 0, 'Fenoterol is used for the treatment of asthma.', 'Beta(2)-receptor stimulation in the lung causes relaxation of bronchial smooth muscle, bronchodilation, and increased bronchial airflow.', 'Fenoterol is a beta agonist designed to open up the airways to the lungs by decreasing bronchconstriction.', 'DrugBank');
