INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Tractocile 7.5mg/ml i.v. conc. vial 5 ml', 28380, 'تراكتوسيل 7.5مجم/مل 5مل', '630', NULL, 'Uterine relaxant', 'Uterine relaxant', NULL, 'Atosiban', 'Ferring pharmaceuticals > multipharma', 'Vial', 'فيال', '7.5mg', '1', NULL, NULL, 'tractocile is indicated to delay imminent pre-term birth in pregnant adult women with: - regular uterine contractions of at least 30 seconds duration at a rate of = 4 per 30 minutes - a cervical dilation of 1 to 3 cm (0-3 for nulliparas) and effacement of', NULL, 773, '2022-08-03', NULL, 0, 'Atosiban is indicated for use in delaying imminent pre-term birth in pregnant adult women with: 



- regular uterine contractions of at least 30 s duration at a rate of at least 4 per 30 min

- a cervical dilation of 1-3cm (0-3cm for nulliparas) and effacement of  at least 50%

- a gestational age of 24-33 weeks

- a normal fetal heart rate', 'Atosiban is a synthetic peptide oxytocin antagonist [L2469,A32685]. It resembles oxytocin with has modifications at the 1, 2, 4, and 8 positions. The N-terminus of the cysteine residue is deaminated to form 3-mercaptopropanic acid at position 1, at position 2 L-tyrosine is modified to D-tyrosine with an ethoxy group replacing the phenol , threonine replaces glutamine at postion 4, and ornithine replaces leucine at position 8.



It binds to membrane bound oxytocin receptors on the myometrium and prevents oxytocin-stimulated increases in inositol triphosphate production [A32685]. This ultimately prevents release of stored calcium from the sarcoplasmic reticulum and subsequent opening of voltage gated calcium channels. This shutdown of cytosolic calcium increase prevents contractions of the uterine muscle, reducing the frequency of contractions and inducing uterine quiescence.



Atosiban has more recently been found to act as a biased ligand at oxytocin receptors [A32694,A32695]. It acts as an antagonist of Gq coupling, explaining the inhibition of the inositol triphosphate pathway thought to be responsible for the effect on uterine contraction, but acts as an agonist of Gi coupling. This agonism produces a pro-inflammatory effect in the human amnion, activating pro-inflammatory signal tranducer NF-κB [A32695]. It is thought that this reduces atosiban''s effectiveness compared to agents which do not produce inflammation as inflammatory mediators are known to play a role in the induction of labour.', 'Atosiban reduces the frequency of uterine contractions to delay pre-term birth in adult females and induces uterine quiescence [L2469,A32685].', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Cholicynar 12 eff. granules sach.', 16269, 'كوليسينار 12 اكياس فوارة', '11', NULL, 'Antioxidant', 'Antioxidant', NULL, 'Boldo ext.', 'Global napi pharmaceuticals', 'Eff', 'فوار', NULL, '1', 'علاج أمراض الكبد والجهاز الهضمي، كما تقوم بتنشيط الغشاء المخاطي المعوي وتقليص العضلات الملساء', NULL, 'boldo and boldine (an alkaloid present in boldo) extracts are known to exhibit choleretic properties (stimulating bile flow). boldo has been shown to exert anti-inflammatory and antipyretic (anti-fever) effects. boldine is an effective inhibitor or prosta', NULL, 1038, '2022-07-23', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Endsav 30 tablets', 2174, 'ايندساف 30قرص', '490', NULL, 'Anti artharitic drug', 'Anti artharitic drug', NULL, 'Glucosamine sulfate+chondoritin sulfate+msm+collagen+hyalouronic acid', 'Savi pharma', 'Tablet', 'أقراص', NULL, '3', NULL, NULL, NULL, NULL, 631, '2025-01-06', NULL, 0, 'Glucosamine is generally used over the counter in the symptomatic treatment of osteoarthritis and joint pain,[A2352] frequently combined with chondroitin sulfate and/or ibuprofen.[L32699]', 'The mechanism of action of glucosamine in joint health is unclear,[A231894] however there are several possible mechanisms that contribute to its therapeutic effects. Because glucosamine is a precursor for glycosaminoglycans, and glycosaminoglycans are a major component of joint cartilage,[A232289] glucosamine supplements may help to rebuild cartilage and treat the symptoms of arthritis.  Some in vitro studies show evidence that glucosamine reduces inflammation via inhibition of interferon gamma[A12619,A12616,A12621]  and Nuclear factor kappa B subunit 65 (NF-κB p65),[A12616,A232284] improving the symptoms of arthritis and joint pain. Clinical relevance is unknown at this time.', 'The administration of glucosamine, in theory, provides a building block towards the synthesis of glycosaminoglycans, slowing the progression of osteoarthritis and relieving symptoms of joint pain. Studies to this date examining the efficacy of glucosamine sulfate have been inconclusive. Glycosaminoglycans contribute to joint cartilage elasticity, strength, and flexibility.[A232144] A systematic review of various studies and guidelines determined that modest improvements were reported for joint pain and function in patients taking glucosamine. A consistent joint space narrowing was observed, but with an unclear clinical significance.[A231894]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Flamotal 500 mg 6 supp.', 19102, 'فلاموتال 500 مجم 6 اقماع', '6', NULL, 'Nsaid.propionic acid derivatives', 'Nsaid.propionic acid derivatives', NULL, 'Ibuprofen', 'Global napi pharmaceuticals > future pharmaceutical industries (fpi)', 'Supp', 'لبوس', '500 mg', '1', NULL, NULL, 'about ibuprofen nsaid a propionic acid derivative analgesic and anti-inflammatory. mechanism of action of ibuprofen ibuprofen has analgesic anti-inflammatory and antipyretic action. it acts by inhibiting prostaglandin (pgs) synthesis and their release at', '6223004199219', 832, '2022-12-05', NULL, 0, 'Ibuprofen is the most commonly used and prescribed NSAID. It is a very common over-the-counter medication widely used as an analgesic, anti-inflammatory and antipyretic.[A39096]



The use of ibuprofen and its enantiomer [DB09213] in a racemic mix is common for the management of mild to moderate pain related to dysmenorrhea, headache, migraine, postoperative dental pain, spondylitis, osteoarthritis, rheumatoid arthritis, and soft tissue disorder.[A39097]



Due to its activity against prostaglandin and thromboxane synthesis, ibuprofen has been attributed to alteration of platelet function and prolongation of gestation and labour.[A39092]



As ibuprofen is a widely used medication, the main therapeutic indications are:



* Patent Ductus Arteriosus - it is a neonatal condition wherein the ductus arteriosus (blood vessel that connects the main pulmonary artery to the proximal descending aorta) fails to close after birth causing severe risk of heart failure. The prostaglandin inhibition of ibuprofen has been studied for the treatment of this condition as it is known that prostaglandin E2 is responsible for keeping the ductus arteriosus open.[A39100]



* Rheumatoid- and osteo-arthritis - ibuprofen is very commonly used in the symptomatic treatment of inflammatory, musculoskeletal and rheumatic disorders.[A39176]



* Cystic fibrosis - the use of high dosages of ibuprofen has been proven to decrease inflammation and decreasing polymorphonuclear cell influx in the lungs.[A39177]



* Orthostatic hypotension - ibuprofen can induce sodium retention and antagonize the effect of diuretics which has been reported to be beneficial for patients with severe orthostatic hypotension.[A1651]



* Dental pain - ibuprofen is used to manage acute and chronic orofacial pain.[A10901]



* Pain - ibuprofen is widely used to reduce minor aches and pains as well as to reduce fever and manage dysmenorrhea. It is very commonly used for the relief of acute indications such as fever and tension headaches.[A39092] It is also used to manage mild to moderate pain and moderate to severe pain as an adjunct to opioid analgesics.[L40208]



* Investigational uses - efforts have been put into developing ibuprofen for the prophylaxis of Alzheimer''s disease, Parkinson disease, and breast cancer.[A39092]', 'The exact mechanism of action of ibuprofen is unknown. However, ibuprofen is considered an NSAID and thus it is a non-selective inhibitor of cyclooxygenase, which is an enzyme involved in prostaglandin (mediators of pain and fever) and thromboxane (stimulators of blood clotting) synthesis via the arachidonic acid pathway.[L4614]



Ibuprofen is a non-selective COX inhibitor and hence, it inhibits the activity of both COX-1 and COX-2. The inhibition of COX-2 activity decreases the synthesis of prostaglandins involved in mediating inflammation, pain, fever, and swelling while the inhibition of COX-1 is thought to cause some of the side effects of ibuprofen including GI ulceration.[A39195]', 'Ibuprofen has multiple actions in different inflammatory pathways involved in acute and chronic inflammation. The main effects reported in ibuprofen are related to the control of pain, fever and acute inflammation by the inhibition of the synthesis of prostanoids by COX-1 and COX-2. Pain relief is attributed to peripheral affected regions and central nervous system effects in the pain transmission mediated by the dorsal horn and higher spinothalamic tract. Some reports have tried to link the pain regulation with a possible enhancement on the synthesis of endogenous cannabinoids and action on the NMDA receptors. The effect on pain has been shown to be related to the cortically evoked potentials.[A39190]



The antipyretic effect is reported to be linked to the effect on the prostanoid synthesis due to the fact that the prostanoids are the main signaling mediator of pyresis in the hypothalamic-preoptic region.[A39190]



The use of ibuprofen in dental procedures is attributed to the local inhibition of prostanoid production as well as to anti-oedemic activity and an increase of plasma beta-endorphins. Some reports have suggested a rapid local reduction of the expression of COX-2 in dental pulp derived by the administration of ibuprofen.[A39190]



The administration of ibuprofen in patients with rheumatic diseases has shown to control joint symptoms.[A39092] 



Ibuprofen is largely used in OTC products such as an agent for the management of dysmenorrhea which has been proven to reduce the amount of menstrual prostanoids and to produce a reduction in the uterine hypercontractility.[A39181] As well, it has been reported to reduce significantly the fever and the pain caused by migraines.[A39182, A39183] This effect is thought to be related to the effect on platelet activation and thromboxane A2 production which produces local vascular effects in the affected regions. This effect is viable as ibuprofen can enter in the central nervous system.[A39190]



In the investigational uses of ibuprofen, it has been reported to reduce neurodegeneration when given in low doses over a long time.[A39184] On the other hand, its use in Parkinson disease is related to the importance of inflammation and oxidative stress in the pathology of this condition.[A39185] The use of ibuprofen for breast cancer is related to a study that shows a decrease of 50% in the rate of breast cancer.[A39186]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Voltaren ophta 0.1% eye drops 5 ml (n/a)', 29576, 'فولتارين اوفثا 0.1% قطرة عين 5 مل (غير متوافرة)', '24', NULL, 'Nsaid.acetic acid derivatives.analgesic.anti-inflammatory', 'Nsaid.acetic acid derivatives.analgesic.anti-inflammatory', NULL, 'Diclofenac sodium', 'Novartis', 'Drops', 'نقط', '0.1%', '1', 'قطرة مسكنة لآلام العين ومضادة للالتهاب', NULL, 'description a non-steroidal anti-inflammatory agent (nsaid) with antipyretic and analgesic actions. it is primarily available as the sodium salt. indication : for the acute and chronic treatment of signs and symptoms of osteoarthritis and rheumatoid arthr', '5413895025983', 921, '2022-12-14', NULL, 0, 'Diclofenac is indicated for use in the treatment of pain and inflammation from varying sources including inflammatory conditions such as osteoarthritis, rheumatoid arthritis, and ankylosing spondylitis, as well as injury-related inflammation due to surgery and physical trauma. It is often used in combination with [misoprostol] as a gastro-protective agent in patients with high risk of developing NSAID-induced ulcers.', 'Diclofenac inhibits cyclooxygenase-1 and -2, the enzymes responsible for production of prostaglandin (PG) G<sub>2</sub> which is the precursor to other PGs.[label,T116] These molecules have broad activity in pain and inflammation and the inhibition of their production is the common mechanism linking each effect of diclofenac.



PGE<sub>2</sub> is the primary PG involved in modulation of nociception.[A179023] It mediates peripheral sensitization through a variety of effects.[T116,A179023] PGE<sub>2</sub> activates the G<sub>q</sub>-coupled EP<sub>1</sub> receptor leading to increased activity of the inositol trisphosphate/phospholipase C pathway. Activation of this pathway releases intracellular stores of calcium which directly reduces action potential threshold and activates protein kinase C (PKC) which contributes to several indirect mechanisms. PGE<sub>2</sub> also activates the EP<sub>4</sub> receptor, coupled to G<sub>s</sub>, which activates the adenylyl cyclase/protein kinase A (AC/PKA) signaling pathway. PKA and PKC both contribute to the potentiation of transient receptor potential cation channel subfamily V member 1 (TRPV1) potentiation, which increases sensitivity to heat stimuli. They also activate tetrodotoxin-resistant sodium channels and inhibit inward potassium currents. PKA further contributes to the activation of the P2X3 purine receptor and sensitization of T-type calcium channels. The activation and sensitization of depolarizing ion channels and inhibition of inward potassium currents serve to reduce the intensity of stimulus necessary to generate action potentials in nociceptive sensory afferents. PGE<sub>2</sub> act via EP<sub>3</sub> to increase sensitivity to bradykinin and via EP<sub>2</sub> to further increase heat sensitivity. Central sensitization occurs in the dorsal horn of the spinal cord and is mediated by the EP<sub>2</sub> receptor which couples to G<sub>s</sub>. Pre-synaptically, this receptor increases the release of pro-nociceptive neurotransmitters glutamate, CGRP, and substance P. Post-synaptically it increases the activity of AMPA and NMDA receptors and produces inhibition of inhibitory glycinergic neurons. Together these lead to a reduced threshold of activating, allowing low intensity stimuli to generate pain signals. PGI<sub>2</sub> is known to play a role via its G<sub>s</sub>-coupled IP receptor although the magnitude of its contribution varies. It has been proposed to be of greater importance in painful inflammatory conditions such as arthritis. By limiting sensitization, both peripheral and central, via these pathways NSAIDs can effectively reduce inflammatory pain.



PGI<sub>2</sub> and PGE<sub>2</sub> contribute to acute inflammation via their IP and EP<sub>2</sub> receptors.[T116,A179044] Similarly to β adrenergic receptors these are G<sub>s</sub>-coupled and mediate vasodilation through the AC/PKA pathway. PGE<sub>2</sub> also contributes by increasing leukocyte adhesion to the endothelium and attracts the cells to the site of injury.[T116] PGD<sub>2</sub> plays a role in the activation of endothelial cell release of cytokines through its DP<sub>1</sub> receptor.[A179044] PGI<sub>2</sub> and PGE<sub>2</sub> modulate T-helper cell activation and differentiation through IP, EP<sub>2</sub>, and EP<sub>4</sub> receptors which is believed to be an important activity in the pathology of arthritic conditions. By limiting the production of these PGs at the site of injury, NSAIDs can reduce inflammation.



PGE<sub>2</sub> can cross the blood-brain barrier and act on excitatory G<sub>q</sub> EP<sub>3</sub> receptors on thermoregulatory neurons in the hypothalamus.[T116] This activation triggers an increase in heat-generation and a reduction in heat-loss to produce a fever. NSAIDs prevent the generation of PGE<sub>2</sub> thereby reducing the activity of these neurons.

', 'Diclofenac reduces inflammation and by extension reduces nociceptive pain and combats fever.[label,T116] It also increases the risk of developing a gastrointestinal ulcer by inhibiting the production of protective mucus in the stomach.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Zovirax 250mg 5 vial i.v. infusion', 12935, 'زوفيراكس 250مجم 5 فيال وريد', '910', '635', 'Antiviral.nucleoside', 'Antiviral.nucleoside', NULL, 'Acyclovir', 'Glaxo smithkline', 'Vial', 'فيال', '250mg', '5', NULL, NULL, '(indications) : this medication is an antiviral agent prescribed for herpes simplex genital herpes herpes zoster and chickenpox. it slows down the growth and spread of the herpes virus. acyclovir is also sometimes used to treat eczema herpeticum herpes in', '6221045010296', 1946, '2024-11-07', NULL, 0, 'An acyclovir topical cream is indicated to treat recurrent herpes labialis in immunocompetent patients 12 years and older.[L7303] Acyclovir oral tablets, capsules, and suspensions are indicated to treat herpes zoster, genital herpes, and chickenpox.[L7315] An acyclovir topical ointment is indicated to treat initial genital herpes and limited non-life-threatening mucocutaneous herpes simplex in immunocompromised patients.[L7318] An acyclovir cream with hydrocortisone is indicated to treat recurrent herpes labialis, and shortening lesion healing time in patients 6 years and older.[L7321] An acyclovir buccal tablet is indicated for the treatment of recurrent herpes labialis.[L7324] An acyclovir ophthalmic ointment is indicated to treat acute herpetic keratitis.[L7327]', 'Acyclovir is becomes acyclovir monophosphate due to the action of viral thymidine kinase.[A180757] Acyclovir monophosphate is converted to the diphosphate form by guanylate kinase.[A903] Acyclovir diphosphate is converted to acyclovir triphosphate by nucleoside diphosphate kinase, pyruvate kinase, creatine kinase, phosphoglycerate kinase, succinyl-CoA synthetase, phosphoenolpyruvate carboxykinase and adenylosuccinate synthetase.[A903,A180781] Acyclovir triphosphate has higher affinity for viral DNA polymerase than cellular DNA polymerase and incorporates into the DNA where the missing 2'' and 3'' carbons causes DNA chain termination.[A180757] In other cases acyclovir triphosphate competes so strongly for viral DNA polymerase that other bases cannot associate with the enzyme, inactivating it.[A180757]', 'Acyclovir is a deoxynucleoside analog that inhibits the action of viral DNA polymerase and DNA replication of different herpesvirus.[L7303,L7315,L7318,L7321,L7324,L7327] Acyclovir has a wide therapeutic window as overdose is rare in otherwise healthy patients.[L7315]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Miniro pieces 10 pieces', 33209, 'مينيرو 10 قطع', '88', '58', 'Multivitamin', 'Multivitamin', NULL, 'Iron + vitamin c + vitamin b + zinc', 'Unit pharma', 'Piece', 'قطعة', NULL, '1', NULL, NULL, NULL, NULL, 902, '2025-08-04', NULL, 0, 'For treatment of patients with documented iron deficiency in whom oral administration is unsatisfactory or impossible. Also used to replenish body iron stores in Non-Dialysis Dependent-Chronic Kidney Disease (NDD-CKD) patients receiving or not receiving erythropoietin and in Hemodialysis Dependent (HDD-CKD) and Peritoneal Dialysis Dependent (PDD-CKD) - Chronic Kidney Disease patients receiving an erythropoietin.', 'After iron dextran is injected, the circulating iron dextran is removed from the plasma by cells of the reticuloendothelial system, which split the complex into its components of iron and dextran. The iron is immediately bound to the available protein moieties to form hemosiderin or ferritin, the physiological forms of iron, or to a lesser extent to transferrin. This iron which is subject to physiological control replenishes hemoglobin and depleted iron stores.', 'Iron dextran is a dark brown, slightly viscous sterile liquid complex of ferric hydroxide and dextran for intravenous or intramuscular use. It is for treatment of patients with documented iron deficiency in whom oral administration is unsatisfactory or impossible. Iron is essential to the formation of hemoglobin and to the function and formation of other heme and nonheme compounds. Untreated depletion of iron stores leads to iron-deficient erythropoiesis and, in turn, to iron deficiency anemia.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Rectivard 20mg 4 f.c.tabs.', 31180, 'ريكتيفارد 20مجم 4 اقراص', '38', '28.75', 'Tonic for men', 'Tonic for men', NULL, 'Vardenafil', 'Marcyrl co.', 'Tab', 'أقراص', '20mg', '1', NULL, NULL, NULL, NULL, 852, '2025-02-07', NULL, 0, 'Vardenafil is indicated for the treatment of erectile dysfunction.[L45563,L45568,L45573]', 'Vardenafil inhibits cyclic guanosine monophosphate (GMP) specific phosphodiesterase type 5 (PDE5), which is responsible for the degradation of cyclic GMP in the corpus cavernosum located around the penis. Penile erection during sexual stimulation is caused by increased penile blood flow resulting from the relaxation of penile arteries and corpus cavernosal smooth muscle. This response is mediated by the release of nitric oxide (NO) from nerve terminals and endothelial cells, which stimulates the synthesis of cyclic GMP in smooth muscle cells. Cyclic GMP causes smooth muscle relaxation and increased blood flow into the corpus cavernosum. The tissue concentration of cyclic GMP is regulated by both the rates of synthesis and degradation via phosphodiesterases (PDEs), and the most abundant PDE in the human corpus cavernosum is PDE5. Therefore, the inhibition of PDE5 by vardenafil enhances erectile function by increasing the amount of cyclic GMP.[L45563]', 'Vardenafil is a potent and selective inhibitor of cyclic guanosine monophosphate (cGMP)-specific phosphodiesterase type 5 (PDE5), an enzyme responsible for the degradation of cGMP in the corpus cavernosum. The presence of cGMP in the corpus cavernosum leads to smooth muscle relaxation, an increased inflow of blood and an erection. Therefore, in patients with erectile dysfunction given vardenafil, normal sexual stimulation will increase cGMP levels in the corpus cavernosum. Without sexual stimulation and no cGMP production, vardenafil should not cause an erection.[L45563,L45568] 



Vardenafil should not be used in men for whom sexual activity is not recommended due to their underlying cardiovascular status. There is also a risk of developing prolonged erections that last longer than 4 hours, as well as priapism. In the event of a sudden loss of vision in one or both eyes, patients should stop using vardenafil. Patients taking PDE5 inhibitors, such as vardenafil, may also develop sudden hearing loss and experience a prolonged QT interval.[L45563,L45568]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Calmepam 3mg 20 tab.', 1936, 'كالميبام 3مجم 20 قرص', '24', '8.6', 'Psychiatric.anxiolytics-hypnotics.narcotic', 'Psychiatric.anxiolytics-hypnotics.narcotic', NULL, 'Bromazepam', 'Glaxo smithkline', 'Tab', 'أقراص', '3mg', '2', 'مضاد للإكتئاب -- علاج الحالات النفسية والعصبية', NULL, NULL, '6221045001478', 10724, '2024-11-05', NULL, 0, 'For the short-term treatment of insomnia, short-term treatment of anxiety or panic attacks, if a benzodiazepine is required, and the alleviation of the symptoms of alcohol- and opiate-withdrawal.', 'Bromazepam binds to the GABA-A receptor producing a conformational change and potentiating its inhibitory effects. Other neurotransmitters are not influenced.', 'Bromazepam is a lipophilic, long-acting benzodiazepine and with sedative, hypnotic, anxiolytic and skeletal muscle relaxant properties. It does not possess any antidepressant qualities. Bromazepam, like other benzodiazepines, presents a risk of abuse, misuse, and dependence. According to many psychiatric experts, Bromazepam has a greater abuse potential than other benzodiazepines because of fast resorption and rapid onset of action.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Gamabion 30 tabs.', 32107, 'جامابيون 30 قرص', '140', '120', 'Vitamin b', 'Vitamin b', NULL, 'Liposomal vitamin b12+liposomal vitamin b6+liposomal vitamin b1', 'Pharma zad > octabiotic', 'Tab', 'أقراص', NULL, '3', 'مكمل غذائى يستخدم فى دعم الجعاز العصبى', NULL, NULL, NULL, 3862, '2025-01-25', NULL, 0, 'Investigated for use/treatment in neuropathy (diabetic) and peripheral vascular disease.', 'The encapsulated formulation (Liprostin) has been shown to improve the therapeutic index of PGE-1, positively impacting many areas of treatment, such as heart attacks, occlusive disease, ischemic ulcers, critical limb salvage, claudicants, and arthritis.', NULL, 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Super natural silver spray 236 ml', 27702, 'سوبر ناتشورال سيلفر سبراي 236 مل', '925', NULL, 'Healing topical', 'Healing topical', NULL, 'Colloidal silver', 'Super natural silver > alia group', 'Spray', 'بخاخ', '236 ml', '1', 'يساعد على إلتئام الجروح والحروق -- مطهر في ضمادات الجروح -- لعلاج القدم السكري والإلتهابات', NULL, NULL, NULL, 2016, '2023-03-07', NULL, 0, NULL, NULL, NULL, NULL);
