INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Novactam 250mg/5ml susp. 60 ml', 23774, 'نوفاكتام 250مجم/5مل معلق 60 مل', '69', '38', 'Penicillins.penicillin with b-lactamase inhibitor', 'Penicillins.penicillin with b-lactamase inhibitor', NULL, 'Ampicillin+sulbactam', 'Eipico', 'Susp', 'معلق', '250mg', '1', NULL, NULL, 'about sultamicillin penicillins mechanism of action of sultamicillin sultamicillin is an oral form of the antibiotic combination ampicillin/sulbactam. it contains esterified ampicillin and sulbactam. sulbactam is an irreversible inhibitor of most importan', NULL, 1313, '2025-04-29', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Methabiogen 8 mg 30 scored tab.', 22636, 'ميثابيوجين 8 مجم 30 قرص', '93', '61.5', 'Glucocorticoid', 'Glucocorticoid', NULL, 'Dexamethasone', 'Future pharmaceutical industries (fpi) > biocapital pharma', 'Tab', 'أقراص', '8 mg', '3', 'حالات الالتهاب الشديد --حالات الحساسية الشديدة -- الازمات -- التهاب المفاصل -- يؤخذ تحت اشراف طبي', NULL, NULL, '6224009667222', 6033, '2024-08-27', NULL, 0, 'Dexamethasone and [ciprofloxacin] otic suspension is indicated for bacterial infections with inflammation in acute otitis media and acute otitis externa.[L10698] Intramuscular and intravenous injections are indicated for a number of endocrine, rheumatic, collagen, dermatologic, allergic, ophthalmic, gastrointestinal, respiratory, hematologic, neoplastic, edematous, and other conditions.[L10701] Oral tablets are indicated for the treatment of multiple myeloma.[L10710] An intravitreal implant is indicated for some forms of macular edema and non-infectious posterior uveitis affecting the posterior of the eye.[L10719] Various ophthalmic formulations are indicated for inflammatory conditions of the eye.[L10704,L10707,L10713,L10716,L10722,L10725]', 'The short term effects of corticosteroids are decreased vasodilation and permeability of capillaries, as well as decreased leukocyte migration to sites of inflammation.[A187463] Corticosteroids binding to the glucocorticoid receptor mediates changes in gene expression that lead to multiple downstream effects over hours to days.[A187463]



Glucocorticoids inhibit neutrophil apoptosis and demargination; they inhibit phospholipase A2, which decreases the formation of arachidonic acid derivatives; they inhibit NF-Kappa B and other inflammatory transcription factors; they promote anti-inflammatory genes like interleukin-10.[A187463]



Lower doses of corticosteroids provide an anti-inflammatory effect, while higher doses are immunosuppressive.[A187463] High doses of glucocorticoids for an extended period bind to the mineralocorticoid receptor, raising sodium levels and decreasing potassium levels.[A187463]', 'Corticosteroids bind to the glucocorticoid receptor, inhibiting pro-inflammatory signals, and promoting anti-inflammatory signals.[A187463] Dexamethasone''s duration of action varies depending on the route.[L10698,L10701,L10704,L10707,L10710,L10713,L10716,L10719,L10722,L10725] Corticosteroids have a wide therapeutic window as patients may require doses that are multiples of what the body naturally produces.[A187463] Patients taking corticosteroids should be counselled regarding the risk of hypothalamic-pituitary-adrenal axis suppression and increased susceptibility to infections.[A187463]', 'DrugBank');
