INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (26547, 'Sanzalera 120 mg 14 f.c. tab.', 'سانزاليرا 120مجم 14 قرص', '14', NULL, 'Antihistamine.antiallergy', 'Fexofenadine', 'Future pharmaceutical industries (fpi) > tabuk pharmaceutical manufacturing company', 'Tab', 'أقراص', '120 mg', '2', NULL, 'about fexofenadine second generation h1 antagonist antihistamine. mechanism of action of fexofenadine fexofenadine blocks one type of peripheral receptor for histamine (the h1 receptor) and thus prevents activation of h1 receptor-containing cells by hista', '6223004710230', NULL, 811, '2022-07-24', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Fexofenadine is an over-the-counter second-generation antihistamine used in the treatment of various allergic symptoms.[L4269] It is selective for the H1receptor, carries little-to-no activity at off-targets, and does not cross the blood-brain barrier[L10779] - this is in contrast to previous first-generation antihistamines, such as [diphenhydramine], which readily bind to off-targets that contribute to side effects such as sedation.[A1452] Fexofenadine is the major active metabolite of [terfenadine][A1495] and is administered as a racemic mixture in which both enantiomers display approximately equivalent antihistamine activity.[L10779]', '["R06AX26"]', '[]');
