INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (27256, 'Sologati 0.3% eye dps. 5 ml', 'سولوجاتي 0.3% قطرة للعين 5مل', '17', NULL, 'Antibiotic.quinolone', 'Gatifloxacin', 'Grand pharma > city pharma', 'Sol', 'محلول', '0.3%', '1', NULL, 'uses: gatifloxacin is used to treat a variety of bacterial infections. it works by stopping the growth of bacteria. this medication belongs to a class of drugs called quinolone antibiotics. this antibiotic only treats bacterial infections. it will not wor', NULL, NULL, 746, '2022-07-24', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Gatifloxacin is an antibiotic agent and a member of the fourth-generation fluoroquinolone family. It works by inhibiting the bacterial enzymes DNA gyrase and topoisomerase IV. It was first introduced by Bristol-Myers Squibb in 1999 under the brand name Tequin® for the treatment of respiratory tract infections. Gatifloxacin is available as tablets and in various aqueous solutions for intravenous therapy. It is also available as eye drops under the brand name Zymar® marketed by Allergan.

The FDA withdrew its approval for the use of non-ophthalmic drug products containing gatifloxacin due to the high prevalence of gatifloxacin-associated dysglycemia adverse event reports and the high incidence of hyperglycemic and hypoglycemic episodes in patients taking gatifloxacin compared to those on macrolide antibiotics.[L43942,L44037]', '["S01AE06", "J01MA16"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (27257, 'Solpadol 2.5mg 6 oro-dispersible tablets', 'سولبادول 2.5مجم 6 اقراص ذائبة بالفم', '60', '42', 'Antimigraine.vasoconstrictor agents.selective serotonin agonist', 'Zolmitriptan', 'Global napi pharmaceuticals', 'Tablet', 'أقراص', '2.5mg', '1', 'لعلاج حالات الصداع النصفي', 'about zolmitriptan selective serotonin (5ht1) agonists anti migraine mechanism of action of zolmitriptan zolmitriptan is a selective agonist for serotonin (5ht1 receptors). it relieves migraine by selective constriction of intracranial blood vessels neuro', '6223002142989', NULL, 5164, '2024-01-28', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Zolmitriptan is a member of the triptan class of 5-hydroxytryptamine(5-HT)1B/1D/(1F)receptor agonists used to treat acute migraine.[A462, L12978] [Sumatriptan] was the first triptan to be developed, but had poor oral bioavailability and lipophilicity. This led to the development of second-generation triptans, including [almotriptan], [eletriptan], [frovatriptan], [naratriptan], [rizatriptan], and zolmitriptan.[A193791] Triptans can be administered alone or in combination with an NSAID like [naproxen], and represent the current "gold standard" for acute migraine treatment.[A193797]

Zolmitriptan was first approved by the FDA for sale by Zeneca Pharmaceuticals under the trade name Zomig® on November 25, 1997. It is currently available in both tablet and nasal spray forms.[L12978]', '["N02CC03"]', '[]');
