INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18116, 'Egyferon 1 m.i.u. vial', 'ايجي فيرون مليون وحدة دولية فيال', '27', NULL, 'Antiviral .interferon alfa', 'Interferon alfa-2b', 'El nile.', 'Vial', 'فيال', NULL, '1', NULL, 'indication for the treatment of hairy cell leukemia malignant melanoma and aids-related kaposi s sarcoma. pharmacodynamics upregulates the expression of mhc i proteins allowing for increased presentation of peptides derived from viral antigens. this enhan', NULL, NULL, 842, '2022-07-24', 'For the treatment of venereal or genital warts caused by the Human Papiloma Virus.', 'Interferon alpha binds to type I interferon receptors (IFNAR1 and IFNAR2c) which, upon dimerization, activate two Jak (Janus kinase) tyrosine kinases (Jak1 and Tyk2). These transphosphorylate themselves and phosphorylate the receptors. The phosphorylated INFAR receptors then bind to Stat1 and Stat2 (signal transducers and activators of transcription)which dimerize and activate multiple (~100) immunomodulatory and antiviral proteins. Interferon alpha binds less stably to type I interferon receptors than interferon beta.', 'Upregulates the expression of MHC I proteins, allowing for increased presentation of peptides derived from viral antigens. This enhances the activation of CD8+ T cells that are the precursors for cytotoxic T lymphocytes (CTLs) and makes the macrophage a better target for CTL-mediated killing. Interferon alpha also induce the synthesis of several key antiviral mediators, including 2''-5'' oligoadenylate synthetase (2''-5'' A synthetase) and protein kinase R.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (9177, 'Oframax 1 gm i.v. vial', 'اوفراماكس 1جم فيال للحقن الوريدي', '70', '47.5', 'Antibiotic.cephalosporin.third-generation', 'Ceftriaxone', 'Cid > ranbaxy', 'Vial', 'فيال', '1 gm', '1', NULL, 'about ceftriaxone third generation cephalosporin antibiotic(parenteral). mechanism of action of ceftriaxone ceftriaxone is a 3rd generation cephalosporin which exerts its bactericidal action against both gram positive & gram negative organisms by inhibiti', NULL, NULL, 1266, '2024-07-29', 'Ceftriaxone is used for the treatment of the infections (respiratory, skin, soft tissue, UTI, ENT) caused by susceptible organisms.[L15082] Organisms that are generally susceptible to ceftriaxone include S. pneumoniae, S. pyogenes (group A beta-hemolytic streptococci), coagulase-negative staphylococci, Some Enterobacter spp, H. influenzae, N. gonorrhoeae, P. mirabilis, E. coli, Klebsiella spp, M. catarrhalis, B. burgdorferi, and some oral anaerobes.[A215582] ', 'Ceftriaxone works by inhibiting the mucopeptide synthesis in the bacterial cell wall.[T324,L15082] The beta-lactam moiety of ceftriaxone binds to carboxypeptidases, endopeptidases, and transpeptidases in the bacterial cytoplasmic membrane. These enzymes are involved in cell-wall synthesis and cell division. Binding of ceftriaxone to these enzymes causes the enzyme to lose activity; therefore, the bacteria produce defective cell walls, causing cell death.', 'Ceftriaxone is a cephalosporin/cephamycin beta-lactam antibiotic used in the treatment of bacterial infections caused by susceptible, usually gram-positive, organisms.[L15082] Ceftriaxone has <i>in vitro</i> activity against gram-positive aerobic, gram-negative aerobic, and anaerobic bacteria.[L15087] The bactericidal activity of ceftriaxone results from the inhibition of cell wall synthesis and is mediated through ceftriaxone binding to penicillin-binding proteins (PBPs).[T324] Ceftriaxone is stable against hydrolysis by a variety of beta-lactamases, including penicillinases, and cephalosporinases and extended-spectrum beta-lactamases.[L15082] However, resistance to ceftriaxone usually occurs through beta-lactamase hydrolysis, altered PBPs, or reduced bacterial cell permeability.[T324] Ceftriaxone should not be mixed with or giving in the same IV line as diluents/products containing calcium as they may cause ceftriaxone to precipitate.[L15082] Ceftriaxone use may also cause biliary sludge or gallbladder pseudolithiasis.[L15082,A215602] ', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (29828, 'Zalosed 10mg 10 caps.', 'زالوسيد 10مجم 10 كبسولات', '12', NULL, 'Psychiatric.anxiolytics-hypnotics', 'Zaleplon', 'Hikma specialized pharmaceuticals', 'Cap', 'كبسولة', '10mg', '1', NULL, 'about zaleplon pyrazolopyrimidine derivative( a nonbenzodiazepine sedative-hypnotic. mechanism of action of zaleplon zaleplon is a non benzodiazepine sedative-hypnotic drug. it binds to the benzodiazepine binding site on gaba-a receptors containing the al', NULL, NULL, 916, '2022-08-05', 'For the treatment of short-term treatment of insomnia in adults.', 'Zaleplon exerts its action through subunit modulation of the GABA<sub>B</sub>Z receptor chloride channel macromolecular complex. Zaleplon also binds selectively to the brain omega-1 receptor located on the alpha subunit of the GABA-A/chloride ion channel receptor complex and potentiates t-butyl-bicyclophosphorothionate (TBPS) binding.', 'Zaleplon is a nonbenzodiazepine hypnotic from the pyrazolopyrimidine class and is indicated for the short-term treatment of insomnia. While Zaleplon is a hypnotic agent with a chemical structure unrelated to benzodiazepines, barbiturates, or other drugs with known hypnotic properties, it interacts with the gamma-aminobutyric acid-benzodiazepine (GABA<sub>B</sub>Z) receptor complex. Subunit modulation of the GABA<sub>B</sub>Z receptor chloride channel macromolecular complex is hypothesized to be responsible for some of the pharmacological properties of benzodiazepines, which include sedative, anxiolytic, muscle relaxant, and anticonvulsive effects in animal models. Zaleplon also binds selectively to the CNS GABA<sub>A</sub>-receptor chloride ionophore complex at benzodiazepine(BZ) omega-1 (BZ1, ο1) receptors. ', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27381, 'Spectrodoxime 200mg 10 f.c. tab.', 'سبيكترودوكسيم 200مجم 10 اقراص', '55', NULL, 'Antibiotic.cephalosporin.third-generation', 'Cefpodoxime', 'Kahira > international trading office', 'Tab', 'أقراص', '200mg', '2', NULL, 'about cefpodoxime third generation cephalosporin antibiotic oral antibiotic. mechanism of action of cefpodoxime cefpodoxime is a 3rd generation cephalosporin which exerts its bactericidal action mainly against gram positive organisms by inhibiting bacteri', NULL, NULL, 723, '2022-07-30', 'Indicated for the treatment of patients with mild to moderate infections caused by susceptible strains of the designated microorganisms.', 'Cefpodoxime is active against a wide spectrum of Gram-positive and Gram-negative bacteria. Cefpodoxime is stable in the presence of beta-lactamase enzymes. As a result, many organisms resistant to penicillins and cephalosporins, due to their production of beta-lactamase, may be susceptible to cefpodoxime. Cefpodoxime is inactivated by certain extended spectrum beta-lactamases. The bactericidal activity of cefpodoxime results from its inhibition of cell wall synthesis. The active metabolite of cefpodoxime binds preferentially to penicillin binding protein 3, which inhibits production of peptidoglycan, the primary constituent of bacterial cell walls.', 'Cefpodoxime is shown to be effective against most Gram positive and Gram negative bacteria, except <i>Pseudomonas aeruginosa</i>, <i>Enterococcus</i>, and <i>Bacteroides fragilis</i>.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15691, 'Cal-d-mag 30 chocolate pieces', 'كال-دي-ماج 30 قرص شيكولاتة', '147', '80', 'Calcium with vitamin d3', 'Calcium carbonate+vitamin d3+magnesium', 'Nutrixia > pharma life', 'Piece', 'قطعة', NULL, '1', 'مكمل غذائى لدعم صحة العظام والجهاز المناعى والوقاية من الكساح وهشاشة العظام', 'once daily.', NULL, NULL, 2873, '2025-01-01', 'Calcium acetate is one of a number of calcium salts used to treat hyperphosphatemia (too much phosphate in the blood) in patients with kidney disease.', 'Calcium acetate and other calcium salts are phosphate binders. They work by binding with the phosphate in the food you eat, so that it is eliminated from the body without being absorbed.', 'Patients with advanced renal insufficiency (creatinine clearance less than 30 ml/min) exhibit phosphate retention and some degree of hyperphosphatemia. The retention of phosphate plays a pivotal role in causing secondary hyperparathyroidism associated with osteodystrophy, and soft-tissue calcification. The mechanism by which phosphate retention leads to hyperparathyroidism is not clearly delineated. Therapeutic efforts directed toward the control of hyperphosphatemia include reduction in the dietary intake of phosphate, inhibition of absorption of phosphate in the intestine with phosphate binders, and removal of phosphate from the body by more efficient methods of dialysis. The rate of removal of phosphate by dietary manipulation or by dialysis is insufficient. Dialysis patients absorb 40% to 80% of dietary phosphorus. Therefore, the fraction of dietary phosphate absorbed from the diet needs to be reduced by using phosphate binders in most renal failure patients on maintenance dialysis. Calcium acetate when taken with meals combines with dietary phosphate to form insoluble calcium phosphate which is excreted in the feces. Maintenance of serum phosphorus below 6.0 mg/dl is generally considered as a clinically acceptable outcome of treatment with phosphate binders. Calcium acetate is highly soluble at neutral pH, making the calcium readily available for binding to phosphate in the proximal small intestine.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15335, 'Bisocard 2.5mg 30 f.c.tab.', 'بيزوكارد 2.5مجم 30 قرص', '51', '30', 'Antihypertensive.beta blocker.selective b1', 'Bisoprolol fumarate', 'Global napi pharmaceuticals', 'Tab', 'أقراص', '2.5mg', '3', NULL, NULL, '6223002143061', NULL, 4392, '2024-08-16', 'Bisoprolol is indicated for the treatment of mild to moderate hypertension.[L7219] It may be used off-label to treat heart failure, atrial fibrillation, and angina pectoris.[A180460,A180463]', 'Though the mechanism of action of bisoprolol has not been fully elucidated in hypertension, it is thought that therapeutic effects are achieved through the antagonism of β-1adrenoceptors to result in lower cardiac output. Bisoprolol is a competitive, cardioselective β1-adrenergic antagonist. When β1-receptors (located mainly in the heart)[T640] are activated by adrenergic neurotransmitters such as epinephrine, both the blood pressure and heart rate increase, leading to greater cardiovascular work, increasing the demand for oxygen.  Bisoprolol reduces cardiac workload by decreasing contractility and the need for oxygen through competitive inhibition of β1-adrenergic receptors.[A180502,T640]



Bisoprolol is also thought to reduce the output of renin in the kidneys, which normally increases blood pressure. Additionally, some central nervous system effects of bisoprolol may include diminishing sympathetic nervous system output from the brain, decreasing blood pressure and heart rate.[L7219]', 'Bisoprolol decreases heart rate (chronotropy), decreases contractility (inotropy), and reduces blood pressure.[T640,L7219] The results of various clinical studies indicate that bisoprolol reduces cardiovascular mortality and all-cause mortality in patients with heart failure and decreased cardiac ejection fraction (EF).[A180466,A180505] ', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (9595, 'Nervoacid 30 caps.', 'نيرفواسيد 30 كبسولة', '117', NULL, 'Multivitamin', 'Thioctic acid(alpha lipoic acid)+benfotiamine+vitamin b6+vitamin b12+folic acid', 'Grow pharma', 'Cap', 'كبسولة', NULL, '3', NULL, NULL, NULL, NULL, 1942, '2024-06-14', 'Investigated for use/treatment in diabetes mellitus type 2, neuropathy (diabetic), and vascular diseases.', NULL, NULL, 'DrugBank', 0, 0, 0);
