INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25489, 'Pulomotax 1 gm i.v. inf. / i.m. vial', 'بالموتاكس 1جم امبولبالموتاكس 1جم امبول', '27', '16.5', 'Antibiotic.cephalosporin.third-generation', 'Cefotaxime', 'Up pharma > debeiky', 'Vial', 'فيال', '1 gm', '1', NULL, 'about cefotaxime third generation cephalosporin parenteral antibiotic. mechanism of action of cefotaxime cefotaxime is a 3rd generation cephalosporin which exerts its bactericidal action against both gram positive & aerobic gram negative organisms by inhi', NULL, NULL, 867, '2023-09-08', 'Used to treat gonorrhoea, meningitis, and severe infections including infections of the kidney (pyelonephritis) and urinary system. Also used before an operation to prevent infection after surgery.', 'The bactericidal activity of cefotaxime results from the inhibition of cell wall synthesis via affinity for penicillin-binding proteins (PBPs). Cefotaxime shows high affinity for penicillin-binding proteins in the cell wall including PBP Ib and PBP III.', 'Cefotaxime is a third generation intravenous cephalosporin antibiotic. It has broad spectrum activity against Gram positive and Gram negative bacteria. It does not have activity against <i>Pseudomonas aeruginosa</i>. Cefotaxime works by inhibiting bacterial cell wall biosynthesis. A positive feature of cefotaxime is that it display a resistance to penicillinases and is useful to treat infections that are resistant to penicillin derivatives.', 'DrugBank', 0, 0, 0);
