INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22987, 'Mornigag 10/10mg 30 delayed rel.tabs.', 'مورنيجاج 10/10 مجم 30 قرص', '66', '43.5', 'Antiemetic', 'Doxylamine succinate+pyridoxine hcl(vitamin b6)', 'Amoun', 'Tab', 'أقراص', '10mg', '3', 'لعلاج حالات الترجيع والقيء الصباحية', NULL, '6221025042156', NULL, 7546, '2024-08-07', 'Used alone as a short-term sleep aid, in combination with other drugs as a night-time cold and allergy relief drug. Also used in combination with Vitamin B6 (pyridoxine) to prevent morning sickness in pregnant women.', 'Like other antihistamines, doxylamine acts by competitively inhibiting histamine at H1&nbsp;receptors. It also has substantial sedative and anticholinergic effects.', 'Doxylamine is an antihistamine commonly used as a sleep aid. This drug is also used to relieve symptoms of hay fever (allergic rhinitis), hives (rash or itching), and other allergic reactions. Doxylamine is a member of the ethanolamine class of antihistamines and has anti-allergy power far superior to virtually every other antihistamine on the market, with the exception of diphenhydramine (Benadryl). It is also the most powerful over-the-counter sedative available in the United States, and more sedating than many prescription hypnotics. In a study, it was found to be superior to even the barbiturate, phenobarbital for use as a sedative. Doxylamine is also a potent anticholinergic.', 'DrugBank', 0, 0, 0);
