INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (3234, 'Sleevar 30 caps.', 'سليفار 30 كبسولة', '350', NULL, 'Multivitamin', 'Vitamin b complex+vitamin e+vitamin a+vitamin k1+vitamin c+vitamin d3+magnesium+copper+iodine+selenium+molybdenum+chromium+zinc', 'Mira international', 'Cap', 'كبسولة', NULL, '3', NULL, NULL, NULL, NULL, 953, '2022-08-07', 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20967, 'Irofol 30 caps.', 'ايروفول 30 كبسولة', '41', NULL, 'Anemia.iron supplement combined with folic acid', 'Ferrous fumarate+zinc+copper+pyridoxine+folic acid+vitamin b12', 'Multicare', 'Cap', 'كبسولة', NULL, '3', NULL, NULL, NULL, NULL, 1451, '2022-06-25', 'Ferrous sulfate is used for the prevention and treatment of iron deficiency anemia in adults and children.[A190804,L2240,L11800]

', 'Iron is required to maintain optimal health, particularly for helping to form red blood cells (RBC) that carry oxygen around the body. A deficiency in iron indicates that the body cannot produce enough normal red blood cells.[A32514,L11800] Iron deficiency anemia occurs when body stores of iron decrease to very low levels, and the stored iron is insufficient to support normal red blood cell (RBC) production. Insufficient dietary iron, impaired iron absorption, bleeding, pregnancy, or loss of iron through the urine can lead to iron deficiency.[A32514,L11794]  Symptoms of iron deficiency anemia include fatigue, breathlessness, palpitations, dizziness, and headache.



Taking iron in supplement form, such as ferrous sulfate, allows for more rapid increases in iron levels when dietary supply and stores are not sufficient.[L2175] Iron is transported by the divalent metal transporter 1 (DMT1) across the endolysosomal membrane to enter the macrophage. It can then can be incorporated into ferritin and be stored in the macrophage or carried of the macrophage by ferroportin. This exported iron is  oxidized by the enzyme to ceruloplasmin to Fe3+, followed by sequestration by transferrin for transport in the serum to various sites, including the bone marrow for hemoglobin synthesis or into the liver.[A32524] Iron combines with porphyrin and globin chains to form hemoglobin, which is critical for oxygen delivery from the lungs to other tissues.[L2263]', 'Ferrous sulfate replenishes iron, an essential component in hemoglobin, myoglobin, and various enzymes. It replaces the iron that is usually found in hemoglobin and myoglobin. Iron participates in oxygen transport and storage, electron transport and energy metabolism, antioxidant and beneficial pro-oxidant functions, oxygen sensing, tissue proliferation and growth, as well as DNA replication and repair.[A32524,A32514]



', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (17813, 'Dompidone 30mg 6 supp.', 'دومبيدون 30مجم 6 اقماع لبوس', '7', NULL, 'Antiemetic', 'Domperidone', 'Arab drug company.', 'Supp', 'لبوس', '30mg', '1', NULL, NULL, NULL, NULL, 734, '2022-12-16', 'For management of dyspepsia, heartburn, epigastric pain, nausea, and vomiting.', 'Domperidone acts as a gastrointestinal emptying (delayed) adjunct and peristaltic stimulant. The gastroprokinetic properties of domperidone are related to its peripheral dopamine receptor blocking properties. Domperidone facilitates gastric emptying and decreases small bowel transit time by increasing esophageal and gastric peristalsis and by lowering esophageal sphincter pressure. Antiemetic: The antiemetic properties of domperidone are related to its dopamine receptor blocking activity at both the chemoreceptor trigger zone and at the gastric level. It has strong affinities for the D2 and D3 dopamine receptors, which are found in the chemoreceptor trigger zone, located just outside the blood brain barrier, which - among others - regulates nausea and vomiting', 'Domperidone is a specific blocker of dopamine receptors. It speeds gastrointestinal peristalsis, causes prolactin release, and is used as antiemetic and tool in the study of dopaminergic mechanisms.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20312, 'Hemaltose 50mg/5ml syrup 120 ml', 'هيمالتوز 50 مجم / 5 مل شراب 120 مل', '31', '24', 'Anemia.iron supplement', 'Ferric hydroxide polymaltose', 'Global advanced pharmaceuticals (gap)', 'Syrup', 'شراب', '50mg', '1', 'علاج الانيميا -- مصدر للحديد -- مقوي للمناعة', NULL, '6223005940544', NULL, 1949, '2024-06-21', 'Ferric carboxymaltose is an iron replacement product indicated for the treatment of iron deficiency anemia in patients ≥1 year of age who have an intolerance to, or unsatisfactory response from, oral iron therapy,[L49379] and in adult patients who have non-dialysis-dependent chronic kidney disease.[L49379] It is also indicated to treat iron deficiency and improve exercise capacity in adult patients with NYHA class II or III heart failure.[L49379]', 'Ferric carboxymaltose is a colloidal iron (III) hydroxide in complex with carboxymaltose, a carbohydrate polymer that releases iron.[L49379]', 'When measured using positron emission tomography (PET), the red cell uptake of 59-Fe and 52-Fe from INJECTAFER ranged from 61% to 99%.[L49379] In patients with iron deficiency, the red cell uptake ranged from 91% to 99%.[L49379] In patients with renal anemia, the red cell uptake ranged from 61% to 84%.[L49379]', 'DrugBank', 0, 0, 0);
