INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (6569, 'Irbesartan 150mg 14 f.c. tabs.', 'اربيسارتان 150مجم 14 قرص', '17', NULL, 'Antihypertensive.angiotensin blocker', 'Irbesartan', 'Mepaco', 'Tab', 'أقراص', '150mg', '2', NULL, 'about irbesartan angiotensin ii receptor blocker a non-peptide compound antihypertensive. mechanism of action of irbesartan it is a competitive antagonist of angiotensin-2 at at-1 receptor. it decreases peripheral resistance and lowers bp in hypertensive', '6223002212422', NULL, 997, '2023-02-06', 'Irbesartan is indicated to treat hypertension and diabetic nephropathy in hypertensive patients with type 2 diabetes, elevated serum creatinine, and proteinuria.[L7456] A combination product with hydrochlorothiazide is indicated for hypertension in patients with uncontrolled hypertension with monotherapy or first line in patients not expected to be well controlled with monotherapy.[L7459]', 'Irbesartan prevents angiotensin II binding to the AT<sub>1</sub> receptor in tissues like vascular smooth muscle and the adrenal gland.[L7456,L7459] Irbesartan and its active metabolite bind the AT<sub>1</sub> receptor with 8500 times more affinity than they bind to the AT<sub>2</sub> receptor.[L7456,L7459] Irbesartan''s prevention of angiotensin II binding causes vascular smooth muscle relaxation and prevents the secretion of aldosterone, lowering blood pressure.[L7456,L7459]



Angiotensin II would otherwise bind to the AT<sub>1</sub> receptor, inducing vasoconstriction and aldosterone secretion, raising blood pressure.[L7456,L7459]', 'Irbesartan is an angiotensin receptor blocker used to treat hypertension and diabetic nephropathy.[L7456,L7459] It has a long duration of action as it is usually taken once daily and a wide therapeutic index as doses may be as low as 150mg daily but doses of 900mg/day were well tolerated in healthy human subjects.[L7456,L7459,A181165]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (14050, 'Ampicillin-misr 500mg vial', 'امبيسيلين مصر 500 مجم فيال', '8', NULL, 'Penicillins.monocomponent', 'Ampicillin', 'Misr', 'Amp', 'أمبول', '500mg', '1', NULL, 'description : semi-synthetic derivative of penicillin that functions as an orally active broad-spectrum antibiotic . indication : for treatment of infection (respiratory gi uti and meningitis) due to e. coli p. mirabilis enterococci shigella s. typhosa an', NULL, NULL, 1288, '2022-07-20', 'For treatment of infection (Respiratory, GI, UTI and meningitis) due to E. coli, P. mirabilis, enterococci, Shigella, S. typhosa and other Salmonella, nonpenicillinase-producing N. gononhoeae, H. influenzae, staphylococci, streptococci including streptoc', 'By binding to specific penicillin-binding proteins (PBPs) located inside the bacterial cell wall, Ampicillin inhibits the third and last stage of bacterial cell wall synthesis. Cell lysis is then mediated by bacterial cell wall autolytic enzymes such as autolysins; it is possible that Ampicillin interferes with an autolysin inhibitor.', 'Ampicillin is a penicillin beta-lactam antibiotic used in the treatment of bacterial infections caused by susceptible, usually gram-positive, organisms. The name "penicillin" can either refer to several variants of penicillin available, or to the group of antibiotics derived from the penicillins. Ampicillin has <i>in vitro</i> activity against gram-positive and gram-negative aerobic and anaerobic bacteria. The bactericidal activity of Ampicillin results from the inhibition of cell wall synthesis and is mediated through Ampicillin binding to penicillin binding proteins (PBPs). Ampicillin is stable against hydrolysis by a variety of beta-lactamases, including penicillinases, and cephalosporinases and extended spectrum beta-lactamases.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (33935, 'Zinobacilly oral drops 10 ml', 'زينوباسيلي قطرات للفم 10 مل', '125', '72', 'Probiotics & lactase enzyme', 'Lactobacillus + vitamin d3', 'Dawak pharm', 'Drops', 'نقط', '10 ml', '1', 'يدعم صحة الجهاز الهضمى', NULL, NULL, NULL, 1684, '2025-07-29', NULL, 'Lactobacillus coryniformis has immunomodulatory activity. Administration of a Lc K8 probiotic prior to the influenza vaccine in adults over the age of 65 showed a higher percentage of vaccine responders and a significantly lower incidence of respiratory symptoms associated with respiratory infections.', NULL, 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (7008, 'K-viton 10mg 20 sugar coated tab.', 'ك-فيتون 10مجم 20 قرص', '14', '7.5', 'Vitamin k', 'Vitamin k4', 'Kahira', 'Tab', 'أقراص', '10mg', '2', NULL, NULL, '6221068000373', NULL, 1656, '2024-12-08', 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15540, 'Brufemol-n susp. 60 ml', 'بروفيمول ان معلق 60 مل', '5', NULL, 'Nsaid.propionic acid derivatives', 'Ibuprofen', 'Arab drug company.', 'Susp', 'معلق', '60 ml', '1', NULL, 'ibuprofen is a type of medicine called a non-steroidal anti-inflammatory drug (nsaid). it works by blocking the action of a substance in the body called cyclo-oxygenase (cox). cyclo-oxygenase is involved in the production of various chemicals in the body', NULL, NULL, 1074, '2022-07-09', 'Ibuprofen is the most commonly used and prescribed NSAID. It is a very common over-the-counter medication widely used as an analgesic, anti-inflammatory and antipyretic.[A39096]



The use of ibuprofen and its enantiomer [DB09213] in a racemic mix is common for the management of mild to moderate pain related to dysmenorrhea, headache, migraine, postoperative dental pain, spondylitis, osteoarthritis, rheumatoid arthritis, and soft tissue disorder.[A39097]



Due to its activity against prostaglandin and thromboxane synthesis, ibuprofen has been attributed to alteration of platelet function and prolongation of gestation and labour.[A39092]



As ibuprofen is a widely used medication, the main therapeutic indications are:



* Patent Ductus Arteriosus - it is a neonatal condition wherein the ductus arteriosus (blood vessel that connects the main pulmonary artery to the proximal descending aorta) fails to close after birth causing severe risk of heart failure. The prostaglandin inhibition of ibuprofen has been studied for the treatment of this condition as it is known that prostaglandin E2 is responsible for keeping the ductus arteriosus open.[A39100]



* Rheumatoid- and osteo-arthritis - ibuprofen is very commonly used in the symptomatic treatment of inflammatory, musculoskeletal and rheumatic disorders.[A39176]



* Cystic fibrosis - the use of high dosages of ibuprofen has been proven to decrease inflammation and decreasing polymorphonuclear cell influx in the lungs.[A39177]



* Orthostatic hypotension - ibuprofen can induce sodium retention and antagonize the effect of diuretics which has been reported to be beneficial for patients with severe orthostatic hypotension.[A1651]



* Dental pain - ibuprofen is used to manage acute and chronic orofacial pain.[A10901]



* Pain - ibuprofen is widely used to reduce minor aches and pains as well as to reduce fever and manage dysmenorrhea. It is very commonly used for the relief of acute indications such as fever and tension headaches.[A39092] It is also used to manage mild to moderate pain and moderate to severe pain as an adjunct to opioid analgesics.[L40208]



* Investigational uses - efforts have been put into developing ibuprofen for the prophylaxis of Alzheimer''s disease, Parkinson disease, and breast cancer.[A39092]', 'The exact mechanism of action of ibuprofen is unknown. However, ibuprofen is considered an NSAID and thus it is a non-selective inhibitor of cyclooxygenase, which is an enzyme involved in prostaglandin (mediators of pain and fever) and thromboxane (stimulators of blood clotting) synthesis via the arachidonic acid pathway.[L4614]



Ibuprofen is a non-selective COX inhibitor and hence, it inhibits the activity of both COX-1 and COX-2. The inhibition of COX-2 activity decreases the synthesis of prostaglandins involved in mediating inflammation, pain, fever, and swelling while the inhibition of COX-1 is thought to cause some of the side effects of ibuprofen including GI ulceration.[A39195]', 'Ibuprofen has multiple actions in different inflammatory pathways involved in acute and chronic inflammation. The main effects reported in ibuprofen are related to the control of pain, fever and acute inflammation by the inhibition of the synthesis of prostanoids by COX-1 and COX-2. Pain relief is attributed to peripheral affected regions and central nervous system effects in the pain transmission mediated by the dorsal horn and higher spinothalamic tract. Some reports have tried to link the pain regulation with a possible enhancement on the synthesis of endogenous cannabinoids and action on the NMDA receptors. The effect on pain has been shown to be related to the cortically evoked potentials.[A39190]



The antipyretic effect is reported to be linked to the effect on the prostanoid synthesis due to the fact that the prostanoids are the main signaling mediator of pyresis in the hypothalamic-preoptic region.[A39190]



The use of ibuprofen in dental procedures is attributed to the local inhibition of prostanoid production as well as to anti-oedemic activity and an increase of plasma beta-endorphins. Some reports have suggested a rapid local reduction of the expression of COX-2 in dental pulp derived by the administration of ibuprofen.[A39190]



The administration of ibuprofen in patients with rheumatic diseases has shown to control joint symptoms.[A39092] 



Ibuprofen is largely used in OTC products such as an agent for the management of dysmenorrhea which has been proven to reduce the amount of menstrual prostanoids and to produce a reduction in the uterine hypercontractility.[A39181] As well, it has been reported to reduce significantly the fever and the pain caused by migraines.[A39182, A39183] This effect is thought to be related to the effect on platelet activation and thromboxane A2 production which produces local vascular effects in the affected regions. This effect is viable as ibuprofen can enter in the central nervous system.[A39190]



In the investigational uses of ibuprofen, it has been reported to reduce neurodegeneration when given in low doses over a long time.[A39184] On the other hand, its use in Parkinson disease is related to the importance of inflammation and oxidative stress in the pathology of this condition.[A39185] The use of ibuprofen for breast cancer is related to a study that shows a decrease of 50% in the rate of breast cancer.[A39186]', 'DrugBank', 0, 0, 0);
