INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15999, 'Cefaxoral 1 gm 6 dispersible tab.', 'سيفاكسورال 1 جم 6 اقراص', '12', NULL, 'Antibiotic.cephalosporin.first-generation', 'Cefadroxil', 'T3a pharma', 'Tab', 'أقراص', '1 gm', '1', NULL, NULL, NULL, NULL, 1010, '2022-07-17', 'For the treatment of the following infections (skin, UTI, ENT) caused by; <i>S. pneumoniae, H. influenzae, staphylococci, S. pyogenes</i> (group A beta-hemolytic streptococci), <i>E. coli, P. mirabilis, Klebsiella</i> sp, coagulase-negative staphylococci and <i>Streptococcus pyogenes</i>', 'Like all beta-lactam antibiotics, cefadroxil binds to specific penicillin-binding proteins (PBPs) located inside the bacterial cell wall, causing the inhibition of the third and last stage of bacterial cell wall synthesis. Cell lysis is then mediated by bacterial cell wall autolytic enzymes such as autolysins; it is possible that cefadroxil interferes with an autolysin inhibitor.', 'Cefadroxil, a first-generation cephalosporin antibiotic, is used to treat urinary tract infections, skin and skin structure infections, pharyngitis, and tonsillitis.', 'DrugBank', 0, 0, 0);
