INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (31520, 'Truevera scrub cream', 'تروفيرا سكراب كريم', '75', NULL, NULL, 'Salicylic acid+ascorbic acid+tocopherol+apricote+coffe aribica+licorice extract+caviar+hyaluronic acide+marine collagen+avocado discorea villosa roots +vit.e', 'Egyptian co. for cosemetics > pharaohs', 'Cream', 'كريم', NULL, '1', 'مقشر ينظف البشرة وينقيها ويمنحها إحساس بالراحة. يزيل خلايا الجلد الميتة ويرطب البشرة ويتحكم في إفراز الدهون. مناسب لجميع أنواع البشرة. يمكن استخدامه للوجه والجسم.', NULL, NULL, NULL, 2422, '2023-04-28', 'Key additive in many skin-care products for the treatment of acne, psoriasis, callouses, corns, keratosis pilaris and warts.', 'Salicylic acid directly irreversibly inhibits COX-1 and COX-2 to decrease conversion of arachidonic acid to precursors of prostaglandins and thromboxanes. Salicylate''s use in rheumatic diseases is due to it''s analgesic and anti-inflammatory activity. Salicylic acid is a key ingredient in many skin-care products for the treatment of acne, psoriasis, calluses, corns, keratosis pilaris, and warts. Salicylic acid allows cells of the epidermis to more readily slough off. Because of its effect on skin cells, salicylic acid is used in several shampoos used to treat dandruff. Salicylic acid is also used as an active ingredient in gels which remove verrucas (plantar warts). Salicylic acid competitively inhibits oxidation of uridine-5-diphosphoglucose (UDPG) with nicotinamide adenosine dinucleotide (NAD) and noncompetitively with UDPG. It also competitively inhibits the transferring of the glucuronyl group of uridine-5-phosphoglucuronic acid (UDPGA) to a phenolic acceptor. Inhibition of mucopoly saccharide synthesis is likely responsible for the slowing of wound healing with salicylates.', 'Salicylic acid treats acne by causing skin cells to slough off more readily, preventing pores from clogging up. This effect on skin cells also makes salicylic acid an active ingredient in several shampoos meant to treat dandruff. Use of straight salicylic solution may cause hyperpigmentation on unpretreated skin for those with darker skin types (Fitzpatrick phototypes IV, V, VI), as well as with the lack of use of a broad spectrum sunblock. Subsalicylate in combination with bismuth form the popular stomach relief aid known commonly as Pepto-Bismol. When combined the two key ingredients help control diarrhea, nausea, heartburn, and even gas. It is also very mildly anti-biotic.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25413, 'Protectazole 600 mg 5 vag. tabs.', 'بروتيكتازول 600مجم 5 اقراص', '56', NULL, 'Antifungals.imidazoles', 'Oxiconazole', 'Egpi > jasper for pharmaceutical industries', 'Tab', 'أقراص', '600 mg', '1', NULL, 'about oxiconazole imidazole derivative antifungal. mechanism of action of oxiconazole oxiconazole is fungicidal or fungistatic depending on concentrations. it inhibits the conversion of lanosterol to 14 demethyl lanosterol by inhibiting the cytochromep450', NULL, NULL, 843, '2022-07-26', 'For treatment of dermal fungal infection.', 'Oxiconazole inhibits ergosterol biosynthesis, which is required for cytoplasmic membrane integrity of fungi. It acts to destabilize the fungal cyctochrome P450 51 enzyme (also known as Lanosterol 14-alpha demethylase). This is vital in the cell membrance structure of the fungus. Its inhibition leads to cell lysis. Oxiconazole has also been shown in inhibit DNA synthesis and suppress intracellular concentrations of ATP. Like other imidazole antifungals, Oxiconazole can increase membrane permeability to zinc, augmenting its cytotoxicity.', 'Oxiconazole is a broad-spectrum imidazole derivative whose antifungal activity is derived primarily from the inhibition of ergosterol biosynthesis, which is critical for cellular membrane integrity. It has fungicidal or fungistatic activity in vitro against a number of pathogenic fungi including the following dermatophytes, and yeasts: <i>T. rubrum</i>, <i>T. mentagrophytes</i>, <i>T. tonsurans</i>, <i>T. violaceum</i>, <i>E. floccosum</i>, <i>M. canis</i>, <i>M. audouini</i>, <i>M. gypseum</i>, <i>C. albicans</i>, and <i>M. furfur</i>.', 'DrugBank', 0, 0, 0);
