INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27929, 'Taurolock u25000 catheter lock solution vial 5 ml', 'تاورولوك يو 25000 محلول قسطرة فيال 5 مل', '2,048', '1050', NULL, 'Cyclo-taurolidine', 'Tauro pharm gmbh > paxton', 'Vial', 'فيال', '5 ml', '1', 'محلول يركب في القسطرة لمنع تكوين البكتيريا', 'indication and effect: taurolockï--hep500 is approved for instillation in all central venous access systems. taurolockï--hep500 remaining in the catheter prevents the formation of a bacterial biofilm. the catheter stays infection-free and works properly a', NULL, NULL, 6019, '2024-07-29', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1579, 'Biotin 5000 mcg 60 veg. caps. (now)', 'بيوتين 500 مكجم 60 كبسولة', '1,200', '650', 'Multivitamins', 'Biotin', 'Soficopharm', 'Cap', 'كبسولة', '5000 mcg', '6', NULL, '*supports amino acid metabolism. *promotes normal immune function. biotin is a water-soluble vitamin necessary for normal growth and body function. biotin functions as a key regulatory element in gluconeogenesis fatty acid synthesis and in the metabolism', '025077134304', NULL, 883, '2025-09-22', 'For nutritional supplementation, also for treating dietary shortage or imbalance.', 'Biotin is necessary for the proper functioning of enzymes that transport carboxyl units and fix carbon dioxide, and is required for various metabolic functions, including gluconeogenesis, lipogenesis, fatty acid biosynthesis, propionate metabolism, and catabolism of branched-chain amino acids.', 'Biotin is a water-soluble B-complex vitamin which is composed of an ureido ring fused with a tetrahydrothiophene ring, which attaches a valeric acid substituent at one of its carbon atoms. Biotin is used in cell growth, the production of fatty acids, metabolism of fats, and amino acids. It plays a role in the Kreb cycle, which is the process in which energy is released from food. Biotin not only assists in various metabolic chemical conversions, but also helps with the transfer of carbon dioxide. Biotin is also helpful in maintaining a steady blood sugar level. Biotin is often recommended for strengthening hair and nails. Consequenty, it is found in many cosmetic and health products for the hair and skin. Biotin deficiency is a rare nutritional disorder caused by a deficiency of biotin. Initial symptoms of biotin deficiency include: Dry skin, Seborrheic dermatitis, Fungal infections, rashes including erythematous periorofacial macular rash, fine and brittle hair, and hair loss or total alopecia. If left untreated, neurological symptoms can develop, including mild depression, which may progress to profound lassitude and, eventually, to somnolence; changes in mental status, generalized muscular pains (myalgias), hyperesthesias and paresthesias. The treatment for biotin deficiency is to simply start taking some biotin supplements. A lack of biotin in infants will lead to a condition called seborrheic dermatitis or "cradle cap". Biotin deficiencies are extremely rare in adults but if it does occur, it will lead to anemia, depression, hair loss, high blood sugar levels, muscle pain, nausea, loss of appetite and inflamed mucous membranes.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (5074, 'Follitone hair cream 60 gm', 'فوليتون كريم للشعر 50 جم', '175', '118', 'Hair care', 'Rosemary extract+jojoba oil+thyme extract+olive oil+eureca sativa extract+chamomile extract+pantheno', 'Leader cosmetics > original pharma group', 'Cream', 'كريم', '60 gm', '1', NULL, 'follicles stimulating cream that prevent hair loss and brittlness', NULL, NULL, 1044, '2025-01-18', NULL, NULL, NULL, NULL, 0, 0, 0);
