INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (16696, 'Comtapone 200 mg 30 f.c.tab.', 'كومتابون 200 مجم 30 قرص', '72', NULL, 'Comt reversible inhibitor', 'Entacapone', 'Chemipharm', 'Tab', 'أقراص', '200 mg', '3', NULL, 'entacapone (inn) is a medication commonly used in combination with other medications for the treatment of parkinson s disease. entacapone together with levodopa and carbidopa allows levodopa to have a longer effect in the brain and reduces parkinsonï¿½s d', NULL, NULL, 1040, '2022-07-22', 0, 'Used as an adjunct to levodopa / carbidopa in the symptomatic treatment of patients with idiopathic Parkinson''s Disease who experience the signs and symptoms of end-of-dose "wearing-off".', 'The mechanism of action of entacapone is believed to be through its ability to inhibit COMT in peripheral tissues, altering the plasma pharmacokinetics of levodopa. When entacapone is given in conjunction with levodopa and an aromatic amino acid decarboxylase inhibitor, such as carbidopa, plasma levels of levodopa are greater and more sustained than after administration of levodopa and an aromatic amino acid decarboxylase inhibitor alone. It is believed that at a given frequency of levodopa administration, these more sustained plasma levels of levodopa result in more constant dopaminergic stimulation in the brain, leading to a greater reduction in the manifestations of parkinsonian syndrome.', 'Entacapone is structurally and pharmacologically related to tolcapone, but unlike tolcapone, is not associated with hepatotoxicity. Entacapone is used in the treatment of Parkinson&rsquo;s disease as an adjunct to levodopa/carbidopa therapy. Entacapone selectively and reversiblly inhibits catechol-O-methyltransferase (COMT). In mammals, COMT is distributed throughout various organs with the highest activities in the liver and kidney. COMT also occurs in the heart, lung, smooth and skeletal muscles, intestinal tract, reproductive organs, various glands, adipose tissue, skin, blood cells and neuronal tissues, especially in glial cells. COMT catalyzes the transfer of the methyl group of S-adenosyl-L-methionine to the phenolic group of substrates that contain a catechol structure. Physiological substrates of COMT include dopa, catecholamines (dopamine, norepinephrine, and epinephrine) and their hydroxylated metabolites. The function of COMT is the elimination of biologically active catechols and some other hydroxylated metabolites. COMT is responsible for the elimination of biologically active catechols and some other hydroxylated metabolites. In the presence of a decarboxylase inhibitor, COMT becomes the major metabolizing enzyme for levodopa, catalyzing the it to 3-methoxy-4-hydroxy-L-phenylalanine (3-OMD) in the brain and periphery.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (16700, 'Conatrate 1% topical cream 15 gm', 'كوناتريت 1% كريم 15 جم', '6', NULL, 'Antifungals.imidazoles', 'Econazole', 'Marcyrl co.', 'Cream', 'كريم', '1%', '1', NULL, 'about econazole synthetic imidazole derivative antifungal. mechanism of action of econazole econazole exerts its effects by altering cellular membrane and interfering with cellular enzymes.it is active against many fungi dermatophytes and yeasts as well a', NULL, NULL, 777, '2022-08-05', 0, 'For topical application in the treatment of tinea pedis, tinea cruris, and tinea corporis caused by <i>Trichophyton rubrum</i>, <i>Trichophyton mentagrophytes</i>, <i>Trichophyton tonsurans</i>, <i>Microsporum canis</i>, <i>Microsporum audouini</i>, <i>Microsporum gypseum</i>, and <i>Epidermophyton floccosum</i>, in the treatment of cutaneous candidiasis, and in the treatment of tinea versicolor.', 'Econazole interacts with 14-&alpha; demethylase, a cytochrome P-450 enzyme necessary to convert lanosterol to ergosterol. As ergosterol is an essential component of the fungal cell membrane, inhibition of its synthesis results in increased cellular permeability causing leakage of cellular contents. Econazole may also inhibit endogenous respiration, interact with membrane phospholipids, inhibit the transformation of yeasts to mycelial forms, inhibit purine uptake, and impair triglyceride and/or phospholipid biosynthesis.', 'Econazole is an antifungal medication related to fluconazole (Diflucan), ketoconazole (Nizoral), itraconazole (Sporanox), and clotrimazole (Lotrimin, Mycelex). Econazole prevents fungal organisms from producing vital substances required for growth and function. This medication is effective only for infections caused by fungal organisms. It will not work for bacterial or viral infections.', 'DrugBank', 0, 0, 0);
