    ) VALUES (2279, 'Cefrone 500 mg i.m/i.v vial', 'سيفرون 500 مجم فيال', '22', '14', 'Antibiotic.cephalosporin.third-generation', 'Cefoperazone', 'Mup', 'Vial', 'فيال', '500 mg', '1', NULL, 'Indicated for the treatment of following infections caused by susceptible bacteria:[label]



