INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (27960, 'Tecavir 0.05mg/ml oral soln. 210 ml', 'تيكافير 0.05مجم/مل محلول بالفم 210 مل', '450', NULL, 'Antiviral.nucleoside', 'Entecavir', 'Eva pharma', 'Sol', 'محلول', '0.05mg', '1', NULL, 'about entecavir nucleoside analog reverse transcriptase inhibitor oral antiviral drug used in the treatment of hepatitis b infection. mechanism of action of entecavir by competing with the natural substrate deoxyguanosine triphosphate entecavir functional', NULL, NULL, 881, '2022-06-17', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Entecavir is an oral antiviral drug used in the treatment of hepatitis B infection. It is marketed under the trade name Baraclude (BMS).

Entecavir is a guanine analogue that inhibits all three steps in the viral replication process, and the manufacturer claims that it is more efficacious than previous agents used to treat hepatitis B (lamivudine and adefovir). It was approved by the U.S. Food and Drug Administration (FDA) in March 2005.', '["J05AF10"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (27963, 'Tecentriq 1200 mg vial conc. for i.v. inf.', 'تيسينتريك 1200مجم فيال', '164,514', '126000', 'Antineoplastic', 'Atezolizumab', 'F.hoffman la roche', 'Vial', 'فيال', '1200 mg', '1', 'لعلاج الاورام -- لحالات الاورام السرطانية', NULL, NULL, NULL, 8174, '2025-08-16', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Atezolizumab is a humanized monoclonal antibody used to prevent the interaction of PD-L1 and PD-1, removing inhibition of immune responses seen in some cancers.[A18493,L7489] This medication is reserved for patients whose tumors express PD-L1, cannot receive platinum-based chemotherapy, or whose tumors do not respond to platinum-based chemotherapy.[L7489] Atezolizumab was granted FDA approval on 18 October 2016.[L7489] In November 2022, the manufacturer (Genentech) voluntarily withdrew the use of atezolizumab for the treatment of urothelial carcinoma, previously approved under the FDA''s Accelerated Approval Program. The rest of atezolizumab indications remain unaffected.[L44246]', '["L01FF05"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (27964, 'Tecfidera 120 mg 14 gast. resist. caps. ss', 'تيكفيديرا 120مجم 14 كبسول', '2,584', NULL, 'Multiple sclerosis', 'Dimethyl fumarate', 'Biogen idec > egyptian pharmaceutical trading companyegypt', 'Cap', 'كبسولة', '120 mg', '1', NULL, 'dimethyl fumarate is an anti-inflammatory. it is indicated for multiple sclerosis patients with relapsing forms and is also being investigated for the treatment of psoriasis. the mechanism of action of dimethyl fumarate in multiple sclerosis is not well u', NULL, NULL, 935, '2022-07-24', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Dimethyl fumarate is an agent indicated for the treatment of relapsing forms of multiple sclerosis.[A253942,L43752] The mechanism of action of dimethyl fumarate in multiple sclerosis is not well understood. It is thought to involve dimethyl fumarate degradation to its active metabolite monomethyl fumarate (MMF) then MMF up-regulates the Nuclear factor (erythroid-derived 2)-like 2 (Nrf2) pathway that is activated in response to oxidative stress.[L43752] Dimethyl fumarate is marketed under the brand name Tecfidera, and it was the third oral disease-modifying agent for multiple sclerosis approved by the FDA, following [fingolimod] and [teriflunomide].[A253917] Prior to its FDA approval, dimethyl fumarate had been used in Germany for treatment of psoriasis.[A253917]', '["L04AX07"]', '[]');
