INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (28406, 'Travonorm 0.004% eye drops. 5 ml', 'ترافونورم 0.004% قطرة للعين 5مل', '91', NULL, 'Antiglaucoma', 'Travoprost', 'Orchidia pharmaceutical industries', 'Drops', 'نقط', '0.004%', '1', NULL, 'about travoprost a synthetic prostaglandin f2a analogue ophthalmic antiglaucoma. mechanism of action of travoprost travoprost is a prostaglandin f2-alpha analogue and it is a prostanoid selective fp receptor agonist which reduces the intraocular pressure', NULL, NULL, 1278, '2022-07-17', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Travoprost ophthalmic solution is a topical medication used for controlling the progression of glaucoma or ocular hypertension, by reducing intraocular pressure [FDA Label]. It is a synthetic prostaglandin F2alpha analog [FDA Label]. Having been a well-received therapeutic agent with demonstrated efficacy and safety, travoprost is currently approved by the US FDA as a first-line treatment for lowering intraocular pressure in patients with open-angle glaucoma or ocular hypotension [L5155]. Furthermore, this approval also solidifies the medication as the first and only prostaglandin analog approved by the FDA for first-line treatment of glaucoma patients that does not contain the preservative benzalkonium chloride [L5155]. Moreover, travoprost is also currently approved in the EU for the decrease of elevated intraocular pressure in paediatric patients aged 2 months to < 18 years with ocular hypertension or paediatric glaucoma [L5146, L5152].', '["S01EE04"]', '[]');
