INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (28422, 'Treczimus 0.03% topical oint. 20 gm', 'تريكزيمس مرهم 0.03% 20 جرام', '114', '78', 'Immunosuppressants.psoriasis', 'Tacrolimus', 'Marcyrl co.', 'Oint', 'مرهم', '0.03%', '1', 'علاج حالات الصدفية.', 'this form of tacrolimus is used on the skin to treat a skin condition called eczema (atopic dermatitis) in patients who have not responded well to (or should not use) other eczema medications. eczema is an allergic-type condition that causes red irritated', '6223003571559', NULL, 12781, '2024-11-16', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Tacrolimus (also FK-506 or Fujimycin) is an immunosuppressive drug whose main use is after organ transplant to reduce the activity of the patient''s immune system and so the risk of organ rejection. It is also used in a topical preparation in the treatment of severe atopic dermatitis, severe refractory uveitis after bone marrow transplants, and the skin condition vitiligo. It was discovered in 1984 from the fermentation broth of a Japanese soil sample that contained the bacteria Streptomyces tsukubaensis. Tacrolimus is chemically known as a macrolide. It reduces peptidyl-prolyl isomerase activity by binding to the immunophilin FKBP-12 (FK506 binding protein) creating a new complex. This FKBP12-FK506 complex inhibits calcineurin which inhibits T-lymphocyte signal transduction and IL-2 transcription.', '["D11AH01", "L04AD02"]', '[]');
