INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (28425, 'Tremfya 100 mg/1 ml pref. syringe for s.c.', 'تريمفيا 100مجم/1مل سرنجات معبأة للحقن تحت الجلد', '44,264', '24026', 'Immunosuppressants', 'Guselkumab', 'Janssen cilag > soficopharm', 'Syringe', 'حقنة', '100 mg', '1', 'علاج الصدفية', NULL, NULL, NULL, 15975, '2024-09-02', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Guselkumab is a human immunoglobulin G1 lambda (IgG1λ) monoclonal antibody that selectively blocks interleukin-23. IL-23 is an inflammatory cytokine that activates the CD4+ T-helper (Th17) cell pathway to mediate the inflammatory cascade that induces psoriatic plaque formation [A20357]. In clinical trials, guselkumab demonstrated improved skin clearance and symptomatic improvements in dermatological manifestations of psoriasis. 

Developed by Janssen, the subcutenous injection form of guselkumab was approved in July 2017 under the market name Tremfya for the treatment of adult patients with moderate-to-severe plaque psoriasis.', '["L04AC16"]', '[]');
