INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27337, 'Spasmocure 40mg/2ml i.m. i.v. s.c. 5 amp.', 'سبازموكيور 40مجم/2مل 5 امبول', '24', NULL, 'Antispasmodic', 'Drotaverine', 'Alfacure pharmaceuticals', 'Amp', 'أمبول', '40mg', '5', NULL, 'about drotaverine drotaverine is an effective spasmolytic drug. mechanism of action of drotaverine drotaverine has spasmolytic and vasodilating action. it exerts its action by inhibiting phosphodiesterase enzyme iv. this will leads to a decrease in cyclic', NULL, NULL, 892, '2022-09-14', 'Drotaverine is used to alleviate gastrointestinal and genitourinary smooth muscle spasms, such as cholecystitis and gallbladder disorders.[L22689]', 'Drotaverine is a selective inhibitor of phosphodiesterase 4 (PDE4), which is an enzyme responsible for the degradation of cyclic adenosine monophosphate (cAMP). Inhibition of PDE4 leads to elevated levels of cAMP, leading to smooth muscle relaxation. Recent research showed that low levels of cAMP have been associated with brain tumorigenesis, leading to the investigation of PDE4 inhibitors as potential anticancer agents.[A231619]', 'Drotaverine is an e spasmolytic agent with a relaxing effect on smooth muscles. It works to relieve visceral spasms and improve cervical dilation. _In vitro_, drotaverine mediated cytostatic effects on several human tumor cell lines and nonmalignant mouse fibroblasts.[A231619] Drotaverine may have minor allosteric calcium channel blocking properties: _in vitro_, drotaverine behaves like voltage-dependent L-type calcium channel blockers.[A231624]', 'DrugBank', 0, 0, 0);
