INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (17666, 'Dilozole susp. 120 ml', 'ديلوزول معلق 120 مل', '6', NULL, 'Antiprotozoal.nitroimidazole', 'Diloxanide furoate+metronidazole', 'Alexandria', 'Susp', 'معلق', '120 ml', '1', NULL, NULL, '6223000176351', NULL, 962, '2023-01-13', 'Diloxanide is used alone as a primary agent in the treatment of asymptomatic (cyst passers) intestinal amebiasis caused by Entamoeba histolytica. Diloxanide may also be used concurrently, or sequentially, with other agents such as the nitroimidazoles (eg. metronidazole) in the treatment of invasive or extraintestinal forms of amebiasis.

', 'Unknown. Diloxanide may inhibit protein synthesis. ', 'Diloxanide is a luminal amebicide, however the mechanism of action of diloxanide is unknown. Diloxanide destroys the trophozoites of E. histolytica that eventually form into cysts. The cysts are then excreted by persons infected with asymptomatic amebiasis. Diloxanide furoate is a prodrug, and is hydrolyzed in the gastrointestinal tract to produce diloxanide, the active ingredient. ', 'DrugBank', 0, 0, 0);
