INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (21428, 'Kipro 30 tabs', 'كيبرو 30 قرص', '60', NULL, NULL, 'Vitamin d3+vitamin k2+magnesium', 'Healthy tech pharma > nutr-med pharma', 'Tab', 'أقراص', NULL, '3', 'فيتامينات متعددة لعلاج نقص فيتامين دال -- وهشاشة العظام', 'support immunity for vitamin d deficiency & osteoporosis avoid heart disease ingredients: vitamin d3 5000 iu + vitamin k2 100 mcg+ magnesium 100 mg dose : 1-2 tablets/day', NULL, NULL, 1138, '2022-05-12', 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (3988, 'Ectomethrin 2.5 % oint. 20 gm', 'اكتومثرين 2.5% مرهم 20 جم', '21', '12', 'Scabicide', 'Permethrin', 'Misr > apc (arab perfumes chemicals and pharmaceutical co.)', 'Oint', 'مرهم', '2.5 %', '1', NULL, 'about permethrin synthetic pyrethroid antiparasitic pediculicide scabicide. mechanism of action of permethrin permethrin exerts its action by inhibiting sodium ion channel and delays depolarization. this will leads to (neuronal) paralysis of insects. perm', '6221035007046', NULL, 1152, '2024-12-05', 'For the treatment of infestation with <i>Sarcoptes scabiei</i> (scabies).', 'Permethrin acts on the nerve cell membrane to disrupt the sodium channel current by which the polarization of the membrane is regulated. Delayed repolarization and paralysis of the pests are the consequences of this disturbance.', 'Permethrin, a pyrethroid, is active against a broad range of pests including lice, ticks, fleas, mites, and other arthropods.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (7489, 'Relaxospasm 30 mg 10 capsules', 'ريلاكسوسبازم 30 مجم 10 كبسولا', '27', NULL, 'Cns.analgesic.skeletal muscle relaxant', 'Cyclobenzaprine', 'Eva pharma', 'Capsule', 'كبسولة', '30 mg', '1', NULL, NULL, NULL, NULL, 1093, '2025-09-22', 'Cyclobenzaprine is indicated as a short-term (2-3 weeks) adjunct therapy, along with rest and physical therapy, for relief of muscle spasm associated with acute, painful musculoskeletal conditions. It has not been found effective in the treatment of spasticity originating from cerebral or spinal cord disease, or spasticity in children with cerebral palsy.[L8408,L8411] Cyclobenzaprine is also occasionally used off-label for reducing pain and sleep disturbances in patients with fibromyalgia.[A184946]', 'The exact mechanism of action of cyclobenzaprine has not been fully elucidated in humans, and much of the information available regarding its mechanism has been ascertained from early animal studies. There is some evidence that cyclobenzaprine exerts its effects at the supraspinal level, specifically within the locus coeruleus of the brainstem, with little-to-no action at neuromuscular junctions or directly on skeletal musculature[L8411,A184982]. Action on the brainstem is thought to result in diminished activity of efferent alpha and gamma motor neurons, likely mediated by inhibition of coeruleus-spinal or reticulospinal pathways, and ultimately depressed spinal cord interneuron activity.[A184982]



More recently it has been suggested that inhibition of descending serotonergic pathways in the spinal cord via action on 5-HT2 receptors may contribute to cyclobenzaprine’s observed effects.[A5896,A184982,A4862]', 'Cyclobenzaprine is a skeletal muscle relaxant that works on areas of the brainstem to reduce skeletal muscle spasm, though its exact pharmacodynamic behaviour is currently unclear.[L8408,L8411,A184982] Despite its long half-life, it is relatively short-acting with a typical duration of action of 4-6 hours.[A184982] Cyclobenzaprine has been reported to contribute to the development of serotonin syndrome when used in combination with other serotonergic medications.[L8408,L8411,A36339] Symptoms of serotonin syndrome may include autonomic instability, changes to mental status, neuromuscular abnormalities, or gastrointestinal symptoms - treatment with cyclobenzaprine should be discontinued immediately if any of these reactions occur during therapy.[L8408,L8411]', 'DrugBank', 0, 0, 0);
