INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22688, 'Miacalcic 200 i.u./ml nasal spray 2 ml', 'مياكالسيك 200وحدة/مل سبراي للانف 2 مل', '150', NULL, 'Osteoporosis.antiresorptives', 'Calcitonin', 'Novartis', 'Spray', 'بخاخ', '2 ml', '1', NULL, 'prescribed for: injectable calcitonin is used to treat osteoporosis and paget s disease. injectable calcitonin also is used to treat seriously elevated blood calcium levels. the nasal spray is used only for osteoporosis in women who are at least 5 years p', '4897102130027', NULL, 1088, '2024-12-11', 'Investigated for use/treatment in myocardial infarction, heart disease, and asthma.', 'While current drugs for treating HF and MI have a single mechanism of action, CGRP appears to work on several levels: for example, it (1) increases local blood flow enhancing hemodynamic proficiency; (2) protects heart muscle cells from the damage due to lack of oxygen (ischemia); and, (3) modulates the immune system by boosting anti-inflammatory cytokine expression while suppressing pro-inflammatory cytokines, which reduces the inflammatory response to provide a cardioprotective effect. All these actions are meant to minimize heart muscle damage and subsequent scar tissue formation, while promoting the healing process.', NULL, 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (30357, 'Cortex shampoo 120 ml', 'كورتيكس شامبو 120 مل', '105', '59', 'Hair care', 'Selenium sulfide+zinc pyrithione+clotrimazole', 'Egyptian co. for cosmetics > evexia pharma', 'Amp', 'أمبول', '120 ml', '1', NULL, 'anti-dandruff for the management of seborrheic dermatitis & tvc.', NULL, NULL, 2780, '2024-11-21', 'For treatment of tinea versicolor, tinea capitis, dandruff and seborrheic dermatitis of the scalp.', 'Topical selenium sulfide may act by an antimitotic action, resulting in a reduction in the turnover of epidermal cells. It also has local irritant, antibacterial, and mild antifungal activity, which may contribute to its effectiveness. An antimitotic mechanism of action is suggested by data showing that selenium sulfide decreases the rate of incorporation of radioactively labeled thymidine into the DNA of dermal epithelial cells. The following organisms are generally considered susceptible to selenium sulfide in vitro: <i>Malassezia furfur</i>, <i>Microsporum sp.</i> including <i>Microsporum audouinii</i> and <i>Microsporum canis</i>, <i>Pityrosporon sp.</i>, <i>Trichophyton sp.</i> including <i>Trichophyton schoenleinii</i> and <i>Trichophyton tonsurans</i>. Selenium sulfide has been shown to be sporicidal to <i>T. tonsurans</i>, the most common etiologic agent of tinea capitis. One in-vitro study demonstrated that 2.5% selenium sulfide was equivalent in sporicidal activity to both 1% and 2% zinc pyrithione.', 'Selenium sulfide is an antifungal agent often used in shampoos for the treatment of dandruff and seborrheic dermatitis. Selenium sulfide is highly active in inhibiting the growth of <i>P. ovale</i>. It is also a proven cytostatic agent, slowing the growth of both hyperproliferative and normal cells in dandruff and seborrheic dermatitis. A 0.6% micronized form of selenium sulfide is also safe and effective for dandruff.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (31023, 'Omega brand plus oral drops 15 ml', 'اوميجا براند بلس نقط للفم 15 مل', '90', '75', 'Multivitamin', 'Omega3+omega6+vitd3+vit e', 'Alpha cure > brand life pharma', 'Drops', 'نقط', '15 ml', '1', 'مكمل غذائى لدعم القدرات العقلية وزيادة التركيز عند الاطفال ودعم الجهاز المناعى للطفل', NULL, NULL, NULL, 1455, '2024-05-06', NULL, NULL, NULL, NULL, 0, 0, 0);
