INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (16784, 'Convulex 300mg/ml oral drops 100 ml', 'كونفيولكس 300مجم/مل نقط بالفم 100 مل', '81', NULL, 'Anti-epileptic.fatty acid', 'Sodium valproate', 'Gerot pharma > b.p pharma', 'Drops', 'نقط', '300mg', '1', NULL, 'about sodium valproate histone deacetylase inhibitor gaba agent anticonvulsant antimanic migraine prophylaxis. mechanism of action of sodium valproate sodium valproate is the sodium salt of valproic acid.after oral administration it is quiickly converted', '9008732008364', NULL, 1077, '2023-04-26', 'For the treatment of small uncomplicated varicose veins of the lower extremities that show simple dilation with competent valves.', 'Sodium tetradecyl sulfate is a potent toxin for endothelial cells in that brief exposure to even low concentrations are effective in stripping endothelium over a considerable distance and exposing highly thrombogenic endothelium in the process. Diluted sodium tetradecyl sulfate is also able to induce a hypercoagulable state, possibly by selective inhibition of protein C, and can also promote platelet aggregation.', 'Sodium tetradecyl sulfate is an anionic surfactant which occurs as a white, waxy solid. It is used as a sclerosing agent in sclerotherapy. Sclerotherapy is an injection of a sclerosing agent directly through the skin into a lesion and is used primarily for slow-flow vascular anomalies, particularly for venous malformation and lymphatic malformation. Intravenous injection causes intima inflammation and thrombus formation. This usually occludes the injected vein. Subsequent formation of fibrous tissue results in partial or complete vein obliteration that may or may not be permanent.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (17182, 'Deferoxinova 250mg 10 disp. tabs.', 'ديفيروكسينوفا 250 مجم 10 أقراص', '393', NULL, 'Iron chelating agents', 'Deferasirox', 'Debeiky > innova pharma', 'Tab', 'أقراص', '250mg', '1', 'يستخدم لحالات تسمم الحديد', 'about deferasirox a synthetic achiral tridentate triazole derived from salicylic acid oral iron chelating agents. indication for the treatment of chronic iron overload due to blood transfusions (transfusional hemosiderosis) in patients 2 years of age and', NULL, NULL, 868, '2022-05-26', 'For the treatment of chronic iron overload due to blood transfusions (transfusional hemosiderosis) in patients 2 years of age and older.', 'Two molecules of deferasirox are capable of binding to 1 atom of iron. Deferasirox works in treating iron toxicity by binding trivalent (ferric) iron (for which it has a strong affinity), forming a stable complex which is eliminated via the kidneys.', 'Deferasirox is an orally active chelator that is selective for iron (as Fe3+). It is a tridentate ligand that binds iron with high affinity in a 2:1 ratio. Although deferasirox has very low affinity for zinc and copper there are variable decreases in the serum concentration of these trace metals after the administration of deferasirox. The clinical significance of these decreases is uncertain.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19767, 'Giga white cream 50 gm', 'جيجا وايت 50جم كريم', '70', NULL, 'Whitening topical', NULL, 'El-helou perfumes & cosmetics > four benifits pharmaceuticals', 'Cream', 'كريم', '50 gm', '1', 'كريم تفتيح وترطيب البشرة وعلاج الكلف والتصبغات', 'whitening cream', NULL, NULL, 1683, '2022-07-07', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22146, 'Lyribalin 75mg 10 caps.', 'ليريبالين 75مجم 10كبسول', '18', NULL, 'Anti-epileptic.gaba analogs', 'Pregabalin', 'Borg', 'Cap', 'كبسولة', '75mg', '1', NULL, 'about pregabalin a gaba agonist anticonvulsant. mechanism of action of pregabalin pregabalin is a gaba neurotransmitter analog. it produces its action by modulating calcium channels and also reduces the release of neurotransmitters such as glutamate norep', NULL, NULL, 1087, '2022-07-09', 'Pregabalin is indicated for the management of neuropathic pain associated with diabetic peripheral neuropathy, postherpetic neuralgia, fibromyalgia, neuropathic pain associated with spinal cord injury, and as adjunctive therapy for the treatment of partial-onset seizures in patients 1 month of age and older.[L7066]', 'Although the mechanism of action has not been fully elucidated, studies involving structurally related drugs suggest that presynaptic binding of pregabalin to voltage-gated calcium channels is key to the antiseizure and antinociceptive effects observed in animal models.[L7066]  



By binding presynaptically to the alpha2-delta subunit of voltage-gated calcium channels in the central nervous system, pregabalin modulates the release of several excitatory neurotransmitters including glutamate, substance-P, norepinephrine, and calcitonin gene related peptide.[A31163] In addition, pregabalin prevents the alpha2-delta subunit from being trafficked from the dorsal root ganglia to the spinal dorsal horn, which may also contribute to the mechanism of action.[A36628] 



Although pregabalin is a structural derivative of the inhibitory neurotransmitter gamma-aminobutyric acid (GABA), it does not bind directly to GABA or benzodiazepine receptors.[A31165]  ', 'Although the structure of pregabalin is similar to gamma-aminobutyric acid (GABA), it does not bind to GABA receptors.[A31163,A187190] Instead, it binds the alpha2-delta subunit of presynaptic voltage-gated calcium channels in the central nervous system.[A31163,A187190] Pregabalin does not modulate dopamine receptors, serotonin receptors, opiate receptors, sodium channels or cyclooxygenase activity.[A187190] ', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27421, 'Sporanox 100mg 4 caps.', 'سبورانوكس 100مجم 4 كبسول', '42', NULL, 'Antifungals.triazoles', 'Itraconazole', 'Janssen cilag > soficopharm', 'Cap', 'كبسولة', '100mg', '1', NULL, 'about itraconazole a synthetic triazole derivative systemic antifungal. mechanism of action of itraconazole itraconazole is a fungicidal or fungistatic depending on the drug concentrations. it inhibits the conversion of lanosterol to 14 demethyl lanostero', NULL, NULL, 1174, '2022-07-22', 'Itraconazole is indicated for the treatment of the following fungal infections in immunocompromised and non-immunocompromised patients:[L50051, L50056]



- Blastomycosis, pulmonary and extrapulmonary

- Histoplasmosis, including chronic cavitary pulmonary disease and disseminated, nonmeningeal histoplasmosis, and

- Aspergillosis, pulmonary and extrapulmonary, in patients who are intolerant of or who are refractory to amphotericin B therapy



It is also indicated for the treatment of the following fungal infections in non-immunocompromised patients:[L50051]



- Onychomycosis of the toenail, with or without fingernail involvement, due to dermatophytes (tinea unguium)

- Onychomycosis of the fingernail due to dermatophytes (tinea unguium).



Itraconazole oral solution is indicated for the treatment of oropharyngeal and esophageal candidiasis.[L50061]', 'Itraconazole mediates its antifungal activity by inhibiting 14α-demethylase, a fungal cytochrome P450 enzyme that converts lanosterol to ergosterol, a vital component of fungal cell membranes.[L50051] The azole nitrogen atoms in the chemical structure of itraconazole form a complex with the active site, or the haem iron, of the fungal enzyme to impede its function.[A34257, A263232] The accumulation of lanosterol and 14-methylated sterols results in increased permeability of the fungal cell membrane, and modified membrane-bound enzyme activity, and dysregulated chitin synthesis.[A34257, A263222, A263227] Other proposed mechanisms of action of itraconazole include the inhibition of fungal cytochrome c oxidative and peroxidative enzymes that also lead to the disruption of fungal cell membranes.[A34257]', 'Itraconazole is an antifungal agent that inhibits cell growth and promotes cell death of fungi.[A34257] It exhibits in vitro activity against _Blastomyces dermatitidis_, _Histoplasma capsulatum_, _Histoplasma duboisii_, _Aspergillus flavus_, _Aspergillus fumigatus_, and _Trichophyton_ species.[L50051]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (630, 'Amoxicid 500 mg 12 caps.', 'اموكسيسيد 500مجم 12 كبسولة', '10', NULL, 'Penicillins.monocomponent', 'Amoxicillin', 'Cid', 'Cap', 'كبسولة', '500 mg', '2', NULL, NULL, NULL, NULL, 1316, '2023-04-19', 'Amoxicillin alone is indicated to treat susceptible bacterial infections of the ear, nose, throat, genitourinary tract, skin, skin structure, and lower respiratory tract.[L11656,L11659] Amoxicillin is given with calvulanic acid to treat acute bacterial sinusitis, community acquired pneumonia, lower respiratory tract infections, acute bacterial otitis media, skin and skin structure infections, and urinary tract infections.[L11650,L7880,L11653] Amoxicillin is given with omeprazole in the treatment of _Helicobacter pylori_ (_H. pylori_) infection.[L9743,L11647]



Amoxicillin is used in combination with [vonoprazan] and [clarithromycin] as co-packaged triple therapy or in combination with [vonoprazan] as co-packaged dual therapy to treat _H. pylori_ infection in adults.[L41695]', 'Amoxicillin competitively inhibits penicillin-binding protein 1 and other high molecular weight penicillin binding proteins.[A6205,A190678]  Penicillin bind proteins are responsible for glycosyltransferase and transpeptidase reactions that lead to cross-linking of D-alanine and D-aspartic acid in bacterial cell walls.[A190678] Without the action of penicillin binding proteins, bacteria upregulate autolytic enzymes and are unable to build and repair the cell wall, leading to bacteriocidal action.[A190642,A190678]', 'Amoxicillin competitively inhibit penicillin binding proteins, leading to upregulation of autolytic enzymes and inhibition of cell wall synthesis.[A6205,A190678,A190642] Amoxicillin has a long duration of action as it is usually given twice daily.[L7880] Amoxicillin has a wide therapeutic range as mild overdoses are not associated with significant toxicity.[L11650] Patients should be counselled regarding the risk of anaphylaxis, _Clostridium difficile_ infections, and bacterial resistance.[L11656]', 'DrugBank', 0, 0, 0);
