INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (17233, 'Deltrexone 50mg 10 tab.', 'ديلتريكسون 50 مجم 10 قرص', '80', NULL, 'Analgesics.narcotic opioid', 'Naltrexone', 'Delta pharma', 'Tab', 'أقراص', '50mg', '1', NULL, NULL, NULL, NULL, 1290, '2022-07-17', 0, 'Used as an adjunct to a medically supervised behaviour modification program in the maintenance of opiate cessation in individuals who were formerly physically dependent on opiates and who have successfully undergone detoxification. Also used for the management of alcohol dependence in conjunction with a behavioural modification program.', 'Naltrexone is a pure opiate antagonist and has little or no agonist activity. The mechanism of action of naltrexone in alcoholism is not understood; however, involvement of the endogenous opioid system is suggested by preclinical data. Naltrexone is thought to act as a competitive antagonist at mc, κ, and δ receptors in the CNS, with the highest affintiy for the μ receptor. Naltrexone competitively binds to such receptors and may block the effects of endogenous opioids. This leads to the antagonization of most of the subjective and objective effects of opiates, including respiratory depression, miosis, euphoria, and drug craving. The major metabolite of naltrexone, 6-β-naltrexol, is also an opiate antagonist and may contribute to the antagonistic activity of the drug.', 'Naltrexone, a pure opioid antagonist, is a synthetic congener of oxymorphone with no opioid agonist properties. Naltrexone is indicated in the treatment of alcohol dependence and for the blockade of the effects of exogenously administered opioids. It markedly attenuates or completely blocks, reversibly, the subjective effects of intravenously administered opioids. When co-administered with morphine, on a chronic basis, naltrexone blocks the physical dependence to morphine, heroin and other opioids. In subjects physically dependent on opioids, naltrexone will precipitate withdrawal symptomatology.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (17234, 'Delvena 100mg 10 tab', 'دلفينا 100 مجم 10 اقراص', '45', NULL, 'Psychiatric.antidepressants', 'Venlafaxine', 'Delta pharma', 'Tab', 'أقراص', '100mg', '1', NULL, 'about venlafaxine an arylalkanolamine derivative serotonin/norepinephrine reuptakeinhibitor antidepressant anxiolytic. mechanism of action of venlafaxine venlafaxine and its metabolite block the re-uptake of neurotransmitter serotonin and noradrenaline fr', NULL, NULL, 881, '2022-07-15', 0, 'Venlafaxine is indicated for the management of major depressive disorder (MDD), generalized anxiety disorder (GAD), social anxiety disorder (SAD), and panic disorder.[L43030]', 'The exact mechanism of action of venlafaxine in the treatment of various psychiatric conditions has not been fully elucidated; however, it is understood that venlafaxine and its active metabolite O-desmethylvenlafaxine (ODV) potently and selectively inhibits the reuptake of both serotonin and norepinephrine at the presynaptic terminal.[A27600,A252065,L43030] This results in increased levels of neurotransmitters available at the synapse that can stimulate postsynaptic receptors.[A252075] It is suggested that venlafaxine has a 30-fold selectivity for serotonin compared to norepinephrine: venlafaxine initially inhibits serotonin reuptake at low doses, and with higher doses, it inhibits norepinephrine reuptake in addition to serotonin.[A252065,A252070] Venlafaxine and ODV are also weak inhibitors of dopamine reuptake.[L43030]', 'Venlafaxine is an antidepressant agent that works to ameliorate the symptoms of various psychiatric disorders by increasing the level of neurotransmitters in the synapse. Venlafaxine does not mediate muscarinic, histaminergic, or adrenergic effects.[L43030]', 'DrugBank', 0, 0, 0);
