INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (8402, 'Septola 100 tab', 'سبتولا 100 قرص', '300', NULL, 'Dietry supplement', 'Vitamin c', 'Ep pharma', 'Tab', 'أقراص', NULL, '1', NULL, NULL, NULL, NULL, 800, '2023-04-26', 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (16509, 'Core cup 20 tablets', 'كور كب 20 قرص', '128', '98', 'Multivitamins + multiminerals', 'Vitamin a+vitamin c+vitamin d3+vitamin e+vitamin k+thiamin+riboflavin+niacin+vitamin b6+folic acid+vitamin b12+biotin+pantothenic acid+calcium+iron+phosphorus+iodine+magnesium+zinc+selenium+copper+manganese+chromium+molybdenum+chloride', 'Core pharm', 'Tablet', 'أقراص', NULL, '2', NULL, NULL, '6225000614215', NULL, 2387, '2025-06-29', 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (7826, 'Meclopram 2mg/ml oral drops 10 ml', 'ميكلوبرام 2مجم/مل نقط للفم 10مل', '6', NULL, 'Antiemetic', 'Metoclopramide', 'Alexandria', 'Drops', 'نقط', '2mg', '1', NULL, 'about metoclopramide it is a dopamine antagonist - antiemetic gastroprokinetic agent. mechanism of action of metoclopramide metoclopramide acts through both serotonergic and dopaminergic receptors. metoclopramide binds to serotonin (5 ht3 &5ht4) and dopam', '6223000172230', NULL, 1043, '2022-12-06', 'Metoclopramide in the oral tablet form is used for symptomatic treatment of both acute and recurrent diabetic gastroparesis, in addition to the treatment of gastroesophageal reflux disease (GERD) in patients who have failed to respond to traditional therapy.[L8414] A nasal spray formulation is also indicated to treat adults with acute, recurrent diabetic gastroparesis.[L14381]



In the intravenous injection form, it is indicated for the above conditions as well as for the prevention of vomiting that may follow emetogenic chemotherapy or nausea and vomiting after surgery. Intravenous metoclopramide facilitates intubation of the small bowel and stimulates gastric emptying and barium flow in patients who require radiological examination of the stomach or small intestine.  In some cases, the delay of gastrointestinal emptying interferes with the radiographic visualization of the gastrointestinal tract, and metoclopramide is used to facilitate emptying in these cases, allowing for adequate diagnostic visualization.[L8417]



Some off-label uses of metoclopramide include the management of radiation-induced nausea and vomiting, gastric bezoars, intractable hiccups, and migraine pain.[A184961,A184964,A184967,A40105]', 'Metoclopramide causes antiemetic effects by inhibiting dopamine D2 and serotonin 5-HT3 receptors in the chemoreceptor trigger zone (CTZ) located in the area postrema of the brain.[A184880,T683] Administration of this drug leads to prokinetic effects via inhibitory actions on presynaptic and postsynaptic D2 receptors, agonism of serotonin 5-HT4 receptors, and antagonism of muscarinic receptor inhibition. This action enhances the release of acetylcholine, causing increased lower esophageal sphincter (LES) and gastric tone, accelerating gastric emptying and transit through the gut. Metoclopramide antagonizes the dopamine D2 receptors. Dopamine exerts relaxant effect on the gastrointestinal tract through binding to muscular D2 receptors.[A184880,T683,L8414]', 'Metoclopramide increases gastric emptying by decreasing lower esophageal sphincter (LES) pressure. It also exerts effects on the area postrema of the brain, preventing and relieving the symptoms of nausea and vomiting. In addition, this drug increases gastrointestinal motility without increasing biliary, gastric, or pancreatic secretions.[T683,L8414,L8417]



Because of its antidopaminergic activity, metoclopramide can cause symptoms of tardive dyskinesia (TD), dystonia, and akathisia, and should therefore not be administered for longer than 12 weeks.[A184886,L8414]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (311, 'Shalgaten 400mg 30 caps.', 'شالجاتين 400مجم 30 كبسولة', '126', '93', 'Anti-epileptic.gaba analogs', 'Gabapentin', 'Egpi > sea pharm for pharmaceuticals', 'Cap', 'كبسولة', '400mg', '3', 'مهديء عصبي مضاد للتشنجات والصرع', 'about gabapentin a gaba analogue anticonvulsant mood stabilizer. mechanism of action of gabapentin gabapentin is a gaba derivative and it bounds to lipophilic molecule. the mechanism of action of gabapentin is not exactly known. it crosses blood brain bar', '6222001400090', NULL, 1413, '2024-06-22', 'In the United States, gabapentin is officially indicated for the treatment of postherpetic neuralgia in adults and for the adjunctive treatment of partial-onset seizures, with or without secondary generalization, in patients 3 years of age and older.[L8717] In Europe, gabapentin is indicated for adjunctive therapy in the treatment of partial-onset seizures, with or without secondary generalization, in patients 6 years of age and older and as monotherapy in patients 12 years of age and older. It is also used in adults for the treatment of various types of peripheral neuropathic pain, such as painful diabetic neuropathy.[L8732]', 'The precise mechanism through which gabapentin exerts its therapeutic effects is unclear.[L8717,L8720] The primary mode of action appears to be at the auxillary α2δ-1 subunit of voltage-gated calcium channels (though a low affinity for the α2δ-2 subunit has also been reported).[A186179,A14097,A186277] The major function of these subunits is to facilitate the movement of pore-forming α1 subunits of calcium channels from the endoplasmic reticulum to the cell membrane of pre-synaptic neurons.[A186179] There is evidence that chronic pain states can cause an increase in the expression of α2δ subunits and that these changes correlate with hyperalgesia.[A14097] Gabapentin appears to inhibit the action of α2δ-1 subunits, thus decreasing the density of pre-synaptic voltage-gated calcium channels and subsequent release of excitatory neurotransmitters.[A186179] It is likely that this inhibition is also responsible for the anti-epileptic action of gabapentin.[A186277]



There is some evidence that gabapentin also acts on adenosine receptors[A11009, A186209] and voltage-gated potassium channels,[A186212] though the clinical relevance of its action at these sites is unclear.', 'Gabapentin is an anti-convulsant medication that inhibits the release of excitatory neurotransmitters, allowing for its use against pathologic neurotransmission such as that seen in neuropathic pain and seizure disorders.[L8717,L8732] It has a wide therapeutic index, with doses in excess of 8000 mg/kg failing to cause a fatal reaction in rats.[L8765]



Gabapentin is ineffective in absence seizures and should be used in caution in patients with mixed seizure disorders involving absence seizures. Gabapentin has been associated with drug reaction with eosinophilia and systemic symptoms (DRESS), otherwise known as multi-organ hypersensitivity. This reaction can prove fatal and early symptoms such as fever, lymphadenopathy, and rash should be promptly investigated.[L8717,L8720,L8732]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18584, 'Estikana 10mg 28 f.c.tab.', 'استيكانا 10مجم 28 قرص', '132', '60', 'Psychiatric.antidepressants', 'Escitalopram', 'Hochster pharmaceutical industries', 'Tab', 'أقراص', '10mg', '2', 'مضاد للاكتئاب', NULL, NULL, NULL, 939, '2024-10-28', 'Escitalopram is indicated for the acute and maintenance treatment of major depressive disorder (MDD) in adults and pediatric patients 12 years old and older and for the acute treatment of generalized anxiety disorder (GAD) in adults and pediatric patients 7 years old and older.[L8513,L8513] It is additionally indicated for symptomatic relief of obsessive-compulsive disorder (OCD) in Canada.[L8516]', 'Escitalopram, like other selective serotonin re-uptake inhibitors, enhances serotonergic activity by binding to the orthosteric (i.e. primary) binding site on the serotonin transporter (SERT), the same site to which endogenous 5-HT binds, and thus prevents the re-uptake of serotonin into the presynaptic neuron.[A185420,A39738,A185726] Escitalopram, along with [paroxetine], is also considered an allosteric serotonin re-uptake inhibitor - it binds to a secondary allosteric site on the SERT molecule to more strongly inhibit 5-HT re-uptake. Its combination of orthosteric and allosteric activity on SERT allows for greater extracellular 5-HT levels, a faster onset of action, and greater efficacy as compared to other SSRIs. [A185825,A185726] The sustained elevation of synaptic 5-HT eventually causes desensitization of 5-HT<sub>1A</sub> auto-receptors, which normally shut down endogenous 5-HT release in the presence of excess 5-HT - this desensitization may be necessary for the full clinical effect of SSRIs and may be responsible for their typically prolonged onset of action.[A185828,A185726]



Escitalopram has shown little-to-no binding affinity at a number of other receptors, such as histamine and muscarinic receptors, and minor activity at these off-targets may explain some of its adverse effects.[L8513,L8516,L8522,A185726]', 'Escitalopram belongs to a class of medications called selective serotonin re-uptake inhibitors (SSRIs). These agents cause an increase in serotonin levels in neuronal synapses by preventing the re-uptake of serotonin (5-HT) into the presynaptic terminals of serotonergic neurons.[A185420,A39738,L8513,L8516] As compared to other SSRIs, it appears to have a relatively quick onset of effect due to its potency.[A185819,A185822]



SSRIs as a class have been associated with abnormal bleeding, particularly in patients receiving concomitant therapy with other medications affecting hemostasis, and with the development of serotonin syndrome. Use escitalopram with caution in patients with a higher-than-baseline risk of bleeding and in patients receiving concomitant therapy with other serotonergic drugs. Escitalopram may also cause a discontinuation syndrome with abrupt removal of the drug, and should be slowly tapered if discontinuation of therapy is warranted.[L8513,L8516,L8522]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (10437, 'Sigmataxim 1 gm i.v/i.m vial', 'سيجماتاكسيم 1جم وريد/عضل فيال', '43', '30', 'Antibiotic.cephalosporin.third-generation', 'Cefotaxime', 'Rameda > sigma', 'Vial', 'فيال', '1 gm', '1', NULL, 'about cefotaxime third generation cephalosporin parenteral antibiotic. mechanism of action of cefotaxime cefotaxime is a 3rd generation cephalosporin which exerts its bactericidal action against both gram positive & aerobic gram negative organisms by inhi', '6221172002270', NULL, 988, '2025-02-07', 'Used to treat gonorrhoea, meningitis, and severe infections including infections of the kidney (pyelonephritis) and urinary system. Also used before an operation to prevent infection after surgery.', 'The bactericidal activity of cefotaxime results from the inhibition of cell wall synthesis via affinity for penicillin-binding proteins (PBPs). Cefotaxime shows high affinity for penicillin-binding proteins in the cell wall including PBP Ib and PBP III.', 'Cefotaxime is a third generation intravenous cephalosporin antibiotic. It has broad spectrum activity against Gram positive and Gram negative bacteria. It does not have activity against <i>Pseudomonas aeruginosa</i>. Cefotaxime works by inhibiting bacterial cell wall biosynthesis. A positive feature of cefotaxime is that it display a resistance to penicillinases and is useful to treat infections that are resistant to penicillin derivatives.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25441, 'Provera 5mg 24 tab.', 'بروفيرا 5مجم 24 قرص', '24', NULL, 'Contraceptives', 'Medroxyprogesterone', 'Pfizer', 'Tab', 'أقراص', '5mg', '2', NULL, NULL, NULL, NULL, 1143, '2022-07-07', 'Medroxyprogesterone acetate (MPA) oral tablets are indicated to treat secondary amenorrhea, reduce the incidence of endometrial hyperplasia in postmenopausal women, and to treat abnormal uterine bleeding due to hormonal imbalance, not organic pathology.[L8657] Oral tablets containing MPA and conjugated estrogens are indicated to prevent postmenopausal osteoporosis and to treat moderate to severe menopausal symptoms such as vasomotor symptoms, vulvar atrophy, and vaginal atrophy.[L8660] Subcutaneous MPA is indicated to prevent pregnancy and manage pain associated with endometriosis.[L8663] Intramuscular MPA is indicated to prevent pregnancy,[L8666] and at higher concentrations for palliative treatment of endometrial or renal carcinoma.[L8669]', 'Medroxyprogesterone acetate (MPA) inhibits the production of gonadotropin, preventing follicular maturation and ovulation, which is responsible for it’s ability to prevent pregnancy.[L8660] This action also thins the endometrium.[L8660] MPA reduces nuclear estrogen receptors and DNA synthesis in epithelial cells of the endometrium.[L8660] MPA can also induce p53 dependant apoptosis in certain cancer cell lines,[A186134] and inhibit GABA-A receptors.[A186140]', 'Medroxyprogesterone acetate (MPA) inhibits gonadotropin production, reduces nuclear estrogen receptors and DNA synthesis in epithelial cells of the endometrium, and induces p53 dependant apoptosis in cancer cell lines.[L8660,A186134] MPA oral tablets have a half life of 40-60 hours and other formulations can have half lives that are considerably longer, so the duration of action is long.[A186086,A186095] The therapeutic window is wide as patients may take doses ranging from 5mg orally daily to 1000mg as a depo injection weekly.[L8657,L8660,L8663,L8666,L8669] Long term use of MPA is associated with a reduction in bone density and patients who taking MPA during adolescence may have lower peak bone mass than untreated patients, which can also increase the risk of osteoporosis and fractures in the future.[L8657,L8660,L8663,L8666,L8669]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (17805, 'Domega 3 gm 15 sachets', 'دوميجا 3 جم 15 كيس', '60', NULL, 'Omega 3', 'Fish oil(dha+epa)+selenium+zinc+magnesium+vitamins(c+b1+b2+b6+b12)', 'Nutrixia > falcon group', 'Sachet', 'أكياس', '3 gm', '1', NULL, 'use: 1 sachet to be added to half cup of water once daily.', NULL, NULL, 4086, '2022-08-01', 'Under FDA approval, fish oil pharmaceuticals are typically products consisting of a combination of the omega-3-fatty acids eicosapentaenoic acid (EPA) and docosahexaenoic acid (DHA) and are indicated primarily as an adjunct to diet to reduce triglyceride levels in adult patients with severe (>=500 mg/dL) hypertriglyceridemia [FDA Label, F36].



Under EMA approval, such fish oil pharmaceuticals comprised of virtually the same fish and fish oil derived omega-3-fatty acids EPA and DHA are indicated specifically for (a) adjuvant treatment in secondary prevention after myocardial infarction, in addition to other standard therapy (ie. statins, antiplatelet medicinal products, beta blockers, ACE inhibitors), and (b) as a supplement to diet when dietary measures alone are insufficient to produce an adequate response, particularly with type IV hypertriglyceridemia in monotherapy or type IIb/III in combination with statins, when control of triglycerides is insufficient [L2661]. In addition, prescribing information for EMA approved fish oil pharmaceuticals are also indicated as an adjunct to diet to reduce very high (>=500 mg/dL) triglyceride levels in adult patients, much like similar FDA approved indications [F37, FDA Label].', 'The specific mechanism of action by which the fish oil EPA and DHA acids are capable of reducing serum triglyceride levels is not yet fully understood [FDA Label, A32933]. Nevertheless, it is proposed that such omega-3-fatty acids may not be the preferred substrates of the enzyme diacylglycerol O-acyltransferase that participates in the generation of triglycerides; that  they might interact with nuclear transcription factors that manage lipogenesis; or that their presence and increase in levels can cause cellular metabolism to subsequently shift toward a decrease in triglyceride synthesis and an increase in fatty acid oxidation [A32933]. Moreover, the EPA and DHA acids are also believed to be able to promote apolipoprotein B degradation in the liver through the stimulation of an autophagic process [A32933]. It may also be possible that these fish oil acids can accelerate the clearance of very-low-density lipoprotein (VLDL) particles and chylomicron [A32933]. The combination of all these actions results in fewer VLDL particles being assembled and secreted, which is of considerable importance as VLDL particles are the major endogenous source of triglycerides [A32933].



Moreover, new paradigms of how inflammation is contained and dissipated involve various newly discovered chemical mediators, resolvins, and protectins [A32933]. Such agents are believed to be directly involved in blocking neutrophil migration, infiltration, recruitment, as well as blocking T-cell migration and promoting T-cell apoptosis [A32933]. Additionally, such protectins can also reduce tumor necrosis factor and interferon secretion [A32933]. Of particular importance, however, is the fact that protectins and resolvins are exclusively derived from omega-3-fatty acids and that EPA is the substrate of the resolvins family and DHA can be converted to both resolvins and protectins [A32933]. It is believed that these effects of such fish oil acids underlie the actions that fish oil have demonstrated on eliciting stability for vulnerable inflammatory plaques [A32933].



Finally, fish oil acids have demonstrated certain direct electrophysiological effects on the myocardium [A32933]. In animal studies, it was shown that the ventricular fibrillation threshold could be increased in both animals fed or infused with omega-3-fatty acids [A32933]. Further studies subsequently revealed that such fatty acids could reduce both sodium currents and L-type calcium currents on a cellular and ion channel level [A32933]. It is consequently hypothesized that during ischemia, a reduction in the sodium ion current protects hyperexcitable tissue, and a reduction in the calcium ion current could reduce arrhythmogenic depolarizing currents - and that perhaps the use of EPA and DHA fish oil acids could facilitate such activity [A32933]. For the time being, however, omega-3-fatty acids in pharmaceutical supplement form have not been shown to elicit such protection against heart conditions [L2662].', 'In general, the only practical method of obtaining and increasing the levels of eicosapentaenoic acid (EPA) and docosahexaenoic acid (DHA) fish oil omega-3-fatty acids in the body is to consume them directly from foods and/or dietary supplements [L784]. Having EPA and DHA is important because they facilitate numerous important functions in the human body. As an example, such supplementation of fish oil EPA and DHA demonstrates a legitimate ability to decrease triglyceride levels as their presence in the body can act as poor substrates for the enzymes that are ordinarily responsible for triglyceride synthesis and also inhibit the esterification of other fatty acids, among other mechanisms [FDA Label, L2661].



Moreover, such fish oil acids can become important components of the phospholipids that form the structures of cell membranes [L784]. Specifically, DHA is particularly high in the retina, brain, and sperm [L784]. Additionally, these acids also provide energy for the body and are used to form eicosanoids - signaling molecules that have similar chemical structures to the fish oil fatty acids from which they are derived [L784]. Furthermore, such eicosanoids possess wide-ranging functions in the cardiovascular, pulmonary, immune, and endocrine systems [L784].', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (12892, 'Zithromax 600mg/15ml (200mg/5ml) susp.', 'زيثروماكس 600مجم/15مل 200مجم/5مل معلق', '89', '61', 'Antibiotic.macrolide', 'Azithromycin', 'Pfizer', 'Susp', 'معلق', '600mg', '1', NULL, NULL, NULL, NULL, 3944, '2024-10-14', 'Azithromycin should be used only to treat or prevent infections that are proven or strongly suspected to be caused by susceptible bacteria in order to prevent the development antimicrobial resistance and maintain the efficacy of azithromycin [FDA label].



Azithromycin is indicated for the treatment of patients with mild to moderate infections caused by susceptible strains of the microorganisms listed in the specific conditions below. Recommended dosages, duration of therapy and considerations for various patient populations may vary among these infections. Refer to the FDA label and "Indications" section of this drug entry for detailed information [FDA label]. 



**Adults**:



Acute bacterial exacerbations of chronic obstructive pulmonary disease due to _Haemophilus influenzae_, _Moraxella catarrhalis_ or _Streptococcus pneumoniae_



Acute bacterial sinusitis due to _Haemophilus influenzae_, _Moraxella catarrhalis_ or _Streptococcus pneumoniae_



Community-acquired pneumonia due to _Chlamydophila pneumoniae_, _Haemophilus influenzae_, _Mycoplasma pneumoniae_ or _Streptococcus pneumoniae_ in patients appropriate for oral therapy



Pharyngitis/tonsillitis caused by _Streptococcus pyogenes_ as an alternative to first-line therapy in individuals who cannot use first-line therapy.

Uncomplicated skin and skin structure infections due to _Staphylococcus aureus_, _Streptococcus pyogenes_, or _Streptococcus agalactiae_. Abscesses usually require surgical drainage.



Urethritis and cervicitis due to _Chlamydia trachomatis_ or _Neisseria gonorrhoeae_.



Genital ulcer disease in men due to _Haemophilus ducreyi_ (chancroid). Due to the small number of women included in clinical trials, the efficacy of azithromycin in the treatment of chancroid in women has not been established.



**Pediatric Patients**



Acute otitis media caused by _Haemophilus influenzae_, _Moraxella catarrhalis_ or _Streptococcus pneumoniae_



Community-acquired pneumonia due to _Chlamydophila pneumoniae_, _Haemophilus influenzae_, _Mycoplasma pneumoniae_ or _Streptococcus pneumoniae_ in patients appropriate for oral therapy.



Pharyngitis/tonsillitis caused by _Streptococcus pyogenes_ as an alternative to first-line therapy in individuals who cannot use first-line therapy.', 'In order to replicate, bacteria require a specific process of protein synthesis, enabled by ribosomal proteins [A6505]. Azithromycin binds to the 23S rRNA of the bacterial 50S ribosomal subunit. It stops bacterial protein synthesis by inhibiting the transpeptidation/translocation step of protein synthesis and by inhibiting the assembly of the 50S ribosomal subunit [FDA label], [A14179]. This results in the control of various bacterial infections [A174193], [FDA label].   The strong affinity of macrolides, including azithromycin, for bacterial ribosomes, is consistent with their broad‐spectrum antibacterial activities [A174193].





Azithromycin is highly stable at a low pH, giving it a longer serum half-life and increasing its concentrations in tissues compared to erythromycin [A174175]. 



', 'Macrolides stop bacterial growth by inhibiting protein synthesis and translation, treating bacterial infections [A174175].

Azithromycin has additional immunomodulatory effects and has been used in chronic respiratory inflammatory diseases for this purpose [A174172].



', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23241, 'Nano age 28 ml', 'نانو ايج 28 ملي', '263', NULL, 'Moisturizing topicals', NULL, 'Isis pharma > biotech egypt', 'Unknown', 'غير محدد', '28 ml', '4', NULL, 'anti-aging serum with moisturizing effect active ingredient: 2% hyaluronic acid serum due to the low molecular weight hyaluronic acid nano age improves biodynamic properties of the skin: anti-aging low molecular weight hyaluronic acid provides transport o', NULL, NULL, 654, '2022-07-24', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4438, 'Ethinyl oestradiol 50mcg 20 tab.', 'ايثينيل استراديول 50 مجم 20 قرص', '7', NULL, 'Estrogen', 'Estradiol', 'Kahira', 'Tab', 'أقراص', '50mcg', '2', 'اقراص هرمون الاستروجين -- تستخدم لمنع الحمل', 'ethinyl estradiol systemic is used in the treatment of: breast cancer palliative hypoestrogenism oophorectomy postmenopausal symptoms primary ovarian failure prostate cancer mechanism of action estradiol enters target cells freely (e.g. female organs brea', '6221068010082', NULL, 4884, '2023-02-05', 'Estradiol is indicated in various preparations for the treatment of moderate to severe vasomotor symptoms and vulvar and vaginal atrophy due to menopause, for the treatment of hypoestrogenism due to hypogonadism, castration, or primary ovarian failure, and for the prevention of postmenopausal osteoporosis. It is also used for the treatment of breast cancer (only for palliation therapy) in certain men or women with metastatic disease, and for the treatment of androgen-dependent prostate cancer (only for palliation therapy).[L11485,L11494,L11497] It is also used in combination with other hormones as a component of oral contraceptive pills for preventing pregnancy (most commonly as [DB00977], a synthetic form of estradiol).



**A note on duration of treatment**



Recommendations for treatment of menopausal symptoms changed drastically following the release of results and early termination of the Women''s Health Initiative (WHI) studies in 2002 as concerns were raised regarding estrogen use.[A31626] Specifically, the combined estrogen–progestin group was discontinued after about 5 years of follow up due to a statistically significant increase in invasive breast cancer and in cardiovascular events.[A31627] 



Following extensive critique of the WHI results, Hormone Replacement Therapy (HRT) is now recommended to be used only for a short period (for 3-5 years postmenopause) in low doses, and in women without a history of breast cancer or increased risk of cardiovascular or thromboembolic disease.[A31628] Estrogen for postmenopausal symptoms should always be given with a progestin component due to estrogen''s stimulatory effects on the endometrium; in women with an intact uterus, unopposed estrogen has been shown to promote the growth of the endometrium which can lead to endometrial hyperplasia and possibly cancer over the long-term. 



', 'Estrogen is found in the the breast, uterine, ovarian, skin, prostate, bone, fat, and brain tissues. The main source of estrogen in adult women during the reproductive period of life is the ovarian follicle, which secretes 70 to 500 mcg of estradiol each day. After menopause, however, the majority of endogenous estrogen is produced by transformation of androstenedione (which is secreted by the adrenal cortex) to estrone in the peripheral tissues. Both estrone and its sulphate conjugated form, estrone sulphate, represent the most abundant estrogens found in postmenopausal women.[L11530] 



Estradiol, however, is considerably more potent than estrone and estriol at the estrogen receptor (ER). As a result, the higher estrone concentration in postmenopausal population, can cause various undesirable effects. These effects may include hot flashes, chills, vaginal dryness, mood swings, irregular menstruation, and chills, in addition to sleep problems.[L11530]



Estradiol workings by binding to subtypes of the estrogen receptor: estrogen receptor alpha (ERα) and estrogen receptor beta (ERβ). It also exerts potent agonism of G Protein-coupled estrogen receptor (GPER), which is recognized an important regulator of this drug''s rapid effects. Once the estrogen receptor has bound to its ligand, it enters the nucleus of the target cell, regulating gene transcription and formation of of messenger RNA. This mRNA makes contact with ribosomes producing specific proteins that express the effect of estradiol upon the target cell. Agonism of estrogen receptors increases pro-estrogenic effects, leading to the relief of vasomotor and urogenital symptoms of a postmenopausal or low estradiol state.[A31620,L11485,L11497]



', 'Estradiol acts on the on the estrogen receptors to relieve vasomotor systems (such as hot flashes) and urogenital symptoms (such as vaginal dryness and dyspareunia).[A190402,A190405,A190408]



Estradiol has also been shown to exert favorable effects on bone density by inhibiting bone resorption. Estrogen appears to inhibit bone resorption [A190402,L11560] and may have beneficial effects on the plasma lipid profile.[A190405,L11530] Estrogens cause an increase in hepatic synthesis of various proteins, which include sex hormone binding globulin (SHBG), and thyroid-binding globulin (TBG).  Estrogens are known to suppress the formation of follicle-stimulating hormone (FSH) in the anterior pituitary gland.[L11485] 



**A note on hyper-coagulable state, cardiovascular health, and blood pressure**



Estradiol may cause an increased risk of cardiovascular disease, DVT, and stroke, and its use should be avoided in patients at high risk of these conditions.[L11560]  Estrogen induces a hyper-coagulable state, which is also associated with both estrogen-containing oral contraceptive (OC) use and pregnancy. Although estrogen causes an increase in levels of plasma renin and angiotensin. Estrogen-induced increases in angiotensin, causing sodium retention, which is likely to be the mechanism causing hypertension after oral contraceptive treatment.[A190402]

', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (30514, 'Ezola 20 sachets', 'ايزولا 20 اكياس', '30', NULL, 'Carminative', 'Anise+peppermint+caraway', 'Pharma zad > sagy pharm', 'Sachet', 'أكياس', NULL, '1', NULL, 'carminative', NULL, NULL, 453, '2023-06-24', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19259, 'Fml liquifilm 0.1% eye drops 5 ml', 'اف ام ال قطرة للعين 5 مل', '6', NULL, 'Glucocorticoid', 'Fluorometholone', 'Eipico > allergan', 'Drops', 'نقط', '0.1%', '1', NULL, 'about fluorometholone (eye.prep.) a synthetic glucocorticoid in steroid responsive inflammations of eye( topical anti-inflammatory). mechanism of action of fluorometholone (eye.prep.) the drug exerts its pharmacological action by penetrating and binding t', '6221032330369', NULL, 975, '2022-07-26', 'For the ophthalmic treatment of corticosteroid-responsive inflammation of the palpebral and bulbar conjunctiva, cornea and anterior segment of the globe.', 'There is no generally accepted explanation for the mechanism of action of ocular corticosteroids. However, corticosteroids are thought to act by the induction of phospholipase A2 inhibitory proteins, collectively called lipocortins. It is postulated that these proteins control the biosynthesis of potent mediators of inflammation such as prostaglandins and leukotrienes by inhibiting the release of their common precursor, arachidonic acid. Arachidonic acid is released from membrane phospholipids by phospholipase A2. Their primary target is the cytosolic glucocorticoid receptor. After binding the receptor the newly formed receptor-ligand complex translocates itself into the cell nucleus, where it binds to many glucocorticoid response elements (GRE) in the promoter region of the target genes. The DNA bound receptor then interacts with basic transcription factors, causing the increase in expression of specific target genes.', 'Corticosteroids such as fluorometholone inhibit the inflammatory response to a variety of inciting agents and probably delay or slow healing. They inhibit the edema, fibrin deposition, capillary dilation, leukocyte migration, capillary proliferation, fibroblast proliferation, deposition of collagen, and scar formation associated with inflammation.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23771, 'Novacortin 1% cream 15 gm', 'نوفاكورتين 1% كريم 15جم', '5', NULL, 'Glucocorticoid', 'Hydrocortisone acetate', 'Pharopharma', 'Cream', 'كريم', '1%', '1', 'كورتيزون', 'this medication is used to treat hemorrhoids and itching/swelling in the rectum and anus. it is also used with other medications to treat certain intestinal problems (such as ulcerative colitis of the rectum and other rectal/anal inflammatory conditions).', '6221094000149', NULL, 5668, '2022-07-07', 'For the relief of the inflammatory and pruritic manifestations of corticosteroid-responsive dermatoses. Also used to treat endocrine (hormonal) disorders (adrenal insufficiency, Addisons disease). It is also used to treat many immune and allergic disorders, such as arthritis, lupus, severe psoriasis, severe asthma, ulcerative colitis, and Crohn''s disease.', 'Hydrocortisone binds to the cytosolic glucocorticoid receptor. After binding the receptor the newly formed receptor-ligand complex translocates itself into the cell nucleus, where it binds to many glucocorticoid response elements (GRE) in the promoter region of the target genes. The DNA bound receptor then interacts with basic transcription factors, causing the increase in expression of specific target genes. The anti-inflammatory actions of corticosteroids are thought to involve lipocortins, phospholipase A2 inhibitory proteins which, through inhibition arachidonic acid, control the biosynthesis of prostaglandins and leukotrienes. Specifically glucocorticoids induce lipocortin-1 (annexin-1) synthesis, which then binds to cell membranes preventing the phospholipase A2 from coming into contact with its substrate arachidonic acid. This leads to diminished eicosanoid production. The cyclooxygenase (both COX-1 and COX-2) expression is also suppressed, potentiating the effect. In other words, the two main products in inflammation Prostaglandins and Leukotrienes are inhibited by the action of Glucocorticoids. Glucocorticoids also stimulate the lipocortin-1 escaping to the extracellular space, where it binds to the leukocyte membrane receptors and inhibits various inflammatory events: epithelial adhesion, emigration, chemotaxis, phagocytosis, respiratory burst and the release of various inflammatory mediators (lysosomal enzymes, cytokines, tissue plasminogen activator, chemokines etc.) from neutrophils, macrophages and mastocytes. Additionally the immune system is suppressed by corticosteroids due to a decrease in the function of the lymphatic system, a reduction in immunoglobulin and complement concentrations, the precipitation of lymphocytopenia, and interference with antigen-antibody binding.', 'Hydrocortisone is the most important human glucocorticoid. It is essential for life and regulates or supports a variety of important cardiovascular, metabolic, immunologic and homeostatic functions. Topical hydrocortisone is used for its anti-inflammatory or immunosuppressive properties to treat inflammation due to corticosteroid-responsive dermatoses. Glucocorticoids are a class of steroid hormones characterised by an ability to bind with the cortisol receptor and trigger a variety of important cardiovascular, metabolic, immunologic and homeostatic effects. Glucocorticoids are distinguished from mineralocorticoids and sex steroids by having different receptors, target cells, and effects. Technically, the term corticosteroid refers to both glucocorticoids and mineralocorticoids, but is often used as a synonym for glucocorticoid. Glucocorticoids suppress cell-mediated immunity. They act by inhibiting genes that code for the cytokines IL-1, IL-2, IL-3, IL-4, IL-5, IL-6, IL-8 and TNF-alpha, the most important of which is the IL-2. Reduced cytokine production limits T cell proliferation. Glucocorticoids also suppress humoral immunity, causing B cells to express lower amounts of IL-2 and IL-2 receptors. This diminishes both B cell clonal expansion and antibody synthesis. The diminished amounts of IL-2 also leads to fewer T lymphocyte cells being activated.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (2871, 'Codiphan 10mg/5ml syrup 120ml', 'كوديفان 120مل شراب', '10', '5', 'Antitussive', 'Dextromethorphan', 'El nile.', 'Syrup', 'شراب', '10mg', '1', 'مهدي للسعال.', 'about dextromethorphan a synthetic morphine (((dextrorotatory methyl ether of levorphanol))) derivative a synthetic narcotic antitussive. mechanism of action of dextromethorphan dextromethorphan hydrobromide is a selective antitussive which raises thresho', '6221077053315', NULL, 1295, '2024-11-06', 'Dextromethorphan is indicated in combination with [brompheniramine] and [pseudoephedrine] in the treatment of coughs and upper respiratory symptoms associated with allergies or the common cold.[L14366] Dextromethorphan is also used in combination with [guaifenesin] as an over-the-counter product to relieve a cough.[L14369] Dextromethorphan in combination with [quinidine] is indicated in the treatment of pseudobulbar affect.[L14363]', 'Dextromethorphan is a low-affinity uncompetitive NMDA antagonist and sigma-1 receptor agonist.[L14363] It is also an antagonist of α3/β4 nicotinic receptors.[A10589] However, the mechanism by which dextromethorphan''s receptor agonism and antagonism translate to a clinical effect is not well understood.[A215397]', 'Dextromethorphan is an opioid-like molecule indicated in combination with other medication in the treatment of coughs and pseudobulbar affect.[L14366,L14369,L14363] It has a moderate therapeutic window, as intoxication can occur at higher doses.[A215402] Dextromethorphan has a moderate duration of action.[L14363] Patients should be counselled regarding the risk of intoxication.[A215402]', 'DrugBank', 0, 0, 0);
